RecMedia["TNG_S1E1+2"] = {
	itemDisplayName = "VHS: ST:TNG S1.01+02",
	title = "Star Trek:TNG S1.01+02 - Encounter at Farpoint",
	subtitle = nil,
	author = nil,
	extra = nil,
	spawning = 0,
	category = "Retail-VHS",
	lines = {
		{ text = "(PICARD) Captain's log, stardate 41153.7.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Our destination is planet Deneb Four, beyond which lies the great unexplored mass of the galaxy.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "My orders are to examine Farpoint, a starbase built there by the inhabitants of that world.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Meanwhile, I am becoming better acquainted with my new command, this Galaxy Class USS Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I am still somewhat in awe of its size and complexity.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "As for my crew, we are short in several key positions, most notably a first officer, but I am informed that a highly experienced man, one Commander William Riker, will be waiting to join our ship at our Deneb Four destination.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You will agree, Data, that Starfleet's orders are difficult?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(DATA) Difficult?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Simply solve the mystery of Farpoint Station.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "As simple as that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(TROI) Farpoint Station.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Even the name sounds mysterious.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "It's hardly simple, Data, to negotiate a friendly agreement for Starfleet to use the base while at the same time snoop around finding how and why the life form there built it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Inquiry.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The word snoop?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Data, how can you be programmed as a virtual encyclopedia of human information without knowing a simple word like snoop?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Possibility, a kind of human behaviour I was not designed to emulate.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It means to spy, to sneak.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Ah!", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "To seek covertly, to go stealthily, to slink, slither", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Exactly, yes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Glide, creep, skulk, pussyfoot, gumshoe.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Captain, I'm sensing a powerful mind.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Collision alert sounds)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(TORRES) Something strange on the detector circuits.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(A massive barrier appears in front of them in space)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "It registers as solid, Captain.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Or an incredibly powerful forcefield.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "But if we collide with either it could be very", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Shut off that damned noise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Go to Yellow Alert.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(WORF) Shields and deflectors up, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The barrier ripples like chain mail)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Reverse power, full stop.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Controls to full stop, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now reading full stop, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(There's a flash of light, and an Elizabethan era soldier appears, complete with breast plate and plumed hat)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Q) Thou are notified that thy kind hath infiltrated the galaxy too far already.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Thou art directed to return to thine own solar system immediately.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "That's quite a directive.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Would you mind identifying what you are?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We call ourselves the Q. Or thou mayst call me that.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "It's all much the same thing.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(The same force barrier stops two people exiting the turbolift)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I present myself to thee as a fellow ship captain, that thou mayst better understand me.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Go back whence thou camest.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(to Helmsman) Stay where thou art!", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(And the helmsman is frozen solid, phaser in hand)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Data, call medics.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He's frozen.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "He would not have injured you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do you recognise this, the stun setting?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Knowing humans as thou dost, Captain, wouldst thou be captured helpless by them?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Now, go back or thou shalt most certainly die.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Captain's log, supplementary.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The frozen form of Lieutenant Torres has been rushed to sickbay.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The question now is the incredible power of the Q being.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do we dare oppose it?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, thy little centuries go by so rapidly.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Perhaps thou will better understand this.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(A flash of light and he is wearing a 20th century US military uniform, with a cigarette in his hand)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Actually, the issue at stake is patriotism.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You must return to your world and put an end to the commies.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "All it takes is a few good men.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "What?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That nonsense is centuries behind us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But you can't deny that you're still a dangerous, savage child race.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Most certainly I deny it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I agree we still were when humans wore costumes like that, four hundred years ago.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "At which time you slaughtered millions in silly arguments about how to divide the resources of your little world.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "And four hundred years before that you were murdering each other in quarrels over tribal god-images.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Since there are no indications that humans will ever change.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "But even when we wore costumes like that we'd already started to make rapid progress.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Oh yeah?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You want to review your rapid progress?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(Flash, and a change into a padded suit)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Rapid progress, to where humans learned to control their military with drugs.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Sir, sickbay reports Lieutenant Torres's condition is better.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Oh, concern for one's fellow comrade.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "How touching.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "And now a personal request, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Permission to clean up the bridge.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(TASHA) Lieutenant Worf is right, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "As Security Chief I can't just stand here and let", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Yes you can, Lieutenant Yar.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(taking a snort of something) Oh, better.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "And later, on finally reaching deep space, humans of course found enemies to fight out there too.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "And to broaden those struggles you again found allies for still more murdering.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "The same old story, all over again.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "No. The same old story is the one we're meeting now.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Self-righteous life forms who are eager not to learn but to prosecute, to judge anything they don't understand or can't tolerate.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What an interesting idea.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Prosecute and judge.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Suppose it turns out we understand you humans only too well.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "We've no fear of what the true facts about us will reveal.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Facts about you?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Splendid, splendid, Captain!", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You're a veritable fountain of good ideas.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "There are preparations to make, but when we next meet, Captain, we'll proceed exactly as you suggest.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(A flash and he is gone)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Sir, respectfully submit our only choice is to fight.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Fight, or try to escape.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Sense anything, Commander?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Its mind is much too powerful.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Recommend we avoid contact.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "From this point, no station aboard, repeat no station, for any reason will make use of transmitted signals or intercom.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We'll try and take them by surprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Let's see what this galaxy class starship can do.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(to Worf) Lieutenant, inform engine room to prepare for maximum acceleration.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Records search, Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Results of detaching saucer section at high warp velocity.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Inadvisable at any warp speed, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Search theoretical.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It is possible, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "But absolutely no margin for error.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Using print-out only, notify all decks to prepare for maximum acceleration.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now hear this, Maximum, you're entitled to know, means that we'll be pushing our engines well beyond safety limits.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Our hope is to surprise whatever that is out there, try and outrun it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Our only other option is to tuck tail between our legs and return to Earth as they demand.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Worf makes his trip to Engineering and back)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Engine room ready, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The board shows green, Captain.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "All go.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Stand by.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Engage.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(They swoop along and past the barrier, which then turns into a ball and chases them)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Velocity warp nine point two.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Heading three five one mark eleven, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Steady on that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The hostile is now giving chase, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Accelerating fast.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "We're now at warp nine point three, sir, which takes us past the red line, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Continue accelerating.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Counsellor, at this point I'm open to guesses about what we've just met.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It it felt like something beyond what we'd consider a life form.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Beyond?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Very, very advanced, sir, or certainly very, very different.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "We're at nine point four, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hostile is now beginning to overtake us, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Are you sure?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hostile's velocity is already warp nine point six, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Shall I put them on the main viewer?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Reverse angle.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Magnifying viewer image.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Hostile's velocity is nine point seven, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Worf, inform the engine room we need more.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Engine room attempting to comply, sir, but they caution us", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Go to yellow alert.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Arm aft photon torpedoes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Place them on ready status.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Torpedoes to ready, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Hostile now at warp nine point eight, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Our velocity is only nine point five, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Projection, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "We may be able to match hostile's nine point eight, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "But at extreme risk.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Now reading the hostile at warp nine point nine, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Now hear this.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Print-out message, urgent, all stations on all decks.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Prepare for emergency saucer sever.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You will command the saucer section, Lieutenant.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I am a Klingon, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "For me to seek escape when my Captain goes into battle.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You are a Starfleet officer, Lieutenant.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Make the mark, Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Note in ship's log that at this startime, I'm transferring command to the battle bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Picard, Data, Yar and Troi leave the Bridge to unnamed crew)", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain's log, stardate 41153.7.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Preparing to detach saucer section.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "so that families and the majority of the ship's company can seek relative safety while the vessel's stardrive, containing the battle bridge and main armaments, will turn back and confront the mystery that is threatening us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Battle Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Families of various races make their way along corridors as the bridge crew settle into a more compact Bridge)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Lieutenant, your torpedoes must detonate close enough to the hostile to blind it at the moment we separate.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Understood, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(O'BRIEN) All decks acknowledging, sir.", r = 0.8, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Worf, this is the Captain.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) At the moment of separation, we will reverse power just enough to get your saucer section out ahead and clear of us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Understood, Captain.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Battle Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Torpedoes away, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Eight red dots shoot out from the stern)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Begin countdown.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mark.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Starship separation in six, five, four, three, two, one.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Gracefully, the huge saucer lifts away from the body of the ship)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Separation is successful, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(The main body does a hand-brake turn and heads back to their pursuer.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The torpedoes detonate.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Torpedoes have detonated, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Let's come to a stop.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Reverse power.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Reverse power.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Decelerating.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Dead stop.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We'll hold this position and wait for them.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That will bring them here in just minutes, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Will we make a fight of it, Captain?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "If we can at least damage their ship we'll have a chance", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant, are you recommending we fight a life form that can do all those things?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'd like to hear your advice.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I spoke before I thought, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "We should look for some way to distract them from going after the saucer.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "All forward motion stopped, sir.", r = 0.8, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you, conn.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Commander, signal the following in all languages and on all frequencies.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We surrender.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "State that we are not asking for any terms or conditions.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "All language forms and frequencies.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(The ball reaches them and surrounds them with it's barrier.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The ship shakes)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Courtroom]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The crew find themselves in a room with a noisy audience.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "One of the future soldiers that Q pretended to be, fires his machine gun for silence.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "An Oriental man takes charge)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(BAILIFF) The prisoners will all stand.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard sits down)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Historically intriguing, Captain.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Very, very accurate.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Mid twenty first century.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The post-atomic horror.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "All present, stand and make respectful attention to honoured Judge.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Careful, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "This is not an illusion or a dream.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "But these courts happened in the past.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I don't understand either, but this is real.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(SOLDIER) Get to your feet, criminals!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(From the distance, a spotlight reveals a figure approaching, dressed in red, and sitting on a throne carried on hydraulics for effect)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "At least we are acquainted with the judge, Captain.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Q gestures the crowd to sit)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Attention!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "On your feet.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Attention!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Tasha disarms him)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(to soldier) You are out of order.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(another soldier kills him) The prisoners will not be harmed until they are found guilty.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Dispose of that.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Can we assume you mean this will be a fair trial?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, absolutely equitable.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Proceed.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Before this gracious court now appear these prisoners to answer for the multiple and grievous savageries of their species.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How plead you, criminal?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If I may, Captain?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Objection, your honour.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "In the year 2036, the new United Nations declared that no Earth citizen could be made to answer for the crimes of his race or forbears.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Q (JUDGE)) Objection denied.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is a court of the year 2079, by which time more rapid progress had caused all United Earth nonsense to be abolished.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Tasha, no.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I must!", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Because I grew up on a world that allowed things like this court.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "And it was people like these that saved me from it.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "This so-called court should get down on its knees to what Starfleet is, what it represents.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Q freezes Tasha)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You barbarian!", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "This woman", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Criminals keep silence!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You've got a lot to learn about humans if you think you can torture us or frighten us into silence.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Will she live?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Uncertain.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "When he froze Lieutenant Torres on the Bridge, we had our Sickbay to help thaw him out.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "You will answer the charges, criminals.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Or what?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Or this?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Her death?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Or worse?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You promised the prisoners will not be harmed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We plead nothing so long as you break your own rules.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I suggest you centre your attention on this trial, Captain.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "It may be your only hope.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "I suggest you are now having second thoughts it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You are considering that if you conduct a fair trial, which was your promise, you may lose.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lose?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Yes, even though you're judge and prosecutor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And jury.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Accepted, so long as you keep to your agreement.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And assaulting prisoners is hardly a fair trial.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is a merciful court.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(Tasha is thawed) Silence!", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Continuing these proceedings, I must caution you that legal trickery is not permitted.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "This is a court of", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "court of fact!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We humans know our past, even when we're ashamed of it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I recognise this court system as the one that agreed with that line from Shakespeare.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Kill all the lawyers.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Which was done.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Which led to the rule guilty until proven innocent.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Of course.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Bringing the innocent to trial would be unfair.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You will now answer to the charge of being a grievously savage race.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Grievously savage could mean anything.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I will answer only specific charges.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Are you certain you want a full disclosure of human ugliness?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "So be it, fool!", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Present the charges.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Criminal, you will read the charges to the court.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard takes his time to read the pad that is handed to him, then gives it back)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I see no charges against us, Your Honour.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You are out of order!", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(Guns are placed at Data and Troi's heads)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Soldiers, you will press those triggers if this criminal answers with any word other than guilty.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Criminal, how plead you?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Guilty.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Provisionally.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Court will hear the provision.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "We question whether this court is abiding by its own trial instructions.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Have I have permission to have Commander Data repeat the record?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "There will be no legal trickery", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "These will be your own words, your Honour.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What exactly what followed his Honour's statement that the prisoner will not be harmed?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The Captain had asked the question.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Can we assume you mean this will be a fair trial?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "And in reply the judge stated, yes, absolutely equitable.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Irrelevant testimony, entirely irrelevant.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Alright!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We agree there is evidence to support the court's contention that humans have been savage.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Therefore I say test us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Test whether this is presently true of humans.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I see, I see.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "And so you petition the Court to accept you and your comrades as proof of what humanity has become.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "There must be many ways we can be tested.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We have a long mission ahead of us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Another brilliant suggestion, Captain.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "But your test hardly requires a long mission.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Your immediate destination offers far more challenge than you can possibly imagine.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Yes, this Farpoint station will be an excellent test.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "All present, respectfully stand.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This trial is adjourned, to allow the criminals to be tested.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "This honourable court is adjourned.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Stand respectfully.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, you may find you are not nearly clever enough to deal with what lies ahead for you.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "It may have been better to accept sentence here.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "[Battle Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(O'Brien is unaware the four were ever away)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What is present course, conn?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It's what it's been all along, sir.", r = 0.8, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Direct heading to Farpoint Station.", r = 0.8, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Confirm.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "We are on that heading, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Know anything about Farpoint Station, sir?", r = 0.8, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sounds like a fairly dull place.", r = 0.8, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We've heard that we may find it rather interesting.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Personal log, Commander William Riker.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Stardate 41153.7.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The USS Hood has dropped me off at Farpoint Station, where I await the arrival of the new USS Enterprise to which I have been assigned as First Officer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Meanwhile I have been asked to visit the Farpoint Administrator's Office, in the old city.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Zorn's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(It is a standard wooden room, a desk with a bowl of fruit on it.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Zorn is humanoid in loose clothing with long straight hair)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(ZORN) Ah, Commander Riker, I thought you'd want to know we've still no word from your vessel.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I trust we made your waiting comfortable?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(RIKER) Luxurious is more like it.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Good.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(gets muddled over a handshake) Good.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Would I seem ungrateful if I ask for some information?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Anything.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Fascinating, the advanced materials used in constructing this space station.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Your energy supply must be as abundant as I've heard.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Geothermal energy is the one great blessing of this planet.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll have all the details of that sent to your quarters.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thank you.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "But it still seems incredible to me that you could have constructed this station so rapidly and so, so perfectly suited to our needs.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Would your care for an Earth delicacy, Commander?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(looking at the bowl of fruit) If there's an apple?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'm sorry, Commander.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It doesn't matter.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What I was saying was (suddenly there is a second bowl of apples) I'll be damned.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Ah yes, there was another selection here", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Groppler, I could have sworn it wasn't here a minute ago.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And did your failure to notice it make it unwelcome?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Not at all, Groppler.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I trust it will be the same with Farpoint Station, Commander.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A few easily answered questions about it won't make Starfleet appreciate it less.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And it's delicious.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Thank you.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Good morning, Groppler Zorn.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Good morning.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Riker leaves with his apple)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(to the air) You have been told not to do that.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why can't you understand?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It will arouse their suspicion.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And if that happens, we will have to punish you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We will, I promise you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Market place]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Doctor Crusher!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(WESLEY) Mother, it's Commander Riker.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Hello, Wes.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Enjoying Farpoint Station?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I saw you.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I thought I might join you for a stroll.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(CRUSHER) Actually, we were about to do some shopping.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I've been meaning to visit the mall myself.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Of course.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "If you're wondering about Mom, Commander Riker, she's not unfriendly.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "She's just shy around men she doesn't know.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Wesley!", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I believe that means he would like us to be friends.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I'm willing, Doctor.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Although we're not officially part of the Enterprise, I thought there might be something useful we can do while we wait.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Useful?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "How, Commander?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Investigating some things that I've noticed since I've been here.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The last was a piece of fruit.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(examining a bolt of cloth) Gold would be lovely with this.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I am sure, Commander, there are reasons for a first officer to want to demonstrate his energy and alertness to a new captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "But since my duty and interests are outside the command structure", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(Suddenly the bolt of cloth has a gold pattern on it)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Isn't it remarkable they happen to have exactly what you asked for?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Thank you.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I'll take the entire bolt.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Send it to our starship when it arrives.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Charge to Doctor Crusher.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(they walk away from the stall)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Let's see, where were we?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I was accusing you of inventing work in order to curry favour with our new captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I apologise.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Mom, that gold pattern wasn't there.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Maybe this is something Jean-Luc would like looked into.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Jean-Luc Picard?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You know the Captain?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "When I was little, he brought my father's body home to us.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Yes, Wes, long, long ago.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "A pleasure to meet you, Commander.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "You will excuse us.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "My pleasure, Doctor.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Wes.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Sir?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "See you on board.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(Crusher and Wesley leave Riker, and another uniformed man arrives, wearing a device across his eyes.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(LAFORGE) Sir, the Enterprise is arriving.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Is this an official report, Lieutenant?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Sorry, Commander.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir, Lieutenant La Forge reporting.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Enterprise arriving, but without the saucer section, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Stardrive section only?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What happened?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I don't know, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain Picard has signalled for you to beam up immediately.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Our new captain doesn't waste time.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's a good idea.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Thank you, Lieutenant.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(moves into a clear space) Enterprise, this is Commander Riker at Farpoint Station.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Standing by to beam up.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Riker materialises)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Lieutenant Yar of Security, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Captain Picard will see you on the Battle Bridge.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "With the saucer gone, I assume something interesting happened on your way here?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Turbolift]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Battle bridge.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "That's for the Captain to explain, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Battle bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do we have clearance?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir, into the standard parking orbit.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Make it so.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Commander Riker, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Riker, WT, reporting as ordered, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Is the viewer ready?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "All set up, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "We'll first bring you up to date on a little adventure we had on our way here, Commander.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Then we'll talk.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Welcome aboard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(to Riker) This way, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(She sits him down in front of a screen and runs a tape of the Q encounter on the Bridge)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(On monitor) Thou art directed to return to thine own solar system immediately.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Go back or thou shalt most certainly die.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You can't deny you're still a dangerous, savage, child race.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Message from the saucer module, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It will arrive here in fifty one minutes.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Inform them we'll connect as soon as they arrive.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And sent the Commander to me when he's finished.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Picard leaves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(On monitor) And four hundred years before that, you were murdering each other in quarrels over tribal god images.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Since then there are no indications that humans will ever change.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "There are preparations to make, and when we next meet, Captain, we'll proceed exactly as you suggest.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "He calls that a little adventure?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Battle ready room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Come.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Not exactly a run of the mill happening, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It seems we're alive only because we have been placed on probation.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "A very serious kind of probation.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(answers comm.) Go.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) The saucer module is now entering orbit with us, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Acknowledged.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Commander Riker will conduct a manual docking.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Picard out.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You've reported in, haven't you?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You are qualified?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Then I mean now, Commander.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Battle bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They say you will be doing this manually, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "No automation.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "As ordered.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(They approach the saucer from astern, of course)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Velocity to one half metre per second.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Adjust pitch angle, negative three degrees.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Watch your roll angle, conn.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "All stations, prepare for reconnection.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Thrusters to station keeping.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "All velocity zero.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Her inertia should do the job now.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Lock up now.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(The clamps rise and the two parts of the ship meet again)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Ready room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A fairly routine manoeuvre but you handled it quite well.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I hope I showed some promise.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I do have some questions for you though.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir, I thought you might.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I see in your file that Captain DeSoto thinks very highly of you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "One curious thing, however, you refused to let him beam down to Altair Three.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "In my opinion, sir, Altair Three was too dangerous to risk exposing the Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I see.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "A Captain's rank means nothing to you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Rather the reverse, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "But a Captain's life means a great deal to me.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Isn't it just possible that you don't get to be a Starfleet Captain without knowing whether it's safe to beam down or not?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Isn't it a little presumptuous of a first officer to second guess his captain's judgment?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Permission to speak candidly, sir?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Always.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Having been a first officer yourself, you know that assuming that responsibility must by definition include the safety of the captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I have no problem with following any rules you lay down, short of compromising your safety.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And you don't intend to back off that position?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No, sir,", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "One further thing.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "A special favour.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Anything, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Using the same kind of strength you showed with Captain DeSoto, I would appreciate it if you can keep me from making an ass of myself with children.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'm not a family man, Riker, and yet, Starfleet has given me a ship with children aboard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And I don't feel comfortable with children.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But, since a captain needs an image of geniality, you're to see that's what I project.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Welcome to the Enterprise, Commander Riker.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Naturally I've heard of your case.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The visor implants you wear", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Is a remarkable piece of bio-electronic engineering by which I quote see much of the EM spectrum ranging from simple heat and infrared through radio waves et cetera, et cetera, and forgive me if I've said and listened to this a thousand times before.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Without the Visor, Geordi La Forge's eyes are white sightless orbs, and he has flashing red lights at his temples)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You've been blind all your life?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I was born this way.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "And you've felt pain all the years that you've used this?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "They say it's because I use my natural sensors in different ways.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well, I see two choices.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The first is painkillers.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Which would affect how this works.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "No. Choice number two?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Exploratory surgery.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Desensitise the brain areas troubling you.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Same difference.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "No, thank you, Doctor.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I understand.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "See you.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Riker enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Yes sir?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Where will I find Commander Data?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Commander Data is on special assignment, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "He's using our shuttlecraft to transfer an admiral over to the Hood.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "An admiral?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "He's been aboard all day, sir, checking over medical layout.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why a shuttlecraft?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Why wouldn't he just beam over?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I suppose he could, sir, but the Admiral's a rather remarkable man.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(A very old man in a cardigan is shuffling along.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "He is played by DeForrest Kelley, so of course we name this character -)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(MCCOY) Have you got some reason you want my atoms scattered all over space, boy?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "But at your age, sir, I thought you shouldn't have to put up with the time and trouble of a shuttlecraft.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Hold it right there, boy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sir?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "What about my age?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sorry, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "If that subject troubles you", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Troubles me?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What's so damned troubling about not having died?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How old do you think I am?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "One hundred thirty seven years, Admiral, according to Starfleet records.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Explain how you remember that so exactly.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I remember every fact I am exposed to, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I don't see any points on your ears, boy, but you sound like a Vulcan.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I'm an android.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Almost as bad.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I thought it was generally accepted, sir, that Vulcans are an advanced and most honourable race.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "They are, they are.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And damned annoying at times.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Well, this is a new ship, but she's got the right name.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now you remember that, you hear.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I will, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "You treat her like a lady, and she'll always bring you home.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Did you signal the Hood, Commander Riker?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Your exact message.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Bon voyage mon ami.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What was my answer, computer?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) You're wasting time, Captain.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(appears on viewscreen) Or did you think I was gone?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Lieutenant!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do you intend to blast a hole through the viewer?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If the purpose of this is to test humans, your honour, we must proceed in our own way.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) You are dilatory.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You have twenty four hours.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Any further delay and you risk summary judgement against you, Captain.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Sorry, sir", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You reacted fast, Mister Worf.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "But futilely.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I will learn to do better, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Of course you will.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We've a long voyage ahead of us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What do we do now, Captain?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "With them monitoring our every move and every word.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We do exactly what we would do if this Q never existed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If we're going to be damned, let's be damned for what we really are.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Personal log, stardate 41153.8.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Of the twenty four hours Q allotted us to prove ourselves, eleven have now passed without incident.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And yet I cannot forget Q's prediction that we will face some critical test.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Ready room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This planet's interior heat provides an abundance of geothermal energy, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "But it's about all this world does offer.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And it's your belief that this is what made it possible for them to construct this base to Starfleet standards?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We have to assume that they've been trading their surplus energy for the construction materials used here.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "According to our ship's scans, many of the materials used are not found on this world.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Perhaps it's like those incidents you describe in your report as almost magical attempts to please us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Those events did happen, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "None of it suggests anything threatening.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If only every life form had as much desire to please.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Ready to beam down?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm looking forward to meeting this Groppler Zorn.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I have a feeling there's more to it than just pleasing us, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "As if it's something Q is doing to trick us?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Over here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I've asked the Counsellor to join us in this meeting.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "May I introduce our new First Officer, Commander William Riker.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Commander Riker, this is our ship's Counsellor, Deanna Troi.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Do you remember what I taught you, Imzadi?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Can you still sense my thoughts?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "A pleasure, Commander.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Likewise, Counsellor.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Have the two of you met before?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We have, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Excellent.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I consider it important for my key officers know each other's abilities.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(They step into the turbolift)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "We do, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "We do.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) I, too, would never say goodbye, Imzadi.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Zorn's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "My crew and I need a bit more information before we make our recommendations to Starfleet.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No objections to that, but but I'm puzzled over you bringing a Betazoid to this.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If her purpose, sir, is to probe my thoughts", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I can sense only strong emotions, Groppler.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I am only half Betazoid.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "My father was a Starfleet officer.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Well, I have nothing to hide, of course.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Good, since we admire what we've seen of your construction techniques.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Starfleet may be interested in your constructing starbases elsewhere also.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We are not interested in building other facilities.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If I may, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Then a trade, Groppler?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Some things you need in return for lending us architects and engineers who can demonstrate your techniques.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Bandi do not enjoy leaving their home world.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If Starfleet cannot accept that small weakness, then we will be forced, unhappily, to seek an alliance with someone like the Ferengi, or", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Troi gasps)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Counsellor?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What is it?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do you want it described here, sir?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Yes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No secrets here if we're to be all to be friends.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Agreed, Groppler?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We ourselves have nothing to hide, of course.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Pain.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Pain.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Loneliness.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Terrible loneliness.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Despair.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I'm not sensing the Groppler, sir, or any of his people, but it's something very close to us here.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "The source of this, Groppler.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do you have any idea?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No. No, absolutely not.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And I find nothing helpful or productive in any of this!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And that's it?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No other comment?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well what do you expect of us?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We offer you a base designed to your needs, luxurious even by human standards", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "While evading even our simplest questions about it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We'll adjourn for now while we all reconsider our positions.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, the Ferengi would be very interested in a base like this.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Fine.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I hope they find you as tasty as they did their past associates.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ensign, can you help me find Commander Data?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I understand he's somewhere on this deck.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(ENSIGN) This way, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You must be new to these Galaxy class starships, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(to wall) Tell me the location of Commander Data.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(COMPUTER) Lieutenant Commander Data now located in Holodeck area 4J.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And as you see, sir, it's pointing you that way.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thank you", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You're welcome, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(She appreciates the sight as he walks away)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The next hatchway on your right.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thank you.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You're welcome, Commander Riker.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And if you care to enter, Commander?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I do.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(The holodeck doors open to show trees and vegetation.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "There is birdsong)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Forest]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Hello?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(He crosses a stream by stepping stones.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "One wobbles.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Someone is trying to whistle 'Pop goes the Weasel'.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Riker finishes the tune)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Marvellous.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "How easily humans do that.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I still need much practice.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "There are some puzzles down on the planet that the Captain wants answered.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "He suggests that I take you with me on the away team that I'll be leading.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I shall endeavour to function adequately, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Yes.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "When the captain suggested you, I looked up your record.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "A wise procedure, sir, always.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Then your rank of Lieutenant Commander is honorary?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Starfleet class of '78.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Honours in probability mechanics and exobiology.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Your file says that you're an", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Machine, Correct, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Does that trouble you?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "To be honest, yes, a little.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Understood, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Prejudice is very human.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Now that does trouble me.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Do you consider yourself superior to us?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I am superior, sir, in many ways, but I would gladly give it up to be human.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Nice to meet you, Pinocchio.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "A joke.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Ah. Intriguing.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "You're going to be an interesting companion, Mister Data.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "This woodland pattern is quite popular, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Perhaps because it duplicates Earth so well.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Coming here almost makes me feel human myself.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I didn't believe these simulations could be this real.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Much of it is real, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "If the transporters can convert our bodies to an energy beam, then back to the original pattern again", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Yes, of course.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And these rocks and vegetation have much simpler patterns.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Correct, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The rear wall.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I can't see it.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We're right next to it.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(He throws a rock at it, and the image pixilates on impact)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Incredible!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) Commander Riker!", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(at the stream) Commander Riker, isn't this great?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "This is one of the simpler patterns.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "They've got thousands more.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Some you just can't believe.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Careful, the next rock is loose!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Wesley falls in the water)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Wesley!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Data rushes down and hauls the boy out with one hand)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Wow!", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Mister Data has agreed to join me on the away team, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Very good, Commander.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir, maybe I should get something to wipe this water up.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Good idea.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There's a low gravity gymnasium, too.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "It'd be hard to get bored on this ship.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Good.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Mom, could you get me a look at the Bridge?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "That's against the Captain's standing orders.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Are you afraid of the Captain too?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I certainly am not.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "But Captain Picard is a pain, isn't he?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Your father liked him very much.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Great explorers are often lonely.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "No chance to have a family.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Just a look, at the Bridge.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I'll stay in the turbolift when the doors open.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I won't get off.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "You are asking for trouble, Wes.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "We'll see what we can do.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Market place]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Recommend that someone could begin by examining the underside of the station, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Our sensors do show some passages, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Perhaps you and I?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Tasha, you and the Counsellor.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And, Geordi, I want your eyes down there.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(to Data) You and I will start with topside.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(As the three explore underground)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Have you noticed anything unusual?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I can't see as well as Geordi, sir, but so far the material seems rather very ordinary.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Construction records?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Construction records show this to be almost identical to that which Starfleet uses.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Offscreen) Team Leader.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Passageway]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We've found something interesting.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "We're in a passageway directly under the station, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "But these tunnel walls are something I've never seen before, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Marketplace]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How are you examining them?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) In every way.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Passageway]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Microscopically, thermally, electromagnetically.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "None of it is familiar.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) What about you, Troi?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Sir, I've avoided opening my mind.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Whatever I felt in the Groppler's office became very uncomfortable.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Marketplace]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm sorry, Counsellor, but you must.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We need more information.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Passageway]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Pain.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Such pain!", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Pain!", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Marketplace]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Hang on, I'm coming.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Enterprise, lock us onto her signal.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Passageway]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Data and Riker beam into the passageway)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I'm sorry.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Close your mind to the pain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Unhappiness.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Terrible despair.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Who?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I don't know.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "No life form anything like us.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "What in the hell kind of place is this?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Geordi, what do you see?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Well, it's of no material I recognise, sir, or have even heard of.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(They carry on exploring)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The turbolift doors open, and Wesley is standing there)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Children are not allowed on the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Crusher appears from where she was hiding)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Permission to report to the captain", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Doctor Crusher.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Sir, my son is not on the Bridge.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "He merely accompanied me on the turbolift.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Your son?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "His name's Wesley.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "You last saw him years ago when", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Well, as long as he's here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I knew your father, Wesley.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Want a look around?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Wesley steps onto the Bridge)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "But don't touch anything!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Try it out.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(the captain's chair) The panel on your right is for log entries, library computer access and retrieval, viewscreen control, intercoms, and so on.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Here we have", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And here, the backup conn and ops panels, plus shield and armoury controls.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "The forward viewscreen is controlled from the ops position there", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Which uses high resolution, multi-spectral imaging sensor systems", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "How the hell do you know that, boy?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Perimeter alert, Captain!", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Wesley!", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I'm sorry.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "You shouldn't have touched anything", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Off the bridge!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Both of you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You have a perimeter alert, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "As my son tried to tell you!", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(Crusher and Wesley step back into the turbolift)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Picard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Go ahead.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Ship's sensors have detected the presence of a vessel approaching this planet.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No ship is scheduled to arrive at this time, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Have Commander Riker and his team beam back up.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Security, could that be the Hood returning here?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) The vessel does not match the Hood's configuration or ID signal.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Put it on main viewer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Identification?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Vessel unknown, configuration unknown, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(It looks like a classic flying saucer)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Hail it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We've been trying, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "No response.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Raise all shields, phasers at ready.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Shields up, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Phasers ready.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Get me Groppler Zorn.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And continue universal greetings on all frequencies.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) This is Zorn, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Zorn, an unidentified vessel has entered into orbit with us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Zorn's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Do you know who it is?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "There are no ships scheduled to arrive until", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I asked if you knew who it is.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You mentioned the Ferengi Alliance to me.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Zorn's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But we have had no dealings with them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It was only a, a thought.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Are you certain?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) I promise you, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Zorn's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We were making an empty threat.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I wanted your cooperation.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Forgive me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Definitely entering orbital trajectory, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(at Tasha's station) It measures twelve times our volume, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(A purple light envelopes the Enterprise)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Sensors say we were just scanned, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Passageway]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Pain again?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Troi, you've been at it enough.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No, I feel close to an answer of some kind.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Commander, something down here is shielding our communicators.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Yes, that's exactly the feeling I've been reading.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "As if someone doesn't want us to be in touch with our ship.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Come on, let's get to the surface.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There is no computer record of any such vessel, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Nothing even close.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Still no response, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We've done everything but threaten them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sensor scans, Mister Worf.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Our sensor signals seem to just bounce off.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Something's happening, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "They're firing on Farpoint, sir!", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Bring photon torpedoes to ready!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Wait, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "They're hitting the old Bandi city, not Farpoint Station.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The ruined and empty city is being smashed by purple blasts)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Passageway]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The away team can hear the blasts)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Those stairs are where we entered down here, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "At this point, it becomes ordinary stone, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Matching what's above.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "My God!", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Was that a phaser blast?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Negative.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "But something similar.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "You, Tasha, and Geordi will beam up to the ship.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Now, come on, I want to see exactly what's happening.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Don't.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "If you should be hurt", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "You have your orders, Lieutenant.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Carry them out.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes sir, I'm sorry, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Enterprise, three to beam up.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Zorn's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Enterprise, Enterprise, come in!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Help us, please!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What shall we do?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Enterprise, help us, please!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Tune that down!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Commander Riker, come in.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Can you hear me?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bandi city]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Data and Riker phaser open a door and rush through)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,MTL+1.0" },
		{ text = "Riker to Enterprise, come in.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) Commander Riker, come in.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Where are you?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "With Data, on the edge of the old city, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's being hit hard, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And Farpoint Station?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Any damage there?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Negative on damage to Farpoint, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Whoever they are, it seems they're carefully avoiding hitting the station.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's from an unidentified vessel that's entered into orbit with us here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bandi city]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) No ID, no answer to our signals.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The old Bandi city's being hit hard, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Many casualties very probable.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) Understand, Commander.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Would you object to your Captain ordering a clearly illegal kidnapping?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bandi city]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No objection, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Groppler Zorn may have the answers we need.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Get him.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Bring him here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bandi city]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They're forcing a difficult decision on me, Counsellor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But I doubt protecting the Bandi would violate the Prime Directive.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "True, they are not actual allies, but", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "We are in the midst of diplomatic discussions with them.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant, lock phasers on that vessel.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Phasers locked on, Captain.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(There's a flash, and the Judge Q stands there)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Typical.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "So typical.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Savage lifeforms never follow even their own rules.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Get off my bridge!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Interesting, that order about phasers.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Standing by on phasers, Captain.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Please, don't let me interfere.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Use your weapons.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "We've no idea who is on that vessel.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "My order was a routine safety precaution.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Really?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "No idea of what it represents?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "The meaning of that vessel is as plain, as plain as the noses on your ugly little primate faces.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "And if you were truly civilised, Captain, wouldn't you be doing something about the casualties happening down there?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Captain to CMO.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Are you reading any of this?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Medical teams already preparing to beam down, Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Compliments on that, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Any questions?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Starfleet people are trained to render aid and assistance whenever", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But not trained in clear thinking.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Let's consider your thoughts.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You call us savages and yet you knew those people down there would be killed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It is your conduct that is uncivilised.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir, they're firing on the planet again.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Go to manoeuvring jets.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Position us between that vessel and the planet.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Force fields full on.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Impulse power to (beep) We have no ship control, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's gone!", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bandi city]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Riker painfully hauls himself up from the ground.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "He is covered in dust)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Are you undamaged?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Yes.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "All systems operating.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(They hear sobbing, and enter - )", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Zorn's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Zorn is hiding underneath his desk)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Please!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Make it stop!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You can drive it away!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Drive who away, Groppler?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I don't know.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Unlikely, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Our records show that you supervised all Bandi contact with other worlds.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "We done nothing anything wrong!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then if we can learn nothing from you, we'll leave.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh no, please, don't leave.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll try to explain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "First officer to Enterprise.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "We've lost Zorn.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Something like a transporter beam seems to have snatched him away.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Question, sir, could it be this Q?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "None of you knows who transported him.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You're running out of time, Captain.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Captain, suddenly I'm sensing something else.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Satisfaction.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Enormous satisfaction.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "From the same source as before?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No, that was on the planet.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "This is much closer.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Excellent, Counsellor.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "He's such a dullard, isn't he?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) Captain from Transporter Room.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "First Officer and Mister Data have beamed aboard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Excellent also.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Perhaps with more of these little minds helping", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "That is enough, damn it!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Have you forgotten that we have an agreement,", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "An agreement which you are at this moment breaking by taking over our vessel, interfering with my decisions.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Either leave or finish us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Temper, temper, mon Capitaine.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "I'm merely trying to assist a pitiful species.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "But perhaps I will leave if Commander Riker provides me with some amusement.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Do nothing that he asks.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But I ask so little, and it's so necessary if you're to solve all this.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Beam over there with your what do you call it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Your away team.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll risk none of my crew on that unknown.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You should already know what you'll find there.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Or perhaps it's too adult a puzzle for you.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "With all respect, Captain, I want to beam over there.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You show promise, my good fellow.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Have you understood any part of what he's tried to tell you?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Humanity is no longer a savage race.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "But you must still prove that.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(And he vanishes)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "At least you impressed him, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That's hopeful.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Captain, if he's not open to evidence in our favour, where will you go from there?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'll attend to my duty.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "To the bitter end?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I see nothing so bitter about that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Crusher's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Can I help you, Captain?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I didn't want you thinking me harsh.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Cold blooded.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why oh why would I ever think that.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I didn't welcome you aboard personally, professionally.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I made you come to me on the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I yelled at your son.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Who, as you pointed out, was quite correct.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He does seem to have a very good grasp of starship operations.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You've just won this mother's heart, Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Ah, but, now, your assignment here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I would consider and approve you a transfer for you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Oh. You consider me unqualified?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Hardly.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Your service record shows you're just the Chief Medical Officer I want.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Then you must object to me personally.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I'm trying to be considerate of your feelings, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "For you to work with a commanding officer who would continually remind you of a terrible personal tragedy.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If I had had any objections to serving with you, I wouldn't have requested this assignment, Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "You requested this posting?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "My feelings about my husband's death will have no effect on the way I serve you, this vessel, or this mission.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Ah. Then, welcome aboard, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(They shake hands)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Offscreen) Riker to Picard.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We're ready to beam over, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I, er, I hope we can be friends?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The away team is Data, Tasha, Troi and Riker)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Phasers on stun.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Energise.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Alien vessel]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(It looks like the passages under the Bandi city)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Most interesting, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It's much the same construction we saw in the underground tunnel.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "But no sound of power.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "No equipment.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "How does this ship run?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "What is it, Troi?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Is it the same as you felt down there?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No. This is much more powerful.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Full of anger!", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Hate!", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Toward us?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "No. It's directed down toward the old Bandi city.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Most intriguing again, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The place that this vessel was firing upon was not the Farpoint starbase, but the home of those who constructed.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Sorry, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I seem to be commenting on everything.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Good.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Don't stop, my friend.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Enterprise, Riker.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "This is turning out to be a very long tunnel or corridor, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Alien vessel]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No ship's crew in sight.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No sign of mechanism or circuitry.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No controls or read-outs.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "This is nothing like any vessel I've seen before.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Groppler Zorn, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "A great fear just ahead.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) There's a different feeling here than in the tunnel.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Very different.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Alien vessel]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) No!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No more!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please, no more!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please, please, make it stop!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Make it stop!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(They find Zorn suspended in a force field, in pain)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "No!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No more, please!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No more!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Zorn.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Can you hear me?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The pain!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Has the alien communicated?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Please!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please, no more!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That's it, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "It's just one alien that I'm sensing here.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Please!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't understand what you want!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Not true.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "He does know.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "No, please, no more.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No more!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No, no, no, don't!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Riker and Data modulate their phasers to disrupt the force field, and Zorn drops onto a big soft mattress.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The alien ship is not pleased about this)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain!", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Transporter chief, yank them back now!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Riker, acknowledge!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Flash!", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Q is in a Starfleet uniform)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Your time is up, Captain.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Transporter Chief, do you have their coordinates?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Transporter Chief!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He can't hear you, Captain.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Transporter Chief, come in!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I've people in trouble over there, Q.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Q sits himself in the Captain's chair)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Everyone at ease.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That's an order.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Q, my people are in trouble.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Let me help them, please.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll do whatever you say.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The away team and Zorn materialise on the Bridge)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You'll do whatever I say?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "It seems I did make that bargain.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The agreement isn't valid, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "It wasn't Q that saved us.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Save yourself.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "It may attack you now.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "It was that which sent us back, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "It is not merely a vessel, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Somehow it is alive.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "She lies.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Destroy it while you have a chance.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Make phasers and photon torpedoes ready.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "No!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do nothing he demands.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, that thing was killing my people!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Was there a reason?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It is an unknown, Captain.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Isn't that enough?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "If you'd earned that uniform you're wearing, you'd know that the unknown is what brings us out here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Wasted effort, considering the human intelligence.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Let's test that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Beginning with the tunnels you have under Farpoint, Groppler.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Identical to the ones on the space vessel lifeform over there.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Why was it punishing you, Groppler?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "In return for some pain you'd given other creature?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We've done nothing wrong.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It was injured.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We helped it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thank you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That was the missing part.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant Yar, rig main phaser banks to deliver an energy beam.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You're right, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It has to be conceivable that somewhere in this galaxy there could exist creatures able to convert energy into matter.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And into specific patterns of matter, just as our transporters do.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "On the viewer, Captain!", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(The alien vessel transforms into a giant space jellyfish)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Groppler, you captured something like that, didn't you?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Warn my people, please, to leave Farpoint Station immediately!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He lies, Captain.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Shouldn't you let his people die?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Transmit the message, leave Farpoint Space Station immediately.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Then it was a pair of creatures I was sensing.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "One down there in grief and pain, the other up here, filled with anger.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "And firing not on the new space station, but on the Bandi and their city.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Attacking those who had captured, capture its mate.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Energy beam ready, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Lock it in on Farpoint Station.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I see now it was too simple a puzzle.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Generosity has always been my weakness.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Let it have whatever it can absorb.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Energise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(A stream of light bathes Farpoint)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Now getting feedback on the beam, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Discontinue.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Groppler Zorn, there'll soon be no Farpoint Station, if I'm right about this.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "A lucky guess.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(Farpoint transforms into another space jellyfish)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Please believe me, we meant no harm to the creature.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It was starving for energy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Which your world furnishes you in plenty.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We did feed it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Only enough to keep it alive, so that you could force it to shape itself into whatever form you needed!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The second jellyfish takes off, and the two touch tendrils, lovingly, before leaving)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Sir!", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Wonderful!", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "A feeling of great joy.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "And gratitude.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Great joy and gratitude, from both of them.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Why do you use other lifeforms for recreation?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If so, you've not provided the best.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Leave us!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We've passed your little test.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Temper, temper, mon Capitaine.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Get off my ship.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I do so only because it suits me to.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "But I will not promise never to appear again.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Captain's log, stardate 41174.2.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The agreement for the rebuilding Farpoint Station has been completed per my instructions.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All stations?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Ready for departure, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Some problem, Riker?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Just hoping this isn't the usual way our missions will go, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Oh no, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm sure most will be much more interesting.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Let's see what's out there.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Engage.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[img=music]  End Credits [img=music]", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,STS-0.4,UHP-3.0" },
	},
};

-- Star Trek:TNG S1.10 - The Battle
RecMedia["TNG_S1E10"] = {
	itemDisplayName = "VHS: ST:TNG S1.10",
	title = "Star Trek:TNG S1.10 - The Battle",
	subtitle = nil,
	author = nil,
	extra = nil,
	spawning = 0,
	category = "Retail-VHS",
	lines = {
		{ text = "(PICARD) Captain's log, stardate 41723.9.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "In response to a Starfleet order we are in the Xendi Sabu star system, having rendezvoused with a Ferengi vessel which has requested a meeting.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Although we arrived here and made appropriate signals to the Ferengi three days ago, they have so far responded only with the message, stand by Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Picard's quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard is reading, when the door bell chimes, and Beverly enters with her medical bag)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(CRUSHER) You sent for me, sir?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Yes, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sit down.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Look, this perhaps may be nothings, but I've been feeling a bit odd of late.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Fatigued.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And now I've got this damned headache.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "A what?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Headache.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Headache.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Surely you know what a headache is.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Of course.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "But I don't often encounter them.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The reason is obvious, of course.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What are the Ferengi up to?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Stand by, Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Stand by for what?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I don't see a thing wrong.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "No, neither can I. Unless they're baiting some kind of trap.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "With your head.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I see nothing physically wrong, but I want to run some additional scans in Sickbay.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Doctor, all I've got is", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Is an order to report to Sickbay.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "From the one person aboard this ship who can give you an order.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) Captain from First Officer.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "They're finally sending a message.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "On my way, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sorry, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Duty calls.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They're prepared to talk, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Have they given any hint of what they've been waiting for?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(TASHA) Negative, Captain.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "They've identified their Captain as Bok.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "DaiMon Bok.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(LAFORGE) You'll be able to see him now, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "They're willing to communicate on visual.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Open hailing frequencies.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hailing frequencies open, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Ferengi vessel.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is Captain Jean-Luc Picard of the Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We are transmitting visually.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Is this the Captain Picard?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do we know each other?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) I know you, Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then you have the advantage.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Is this Bok?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) I am Bok, DaiMon of the Ferengi.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I have asked you here to discuss a mutual problem, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What problem is that, DaiMon Bok?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) I insist on speaking of that matter in person.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Shall we meet on your vessel or mine?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Troi signals the transmission cut)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Hailing frequencies closed.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(TROI) Captain, I sense considerable deception on Bok's part.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "And danger.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Then we should meet him here.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Keep him under our control.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Picard nods)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Now open, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I appreciate your offer, DaiMon Bok.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We would like you to be our guest here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) As you wish, Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Perhaps this will begin a new era of cooperation for both our peoples.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "In one Earth hour, then?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "In one hour, DaiMon Bok.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "End transmission.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(WORF) I can't believe they're coming here.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "They did agree a bit easily.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well, in one hour we shall know why.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard is lying on an examination table)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Are you always accustomed to getting your way, Doctor?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Only when my way makes sense, Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "There are still forty minutes before the Ferengi beam over.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I'll have these scans done in ten.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It seems an awful lot of effort for something as simple as a headache.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "As simple?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "You should not have a headache unless there's something wrong, sir.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It may be true that headaches were once quite common, but that was in the days before the brain was charted, before we understood the nature of pain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "When we were suffering from such things as the common cold.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "So what's the cause of my headache?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I haven't the slightest idea.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(she puts something against his temple) Feel better.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The pain's gone.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Medical fakery.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The pain is actually still there.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It's just cloaked.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I'll want further exams.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Doctor!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "When the Ferengi matter is settled.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Wesley enters, in his new acting Ensign uniform.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "It looks awful)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(WESLEY) Commander, you'll soon be getting an intruder alert.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "What?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Wesley, if you've something to report.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "If you'll scan heading four four mark one six three, Lieutenant, you'll find", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Intruder alert, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I've got something, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's an old style starship, Constellation Class, heading this way under impulse power, sir.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(Picard enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Says who?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Ensign, answer the First Officer's question.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Says the long distance sensors, sir.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I was in Engineering, playing around with boosting sensor output.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(DATA) Boosting it?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "How?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(catches Picard's eye) We will discuss this later.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I read it now, sir, as a Constellation class starship heading this way under impulse power.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sending no call letters, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The correct procedure,", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What's wrong, sir?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Oh, it's nothing.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's just a, just a mild headache.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The correct procedure, Ensign, would have been to signal the Bridge of your finding immediately.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Bringing it here personally, perhaps with the idea of being on hand for the Ferengi beam over, might have imperiled this vessel had it been something hostile approaching.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "We are receiving no signal at all from the approaching starship, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Time, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Time?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Oh, for the Ferengi to beam over.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do you see any problems connected with this old starship coming in, Number One?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Suggest it would be safer, sir, to have the Ferengi here whatever happenings.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Concur.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Stand by.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hailing frequencies open, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(The Ferengi transporter is a curved effect, instead of vertical.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Three big-eared little men appear in front of the viewscreen)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Welcome you in peace to the Enterprise, DaiMon Bok.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It is our pleasure, Captain Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Might I introduce my First Officer, Kazago, and my Second, Rata?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is my First Officer, Commander William Riker.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Second in command, Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Counsellor Troi.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We have heard that you use females.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Clothed females.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Most interesting.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They are that, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(KAZAGO) And the android was mentioned too.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What is its price?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We should like to purchase it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He is not for sale.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Commander Data is, um, is, um", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Is second-hand merchandise.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You wouldn't want him.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Second-hand, sir?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Oh, of course.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "A human joke.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Excuse, Captain, but the unidentified starship is coming in.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Still no signal at all.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Think nothing of it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It is under our control.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "One of our starships under your control?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do not be alarmed, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It is a gift from us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "With which we honour the Hero of Maxia.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Who?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why you, Picard, of course.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do you not remember the Battle of Maxia?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm sorry, I do not remember it, DaiMon Bok.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Data?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, he may refer to an incident which occurred nine years ago in the Maxia Zeta star system, in which an unidentified starship", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Unidentified?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That fine vessel was Ferengi.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Which you destroyed, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The Battle of Maxia.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I've never heard it referred to so dramatically before.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "My sincere regrets, Bok, but that vessel refused to identify itself.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It simply attacked us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We defended ourselves.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Such mistakes happen in space.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Hardly a mistake, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Your report shows that it deliberately attacked.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Do you want the arriving vessel on main viewer, sir?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "It is only a thousand kilometres away now.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Put it on your viewer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Main viewer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "There is no one aboard it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(RATA) The log should be downloaded into the Enterprise's records.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "At a price.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No price!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No price?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "For what purpose?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What (clutches his head in pain)", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I just felt something too, Captain.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Perhaps it is his conscience?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Bridge to Sickbay.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No, no.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm fine", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It felt as if it were something from your past.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "It's right.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm fine.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What is this all about?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It is about the battle I mentioned, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A gift, in honour of that occasion.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Look at that ship closely.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Magnify please, Lieutenant La Forge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why, it's the Stargazer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's my old ship.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How did you find it?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It was a derelict, adrift in space on the far side of this star system.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How it got there is none of my business, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But now, that vessel is yours, if you wish to have it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We are not selling it to him?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Consider it an act of friendship.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "At no cost?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh, ugly.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Very ugly.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain's log, supplemental.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Bok and his officers have returned to their vessel, inviting us now to officially take possession of the Stargazer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(This time, Picard is on a full bio-bed)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Like before?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "No. It hit with more impact.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hit?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I'm sorry, but anything could be important.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "You said you felt something yourself.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I believe so.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Like a thought, but rather mechanical in nature.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Are you sure it wasn't one of my thoughts?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "At that moment, I was remembering being at the helm of the Stargazer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "A manoeuvre was being made.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We were hit.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Something's burning.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I can smell smoke.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Can you smell it?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "There's nothing burning, Jean-Luc.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "That was just part of my memory.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Memory or nightmare?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Riker enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Well, it was strong, whatever it was.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Are you ready, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Staff's waiting, if Doctor Crusher approves.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'll do better than that.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I'll go along.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Observation lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(All the senior staff are seated around the long table)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "We were traveling at warp two through the Maxia Zeta star system when this unidentified starship suddenly appeared and fired on us, point-blank range.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Where did it come from?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It must have been lying in some deep moon crater.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "First attack damaged the shields.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "In the confusion, they hit us a second time.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No clue who they were?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "No names, no reason.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Can you identify them, Vigo?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If they come in a second time with our shields damaged", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Who's Vigo?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "He's my weapons officer on the Stargazer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm getting quite caught up in this.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Your shields were failing, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I improvised.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "With the enemy vessel coming in for the kill, I ordered a sensor bearing, and when it went into the return arc", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You performed what Starfleet textbooks now refer to as the Picard Manoeuvre.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Well, I did what any good helmsman would have done.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I dropped into high warp, stopped right off the enemy vessel's bow and fired with everything I had.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And blowing into maximum warp speed, you appeared for an instant to be in two places at once.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And our attacker fired on the wrong one.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I did what any good helmsman could have done.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You did it first, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It was a save our skins manoeuvre.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We were finished.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "On fire.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We had to abandon ship.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We limped through space in shuttlecraft for weeks before we were picked up.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I haven't thought about this for years.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir, the Ferengi are standing by for us to take possession of the Stargazer.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I want to go over to her.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I understand, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "As soon as my people have made certain she's safe.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And after I have another look at you, Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Stargazer Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Tasha and Data are exploring by torchlight)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(reading the plaque) USS Stargazer.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Constellation Class.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Starfleet Registry NCC 2893.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(lights come on and Geordi enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I activated the emergency power cells.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Amazing they still work.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The rest of the ship is clear of surprises, Lieutenant Yar.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I read about this ship at the Academy, I never dreamed I'd ever be on her.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yar to Enterprise.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "All clear, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Picard and Crusher beam in)", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Hello, old friend.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You'll find this most intriguing, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "What did you find, Data?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The last entry dated nine years ago, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "By you.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(reading screen) We are forced to abandon our starship.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "May she find her way without us.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Apparently she did, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "How do you feel, Captain?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Oh, I'm fine, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant Yar, run a structural analysis on the Stargazer for an impulse tow.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Data, download all computers to the Enterprise and file.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm going to look at my old cabin.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Stargazer Captain's Quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard is going through the books he left behind, and a trunk of clothes.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Inside the trunk, something lights up, and he doubles over in agony)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Ferengi Science Lab]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Bok is working at a double of the thing in the trunk)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Try this, Hero of Maxia.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(He slides the top of the sphere around and)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Stargazer Captain's quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard jerks up in pain and clutches at his head before collapsing.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Crusher enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Another headache?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "This really worries me.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I want you back on the Enterprise.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "But my things", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll see they're sent to your Enterprise quarters.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Enterprise now taking possession of Stargazer, Kazago.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(On viewscreen) Permission granted, Riker.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Actually it was quite a bargain, Kazago.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I thought the Ferengi always made a profit on things.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Kazago growls and ends the transmission)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Set sub-warp speed for towing, LaForge.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Picard enters, in pain)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Starfleet has responded to our request, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "A tug will rendezvous with us and tow the Stargazer back to Xendi Starbase Nine.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Very well, Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How was it, Captain?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Very strange, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Like going back to the house you grew up in, but no one's home, except the phantoms of the past.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It has troubled you?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Not half as much as this damn headache.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Take over, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Picard leaves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What's wrong?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I wish I could say.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Picard's quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The trunk has been delivered.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Picard goes to lie down on his bed)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Offscreen) Shields weakening, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Torpedoes armed.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Where are they?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh my god, sir!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Fire!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Fire!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Ready Room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What is it, Data?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Why all the mystery?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The records of the Stargazer, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "What the Ferengi call the Battle of Maxia.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It seems the Captain's personal log contains a much different version of that conflict than the official historic account.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "What are you saying?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It would appear that the starship which Captain Picard attacked, had in fact, been under a flag of truce.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "What?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And apparently, the Captain destroyed the ship without notice or provocation.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Impossible.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What about the fire aboard the Stargazer?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "An accident in Engineering.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "And what proof?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It is logged in his own voice, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Would you care to hear it, sir?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Sir?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Picard's quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(PICARD [Stargazer log]) This is a confession given by me, Jean-Luc Picard commanding USS Stargazer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What does this mean?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I don't know, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It sounds like your voice.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It is, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I refuse to believe you ever said that.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I admit I must have mistaken their subspace antenna for a weapons cluster.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Unfortunately, I fired our main phasers and our direct hit destroyed the unknown vessel.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I've assumed they've simulated your voice somehow.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I've already put Data to work on it.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Thank you, Will.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I never made that log entry, of course, but it still leaves you with a duty to perform.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I know, sir, I must report it to Starfleet.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "That's at least one full day for subspace communications to reach there.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And one more full day for their answer to return.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'd like the truth on this by then.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'd hate to have to prepare a formal defence.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I can't believe they'd ask for your command.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Why wouldn't they?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "With the Ferengi making these friendship overtures, I could become a severe embarrassment to Starfleet.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm certain the Ferengi are behind the faked log.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No wonder they're waiting out there.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Headache back, sir?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Damn!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll call the Doctor again.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's no wonder, with all this going on.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Try to relax.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Riker leaves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "PICARD [Stargazer log] This is a confession given by me, Jean-Luc Picard, commanding USS Stargazer, in the hopes that my belated honesty will be taken into account by Starfleet when judging my actions during a confrontation with an unidentified vessel.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Open hailing frequencies, Geordi.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Open, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll take it in the Ready room.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Secure channel, La Forge.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Secure, Sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Ready room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Starship Ferengi, this is Commander Riker here.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'd like to speak to First Officer Kazago.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) A problem, Riker?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Are our channels secure on your end?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(On monitor) It is now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Are you aware of the details of the Battle of Maxia?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(On monitor) Captain Bok has just made me aware of it, Riker.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The infamy of your Picard is now fully known.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Infamy?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(On monitor) I would call the wanton destruction of an unarmed vessel infamy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And if I produced evidence that Captain Picard's log entry was falsified to indicate that?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(On monitor) I can hardly imagine you contacted me to discuss an ancient battle.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What do you want of me?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Just one question.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(On monitor) As you humans say, I'm all ears.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "First Officer to First Officer, Kazago, if your Captain Bok knew about this, then why this peaceful meeting to present us with the Stargazer?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(On monitor) We freely give you back your derelict warship and now you accuse us of crime, Riker?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I can bear no more insults!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Picard's quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(the doorbell chimes)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Yes?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Who the hell is it?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Crusher enters.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Not resting, Captain?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "More like dying, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Over here.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "What is wrong with me?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I wish to hell I knew, Captain, but something unusual has definitely been happening to you.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Why do doctors always say the obvious as though it's a revelation?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why do captains always act like they're immortal?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(she tries another painkiller)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "No.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Ferengi Science Lab]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Bok turns up the device)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Picard's Quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You didn't tell me it had been this bad.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It wasn't this bad.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But it's getting worse.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This should help a little.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It's got to be some kind of emotional pressure connected with the Stargazer.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I got this headache long before I even knew my old ship still existed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Still, perhaps you're partly right.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Want to talk about it?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I'm here.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The fight at Maxia.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I destroyed an entire vessel.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "An entire crew.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Did you have a choice?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I don't know anymore.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I just don't know.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Commander Riker's told me about the altered log, if that's what's troubling you.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The last three nights I've, I've heard these voices.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm on the bridge of my old ship.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "There's fire all around me.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The klaxons, smoke.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And then I give the order.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And now the Stargazer is really here!.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And that log.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Am I going crazy?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How do I know I was in my right mind at Maxia?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How do I know I'm in my right mind now?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Crusher gives him an injection)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What was that?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Something to let you sleep.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(She helps him to his bed)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Yes sleep.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sleep.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Crusher puts two small devices on his forehead, turns the lights out and leaves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Offscreen) Shields weakening, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Torpedoes armed.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Where are they?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Ferengi Science Lab]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And now, dear Captain, you are ready to live the past.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(He winds the contraption up to maximum)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Picard's quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Where did they come from?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Phasers, sir?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sir?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sir?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What should we do, sir?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Should we fire back?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Fire, Captain?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Fire!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Ferengi Science Lab]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You will injure yourself as you once injured me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Picard's quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Sir!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard opens his eyes, and he sees the ghost of the Stargazer's bridge and her crew.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "He is reliving the past)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Damage report!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Fusion generator under surge control, sir!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Power systems failing!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sensor beam bearing on hostile ship!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Seven mark nineteen, sir!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Phasers, sir?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sir?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ready phasers, and lock!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Stand by on warp nine.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Heading seven, seven mark twenty.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Engage.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Steady.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now, reverse and stop!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Phasers fire, torpedoes away!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Fire.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Fire.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Ready Room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "By comparing the Stargazer's main computer log with Captain Picard's personal log, I have found checksum discrepancies, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "What does that mean?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "All information is time-coded by entry, and the bits when totaled produce an aggregate amount which", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I don't want a computer science lesson, Data.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Bottom line.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "One of these two logs is a forgery, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Correction.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The log just found aboard the Stargazer is a forgery.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "As I said, that is one of them, is it not?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Picard enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You're looking better, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "A little sleep, thanks to the good Doctor, works wonders.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What report on the logs?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Whoever tampered with your personal log was clever.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "But a bit clumsy.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's definitely a fabrication, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Crusher enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Number One, I'd like you to take a look at this brain scan graph.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "What are you doing here?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I thought I was Captain of this starship.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Of course you are, but I", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Thank you for the confirmation, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But now, except for Riker, I would like you all to return to your stations.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Is that clear?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Data and Geordi leave)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You too, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I have business with the Commander.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Under protest, Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(Crusher leaves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You have orders for me, sir?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Release the Stargazer from the tractor beam, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The tractor beam.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir, are you abandoning?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No, but her inertia will carry the Stargazer along with us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Or did you sleep through the Academy lecture on conservation of tractor beam power?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'll release her, of course.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The output from the forehead devices is on a wall display)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Obviously, there's some thought process disorder, but I can't find a physical reason for it.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Anything?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I'm puzzled too.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I keep sensing random thoughts but two sets of them.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "As if they were his, but intermixed other thoughts which are also his.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Wesley enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I'm busy at the moment, Wesley.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I know, Mom, but this is important.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "When I went back to the main sensors in Engineering to try some more sensitivity experiments", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Does this have something to do with Captain Picard?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Yes, ma'am, if this is what you're talking about here.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I don't know much about brain scans but I glanced at these when you were studying them, and I noticed that these patterns are the same as those picked up from the low-intensity transmissions from the Ferengi ship.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I went back and checked, and they're exactly the same.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "What kind of transmissions?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I don't know.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Engineering has nothing like it on record.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Let's get to the Captain.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "No, they might be affecting the Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "To Riker.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(Crusher and Troi leave)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You're welcome, ladies.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Adults.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The captain, Commander?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Resting in his quarters after ordering both of you to your duty stations.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "There have been some, did he say low intensity?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Some unusual low intensity transmissions from the Ferengi vessel.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Did who say?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "My son.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Transmissions which exactly match certain anomalies found in the Captain's brain scans.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Something over there is affecting the Captain's thought patterns.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Computer, give me a location on Captain Picard.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(COMPUTER) Captain Picard is in Transporter room three.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Computer, emergency order to Transporter room three.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "New information.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain Picard is no longer aboard the Enterprise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Stargazer Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard beams in and takes the Captain's chair)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Welcome back, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Bok is holding the device he has been using on Picard)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What is happening?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Shields up, computer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What are you doing?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Collecting on an old debt.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard collapses in agony)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Stargazer, Captain, respond!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Shields up, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "No way to beam over any help.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Commander, I'm reading something very strange here.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "A low intensity beam of intermittent pulse inside this starship.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I have a fix on it, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Inside Captain Picard's quarters.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "You transferred some of his belongings from the Stargazer?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Including a fairly heavy chest.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Go take a look, fast!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Commander, Stargazer is now powering up, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Stargazer Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I have been waiting a long nine years for this, Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't know what you're talking about.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do you not, human?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Can you not remember the crime you committed against my very blood?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You murdered my only son.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Your son?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He was the commander of the ship you destroyed!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "On his first voyage as DaiMon.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The ship?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Ferengi ship that attacked me.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Or is it about to attack me?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And I have spent these years searching, seeking a proper blood revenge!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And I found it!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am rich, Picard, yet two of these cost me the profits of an entire life.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You are back in command of the Stargazer, Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Its computers will answer your orders.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Die well, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Bok beams away)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "First officer's log.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Captain Picard has beamed himself to the Stargazer, which is now moving away from us under its own power.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Enterprise to Stargazer, please respond.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Enterprise to Captain Picard aboard Stargazer.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Please answer.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Mister Data, what was Stargazer's condition?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Considerable fire damage to interior surface reported, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "But none of her main systems were crippled.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Armaments, Lieutenant Yar?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Six photon torpedoes short, sir, probably used when the Captain destroyed his Ferengi attackers nine years ago.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Otherwise fully armed.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Geordi is examining the globe device from the chest)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What do you make of it, La Forge?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It seems to be a network of miniature circuitry, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Incredibly complex.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Maybe an amplifier.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Where was it in the Captain's quarters, Mister Worf?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "His chest from the Stargazer, sir?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Just where I had left it.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "He hadn't yet unpacked.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "So, he may not have even known it was there.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "And if this is able to pick up or magnify thought-altering transmissions", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It could have prepared him for whatever is happening now.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Let's find out, or try to.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Contact the Ferengi vessel, Lieutenant.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Hailing frequencies open, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Enterprise to Ferengi vessel, we transmit visually.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Do you respond?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(On viewscreen) Why is our gift to you under power, human?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I will discuss that with your captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(On viewscreen) He is working in our ship's laboratory.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Where is your Captain?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He's beamed himself aboard the Stargazer, and I'm wondering if this has something to do with it.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(On viewscreen) How do you have possession of that?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It was found in our Captain's chest, which brought over from the Stargazer.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(On viewscreen) It is a forbidden device.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A thought maker.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If your Captain is criminal enough to own one", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Kazago, you know who controls those spheres.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Now I ask you again, First Officer to First Officer.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What's going on?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(On viewscreen) It is not seemly to question one's own DaiMon, Riker.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am not prepared to do that.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sir, I have the Stargazer.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Switch, now!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Enterprise to Picard.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(On viewscreen) Do not attack again!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We are on a peaceful mission.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Give your identity.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You force us to defend ourselves.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Phasers full up.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Arm torpedoes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why aren't the shields at full power?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We've lost him, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "When he put up the shields, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Stargazer Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard is surrounded by the ghosts of his crew, and the flames)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Damn, I said put fusion generators under surge control.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You're moving too slowly.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
