	lines = {
		{ text = "(PICARD) Captain's log, stardate 41590.5.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Having dropped off Counsellor Troi at Starbase G6 for a shuttle to visit home, we were fortunately close to the Sigma Three solar system when its Federation colony transmitted an urgent call for medical help.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
