		{ text = "(CRUSHER) Include a burn unit with each kit.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Upon arrival, identify the most critically injured and beam them up to cargo bay six.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
