		{ text = "Additional information.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The number of colonists at the site is five hundred and four.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Are you prepared for that many, Doctor?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) We believe so, sir.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(LAFORGE) Captain, we are now at warp nine point one, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(DATA) Which will bring us to the colony in three point two hours, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(RIKER) Captain, I have a schematic of the explosion site.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It suggests the cause as a methane-like gas seeping in from underground.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Captain, I'm picking up a forcefield out there of some kind.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's almost", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The chain-link that envelopes the Enterprise is straight out of Encounter At Farpoint.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The Q entity, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It is identical to the grid we encountered when", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(WORF) It reads solid, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Emergency.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Full stop.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Reversing power, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Not now, damn it, Q.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(TASHA) Shields and deflectors up, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Now reading full stop, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(There's a flash, and something similar to Prince of Wales' feathers hovers.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Q) Humans, I thought by now you would have scampered back to your own little star system.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "If this is Q I'm addressing, we are on a mission of rescue where a group of badly injured", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We the Q have studied our recent contact with you, and are impressed.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "We have much to discuss, including perhaps the realisation of your most impossible dream.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "However intriguing that may be, we are now in the midst of an urgent journey.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
