		{ text = "If my magnificence blinds you, then perhaps something more familiar.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(Flash, and the familiar human shape of John de Lancie, this time costumed as...)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Starfleet Admiral Q, at your service.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Captain's log, supplemental.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Our rescue mission to the Sigma Three solar system has been halted by an immense grid and an untimely visit from Q", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're no Starfleet Admiral, Q.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Neither am I an Aldebaran serpent, Captain, but you accepted me as such.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "He's got us there, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
