		{ text = "You seem to find this all very amusing.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "I might, if we weren't on our way to help some suffering and dying humans who", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Your species is always suffering and dying.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "No, Lieutenant Worf.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
