		{ text = "Macro head with a micro brain.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You said you had the realisation of impossible dreams to offer us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "When this rescue is completed, I am prepared to listen carefully to whatever proposal you wish to make and subject to it being acceptable", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Subject to your foolish human values?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Oh, come, Picard.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Why do you distrust me so?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Why?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "At our first meeting you seized my vessel.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You condemned all humans as savages, and on that charge you tried us in a post-atomic twenty first century court of horrors, where you attacked my people.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
