		{ text = "And that angered you, did it?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Seized my vessel, seized my vessel.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You interfered with our Farpoint mission.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You threatened to convict us as ignorant savages, if, while dealing with a powerful and complex life forms, we made the slightest mistake, and when that didn't happen", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Q became interested in you.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Does no one here understand your incredible good fortune?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Seized my vessel.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "These are the complaints of a closed mind too accustomed to military privileges.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "But you, Riker, and I remember you well, what do you make of my offer?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "We don't have time for these games.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
