		{ text = "To the game.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(A flash, and Picard is alone on the Bridge.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
