		{ text = "(Sand and rocks under a green sky with yellow horizon.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Where are we?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
