		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Security, this is the Captain.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Security?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Engineering, this is the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Control panels don't operate.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Doors don't open.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Turbolift Control, do you read?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is the Captain.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(But answer came there none.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sir!", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Over here.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(It's Q, in a French uniform complete with tricorn hat and tricolor cockade.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Join me, Riker.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "A good game needs rules and planning.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Wasn't it your own Hartley who said, nothing reveals humanity so well as the games it plays?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Almost right.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Actually, you reveal yourselves best in how you play.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Sir, what he has in mind might provide us with vital information.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Riker joins Q at a table in front of a tent.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "He picks up a glass to drink.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Incredible.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I was just thinking about an old-fashioned lemonade.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And so it became that.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "An excellent thirst quencher.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "It gets rather hot out on this plain.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "What about my people?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Whatever they'd like, of course!", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(Glasses appear in everyone's hands.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Worf ostentatiously pours his onto the ground.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Drink not with thine enemy.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "The rigid Klingon code.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "That explains something of why you defeated them.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You're still fascinated with the human past?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Perhaps you're not that original.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Au contraire!", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "It's the human future which intrigues us, and should concern you most.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You see, of all species, yours cannot abide stagnation.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Change is at the heart of what you are.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "But change into what?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "That's the question.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "That is what humans call a truism.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "You mean hardly original?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You're the one who said it.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "While we're at it, this isn't part of any human future.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "True.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "I borrowed this from your stodgy Captain's mind.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "This is dressing for a game that we will play.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Now games require rules and rewards and dangers and familiar settings.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "That sort of thing.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "This isn't that familiar to me.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Data?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "This is from Europe's Napoleonic era, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Late eighteenth, early nineteenth centuries.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "This is a campaign headquarters tent, his uniform is that of a French Army marshal.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "And a marshal outranks even an Admiral", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Well, do you think I would go from a Starfleet Admiral to anything else?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Of course you wouldn't.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "But Napoleonic equipment on an alien planet.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "One so different it has twin moons?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Well, as you said, I'm nothing if not imaginative.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "And the game should reflect that.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Shall it be a test of strength?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Meaningless, since you have none.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "A test of intelligence, then?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Equally as meaningless.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "But it needs risk, something to win and something to lose.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "If we must play a game, what would we win?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The greatest possible future that you can imagine.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Which, of course, requires something totally disastrous if you lose.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Now the point of this game shall be, can any of you can stay alive?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "If your game is fair, we will.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Oh, for shame, Worf.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Fairness is such a human concept.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Think imaginatively!", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "This game shall in fact be completely unfair.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You've gone too far!", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Game penalty!", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(Tasha vanishes.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Where is she, Q?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You can forget your game if", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "To use a twentieth century term, she's in a penalty box.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Where she will remain unharmed unless one of you merits a penalty.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Unfortunately, there is only one penalty box.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "If any of you should be sent there, dear Tasha must give the box up to you.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "And where does she go?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Into nothingness.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "I entreat you to carefully obey the rules of the game.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "The only one who can destroy your Tasha now is you.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain's log", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(COMPUTER) Captain's log", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Damn it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I can't even make a log entry.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I wish I could help you, Captain.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Where is everyone else?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Down on some planet.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Some planet?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What are you doing here?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well, I, er.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "It sounds strange, but I'm in a penalty box.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "A penalty box?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Q's penalty box.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "It sounds strange, but it definitely isn't.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I know that one more penalty by anyone and I'm gone.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Gone?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes!", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I am gone!", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "It is so frustrating to be controlled like this!", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Tasha, it's all right.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What the hell am I doing?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Crying?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Don't worry.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "There's a new ship's standing order on the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "When one is in the penalty box, tears are permitted.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Oh, if you weren't a captain.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Consorting with lower rank females, Captain?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Especially ones in penalty boxes?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Destructive to discipline, they say.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "But then again, you're what?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You're only human?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Penalty over.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "A marshal of France?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Ridiculous!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "One takes what jobs he can get.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "For example, star log entry, stardate today.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "This is Q, speaking for Captain Jean-Luc Picard, who we consider too bound by Starfleet customs and traditions to be useful to us.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "The Enterprise is now helpless, stuck like an Earth insect in amber while its bridge crew plays out a game whose real intent is to test whether the First Officer is worthy of the greatest gift the Q can offer.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "So you're taking on Riker this time.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Excellent.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He'll defeat you just as I did.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Shall we wager on that, Captain?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Your starship command against?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Against your keeping out of humanity's path for ever.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Done?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Done!", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You've already lost, Picard.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Riker will be offered something impossible to refuse.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Geordi, can you see Worf?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'd see the freckles on his nose if he had them, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "He's at the third ridge.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The third ridge?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Moving well too.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Oh, oh.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Good, he sees them.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Them being soldiers in an camp.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "And they grunt like pigs.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Worf runs back.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Ready Room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Listen to me, Q. You seem to have some need for humans.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Concern regarding them.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Whatever it is, why do you demonstrate it through this confrontation?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why not a simple, direct explanation, a statement of what you seek?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why these games?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why these games?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Why, the play's the thing.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "And I'm surprised you have to ask when your human Shakespeare explained it all so well.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "So he did, but don't depend too much on any one single viewpoint", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's a pity you don't know the content of your own library.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Hear this, Picard, and reflect.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "All the galaxy's a stage.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "World, not galaxy.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "All the world's a stage.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Oh, you know that one?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Well, if he were living now he would have said galaxy.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "How about this?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Life is but a walking shadow, a poor player that struts and frets his hour upon the stage and then is heard no more.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "It is a tale told by an idiot, full of sound and fury, signifying nothing.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "I see.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "So how we respond to a game tells you more about us than our real life, this tale told by an idiot?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Interesting, Q.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Oh, thank you very much.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "I'm glad you enjoyed it.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Perhaps maybe a little Hamlet?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Oh, no.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I know Hamlet.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And what he might say with irony, I say with conviction.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What a piece of work is man.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How noble in reason.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How infinite in faculty.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "In form, in moving, how express and admirable.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "In action, how like an angel.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "In apprehension, how like a god.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Surely you don't really see your species like that, do you?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "I see us one day becoming that, Q. Is it that which concerns you?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Planet]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Those soldiers have formed a skirmishing line, I think you'd call it, and they're headed this way.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Armed with ancient ball and powder muskets?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "That's what their weapons look like, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Muskets are appropriate to the 1790 to 1800 French army uniform, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "But it is hardly a weapon by our standards.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "A lead ball propelled by gunpowder.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "One hundred metres at best with any accuracy.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Yeah, but against phasers?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Just one of our hand phasers could finish off an entire regiment.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Except for one thing.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It hardly sounds like Q to give us an advantage like that.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Unless.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(He does a test firing.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "A rock blows up most satisfactorily.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Drop your weapons!", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm afraid that was me, Worf.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I was checking to see if the phasers still operate.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Incredible, Worf!", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You came out of nowhere.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "A warrior's reaction.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Report.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What did you find?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Sir, what they're wearing may be old Earth uniforms, but what's inside of them isn't human at all.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "More like vicious animal things.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(And here they come.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Those soldiers are moving in fast, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Data, if you've got a theory about what's happening?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(But it's not Data, it's Q made up to look like him.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Q (DATA)) Think fast, Commander Riker, and move fast.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(One of the soldiers fires.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "An energy bolt comes from the musket.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Those aren't muskets.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(He vapourises two soldiers.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You have only one chance to save them now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Send them back to the ship.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You'll let me beam them?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Send them the same way as I do.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I've given you that power.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do you understand?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I have given you the power of the Q. Use it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Q vanishes.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Offscreen) Use your power.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(The real Data reappears.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Offscreen) Use your power.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(Riker holds up his hand and beams Worf, Data and Geordi away.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The forcefield vanishes while Tasha is sitting alone on the Bridge.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Everything comes back to life and Picard comes out of the Ready room.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Lieutenant, take the conn position.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Engineering, this is the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Engineering here, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Engineering, are all systems back online?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Back online, sir?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They were never off.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, you'd better look at this.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "There's been no interruption in course or speed.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Both have remained constant.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "It's as though we never stopped.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "We never did, Lieutenant.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Q suspended time.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Worf, Data and Geordi appear.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Where's Commander Riker?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "He was with us.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "He must still be on the planet.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We were under attack by these, these animal things.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Animal things?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well, maybe Data could explain better, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You may find it aesthetically displeasing, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I could just file a computer report on that.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Data!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir, the important thing right now is why is Commander Riker missing?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Understood, Lieutenant, but I suspect that Commander Riker is probably perfectly safe, at least in a physical sense.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Q has an interest in him.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "In fact, Q's entire visit has something to do with our First Officer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And the reason for that, sir?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I wish I knew.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Q first became interested in him at Farpoint.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I have no idea what it means.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Meanwhile, we must proceed with our rescue mission.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Riker is sitting on a rock, laughing.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Something amuses you?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Perhaps you'll share the joke with me?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "The joke is you.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Strange gratitude, from one who has been granted a gift beyond any human dream.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "How can you not appreciate being able to send your friends back to their ship, or sending the soldiers back to the nothingness from which they came?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Certainly, you must understand that at this moment you can send yourself back to the ship or to Earth, or change your shape and become anything else you want to be.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "What do you need, Q?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Need?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You want something from us, desperately.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What is it?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Want something from you foolish, fragile, non-entities?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Oh come, Riker.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You're beginning to sound like your Captain.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Now that's a compliment, Q. But that's not an answer.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Riker, we have offered you a gift beyond all other gifts!", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Out of the goodness of your heart.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "After Farpoint, I returned to where we exist.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "The Q Continuum.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Which means exactly what?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The limitless dimensions of the galaxy in which we exist.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "I don't understand.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Of course you don't, and you never will until you become one of us.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Until?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Would you mind going over that again?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Well if you'll stop interrupting me.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "This is hardly a time to be teaching you the true nature of the universe.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "However, at Farpoint we saw you as savages only.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "We discovered instead that you are unusual creatures in your own limited ways.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Ways which in time will not be so limited.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "We're growing.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Something about us compels us to learn, explore.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes, the human compulsion.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "And unfortunately for us, it is a power which will grow stronger century after century, aeon after aeon.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Aeons.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Have you any idea how far we'll advance?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Perhaps in a future that you cannot yet conceive, even beyond us.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "So you see, we must know more about this human condition.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "That's why we've selected you, Riker, to become part of the Q, so that you can bring to us this human need and hunger, that we may understand it.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "I suppose you mean that as a compliment, Q. Or maybe it's my limited mind.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "But to become a part of you?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I don't even like you.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You're going to miss me!", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(Q vanishes and the Bridge crew appear.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Come on, not again!", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(WESLEY) Commander Riker, what's going on?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I was sitting in school and", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Worf, my phaser's gone.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Are you armed?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "No.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The animal solders advance.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Where is Q?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If you have any answer to any of this?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Worf charges to meet their enemy.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "He knocks two down before being bayoneted in the stomach.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Worf!", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(He dashes to Worf.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Look out!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Wesley, no!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Wesley gets bayoneted from behind.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I confess, I cheered.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Wesley!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Wesley!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Damn it!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Damn it to hell!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Riker throws up a Q forcefield in front of the soldiers.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Riker.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You did that!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And that's not all!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(They are all back on the Bridge, alive and well, including Wesley - sob.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "That grid, their wounds.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Only the Q can do that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain's log, stardate 41591.4.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Twelve minutes out from Quadra Sigma Three where the survivors of an underground disaster desperately need our help.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aboard the Enterprise, First Officer William T. Riker needs help nearly as badly.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But this is a subject far out of my experience.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Out of any human's experience.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Ready Room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Will.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How the hell do I advise you?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You know the implications as well as I.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No one has ever offered to turn me into a god before.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What the Q has offered you has got to be close to immortality, Will.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They're not lying about controlling space and time.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We've seen it in what they can do.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You've also seen it in what I can do.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "If you are going to refuse his offer, you must not allow yourself to use this power again.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's too great a temptation for us at our present stage of development.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Are you worried that I won't be able to say no to it?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You tell me.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Are you strong enough to refuse to use that power.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Certainly.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No matter how tempted?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No matter how difficult Q makes it for you?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You have my word.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Good.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I know what your word means.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) In orbit of Quadra Sigma Three, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Ready to beam down rescue team to underground emergency area.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Disaster Area]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This way sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Data forces open a jammed door and they enter a room with some water on the floor and people groaning.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Crusher and her medics start tending to them.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Are there any others?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(WOMAN) Gone.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's just us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Commander.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(by a rock fall) There's someone under here.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Data tosses rocks as if they were made of polystyrene, which they are.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You're getting close, Data.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Data uncovers a little girl, and lifts her out.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Check the number on the pipe on the wall.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "It's too late.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "She's dead.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "If only we'd gotten here a little sooner.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Sir, if indeed you have the power of Q.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I don't understand.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Certainly you can't bring her back to life.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I can't.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'm prevented from that by a promise.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I should never have made that agreement with you.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I could have saved that child.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You were right not to try.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Once you became accustomed to that power, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "When I used it before, what happened?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I saved most of our Bridge crew.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And when you grow to like it too much?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "As soon as it's convenient Captain, I want a meeting with you and your Bridge staff.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "As soon as we are secure of this rescue operation, I'll discuss all of this new power", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(But Riker has already gone to the turbolift.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Later, Riker returns.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "We can confer here on the Bridge, if no one has any objections.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Bridge will be fine, since I've called the entire staff.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Correction, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Knowing the decision you face, I have permitted you this gathering.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Of course, Jean-Luc.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Crusher and Wesley enter.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Wesley, this meeting is not for you.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Why not, sir?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "You helped make me a Bridge officer.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Acting Ensign.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "All right, he stays.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Because I've been given unusual powers, I am not suddenly a monster.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Except for these abilities, and I don't yet know how far they go, I'm the same William T. Riker you've always known.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Well?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Everyone still looks uncomfortable.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Perhaps they're all remembering that old saying.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Power corrupts.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And absolute power corrupts absolutely.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Do you believe I haven't thought of that, Jean-Luc?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And have you noticed how you and I are now on a first name basis?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Number One, Will, something has happened already.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "In what way?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Haven't you seen how much I regretted not saving that child?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Using the Q power to save her may not have been wrong.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No more than it was wrong to save the rest of you from those soldier things.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Let's keep in mind that that particular danger was invented by Q.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What we represent to the Q, Commander, are lowly animals, tormented into performing for their amusement.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Actually, they think highly of us, Tasha.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We have a quality of growth which they admire.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Or fear.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "No, we've learned the Q do not admire us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Q has muddled your mind.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Don't you understand his incredible gift to me?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Are these truly your friends, brother?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(Everyone turns to see Q in a monk's habit.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Let us pray.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "For understanding and for compassion.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Let us do no such damned thing!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What is this need of yours for costumes, Q?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Have you no identity of your own?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I come in search of the truth.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You come in search of what humanity is!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I forgive your blasphemy.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Don't you see, Riker?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He's nothing but a flim-flam man!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He's been that ever since we first met him at Farpoint.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Flim-flam?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You offer Riker jealousy.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "What I offer him is clearly beyond your comprehension.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "How can you claim friendship for Riker while obstructing his way to the greatest adventure ever offered a human?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Obstructing him?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Then it's not yet certain.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He's not yet committed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The truly evil part of this, Captain, is your jealousy.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(to Riker) You love each one of your people.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Demonstrate it.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You have the power to leave each of them with a gift proving your affection.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "There'd be no harm, would there, if I gave them something I know they'd like?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "How touching.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "A plea to his former Captain.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "May I please give some happiness to my friends, sir?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Please sir?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "In fact I authorise and support your idea, Riker.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Please, feel free to cooperate with him if you wish.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Are you certain, sir?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Quite certain, Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "By all means, demonstrate your gifts of affection.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Don't be frightened.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "There is no way I could harm any of you.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Shall I guess your dreams?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Leave now, Wesley.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "No!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Wesley, I may know best of all.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Our friendship, our long talks", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No, please!", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Have your favourite wish, my young friend.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Wesley is transformed from teenager to hunk.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You're ten years older.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "A man.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Hey, Wes.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Not bad.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Data.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No. No, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "But it's what you've always wanted, Data, to become human.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes, sir, that is true.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "But I never wanted to compound one illusion with another.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It might be real to Q, perhaps even you, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "But it would not be so to me.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Was it not one of the Captain's favourite authors who wrote, This above all, to thine own self be true?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Sorry, Commander, I must decline.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Well, my friend, I know what you want.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(He waves his hand in front of Geordi and takes off the visor.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(to Tasha) You're as beautiful as I imagined, and more.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Then we can throw away the visor?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I don't think so, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The price is a little high for me, and I don't like who I would have to thank.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Make me the way I was.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Please!", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Riker does.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Proud warrior Worf, without a single tie to his own kind.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(A Klingon woman is kneeling at his feet.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "She gets up, tries to swipe at Tasha, and gets knocked down by Worf.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "No!", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "She is from a world now alien to me!", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Worf, is this your idea of sex?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is sex.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "But I have no place for it in my life now.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "No place, micro-brain?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "What possesses you?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Commander Riker, it's too soon for this.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "If this is because your mother objects?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No. I just want to get there on my own.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Honest.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "But it's easier, boy.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Listen to Riker.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "How did you know, sir?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I feel like such an idiot.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Quite right.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "So you should.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's all over, Q. You have no further business here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Human, you have just destroyed yourself.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Pay off your wager.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I recall no wager!", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "I'm sure your fellow Q remember you agreed to never trouble our species again.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Just as they're aware you failed to tempt a human to join you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(to the ceiling) No, if I could just do one more thing.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Q, I strongly suspect it's some explaining you have to do now.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Q screams as he disappears.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Wesley returns to gangly teenager, the Klingon woman vanishes and everyone is moved to new positions.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Extraordinary!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, we are showing that same hole in time again.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Our instruments say we've just now beamed back from our rescue mission.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir, how is it that the Q can handle time and space so well, and us so badly?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Perhaps some day we will discover that space and time are simpler than the human equation.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No coordinates laid in, Number One?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You have my coordinates, La Forge.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "On the board.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Engage.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[img=music]  End Credits [img=music]", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,STS-0.4,UHP-3.0" },
	},
};

