RecMedia["TNG_S1E12"] = {
	itemDisplayName = "VHS: ST:TNG S1.12",
	title = "Star Trek:TNG S1.12 - Too Short A Season",
	subtitle = nil,
	author = nil,
	extra = nil,
	spawning = 0,
	category = "Retail-VHS",
	lines = {
		{ text = "(PICARD) Captain's log, stardate 41309.5.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We are in orbit around Persephone Five, where I have been sent to confer with Admiral Mark Jameson in regard to an extraordinary situation.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(A very old man is on the viewscreen)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Starfleet received this subspace transmission two days ago, Admiral.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Let's see it, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "On viewer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(A middle aged man sitting behind an empty desk)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(On viewscreen) I am Karnas, governor of Mordan Four.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A dissident group of terrorists have taken Federation Ambassador Hawkins and his staff hostage.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They will not discuss terms with me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is a crisis I cannot resolve.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The terrorists are demanding a Federation negotiator.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I feel there is only one negotiator with the skills to resolve the situation.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The lives of the hostages will depend on Starfleet delivering this man to Mordan.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Commander Mark Jameson.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Admiral Jameson.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The terrorists have given you six Earth days to bring him here, or the hostages will die.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Starfleet cannot understand how or why this situation has developed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mordan Four has finally gained peace after decades of civil war", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Forty years of civil war, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Karnas was largely responsible for the planet's unification and peace.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why should he now be unable to deal with this rebellious faction?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Forty-five years ago I negotiated a hostage situation on Mordan, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Karnas was a young man then, but so was I. He seems to feel that I can handle the situation again.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm detailed to take you to Mordan Four as soon as you can be ready, Admiral.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "My wife and I will beam aboard at fifteen hundred hours, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Acknowledge.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Received and acknowledged, sir.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Picard out.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(TROI) Nearly fifteen hundred now, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Well, Number One, let's go and welcome the Admiral aboard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Jameson is in an enclosed wheelchair similar to Captain Pike's, but with his upper torso free to move)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Welcome aboard, Admiral.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is my Executive Officer, Commander William Riker.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(RIKER) My pleasure, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Ma'am.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "My wife, Anne.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, there are certain details of this mission that you should understand before we begin.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I am not simply an advisor.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "On any assignment I accompany, Starfleet has designated me Senior Mission Officer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I control the away team and all its actions.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is that understood?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Of course, Captain, you command the ship, but the mission is mine.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I trust you are in complete agreement.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Of course.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(GEORDI) Message coming in from Mordan, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Put it on the screen, Mister Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Mordan Four to Enterprise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is Karnas.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I will speak to the Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am Captain Picard, commanding the Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Is Admiral Jameson on board?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He's here, beside my First Officer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do you wish to speak to him now?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) So, Jameson.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I see time has not been kind.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It seldom is, Karnas.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "However, we could save a good deal of it now if you'll outline the terrorists' demands.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(On viewscreen) They insist all discussions will take place here on Mordan.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They refuse to speak to me, only to a Federation mediator.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They say they will brook no excuses.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If there are any offered, your ambassador and his people will die.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Unpleasantly.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I believe them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "So should you, Admiral.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Tell them I accept the conditions of negotiation.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(On viewscreen) I will do so immediately.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(transmission ends)", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "For a man in his position, he doesn't seem to know much about the situation.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Wrong, Number One.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He told me that the terrorists are desperate enough to kill if they're crossed.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They are willing to talk, one on one, but not to him.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Counsellor?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "From his body language as well as his words and tone, I'd judge that what Karnas said was honest.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "But I sensed a holding back.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Perhaps he knows more about the terrorists than he's prepared to admit.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Possible, but I suspect it's closer to the mark if we said he doesn't want to admit failure of his own efforts to negotiate.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He is a proud man.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That could be, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Karnas has established himself as a powerful man on Mordan.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Suddenly, in this situation, his power is useless.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) Sickbay to Bridge.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I'm ready for Admiral Jameson now.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "In a few moments, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Ready for what, Captain?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Just a routine medical checkup.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Ship's regulations.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Ready room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What I don't understand, sir, is how Karnas knew you were still available.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Still alive, you mean.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "So far as he knew, sir, you could have been dead.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Quite right, Commander, but I am not, and I imagine Ambassador Hawkins has told him that.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I briefed the Ambassador about Mordan before he left to take up residence.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All right.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Karnas has a problem he thinks you can resolve.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What do the terrorists need that Karnas can't, or won't, give them?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(DATA) Mordan has had peace for five years.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "They have finally come to a state of productivity which satisfies their planetary needs, but no more than that.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Perhaps the dissidents feel the Federation could provide them with more.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I have negotiated many treaties on many planets, Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I've found that peace, or the appearance of it, is often a prelude to war.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Admiral, are you suggesting the terrorists want arms and weapons?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "They may.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am suggesting Karnas is the last man to give them such weapons.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I see.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "You believe that Karnas would not negotiate on that point, and so the terrorists demanded an outside negotiator.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I know Karnas.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He is a warrior.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He lives for challenge.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "These people have insulted his honour by taking the embassy staff hostage.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll negotiate, but I'll have to be very careful in dealing with Karnas.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He'll want revenge for that insult.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Jameson's quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Anne is unpacking)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(ANNE) Well, it's about time.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Couldn't get away from the Bridge?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Didn't want to.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Feels good to be on one again.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm glad, Mark.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do you want me to help you up?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The front of the wheelchair opens, and he eases himself out and into a nearby chair)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You're stronger today.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm fine.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I feel like a kid again.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This ship is magnificent.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It even has family quarters.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Pity we didn't have them twenty, thirty years ago.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We could have been together almost all of your career.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Mark!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(he's in pain) Mark?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll get Sickbay.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No. There's no need.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's happened before.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "When?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You haven't said anything.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It'll pass.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Just give me a moment.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "See, I'm better.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's going away.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What is it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do the doctors know?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Just body changes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Last checkup they said I'd have to expect it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're sure?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Don't lie to me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm sure.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Ready room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All the medical information the Admiral provided is satisfactory, sir.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Excellent, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Except for one thing.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The test results he gave me aren't two days old, they're two months old.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The medical file coder always includes the date as part of the file number.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "He lied to me, sir, and I don't know why.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "He is eighty five years old, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "For some, the memory begins to fail.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He suffers from Iverson's Disease, sir.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It affects the body, not the mind.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "No, I have a gut feeling he's hiding something.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "That is an observation I'd expect from Counsellor Troi.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Doctor, I do respect your opinion, and I'll want you on the Bridge for the next day or so.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Admiral must remain in the best of health for these negotiations, and he might require your services.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Admiral, we're approaching the Idini Star Cluster.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Would you like to take the conn as we make transit?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I would.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(And he gets out of his wheelchair and walks a little unsteadily down the slope to Geordi's station)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Well, Admiral, quite a little surprise you've pulled on us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, quite.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Neglected to mention I began some new therapy before I left.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Seems to be working.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I haven't felt this good since the last time I was in space.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Gettysburg.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All I needed to get me out of that chair was the thought of walking the decks in command of a starship again.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Admiral, you only have the conn temporarily.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "A figure of speech.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Of course she's your ship.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll just keep an eye on her for a while.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Ready room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The admiral displayed a remarkable improvement out there, wouldn't you say?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, no one recovers from Iverson's Disease.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "There is no known cure, and there are no cases where it has gone into remission.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I have never heard of any therapy that would produce results like that.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Then how do you account for it?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I can't.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "All I can tell you is that the Admiral has been confined to his support chair for the last four years by the effects of Iverson's.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "By all the medical facts we know, he should never have walked again.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I want you to look into it, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thoroughly.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Jameson's quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(On monitor) The terrorists have given you six Earth days to bring him here, or the hostages will die.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't know how much more information you think you can get from that tape.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Jameson stands up) Mark!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "My God, what's happened?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's the new therapy I'm taking.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's working, Annie.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(His hair is sleeker and thicker too.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "And blond now)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Oh, darling!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(She kisses him, but when he responds amorously)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Mark?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I told you I just needed to get back into space again.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You really are looking much better.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's the new treatment.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No. Come here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I hope you're heading for the bedroom.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You stop it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(She brings him to a mirror and puts the light on)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Darling, you look like you looked twenty years ago.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're flatter me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now you tell me what the hell is going on?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't know (collapses in pain)", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sickbay!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Medical emergency in the Admiral's quarters!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Crusher's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I found traces of chemical substances in his blood and tissue samples, but none of them are in our pharmacopoeia.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I'm still working on alien references to substances like these.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "All I can tell you is that he's ingested something that's strongly affecting his body.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Specifics, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, there are so many things going on, I can't give you specifics until I do a lot more tests.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Give me what you have now.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "His red cell count is running riot.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The cellular structure of his body is radically changing, but we can't make any decisions on that until we know what it's changing to.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "His DNA is skewed.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Don't ask me how, but he even looks younger.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "And Captain, there are absolutely no traces of Iverson's Disease.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "You said there is no cure for Iverson's.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "None that we know of.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "But whatever these substances are doing to his body, at least they've done that for him.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "But how or why?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It's too early to say.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Get me some answers, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "As soon as possible.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Jameson's quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Mrs.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Jameson, I have to ask the Admiral some questions.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Ask away.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There's nothing to hide now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(He is a lot younger, thirties or forties)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Admiral, in addition to your rank, you are a particularly valuable commodity just now.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Starfleet has a right to some answers.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I've planned this for a long time, ever since I learned I had Iverson's Disease.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Since it put me in that chair.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What did you do, Mark?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There's a planet in the Cerebus system, Cerebus Two.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They say the natives have a process that rejuvenates the body, gives you your youth back.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, I've heard of that story.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's a myth.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's true, Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm living proof.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh, it's dangerous.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The mortality rate is high, and it's very painful.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Aliens are seldom allowed to obtain the process, but I managed it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I negotiated a treaty for Cerebus Two some years ago, and they felt obligated to honour my request for the process.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Obviously it works very rapidly, but how does it work?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The herb and drug combinations are self-administered slowly over a period of two years.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Every response is different, depending on a being's DNA. I got enough for both of us, Annie, but I had to test it on myself first.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I couldn't risk you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If I died, well, I was half a man, so what did it matter.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It would matter to me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But I was starting to change, Anne.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It was almost undetectable, but the improvement was there.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then when this hostage situation came up, I knew I'd have to be at my peak to deal with it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I didn't have time to wait for the drug to work naturally.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "So you took the whole dose.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I took both of them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And look at me, Annie, look at me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm strong.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm alert, Fit.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm fitter than you are, Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And I'm getting younger!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The only question I have, Admiral, is why you thought it necessary to be young to negotiate the release of the hostages.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Even willing to put your life at risk for it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll be on the Bridge, Admiral, if you want to talk further.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Picard leaves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Why didn't you tell me, Mark?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We've always been honest with each other, until now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Annie, I did it for us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh, I'm sure you believed that, until you had a chance to head up a mission, to command again, and then you just upended the bottle or whatever it was, and damn everything.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Did you think about me at all?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I obtained enough of the dosage for both of us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But you never asked if I wanted it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's just like you, Mark, to assume that what you felt was right was the only answer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But it was the right answer for me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It was killing you, having to take care of an invalid.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Annie, what good was I to you?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We can be together again.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let me go!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I can get you another supply of the drug.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Observation lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The lounge is in darkness, and Jameson is at the table with a desk monitor)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Commander Data, I wish to open a communication frequency to Karnas.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "One moment, Mister Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Admiral, this is Picard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "May I know the reason for this communication?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Observation lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Karnas must have tried to negotiate with the terrorists before he was forced to call on me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I want all the information he has access to.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You said Karnas was a proud man, Admiral, probably sensitive about his failure to deal with this situation.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Is it wise to press him on this?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Observation lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm the negotiator, Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Karnas will have to cooperate with me and the terrorists, and damn his sensitivities.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then I suggest a secured channel, Admiral.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Observation lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A sensible precaution, Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Make it so.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Make it so.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Frequency open and secured.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Observation lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Karnas.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) What is it you want, Admiral?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We're on a secured channel, Karnas.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You can talk freely.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Who's behind this thing?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You said dissidents.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(On monitor) Yes, political opponents who feel the Federation will disavow me as Governor if they stir up enough trouble on Mordan.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then who is it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ardan?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Gilnor?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(On monitor) They're dead, Jameson.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A long time now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "For a long time I thought you were dead, too.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I almost died a couple of times on missions, but I always made it through somehow.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(On monitor) Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now you're coming back to Mordan to negotiate for hostages again.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's almost as though the forty five years in-between never happened.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You've never forgiven me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(On monitor) Of course I have.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It was years ago.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There are no dissidents, are there?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No terrorists.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You have the hostages.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(On monitor) And if I have?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're coming to Mordan, Jameson, and you're going to negotiate for their lives.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And I'm going to ask a very, very high price.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What if I refuse?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(On monitor) Then the hostages will die.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You've seen how my executioners work.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They are still as efficient as they were, perhaps even more skilled now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You'll come, even if you don't have much honour left.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Conn, are we still on schedule to arrive at Mordan in thirty eight hours?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(LAFORGE) Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We're at warp four.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Picard, I want a jump to warp eight, so that we arrive ahead of our announced ETA. It'll throw Karnas off balance, and we can catch him before he's ready.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ready for exactly what, Admiral?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I believe Karnas has the hostages, and there never were any dissidents.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What has happened to bring you to that conclusion?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That's quite a big jump.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm not at liberty to say, Picard, but negotiations are no longer the answer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Isn't the most important thing the hostage's lives?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And you agree with that too, Riker?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I do, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Good.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Because I plan to personally lead an away team on an armed rescue mission to get them out of there.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Later, Jameson is in the Captain's chair, giving the orders)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Commander Data, bring up the plans I requested on the viewscreen, please.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Most of Mordan's principal city was devastated during the war.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What was left consisted largely of a network of underground tunnels.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A rat's maze.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "When they rebuilt the city, they simply built over the tunnels.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I have it, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Pardon me, sir, but where do these tunnels come into the picture?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is where Karnas held his hostages before and where I believe he has them now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Wouldn't that be a little obvious, Admiral?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Karnas is a dogged strategist, Picard, not a brilliant one.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He sticks to what works.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He will kill the hostages if we do not get them out of there.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Therefore you see this armed raid as the only option.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You don't agree?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "With respect, Admiral, I would point out no one else has heard or considered Karnas's demands.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Federation might view them differently if they knew what they were.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Starfleet has given me command of the away team, Picard, and I intend to use them as I see fit.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Doctor's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Our anniversary is next week.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Our fiftieth.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "In his heart, he's still the same man you married, Anne.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "That's true.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "He still cares for you, despite the physical changes he's undergoing.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "But he's getting younger, and I'm, well look at me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why did he do this?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Because there are lives at stake, Mrs.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Jameson.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "What about our lives, his and mine?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I was looking forward to time together finally.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Our retirement.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now he's young again, and has his life to live over.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Doctor, she has to know.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Know what?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Anne, your husband.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "What is it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He's not stabilising.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "He may not have that life to live over.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Observation lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(It's dark, so we can assume that Jameson is even younger than last time.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Picard enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "We'll be at Mordan in three hours.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I couldn't sleep either.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I never could before a mission.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I always wound up in the observation lounge, staring out at the stars, thinking.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Perhaps in this case, rethinking?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Jameson turns so we can see the brown-haired, smooth-skinned twenty something he has become)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Why is this mission so important to you?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why did you risk your life to lead it personally?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I want to save lives, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Noble sentiments, but that's too easy an answer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You've been here before.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You negotiated a release of the hostages with Karnas before.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why is it now your answer is an armed raid?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's my away team you're sending in there.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I think there's something you're not telling me, and I have a right to know what it is.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do you know the background?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Mordanites had some sophistication, but were still ruled by tribal family units.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Karnas's father was the ruling chief of one of the families.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Another tribe had him assassinated.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Karnas seized the passengers of a starliner and held them hostage, demanding that Starfleet provide him with weapons that would enable him to defeat his rivals.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That's the official record.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Officially, the story is that after two other mediators were murdered, I went in and negotiated with Karnas to bring out the hostages safely.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Are you saying that's not the truth?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It wasn't my golden oratory that saved them, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I gave Karnas the weapons he wanted.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You did what?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I gave exactly the same weapons to his rivals.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "My interpretation of the Prime Directive.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let them solve their problems with those arms on an equal basis.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And that decision plunged them into forty years of civil war.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I didn't know that would happen.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I thought a minor war.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It would be settled in less than a year.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How would I know it would take four decades?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But I falsified the reports to Starfleet, and I lived with that on my soul, Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sixty-three people came away safe but millions died on Mordan because I delivered those weapons.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Karnas could have worked for peace during those years instead of continuing the war.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's not all on your head, Admiral.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But I started it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I lit the match.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now finally I can vindicate myself, if only in a small way.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I came to negotiate, but that isn't what Karnas wants.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Revenge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That's why he demanded you when he knew that you were still alive.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And I'm not going to give it to him, Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm going to do what I should have done the first time.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We're taking the hostages out by force, if necessary.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No deals.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Worf, Tasha, Data and Geordi are already on the pads)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Prepare to energise, Chief.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Belay that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Admiral, your proper place is on the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I will remind you one more time, Picard, I am the Senior Mission Commander and I'm leading this team.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sir?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The Admiral is correct, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He has that right.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But I am the Captain of this ship, and I have a right to accompany him.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Riker, you're in command of the Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Energise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Tunnel]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(It is spacious, good high ceiling with light streaming through, curved concrete walls)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Yes, perfect.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We are in the M4 tunnel, directly under the Governor's residence.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "M-4 is a subsidiary tunnel, but it crosses and links with several main ones.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Your information is incorrect, Commander.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I know these tunnels like the back of my own hand.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Keep scanning for signs of human life forms.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Karnas held his hostages in these tunnels before.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "He'll do it again.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(The group head down a tunnel)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain, the Admiral is definitely incorrect.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The tunnel schematics we have show this to be a dead end.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It was sealed off two years ago.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "No doubt you're right, Mister Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "However, forty five years ago, I'm sure it linked in with the tunnels the Admiral remembers.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(They arrive at the dead end)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Damn.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Geordi?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is steelplast, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Fairly recent installation.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is the most direct route.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Set phasers to cut through it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Worf and Tasha cut an opening in the wall)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(TASHA) If you have the coordinates where you think the hostages are, sir, we could just beam in over there.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Karnas may not have them in the same place.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There's no substitute, Lieutenant, for personal reconnoitre.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(He leads them through the hole)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Admiral, there's an infrared light signal ahead.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Steady beam, straight across the tunnel, chest high.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "There's another at waist level.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "An alarm trigger or", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Reset phasers to stun.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(A phaser beam just misses them)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Take cover!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Armed soldiers appear.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Every one ducks behind convenient barrels and suchlike.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Tasha gets in front of Picard.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Jameson stands exposed)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(WORF) Sir, look out!", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Worf knocks Jameson out of the way of another blast)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Their phasers, sir, set on kill.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Thank you, Mister Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I have heard the sound before.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Tasha takes out two soldiers, then Jameson cries in pain)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Is he hit?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "No sign of a wound.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Enterprise, this is Picard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Six to beam up, now!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Admiral?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Sickbay.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Not good is a galactic understatement.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Karnas again, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "On screen.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Enterprise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There has been an armed intrusion in the tunnels beneath the city.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That smells of Jameson.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Where is he?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Karnas, this is Picard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm sorry to inform you the Admiral is critically ill.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) That is not my concern.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sick or well, you have ten minutes to beam him down.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Sickbay to Bridge.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Picard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Doctor Crusher", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Requests your presence in Sickbay immediately, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm on my way.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Turbolift]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sickbay.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What is it, Doctor?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I think you'd better see for yourself, Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(Jameson is on a bed, very ill)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Picard, I have to get to Karnas.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I can't allow him out of my care, Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It's my last option, Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The raid failed.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All I have left to bargain with is myself.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Bridge to Picard.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Picard here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) A new message from Karnas, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If we don't deliver Jameson in five minutes, one of the hostages will be executed.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Fifteen minutes after that, another.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He promises the method of execution will be most painful.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let me go.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If I give myself over to him, he'll let the hostages go.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's me he wants.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It means almost certain death for you, Admiral.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "My life for how many hostages, Picard?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Twenty?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let me go.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Number One, inform Karnas we'll be beaming down in less than five minutes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Did you say we, sir?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You have your orders, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Aye, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Karnas's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard, Crusher and the young Jameson beam in)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Who are you?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I want Jameson.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Karnas, this is Admiral Mark Jameson.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This charade will accomplish nothing, Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Beam down Jameson, and the hostages will be freed.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Continue this and I promise you they will die.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And you with them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Peretor Karnas, you always were a stubborn man.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I thought you wanted me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Release the hostages now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Peretor was my old title.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why do you call me that?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I warn you, Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This trick is going too far.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He has been coached for some devious reason.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If this farce does not stopped immediately, you and the hostages will be put to death.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Jameson doubles up in pain)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Doctor!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I am waiting for an explanation, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He is the Admiral, Karnas.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Before he left on this mission, he administered an overdose of an alien de-aging drug to himself.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He wanted to face you with strength again, on even terms.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is what the drug has done to him.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This story you are telling is unbelievable.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Jameson is a man of more than eighty years, not this.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I want that old man, Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I want to show him this world he helped to make.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The scars on old soldiers' bodies.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The graveyards of our young dead.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The wasted cities we are still rebuilding.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And all of it caused by him.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "As I understand it, Karnas, you were the one who demanded the weapons to avenge your father's death.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But Jameson didn't give weapons to only us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If he had, we would have quickly triumphed over our enemies, and there would have been peace in all these decades.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I want him to pay for that, Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, I think Mrs.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Jameson should beam down now.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Enterprise, this is Picard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Have Mrs.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Jameson prepare to beam down.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Karnas, I asked Doctor Crusher to bring with her the visual records we've made of Admiral Jameson's deterioration.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Will you look at them?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You're wasting my time, Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Just a few moments.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You saw the Admiral on the Enterprise when he was en route to Mordan?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And you recognised him?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is this the man you saw?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(picture of old Jameson on a small screen)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "This is the man I want.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He's here, Karnas.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Look.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Once he took the compound there was no way to stop it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Anne beams down and goes straight to Jameson and Crusher)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You'll have to deal with me, Karnas.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Whatever you wanted from Jameson isn't possible any more.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And you wanted revenge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You blamed your war on him, and there's no doubt he had a lot to do with it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But you had the weapons and you used them.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You could have tried for negotiations for peace on your planet long ago.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Instead you chose to fight.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How many of those forty years of civil war are on your head, Karnas?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Jameson has to pay for his crimes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I have sworn it to my people.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Forty five years ago he made the wrong decision.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He wanted to come here to somehow right it, to atone for what he did.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now all he can do is to give himself up to you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He brought this retribution on himself.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No. I don't believe you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're shielding Jameson on the ship and you're telling me this tale to save his life.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Peretor Karnas, there were only two of us in the meeting.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You didn't even trust your lieutenants there.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You told me that old Peretor Sain had ordered your father's assassination.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You told me that you wanted arms to destroy him.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Peace wasn't on your mind.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All you wanted was revenge.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And I gave you the weapons to do it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Jameson told you this.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If it is you, show me the scar.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Jameson shows his wrist)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "There.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The blood cut you gave me to seal our bargain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It is you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Somehow it is you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(takes a gun from the wall) Then die by your own weapon!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No. No, my revenge will be in seeing you live like this.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Such pain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The compound he took is forcing his cells and organs to go further, younger, and they can't take the stress.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It's like they're imploding.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "And I can't give him anything to stop it.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Can you ease the pain?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Crusher shakes her head)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Mark, can you hear me?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll always love you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Annie with the golden hair.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Flatterer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's grey now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I see only the gold.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(He dies in her arms)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,STS+0.2" },
		{ text = "Rest, Jameson.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Your long night, and mine, are over.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The hostages will be freed immediately, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm prepared to be cooperative.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The hostages have been freed by Karnas, unharmed, and the body of Admiral Mark Jameson has been buried on Mordan, at the request of his widow and by the permission of Karnas.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The quest for youth, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "So futile.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Age and wisdom have their graces too.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I wonder if one doesn't have to have age and wisdom to appreciate that, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I hope not, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister La Forge, prepare to take us out of orbit.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Set course for Isis Three.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Leaving orbit.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[img=music]  End Credits [img=music]", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,STS-0.4,UHP-3.0" },
	},
};

-- Star Trek:TNG S1.13 - The Big Goodbye
RecMedia["TNG_S1E13"] = {
	itemDisplayName = "VHS: ST:TNG S1.13",
	title = "Star Trek:TNG S1.13 - The Big Goodbye",
	subtitle = nil,
	author = nil,
	extra = nil,
	spawning = 0,
	category = "Retail-VHS",
	lines = {
		{ text = "(RIKER) First Officer's log, stardate 41997.7 We are about to make a brief but necessary contact with the Jarada, a reclusive, insect-like race known for its idiosyncratic attitude towards protocol.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The Jarada demand a precise greeting, in this case from Captain Picard.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Their language is most unusual.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The slightest mispronunciation is regarded as an insult.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Ready room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(PICARD) Unless it's followed by?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(TROI) The double bars indicate an elongated S sound.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "And the inverted T means to hold the Z.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Unless?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Unless it's followed by three wavy lines, in which case the Z becomes a B.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Exactly.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "What a language.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But you spell knife with a K.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I spell knife with an N. But then, I never could spell.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well, this is an insect mind, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "My mind is barely working.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Take a break.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "No, no.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I want to go over this again.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You could be over-preparing.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "You've been looking forward to the upgrade of the holodeck.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "You have the time.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Captain, you need the diversion.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Dixon Hill.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The program's installed and waiting.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Outside Holodeck]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(COMPUTER) Programme desired location.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Earth, United States, San Francisco, California.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Time period?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "1941, A.D.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "File or access code.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "File Dixon Hill, private detective.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Enter when ready.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain's personal log.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm entering the ship's holodeck, where images of reality can be created by our computer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Highly useful in crew training, highly enjoyable when used for games and recreation.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Hill's Waiting room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(In uniform, Picard goes along a corridor being washed by a cleaner, listening to music - you came to me from out of nowhere, you took my heart - and opens a door marked 312 - Dixon Hill Private Investigator)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(SECRETARY) Very funny, Dix.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What'd you do, lose another bet?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm sorry, I don't understand.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The bellboy suit.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Are you moonlighting at the Fremont?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The uniform.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's totally inappropriate.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I should have changed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Detective Bell, your cop friend, McNary's new partner, was here nosing around.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If he'd have seen you in those threads he'd have you sent to the funny farm.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Mister Leech called twice, and there's a lady named Bradley waiting in your office.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Nice legs.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Not you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Her.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Got a hot date with my fella.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "See you in the morning.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(She leaves, laughing.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Picard opens the door to his inner office and we go into Maltese Falcon territory)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I lost a bet.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(JESSICA) Oh well, at least you're ready for Halloween.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Halloween?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I need your help, Mister Hill.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Someone is trying to kill me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain's personal log.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm delighted with how the Holodeck has created the fictional world of Dixon Hill, the twentieth century detective who has been a hero of mine since childhood.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The illusion is flawless.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The characters I meet are generated by the computer, of course, yet they feel real, they seem real in every way.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Hill's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm not sure who wants me dead.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "My husband, my stepdaughter.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Or a lover, perhaps?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Perhaps.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Or perhaps it's Cyrus Redblock.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I need you to find out.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Name your fee.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Twenty dollars a day, plus expenses.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Agreed.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I haven't said yes yet.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Oh, you'll say yes, Mister Hill.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(she kisses him) If it is Redblock, he must think I've got what he's looking for.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But believe me, I don't.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll take your word for it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Here's a C-note in advance.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Consider it a retainer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(she tucks it in his collar) And next time, wear a suit.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Au revoir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(She takes one of his calling cards and leaves.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Picard looks out of the window)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Remarkable.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Exit.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(He is just leaving the holodeck when there is a knock on the office door)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You'll have to call again.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm just leaving.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm not dressed properly.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll be back.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Picard leaves, the exit vanishes and the visitor enters the room)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(LEECH) (Peter Lorre accent) Mister Hill?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Where are you?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Outside Holodeck]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Memory, save current setting.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Current setting saved.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Holodeck off.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The crew notice the red lipstick on his face as he goes along the corridor)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Observation Lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And when I looked down into the street, I actually saw automobiles!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(WORF) Automobiles?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(DATA) An ancient Earth device used primarily for transportation.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Ah.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Also seen as a source of status and virility.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Often a prime ingredient in teenage mating rituals.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(WESLEY) Teenage mating rituals?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "From that window, I could see an entire, er", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "City block.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "That's right.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sounds, Smells.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(CRUSHER) You make it sound so real.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "That's how it felt.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Incredible.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(She wipes the lipstick off his face)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I'm going to go again, only this time I'm going to dress the part.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why not come with me?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, I'd like that.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I want to take that twentieth century historian.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Who?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Whalen?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Yes, Whalen.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I bet he knows more about Dixon Hill than I do.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Shall I tell him, sir?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Invite him, Mister Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is supposed to be a recreational activity.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The sense of reality was absolutely incredible.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "When that woman kissed me, it was so", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Exciting?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Real.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The subject of this meeting is the Jaradan rendezvous.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister Riker, will you go ahead with the briefing.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is primarily a diplomatic mission.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The Jaradan are strategically important to the Federation.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Previous attempts have failed because they are so easily irritated.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "A slip in the pronunciation of the greeting caused a twenty year rift.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The Captain has to recite the entire greeting without making any mistakes.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(LAFORGE) Simple as that, huh?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "If, on the other hand, the Captain makes even the slightest error", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The Captain is well aware of the gravity of the situation, Commander.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "We are all aware of the tape of the last Federation starship to come in contact with the Jaradan.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It graphically demonstrates what happened when that Captain offended them.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Captain Picard is familiar with that, Data.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Should we not rerun it?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It's not necessary, Mister Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Meeting adjourned.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why would the Captain not want to review all available information on the subject?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Data, when you've seen the Jaradan react once, you don't ever have to see it again.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "This Dixon Hill is a most puzzling character.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Not really.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "He was just a twentieth-century Sherlock Holmes.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Ah, but was his modus operandi not dissimilar?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Worth investigating.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Indubitably, my dear Data.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Indubitably.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "request all biographical information on fictional character Dixon Hill.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Working.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Character first appeared in pulp magazine, Amazing Detective Stories, copyright 1934, AD. Second appearance in novel The Long Dark Tunnel, copyright 1936.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Request complete text of all stories involving said character.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Increase speed.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(The ultimate speed reading course whizzes before Data's optics)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain's log, supplemental.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Jaradan rendezvous still is eleven hours away.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I am about to reenter the world of Dixon Hill, this time properly dressed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "An experience like this is more enjoyable when shared, so I've invited our fiction expert, Whalen, to accompany me.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Doctor Crusher will join us shortly.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Outside Holodeck]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(In trench coat and fedora, Picard looks the part)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Ready for San Francisco, Mister Whalen?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(WHALEN) More than ready, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well, if it's anything like the last time, I'm sure you won't be disappointed", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Request permission to accompany you, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I am totally versed in the genre of the period.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Well, shall we?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Street]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(It has been raining, the tarmac glistens and cars sound their horns)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(VENDOR) Extra!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Extra!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Read all about it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The group cross the street to the news stand)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Hey Dix.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How's tricks?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh, she's fine, fine.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He actually thinks you're Dixon Hill.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Say Mac, I would like to buy a newspaper too, but I don't have any money.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You catch me next time, Dix.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thank you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(reads) Hitler on the move.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Roosevelt presses Congress for British aid.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "DiMaggio streak reaches thirty seven?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "DiMaggio, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Jolting Joe, the Yankee clipper.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Baseball, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It was a national obsession at the time.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The streak they refer to will eventually reach fifty six games.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "And be snapped by a pair of journeyman hurlers for the Cleveland Indians.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Cleveland?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ha!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They got no pitchers!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They ain't never got no pitchers.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What are you, nuts or something?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The record will stand until the year 2026, when a shortstop for the London Kings", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Hey Dix, what gives with this guy?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He's not from around here, is he.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No he's not.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He's er, he's from South America.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yeah.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He's got a nice tan.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard reads the inside headline - Wealthy Socialite Murdered.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Police have several leads in brutal slaying of Mrs.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Arthur Clinton Bradley)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I should have listened to her.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "She told me someone was trying to kill her.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, she's a page from a book.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That's all she ever was.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(BELL) Well, well, look what the cat dragged in.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Cat?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Where the hell did he come from?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "South America.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Can't you tell?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Wherever you're from, while you're in my town, keep your nose clean.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well, tough guy, this time you've really done it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Your goose is cooked but good.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(MCNARY) I'm sorry about this, Dix.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Don't apologise to him.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Where were you last night between ten and midnight?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That would be a bit hard to explain.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yeah?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well you'll have plenty of time to come up with something.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're going downtown.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What for?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "For the murder of Jessica Bradley.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We found this in her purse.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(the business card)", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Status report.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(TASHA) We're being probed, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "The Jarada.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Most likely, but it's long range.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Can't be certain where it's originating.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(The ship shakes as a beam passes through it, including the holodeck, whose controls flicker and door briefly opens and closes a few times)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "One could get the feeling they don't exactly trust us.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Commander, I'm receiving a subspace message from the Jarada.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "That's not part of the plan.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Pipe it through.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) En-ter-prise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We speak to you in your language.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The time has come to honour us in ours.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is Commander Riker, First Officer of the Enterprise.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) You are not captain?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No sir, I'm not.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I suggest we commence with screen to screen communication so we can see each other", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) You offend us!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We will not show ourselves to a mere subordinate.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We await your Captain's greeting with growing unrest.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "End of communication.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Terrific.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Find the Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "He's in the Holodeck.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Outside Holodeck]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Crusher is trying to straighten her stocking seams)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Enter when ready.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "When ready.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "When ready.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(It takes a couple of goes to get the doors open enough to go through)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Police Station]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Crusher enters, wobbling a little on her stiletto heels)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Bogart) Hiya Doc.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "What's cooking?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "You know I had some trouble getting through.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Where's Captain Picard?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "He's on ice.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Pardon?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "He's being grilled.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "What is he, a fish?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "H's being interrogated.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They think he's committed a murder.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why aren't we all being interrogated?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Maybe I should go and help him.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "No, relax.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He's having the time of his life in there.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well, why should he have all the fun?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(She sits down and copies the woman next to here by crossing her legs and pulling up her skirt slightly)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(POLICEMAN) Come on, Toots, let's go.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Interrogation room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Spill it!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I've told you everything I know.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well, you'll just have to tell us again.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "From the top.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "From the top!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh, very good.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I've read all this before, you know.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's absolutely as it should be.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Spill it!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Outside Holodeck]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "LaForge to Bridge.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Riker here.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I can't find the Captain, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) I told you, he's in the holodeck.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'm at the holodeck.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Something's gone wrong.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) What are you saying?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I can't communicate with him, I can't access the programme, and I can't open the doors.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Enterprise log, supplemental.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "First Officer reporting.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Due to an unknown breakdown in the holodeck, we are unable to contact the Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Tasha, take over.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'm going to holodeck three.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Commander?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I've studied all the technical manuals on the holodecks, sir.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I think I can be of some help down there.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Geordi's well equipped to deal with the situation, Wes.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Right now, your duty's here on the Bridge.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Will.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "His mother's missing too.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Come on, Wesley.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Interrogation room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And you say you never met her before she came to hire you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I've already told you that twice.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yeah?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well you're going to have to tell me again.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Look fellas, this is no longer amusing.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Easy, Dan!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You think you're tough, Hill, but you're nothing!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Bell leaves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Don't take him too seriously, Dix.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "His old lady's been giving him a hard time.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You know how it is.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Actually, I do need to get out of here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll see what I can do.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Police station]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Crusher is continuing to try and imitate the other women, this time by powdering her nose.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The Desk Sergeant is enjoying the view)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Something on your mind?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(SERGEANT) Yeah, nut I'm not sure it can be repeated in mixed company.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(he offers her gum) You're a pretty hep lookin' broad.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is that good?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It ain't bad.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You like Tommy Dorsey?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(she nods) I got two tickets for the dance tomorrow night.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Crusher swallows the gum)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Outside Holodeck]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Have you tried the intercom?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Riker to holodeck.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Riker to holodeck!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Wesley takes a piece of equipment and uses it to 'look' at the holodeck controls)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Are you seeing anything?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No, sir.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "What can I do?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Not a thing.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We have to go through this millimetre by millimetre.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "All right.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Get it fixed.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Easy, Wes.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Slow it down.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "If there's an anomaly, you could go right past it, okay?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Interrogation room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Okay, Dix, we're cutting you loose.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That's welcome news.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You better not try to leave town.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If I leave town, the town leaves with me.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I get the feeling your friend doesn't like me.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We know you're dealing with Redblock, Dixon.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Take it from a friend, it doesn't help your case.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Redblock?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll keep it in mind.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you for helping me.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Takes a cigarette, inhales and coughs)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Forget it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Hey, Dix, when are you coming over for supper?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The kids'd love to see you, and you know how much you love Sharon's cooking.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Soon, my friend.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "For the moment, I have other duties.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Blonde or brunette?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "She's a lady, all right, and her name is Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sounds like a working girl to me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll stop by the office with a bottle of scotch.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You can tell me all about her.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Police station]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(McNary sees the look on Picard's face when he sees Crusher, and leaves without another word)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Have a good time?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I don't know.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sometimes it almost seemed too real.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I must say, you wear it well.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm glad you could make it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why, thank you, Mister Hill.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Maybe we should be getting back to the Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We are on the Enterprise.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Oh, yes, of course, so we are.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do we have time to see your office?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Yes, of course.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why not.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, mind if I join you?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yeah, me too, boss.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I'd love to take a gander.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(rolled eyes all round)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Hill's Waiting room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) The holodeck makes excellent use of finite space.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The group enter)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Ah, Mister Hill.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You've been avoiding me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's Felix Leech!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It has to be.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You know me, sir?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well, I've read about you many times.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm very sorry, Mister Leech, but we have to be going.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Call again tomorrow.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You're being quite rude, Mister Hill.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You haven't even introduced me to your charming companions.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That'll have to wait.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But we have business!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(pulls a gun) Urgent business.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Hill's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're not going anywhere.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Not until we have a little chat.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Outside holodeck]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Bridge to holodeck.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "We're approaching the Jaradan sector.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Any word from the Jaradans?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) Not a thing, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "They may be testing us.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Seeing if we'll stick to the arrangements.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) What do we do now, sir?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "We wait.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Well, everything checks out so far.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Ensign Crusher believes the trouble may have been caused by the Jaradan probe.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "If so, it could be very difficult to locate.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Hill's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am not a man to be toyed with, Mister Hill.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You were hired to locate a certain object.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I demand to know what you have done with it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well, I suppose a few moments longer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm afraid I can't help you, Mister Leech.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The game is over.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I assure you, this is not a game.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Jimmy Cagney) Take it from me, Leech, you'll never find it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now, give me the gun.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Leech shoots him in the chest.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Crusher applauds his performance until she sees the blood)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "But, they're not real.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There's massive internal bleeding.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "We have to get him to Sickbay.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "How could this happen?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It will happen again if you don't cooperate.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Quickly, Picard disarms him and thumps him for good measure)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You struck me!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How dare you!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're going to be sorry!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I promise you, Redblock isn't going to like this!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Leech runs from the office)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "If we don't get him to sickbay, he will die!", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Exit!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Computer, exit!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(nothing) Data, try the other exit in the hall.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Hall]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Computer, identify exit.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Hill's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There is a programming malfunction.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The computer refuses to identify the exits.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I'm losing his pulse!", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Computer, this is the Captain!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Computer, identify exit!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Standard orbit, Mister Worf.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Standard orbit.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Riker to Holodeck.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) Nothing yet, Commander.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We're running out of time.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Outside holodeck]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) The Jarada will expect the Captain's greeting.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Well you may have to stall them.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Hill's Office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's no use.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It's just not there.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Suggestions, Mister Data?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I am at a loss, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "We are in a holodeck-created building of 1941.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The computer refuses to accept voice commands.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The controls for the environment are, therefore, not accessible.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I could use some light.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(Data carries a standard lamp over, and is baffled when it goes out because the plug has come out of the wall.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Picard plugs it in nearer the patient)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Leech returns with a gunman and a fat man)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(REDBLOCK) Good day, Mister Hill.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "My name is Cyrus Redblock.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I hope you don't mind us dropping in.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(A very poor, skinny version of Sydney Greenstreet)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I see I have no choice.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Life is an endless stream of choices.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Unfortunately, you have chosen to make my life more difficult.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't suppose you'd be foolish enough to hide it here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't suppose so.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Still, I'm sure you won't mind if we take a look around.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I wish you'd quit asking, since it's obvious you're going to do it anyway.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It's just a waste of time.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Good manners, Madam, are never a waste of time.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Civility, gentlemen, always civility.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Get that stiff out of here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He's not dead.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "From his pallor, he soon will be.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(THUG) You want I should throw him in the garbage, boss?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The room next door will be good enough for now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Don't touch him.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Which one struck you?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It was Hill.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Leech pistol-whips Picard)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "For every action, there is an equal and opposite reaction.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A Newtonian truism which you have obviously neglected.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Dix, you in there?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The Thug greets the policeman and takes the bottle of drink away from him, then his gun)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I saw your light, figured you were working late.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Guess I was right.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Nice company you're keeping, Dix.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You forget to take the trash out this morning?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(That earns him a punch in the solar plexus)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I'm a tolerant man, but I do not tolerate disrespect.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Your devotion to etiquette is highly admirable, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "However, your methods leave much to be desired.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "What have we here?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Looks like a ghost.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yeah.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Where do you suppose he's from?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Where were you hatched, anyway?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I was created on a planet", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "South America.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I've been all over this world and I've never seen anything like you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He's not from this world.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "None of us are.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We are from a world, we're from a world of fabulous riches.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "A world where there are objects far greater than the one you seek.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That's ridiculous!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're a private dick.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We've met before and you never even mentioned any of this.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He's right, Dix.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That's pretty weak stuff.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I wouldn't even buy that line of guff.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am not Dixon Hill.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I just look like Dixon Hill.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He speaks the truth, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "From your point of view, he is only a facsimile, a knock-off, a cheap imitation.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Thank you, Mister Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sorry sir, that did not come out quite the way I intended.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Very, very, good.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What wonderful fiction.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Quite entertaining.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I admire your skill at trying to obfuscate our sense of reality.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It is you who are not real, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It is you who are imaginary characters derived from a work of fiction.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Give it up, fellas.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "These guys are too smart to fall for that story.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am afraid you are not real either, Lieutenant.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I don't want to hear any more of this.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're making me crazy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let me shoot them, Mister Redblock.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let me kill them, one by one.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What an interesting situation.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Perhaps we should test this theory by killing one of them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You've already done that.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "This man is dying.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "This whole thing is senseless.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Hardly.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Senseless killing is immoral.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But killing for a purpose can be quite often ingenious.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well said, Mister Redblock.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What is our purpose?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We are on a quest for knowledge, Mister Leech.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We want the item.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We don't have it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Shall I kill him?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No, kill the woman.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Redblock.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I have the item.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "At last.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I knew it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "God, man, you are a character you are.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Waiting until the last moment, testing my resolve.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All right, where is it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll explain.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But first, tell Leech to back off.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Put the gun down, Mister Leech.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But I so much want to kill her.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Maybe later.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It won't harm us to listen to Mister Hill.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Don't make any deals with that slime, Dix!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The Thug knocks him down again)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "All right, let's begin.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Make your thoughts fruitful and your words eloquent.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Because I don't have to tell you your lovely lady friend's life depends upon it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There is a price.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "By God, I knew you were a man of stripe.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All right, what is it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Mister Whalen's life.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You must help us to save him.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We can't delay much longer.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "We're going to have to tell them something.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Open hailing frequencies.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Frequencies open, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "This is Commander Riker, aboard the Enterprise.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We demand that you (ear splitting noise) Cut that off!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "They're not going to be satisfied with anyone less than the Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Outside Holodeck]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "La Forge to Bridge.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Riker here.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We think we have something, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll let Ensign Crusher explain.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The bi-converter interface has been affected", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Forget the explanation!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Can you do it?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Outside Holodeck]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't know if I should.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "If this isn't done correctly, the programme could abort and everyone inside could vanish.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do you need more time to study it?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) Whether we do it now or later, the risk is the same.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Do it.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Hill's Office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If we can find away of getting him to our Sickbay by fixing our computer", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Computer?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't know that word.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "An electronic or mechanical apparatus capable of carrying out repetitious or complex mathematical operations at high speed.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Computers are used to control, process, perform, or store", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Enough!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let me kill him.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He's really beginning to irritate me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Wesley does something, and the office briefly turns into an arctic snowstorm)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain, the exit.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "That's it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The way into our world.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Remarkable.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is this a two-way passage?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Can one enter your world and return to this one simply by stepping through?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh, yes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Allow us to help Mister Whalen, and we will return with the item.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You really are a scamp, aren't you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do you actually think I'd stay here", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If you were going to go through yourself, sir, that is not possible.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "One look at you, sir, is proof that anything is possible.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Step back, Mister Hill.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'd shoot you myself, but I don't want to deprive my assistant of his greatest pleasure.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "After we've gone, kill them all.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Make sure the bodies are never found.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're insane.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You think you can kill a cop and get away with it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why not?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I've done it before.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Come on, Mister Leech.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Au revoir et bonne chance, mon ami.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Our destiny awaits.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Another world.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A whole new world to plunder!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Leech looks down.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "They disappearing from the feet upwards)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What is this?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What are they doing?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They can't do this to me!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Don't they know who I am?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm Cyrus Redblock!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Cyrus Redblock!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The corridor is now empty)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Hill's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Data takes the gun off the Thug, and squeezes the barrel closed)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
