		{ text = "Data, pick up Whalen.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Take him to Sickbay.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And you, sir?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I'll follow.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You go now.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Data, Crusher and Whalen leave)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I wish I could take you with me.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Someone has to book this creep.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Once a cop always a cop, I guess.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I have to go.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "So this is the big goodbye.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Tell me something, Dixon.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "When you've gone.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "will this world still exist?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Will my wife and kids still be waiting for me at home?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I honestly don't know.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Good-bye my friend.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain!", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Are you ready, sir?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "As ready as I'll ever be, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Open hailing frequencies.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Frequencies open.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Picard loosens his tie, puts his hands on his hips and takes a deep breath)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "This is Jean-Luc Picard, Captain of the USS Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aaaaard klaxon leeeeesss blag blan ar'nik ka'nik.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aaaaard krasulaaa.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Rassss trassss trasulaaaah.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) You have honoured us with your words of greeting.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A new day dawns between us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Applause from the Bridge crew)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "So, did you have a nice vacation?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
