	lines = {
		{ text = "(PICARD) Captain's log, stardate 41365.9.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Enterprise has been ordered to Starbase 74, in orbit around Tarsas Three.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "A routine maintenance check of all systems will be made, and certain upgrades completed, including the holodeck, with which we've had problems.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I anticipate a glowing report.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The ship has performed magnificently, beyond anyone's expectations.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Enterprise glides serenely through the big space doors, into Spacedock)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(LAFORGE) Coming into position.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(RIKER) Stand by to dock.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(DATA) Ready to dock, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Engage mooring beams and lock off.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Aye sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(clunk) And docking complete.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well done.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And well done all.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(COMPUTER) Starbase maintenance approaching airlock five.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The airlock door opens)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(QUINTEROS) Commander Quinteros.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Request permission to come aboard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Granted.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Welcome.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Commander Riker.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The group head down the corridor, including a pair of 'mirror image' small humanoids)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Commander.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're late.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We expected you a week ago.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We were unexpectedly delayed at Omicron Pascal.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Is there anything specific you wish to report, or that we should be aware of?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No. We are very pleased with the operation of the Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I knew you would be - I was in charge of the team which put her together.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And are these gentlemen the Bynars?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They're not gentlemen, or ladies, Commander.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They are a unified pair.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They're always together.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is One Zero.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(device on left of head) And this is Zero One.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(device on the right) They just finished upgrading the computers on the Wellington.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Did a great job.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Your reputation precedes you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm very pleased you're going to be improving our system.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(ONE ZERO) It is a great pleasure", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(ZERO ONE) to work on such a large mobile computer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You have forty eight hours, because at forty eight plus six we have an appointment at Pelleus Five we must keep.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I thought we'd", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "have more time.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm sorry.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This mission can't be delayed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If you want to postpone the work?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Oh, no.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "the best time", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "to do it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We can complete", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "our work within the", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "time allotted.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They work very quickly, but it'll be very tight.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Commander Riker and I will stay on board, be available should you need us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't think we'll need you until we're ready for inspection.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Bynars seem perfect for this.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Even though this is the first time I've ever come in contact with them.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "As I understand it, over time they have become so interconnected with the master computer on their planet that their language, their thought patterns have become as close to binary as it's possible for organic beings.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It'll be interesting to see how they improve a computer as advanced as ours.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Turbolift]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well, I have a little work to finish up, then I'm going to my cabin.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm going to put my feet up, I'm going to turn on my personal relaxation light and I'm going to lose myself in the pages of some old novel.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What about you, Number One?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You've earned a rest.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I've never been very good at organising my time off.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Something'll turn up.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It always does.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(There are four Bynars at a rear computer station.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Wesley is watching them as Riker and Picard enter)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I'll be in my ready room.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And Number One, good work.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I thought there were only going to be two of you.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Because of the limited time allotted us", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We need others", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is there a problem?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No. No problem.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then why are you acting so excited?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Because we have to", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The stacking", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "To reconfigure the computer", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "To communicate with itself", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "More efficiently", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And at higher speeds.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(They turn back to their work)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(WESLEY) You act like you don't believe them.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I'm not sure that I do.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Maybe it's probably nothing.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Perhaps it's just how another species behaves.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Maybe.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'm going to stroll the ship.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You've got the Bridge.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Keep your eye on them.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Tasha, Worf and two others are wearing vivid blue catsuits with knee and elbow pads)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(TASHA) We've been challenged to a friendly game of Parrises Squares by some of the maintenance personnel.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Want to join us on the starbase?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You've already got all the players you need.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We can switch off.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "No, you know if you do that in Parrises Squares you lose the rhythm of the game.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I can't talk you into coming with us?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "No. But win, all right?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The pride of the Enterprise goes with you.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(WORF) Rest assured, Commander, we will be victorious.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "At whatever the cost.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Worf, it's just a game.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "A little friendly competition, You work up a sweat, you have a few laughs, and you make new friends.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "If winning is not important, then, Commander, why keep score?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I think he's pulling your leg.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Believe it or not, Worf is developing a sense of humour.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I hope so, for their sake.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(The team go down another corridor, then the lights go out.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Riker activates panel 40271))", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Computer.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "There's a power reduction in section L-Ninety Four.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Explain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Unoccupied sections are being closed down in order to clear space in the main computer banks.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It is necessary to facilitate the system upgrade.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Full power can be restored if needed.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Observation Lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Data is painting an image on glass)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Well, what do you think?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Tell me what it's supposed to be, then I'll give you my opinion.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It is an attempt at pure creativity.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "What we're investigating is, can Data be creative?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "And this is my attempt, with guidance from Geordi.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I suggested the zylo eggs.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Is that what those are?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Keep notes.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "This project might turn out to be of interest to scholars in the future.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Really?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well of course.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Think about it.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "A blind man teaching an android how to paint?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "That's got to be worth a couple of pages in somebody's book.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Doctor's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You look like you're packing to leave forever.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(CRUSHER) Oh, Commander Riker.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "No, I'm just gathering my notes.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Professor Terence Epstein is at this starbase.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Is that someone I should know of?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "He's the leading mind in cybernetics.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "He lectured at my medical school.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "You know the disaster at Micromius?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well, since then I've been working on an approach that combines cybernetics and regeneration.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It sounds impossible, I know, but I have found an approach which will work.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I mean, what an opportunity.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "To have a chance to talk with Doctor Epstein.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Sorry, Will, I'd love to chat, but I have to go.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Outside holodeck]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Bynars with a different style of clothes are working)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "How's it going?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(ZERO ZERO) (left hand side) We are almost done.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(ONE ONE) (right hand side) The deviation", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "caused by a previous", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "probe has been corrected", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You may use the equipment", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "anytime you wish.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How much has been changed?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What exactly did you do?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Enhancement.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Nothing more.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(They talk to each other in binary)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Would you like to try", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "the enhancement?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All right.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What should I choose?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Computer, I'd like some place to play some music.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "A little atmosphere.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Specify.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Jazz.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Era?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Circa 1958.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Location.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Kansas City.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No, wait.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "New Orleans.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Bourbon Street Bar, New Orleans.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Around two a.m.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Programme complete.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Enter when ready.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Holodeck]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(A nightclub with a slightly raised stage, tables with candles for light)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Very good.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Very good indeed.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Now I'll need someone to play with.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "A trio.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Piano, bass and drums, and a 'bone for me.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(The musicians appear)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Now an audience.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Whoa.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Too many.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I was thinking of something a little more intimate.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(A single blonde in a red dress)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Great job, boys.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "But, computer, blondes and jazz seldom go together.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(a red-head) Now that is truly exceptional.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "But more sultry.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(A brunette at the bar)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Gentlemen, if this is what you call enhancement, you've got a gift for understatement.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(The Bynars work on the internal controls as the band starts playing)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What's your name?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Tell me you love jazz.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(MINUET) My name is Minuet and I love all jazz except Dixieland.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why not Dixieland?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You can't dance to it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "My girl.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What's a knockout like you doing in a computer-generated gin joint like this?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Waiting for you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Waiting for me?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You can't be serious.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Oh yes, Will.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I've never been more serious in my life.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The Bynars leave)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Can I ask you a question about the Bynars?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Why not just ask them?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What is that high-speed sound you make?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "That is our", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "primary language.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How can you process information at that speed?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "We store the information", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "with these buffers.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(on their belts)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "We receive information", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "all the time", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "and save it", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "until we need it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How did you happen to develop this ability?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "It happened over", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "a long period of time.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "To have a society so intermixed on computers has tremendous advantages.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "And a few", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "disadvantages.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Everything under control, Ensign Crusher?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Fine, Captain.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I was just trying to find something about the Bynars.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Where's Commander Riker?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He's in holodeck four.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Shall I call him for you, sir?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "No, I'll go down there myself.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Keep me apprised.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "[Bourbon Street Bar]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Riker joins the band with his trombone, and serenades Minuet with 'the nearness of you)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(BASS) Hey, man, the chick digs you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What makes you say that?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Hey, look at her.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Maybe it's my music.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(PIANO) Yeah, well, about that.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Don't give up your day job.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Too real.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You got that straight, Slim.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Too real is too right.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thanks for letting me sit in.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Ain't nothin' to it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm going to have to leave for a while to see to my duties.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Your work's very important to you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It is me.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's what I am.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Can we dance once before you leave?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sure, why not.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(She snuggles in close) How did you learn to dance so well?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "From following you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I can anticipate your lead.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "So, tell me about your work.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What is it about it that consumes and enthralls you?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Interesting choice of words.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "That's exactly what it does.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You're very fortunate.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I know that.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "To be exactly where you want to be.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And it's great that you realise it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'd be a fool not to realise how lucky I am to be on this ship serving with these people.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's like a dream come true.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Just like this.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "A dream?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is that what this is?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is that what I am?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I know you are a computer-generated image, but your smell, your touch, the way you feel.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Even the things you say and think seem so real.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Thank you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How far can this relationship go?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I mean, how real are you?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "As real as you need me to be.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(They kiss for a long time, then the holodeck doors open and)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Astounding.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Oh, I'm sorry, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I didn't mean to interrupt.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No, it's all right.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Come on in.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You picked a wonderful locale.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is something I might have chosen myself.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aren't you going to introduce me?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain Picard, this is Minuet.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Minuet, Captain Jean-Luc Picard.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Enchantee.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Comme c'est merveilleux de vous voir ici.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Incroyable!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Vous etes Parisienne?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Au fond, c'est vrai, nous sommes tous Parisiens.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oui, au fond, nous sommes tous Parisiens.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The spirit of that city can always enchant my soul.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I have been hoping to meet you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh. Have I been the subject of conversation?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Indirectly.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Come, join us, Jean-Luc.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A glass of wine?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thank you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Will was saying how much he enjoys this assignment.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's a credit to you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "For a ship and crew to function well it always starts with the Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You set the tone.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "At the moment, it's you who are setting the tone.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The sophistication of this programming is remarkable.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "In what way?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The holodeck has been able to give us woodlands and ski slopes, figures that fight and fictional characters with which we can interact, but you, you're very different.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You adapt.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You spoke to me in French.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It was very simple.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "When I heard your name, I merely accessed the foreign language bank.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That's very impressive.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Oui, mon chou.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Observation Lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Data is continuing to paint, or not to paint)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Now what are you doing?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I am awaiting inspiration.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Offscreen) Commander Data?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "This is Commander Data.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Go ahead, please.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm getting an indication of possible trouble in main Engineering, sir.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "[Observation lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Can you be more specific?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm afraid not.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I'm reading a problem with the magnetic containment field which contains the antimatter.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Could you come up here?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "[Observation lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We'll go to Engineering since that's where the trouble is.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Wes, don't disturb the Captain or Commander Riker until we check this out.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Bridge, this is main Engineering.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) This is the Bridge.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Are you running any test programs up there?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) No.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Are the Bynars?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Not to my knowledge.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Is there a problem?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I don't know.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "There's no one on duty here, and we're getting some very strange readings from the magnetic containment field.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The field is deteriorating.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Contact the captain immediately.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I am initiating Red Alert.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Data, I can't maintain the integrity of the containment field.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Engineering to Captain.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(no reply) If the antimatter is released, the ship will be destroyed.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Nothing I do has any effect.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm losing it.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Data, I've rechecked every circuit.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is not a misread by the computer.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Computer, situation analysis.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(male voice) Estimate release of antimatter in four minutes eighteen seconds.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Seventeen seconds.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sixteen seconds.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Engineering to Bridge.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is the bridge.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Alert starbase.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Inform them we are abandoning the ship.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Tell them why.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Initiate automated sequence for departure.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Set course and speed course and speed to put maximum distance between the Enterprise and any inhabited planets.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Shouldn't we wait for the Captain's approval?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(Offscreen) There is no time.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Based on all information presently available, the decision is correct.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "This is Lieutenant Commander Data speaking for the Captain.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Abandon ship.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "This is not a drill.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "All personnel.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "This is not a drill.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(around the ship) I say again, abandon ship.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "All personnel, this is not a drill.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Abandon ship.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(People are walking briskly, but not running.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Well, some are running)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Decks two through four to cargo transporters.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Decks five through ten, proceed to transporters one, two, three and four.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Decks six through sixteen, proceed to transporters five, six, seven, eight, nine and ten.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(the queue is back into the corridor and Wesley and a couple of children and parents beam out)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(CHIEF) Hold your positions please.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Prepare to energise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And energise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Decks seventeen through twenty eight proceed to transporters eleven, twelve, thirteen and fourteen.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Airlock]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Decks twenty nine through forty two, proceed to transporters fifteen, sixteen, seventeen, eighteen, nineteen and twenty.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is not a drill.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is not a drill.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Starbase Command Centre]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Tasha and Worf enter.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Crusher is already there with Quinteros)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What's going on?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Please stand out of the way.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Enterprise is being evacuated.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Why?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Some problem in Engineering.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Get a Security team together.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, Lieutenant.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Ship's log, supplemental.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is Lieutenant Commander Data.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I have put the ship on automated departure and ordered the complete evacuation of the Enterprise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Everyone remaining is leaving on foot or beaming off.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Computer, where are the Captain and Commander Riker?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(female) All decks empty.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Curious.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The Captain is usually the last to leave.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Let's go.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We've only got forty one seconds.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(They step into the turbolift)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I think we're the last.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I hope we are.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Starbase Command Centre]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Data and Geordi beam in)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Did you see the Captain and Commander Riker?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "No. Are they not here on Starbase?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "No, we thought", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You mean they're still on the Enterprise?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir, where is your transporter room?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We have to beam back and get them.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "You haven't time.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(STARBASE) This is Starbase 74.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Enterprise magnetic field is regenerating.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Wait a minute.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "How is that possible?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Unknown.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That changes nothing.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "The Captain and Commander Riker must be in trouble, or they'd be here.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Look.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Your ship is almost clear.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Enterprise gracefully backs out of the space doors and warps away)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Holodeck]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The only red here is Minuet's dress.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Riker and Minuet are staring into each other's eyes)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "And the boy never found out?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh, yes, but not until later when he came back into the room with his little sister.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'll bet both of them were all smiles.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, but by that time both of them had been found out.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You handled that in a very thoughtful way.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're very good with people.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Don't you agree, Jean-Luc?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "She's so very different from the images we've experienced on the holodeck, isn't she?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "She's more intuitive.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's as though she's been plugged into my subconscious.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "She already knows what I want her to say before I'm aware of it myself.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I suppose it's an understandable progression.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Computers make decisions based on inputs and we humans give off a multitude of subtle signs that communicate our emotions.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's uncanny.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I could develop feelings for Minuet, exactly as I would for any woman.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Doesn't love always begin that way?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "With the illusion being more real than the woman?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Oh, Jean-Luc, spoken like a true Frenchman.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well, I think I'll be leaving.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Oh, don't go.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Two's company.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We have time.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There's no rush.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'd really like it if you would stay.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, Captain, stay.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "This is your diversion, Number One, not mine.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Wait!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We haven't danced.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't dance.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Then some more wine.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No, thank you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Wait!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please, don't go.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You can't.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Not yet.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What's the matter?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Why can't he leave?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Exit!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The holodeck doors open and they seen and hear the Red Alert.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Picard goes to the arch controls)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain to Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Situation report.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Riker to Bridge.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Computer, explain Red Alert.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Initiated as a programmed response.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The magnetic field containing the antimatter had weakened.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There was no fail-safe available.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why wasn't I notified?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Unknown.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Present condition?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The magnetic field is now restored.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Containment is restored.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Propulsion is at maximum efficiency.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Locate Lieutenant Commander Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Not on board the Enterprise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Explain.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "All Enterprise personnel except the Captain and First Officer have been evacuated.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Evacuated?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Was the condition that critical?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Are we still docked at the starbase?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Position report.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Co-ordinates four one five nine point two six by eight one nine two one by three one two.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Heading two three three mark four five.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Destination?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Planet Bynaus in the Beta Magellan system.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Bynars.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Am I to understand the Bynars have stolen the Enterprise?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That information is not available.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's the Bynars, and you're part of this.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They made you the lure to keep me here.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "They programmed you while I was relaxing.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "When they saw your interest in me, they thought I could distract you and keep you here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That explains, Riker.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What about me?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Your being here was just a fortunate happenstance.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why do they want with me?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What do they want with the Enterprise?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What's their purpose?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm not programmed to give you that information.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Come on, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We've got to regain control of our ship.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Starbase Command Centre]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Okay, so what do we do?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Which is the nearest Starfleet vessel?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The Trieste.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I know the Trieste.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Too small, too slow.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Plus it's sixty six hours away.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Where are the Bynars?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I haven't seen them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They are obviously still aboard.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Another Starfleet vessel must be sent to intercept the Enterprise at Bynaus.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "They might be taking the ship to their home planet.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "What makes you think so?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It is the best place for us to start.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(to computer) Picard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Access.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Picard and Riker enter the Weapons Room)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain's log, supplemental.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We now know who has commandeered the Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Bynars.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We can't communicate with the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Commander Riker and I will now try to regain control of our ship.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This way.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That's toward Engineering.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "That's our first stop.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Verify containment and initiate auto-destruct.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Initiate auto-destruct?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Our ship has been commandeered by a force of unknown size and intent.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We're here alone.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We must assume the worst.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If we don't regain control, then no one else must have it either.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now, this is the one decision involving the operation of this vessel which requires you and I to be in total agreement.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's the time allotted that concerns me.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "As to that, there's no option.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I know.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's a five minute countdown.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "That's sufficient to get to the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Once there, either we'll get control of the ship and shut off the auto-destruct, or we won't.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This vessel must not fall into hostile hands.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Then let's set it and get going.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(They place their hands on computer screens)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(male) Recognise Picard, Jean-Luc, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Recognise Riker, William T, Commander.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Set auto-destruct sequence.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Does the First Officer concur?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Set auto-destruct sequence.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Now.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Auto-destruct will detonate in four minutes and fifty-nine seconds.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The only place we can stop this is on the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Let's go.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Riker spots strange symbols flashing across a wall display)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What do you make of this, sir?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "An enormous amount of data is being received and stored in the main computer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Just stored.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Why?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's another piece to of puzzle.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Outside Turbolift]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Bridge access denied.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They've locked it off.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We must find another way to get in.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "One of us could beam into the Bridge.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No, it takes several seconds to materialise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You wouldn't stand a chance.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I could beam into the turbolift, force the doors, take them by surprise.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No, we'll both beam in simultaneously in two different areas of the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "With luck one of us will be a distraction.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "There must be only four minutes left.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Starbase Command Centre]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Still no response from the Enterprise.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "They would answer if they could.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Someone else has control of the ship.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do you think I am responsible?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Responsible?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "How could you possibly have known?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "My station is on the Bridge.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "You can't be on the Bridge every second, Data.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You are wrong, Geordi.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I can.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I do not need rest or diversion.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I should not have been painting.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I was negligent.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It's a pointless discussion, Data.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You could have been on the Bridge and it still might have happened.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Commander Quinteros, you have three ships in here.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "We need one of them now.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "We're rushing repairs on the USS Melbourne, but it's still eighteen hours until she'll be ready.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There's nothing else you can do.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Transporter Room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Set coordinates for main Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Port forward for me and aft for you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You're on pad two.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I've set a ten second delay.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Set phasers on stun.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Number One, good luck.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard and Riker beam in but there is no resistance.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The four Bynars are lying propped up against each other, and do not seem well)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Over here, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Why did you steal my ship?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Please try to", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "help us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(They pass out again.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The Enterprise enters planetary orbit as the countdown passes two minutes)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Cancel auto-destruct.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(male) Does the First Officer agree?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Affirmative.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Auto-destruct cancelled.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We're in orbit around Bynaus.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "How are they?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "They appear dead.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Let's see if we can get some answers from Bynaus.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Their main computer is off.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "All sensors reveal that all of the equipment on the planet is inert.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "They can neither receive nor send any messages.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What about all the people in that world who are totally dependant on their computer?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Are they still functioning?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They're probably like these Bynars here.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Dying.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What is this all about?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Why did they steal the ship and bring it here?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What is their purpose?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "They went to an awful lot of trouble to clear computer space.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Let's see what they've stored.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, it's enormous.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Every byte of free space in the computer has been filled.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "They must have made a core-dump from their world to our computer.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I can't get in.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I wish they'd left a note.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Maybe they did.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Minuet.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Maybe.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bourbon Street Bar]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Minuet comes back to 'life' when they arrive at the table)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Tell me what this is all about.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "A star in the Bynar system went supernova and they miscalculated.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The electromagnetic pulse from the explosion was going to knock out their main computer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "So their only choice was to transfer all the stored information and shut down until after it passed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And then reactivate their system and transfer the information back to this main computer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Enterprise has the only mobile computer large enough to handle all that information.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "So what went wrong?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why are they dying?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The star went supernova before it was expected, and you were late arriving at Starbase 74.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why didn't they say something?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why didn't they just ask for help?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I don't know.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't have those answers.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is there anything we can do?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Return the data stored on the Enterprise's computer back to the one on Bynaus.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How can we do that?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You don't know?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "If we had the file name.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "But we don't.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Do you?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I don't know what you're talking about.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They wouldn't intentionally hide it or make it difficult for us to find.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It must be right in front of our faces.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We should call Starbase 74, and see if anyone can decipher this.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "They're dying.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They meant you no harm.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It was their world they were trying to save.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Help them, Will.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Hurry.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Starbase 74, this is the Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do you read me?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Starbase Command Centre]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is Starbase 74.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
