		{ text = "File names can be anything, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They want us to find it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
