		{ text = "In this case, in binary language, which is ones and zeros in groups of eight or sixteen characters.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
