	lines = {
		{ text = "(PICARD) Commander Riker, report to the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(RIKER) On the way.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(BERNARD) Harry!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Harry, come back here!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The running child bumps straight into Riker)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What's your hurry, Harry?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(HARRY) Sorry, Commander.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I was just", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(His father arrives)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Harry!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm sorry if he bothered you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No bother, Doctor Bernard.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'm not going back.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I hate that teacher and I hate calculus.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Everyone needs an understanding of basic calculus, whether they like it or not.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(CRUSHER) You're limping.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Had a small run-in with an aspiring sprinter.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Problem, Captain?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No, more a curiosity.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "For the past few hours we've been tracking faint energy readings in an attempt to locate the source.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's like following a trail of bread crumbs.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The pathfinder lead here and stopped, which is what I knew would interest you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What's our position, Geordi?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(LAFORGE) The Epsilon Mynos system, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you, Captain, you're right.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I wouldn't miss this for anything.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(TASHA) What's so interesting about this system?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Aldea.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Tasha, I'm surprised you haven't heard the stories about Aldea, the wondrous mythical world.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Like Atlantis of ancient Earth or Neinman of Xerxes Seven.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Advanced culture, centuries old.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Self-contained, peaceful.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Incredible technical sophistication providing the daily needs for all the citizens, so that they could turn themselves over to art and culture.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Where is it supposed to be?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "That's the myth.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Somehow, as the legend goes, the Aldeans were able to cloak their planet in darkness and go unseen by marauders, and other hostile passers-by who might rob and plunder.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What a wonderful fairy tale.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(DATA) Scanners still show nothing, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(TROI) Scanners may show nothing, sir, but I'm sensing something very strong.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Thousands of minds.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "From where?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Very close.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "All stop, and hold this position.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "All stop, and hold.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Anything?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(WORF) Captain, I'm recording a distortion in quadrant one, mark nine zero.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "On screen.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The stars wobble)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Shields and deflectors up.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Then a golden planet comes into view)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "It's Aldea, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It has to be.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Captain's log, stardate 41509.1.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Either by chance or intent, we've been led to the planet Aldea, which appeared out of nowhere, hidden behind a sophisticated shielding device.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sensors indicate that the shield is electromagnetic, a complicated light refracting mechanism.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "A cloaking device?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It's got to be pretty sophisticated to hide an entire planet .", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We're being scanned, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We'll let them know our peaceful intention.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Open hailing frequencies, Lieutenant Yar.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Frequencies open, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(A young woman comes on the viewscreen)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(On viewscreen) I am Rashella.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Welcome to Aldea.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am Jean-Luc Picard, Captain of the USS Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We come in peace.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) We know.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We've heard the stories about Aldea, but frankly I never believed they could be true.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Our shield has confused outsiders for millennia, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That's a very long time to have such technology.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why do you reveal yourselves to us now?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) We're eager to meet in person to discuss that, and other subjects of mutual interest.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We're ready anytime.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Excellent!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Rashella and a man appear on the deck, and seem dizzy at first)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(RADUE) We mean no harm.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Our arrival seems to have startled you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It was a little sudden.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm Radue, First Appointee to Aldea.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Welcome aboard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, they haven't been through decontamination.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Our medical doctor is concerned that you didn't go through the regular transporting procedure.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You couldn't transport us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The only way through our shield is our way, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Our cloaking device may be off, but our shield is operating.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We've brought you small tokens of welcome.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(She hands over a bouquet)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "And a personal invitation to a celebration on Aldea.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That would be delightful.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Number One, assemble the away team.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We will prepare for your arrival, Commander Riker.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How do you know", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Your name?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We've been monitoring your ship's communications.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We must return now to Aldea.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Our eyes are very sensitive to bright light.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Rashella.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(They vanish again)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Amazing.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "To exist only in that dreamworld of mythology and then suddenly to be here, right in front of us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now we know who placed the bread crumbs.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We're not here by accident.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Counsellor, do you sense anything?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They want something from us, something we value greatly.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "So much, that they're afraid we won't part with it.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) We are ready to receive you, Commander Riker, and two of your colleagues?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Riker, Troi and Crusher disappear)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Interesting choices.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[First Unit]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I hope Duana and I know how to greet you properly, Commander.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(WESLEY) How do they cloak the planet?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "The theory is simple.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The shield bends light rays around the planet's contour, similar to the Romulan cloaking device.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "But the implementation is quite difficult.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(A golden beam starts to sweep the Bridge)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain!", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Data, what do you read?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Some sort of scanning device, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(It holds on Wesley)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Don't touch him!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This beam is emanating from Aldea.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Shields up.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "La Forge, contact Commander Riker.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Worf, check all decks.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Shields inoperable, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "All decks being probed in a methodical pattern, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "All decks?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Everything?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The entire ship?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I can't reach the Commander.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We're being blocked.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The beam vanishes)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Wesley, are you all right?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It was scary at first, but I really didn't feel anything.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Sir, similar incidents all decks, but only with the children.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[First Unit]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, we are an ancient power, Commander, but we've suffered for it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Our history is littered with the deaths of those Aldeans who wanted more and more things.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Nothing was ever enough.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "So we've evolved a very simple way of living.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "For whatever is taken, something is given in return.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It seems an equitable code.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's brought us peace.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Unfortunately, there are very few of us left to enjoy it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "That is part of what we wish to discuss.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We need help from the Federation to rebuild our heritage.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We need a younger generation, strong and healthy, following in the Aldean traditions.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't understand.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "How can the Federation help influence your children in your traditions?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Because we have no children, Commander.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Rashella was the last child born on this planet.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And your medical community can do nothing?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "They've tried, Doctor Crusher.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And failed.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Perhaps we can help.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Radue, exactly why were we invited here?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We propose a trade.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "One which will solve our problem and give something back to the Federation.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And the nature of this trade?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We need some of your children.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "In payment, we will give you information that would take you centuries to acquire.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That might be acceptable to some other races, but humans are unusually attached to their offspring.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Our children are not for sale at any price.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "We sympathise with your situation, but what you ask is not possible.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And that's your final answer?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That's my only answer.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Now if there's some other way we can help you.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I am sorry you are intransigent.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Rashella beams them away)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Still no response, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The away team return, and Wesley vanishes)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Wesley!", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "They've taken my son.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(all over the ship, children are disappearing.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Alexandra while playing at her mother's feet, Katie while playing a musical instrument)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain, Saucer Section reports six more children are gone.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's the children.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "That's why we've been brought here.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "That's what they wanted.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And that's what they have.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Status.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aldean shield still up.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Hailing frequencies open.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "No response.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Keep trying.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Counsellor, you'll have to gather the parents.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They'll need to speak to you as well, Captain.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Captain, your children are with us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "My word of honour, no harm will ever come to them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Harm has already come to them.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Captain, let us begin discussions regarding appropriate compensation.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Compensation?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You have stolen our children away from their classrooms, away from their bedrooms and you talk about compensation?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You claim to be a civilised world and yet you have just committed an act of utter barbarity!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Captain, we will continue these discussions when you've calmed down.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[First Unit]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(KATIE) (blonde girl half Wesley's height) Wesley, I'm frightened.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I know you are, Katie.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "We all are.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "But we're going to be all right.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Everyone knows where we are, and no one is going to hurt you.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I promise.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(Radue, Rashella and Duana enter)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Greetings, Wesley Crusher.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Custodian indicated that you would be the leader.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Custodian?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Who is the Custodian?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "The Custodian is not a person.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You have been brought to Aldea as our guests.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We'll provide anything you need or want.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We want to go home.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Wesley, all of you have been chosen because you are special.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Just ask for anything you want, and you shall have it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Alexandra, a self-willed red head, goes up to Rashella and shakes her hand)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Observation lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(TOYA) What's happened to Alexandra and the other children, Captain?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How are you getting them back?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Toya, sit down.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Don't give in to fear.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Now, we all knew what the risks were when we signed on, and that's the choice we made.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Now, Captain Picard will do everything possible to bring our children back.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The Aldeans are anxious to discuss compensation.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Compensation?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That will buy us time.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I've asked Doctor Crusher to be involved in the negotiations as your representative.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why were these specific children taken?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We can't be sure.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "We do know the Aldeans can't have children of their own.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Then they won't let them go easily.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I won't insult you by pretending that any of this will be easy.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But I can tell you that the Enterprise will not leave them behind.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You can be assured of that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Can we talk with our children?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Give them some reassurance.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let them know that we are here, and that they will be safe.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll do my best to arrange that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The last time I saw him, I yelled at him.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Rashella's room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There you are!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I have you now!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(She and Alexandra are laughing and happy)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[First Unit]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Two other couples are waiting)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Come in.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Don't be alarmed.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The other children have been assigned to their units.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Units?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "A unit is a group where those of similar talents and interests live together.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do you mean like a family.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Yes, you could call it that.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We already have families.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Does Captain Picard know you're doing this?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "We are keeping nothing from him.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(LEDA) We have been waiting for you so long, Harry.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We are artists, just like you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We see within you the potential of what you can be.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm not an artist.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(ACCOLAN) You will be.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And a great one.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This way.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(they step into an alcove) Unit B, three seven five.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(and they vanish)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Katie.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(MELIAN) (an old man) I am honoured.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thank you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Melian is the foremost musician on Aldea.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Music speaks its own language.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I know we'll have much to learn from each other.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Don't worry, Katie, you'll be seeing Wesley soon.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Katie and Melian leave)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What about me?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Don't I get a say in this?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Wesley, sometimes something happens which you just must accept.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You and the other children are now members of this society.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That will not change.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It is up to you to make the transition as easy as possible for the others.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's your duty, because you are their leader.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Help them to accept it because nothing you, or those on the starship can do will change it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Because like you, we also have no choice.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Alexandra and Rashella come running in, playing)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Where have you been Rashella?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Zena and Aran are waiting to take Alexandra.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I told you that she", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No, Radue.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They can't have her.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I will never let her go.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Any response from Aldea, Lieutenant?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Not yet, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, Data has found something interesting.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The Aldean shield gives evidence of random fluctuations, weakening its structure in isolated sections.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You're saying there are holes in their defence system?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "In essence, yes.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I find it surprising after all these eons that the Aldeans' have not perfected their technology.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "One would assume they would be aware of the weakness and repair it.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Can we beam down an away team through one of those holes?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's a possibility.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It would have to be perfectly timed, and there's no guarantee.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We may be more successful in deciphering the code they use to transport through the magnetic shield.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Can you do that?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Eventually, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I am working on it now but the number of permutations is almost endless.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Keep trying.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "While he's doing that, Number One, I want you and La Forge to try and find some way of getting through one of those holes, because one way or the other we have got to get an away team on the planet, locate the power source for the shield and neutralise it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You're certain they'll negotiate?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Oh, they'll negotiate, or they'll call it that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They've taken what they want.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now they'll rationalise it by throwing us some sort of bone.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And when we don't accept their offer?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The minute they believe that we won't accept their compensation for the children, they'll break off the discussion, they'll disappear behind their shield, locking us out and the children in forever.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That's why I've got to keep them talking.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Custodian room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(There are hexagonal screens on the walls, a single console, and some chairs around the edge of the room)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(DUANA) Custodian, Wesley Crusher's voice will be entered into the authorised file for Third Level clearance.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(CUSTODIAN) Proceed, Duana.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Announce yourself to the Custodian.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Hello, Custodian.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I am Wesley Crusher.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Hello, Wesley.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Your voice will now activate Third Level clearance.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What does that mean?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "You may ask any questions you wish.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thank you.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Duana, what does the Custodian do?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "It frees us from all burden.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It takes care of all our needs.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It regulates our lives.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Who built it?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "The Progenitors.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "When?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Oh, hundreds of centuries ago.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What is its power source?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Wesley, why is that important?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It does what we ask it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It always has.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What difference does it make how it works?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If you don't know how it works, then how can you repair it?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Why would we want to repair it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "From time to time, every computer, I mean Custodian, requires maintenance.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Wesley, Radue is right.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You ask questions I can't answer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ask the Custodian instead.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Custodian, can you show me where Harry is?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Custodian, show me Harry.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(on the little screen, Harry is having his first go with the sculpting laser on a block of wood.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Sawdust flies everywhere)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(HARRY [on screen]) I never did this before.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's fun.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You are going to make him into a sculptor?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "He already is.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He was just never encouraged properly.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The tool Leda is giving him will allow him to bring out his visions.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It will take him years to learn to do that.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Oh, no.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It will happen quite quickly.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Custodian, show me Alexandra.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(She and Rashella are playing with a 3d hologram controlled by a joystick)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Custodian, show me Mason.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Stop, Custodian.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We must work now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There is much to learn.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Wesley looks at the door with the symbol on it)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What's in there?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "The power source?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I don't know.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's forbidden.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain's log, supplemental.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We remain in orbit around Aldea.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's becoming all too evident that talk will not effect the return of our children.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We must neutralise the Aldean shield.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Until then, we are helpless.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sir, Radue is hailing us.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Captain Picard?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Doctor Crusher and I are ready, Radue.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Are you ill, Captain?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No. Doctor Crusher is a Staff Officer, Radue.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Starfleet Regulation six point five seven requires that at least two Staff Officers are present during any treaty or contract negotiations.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Very well.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Not much on pleasantries, is he?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I am not aware of Regulation six point five seven.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "No, Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Neither am I.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I see, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Oh, I see, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Doctor Crusher, please report to the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now, we'll see what they have in mind.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[First Unit]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Before we begin, we want to see the children.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No. We're here to negotiate appropriate compensation, not to pander to emotions.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please sit.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We do not condone your actions.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You're attempting to salve your conscience by offering goods in exchange for our children.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why do you want them back so badly?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You can always have more.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You are trifling with the primal instincts of our species.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I must warn you that human parents are quite willing to die for their children.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Returning the children is non-negotiable.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I promise you, they'll have a beautiful life with us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And they'll be father and mother to a new breed born of an ancient culture.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But how can you be so sure that they can have children?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "You can't.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "But they will.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Our inability to bear children is a genetic dysfunction.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's not contagious.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We sympathise with your situation.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Surely there is a solution which can be mutually satisfactory?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "For us, the problem is solved.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And we're offering you information on areas of the galaxy you do not even know exist.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What more can we offer?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What more would you like?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A significant beginning.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But first, I must ask that Doctor Crusher be allowed to see her son before we continue.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You are a stubborn people.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ah, well, that too can be a positive trait.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You may see him.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Wesley's room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Duana shows Beverly in)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Mom!", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Wes.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(She activates her tricorder)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I knew Captain Picard would get us home.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Not yet, Wes.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(She gives him the hand scanner)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Oh.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Are you okay?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "And the others?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Okay, really okay.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "We've been assigned to our units, like families.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "They're treating us like gods.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(Meanwhile, he walks around Duana, scanning her)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Don't let it go to your head.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I won't.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "[First Unit]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Wes seems fine.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "He says the other children are too.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Of course they're well.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain Picard, you have our offer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It is final.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Rashella.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(On viewscreen) Captain, we want you to understand the nature of your choice.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A small demonstration of our power.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What was that?", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I believe it was a repulsor beam.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Position report.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is unbelievable, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "According to my calculations, we're three days from Aldea.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "At warp nine.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "And they call that a small demonstration?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Geordi, get us back to Aldea.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Warp nine.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Captain, if you don't accept our terms, the Enterprise will be pushed so far away that by the time you return, your children will be grandparents.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain's log, stardate 41512.4.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We are twenty hours away from Aldea, and no closer to a solution.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Their deliberate show of force pushed us out of orbit and could have easily disintegrated the ship.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Music Unit]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Melian is showing Katie how to play his electronic instrument)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Now, hold it a little bit more firmly.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That's it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Perfect.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now think.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No, think's not right.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Feel the notes inside your head.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(She closes her eyes, and the machine lights up with a loud chord.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "She drops it)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I'm sorry.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Did I break it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No, it's all right.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You can't break this.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It did exactly what I thought.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I mean, felt.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, it's a direct expression of the music within.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now we just have to teach you to structure that feeling.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Try it again.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(She produces a melancholy little tune)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "That was beautiful.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now, play something happier.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That's not the way I feel.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't want to do this any more.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Artist's Unit]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You see, Harry, I told you you were going to be an artist.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Are you sure?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Did I really do this?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh, yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Your talent has always been there.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The sculpting tool is just helping you develop it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's what you were meant to do.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You mean I don't have to take calculus anymore?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You don't have to do anything you don't want to do.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What's calculus?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Nothing important.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Can I do some more?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain's log, supplemental.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It has been three days since the Aldeans pushed us away.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We have had no contact with Radue since then.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Open hailing frequencies, Lieutenant Yar.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Frequencies open, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Radue, this is Picard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Welcome back, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What have you decided?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That there is room for discussion, Radue.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) We will bring you down when we are ready.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Data, find a way to defeat that shield.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That may be impossible, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Things are only impossible until they're not.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Crusher enters.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Here's what I've learned so far.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Duana is dying.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I have no reason to believe the rest of the Aldeans are any healthier.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "They're suffering from some kind of chromosomal damage.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It could be genetic or environmental.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "And is that why they can't conceive?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It also explains their lack of appetite, extreme pallor and sensitivity to light.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "An entire civilisation that's terminally ill.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Is it reversible?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I won't know until I find the cause.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "And you don't need to tell me time is running out.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Good work, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Artist's Unit]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Have the lesions gone?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The medicine worked again.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Good.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Come see what Harry's doing.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Beautiful!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Harry is polishing a sculpture)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Yeah, it really turned out well.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It is so alive and graceful.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What do you call it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's a dolphin.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Dolphin?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You know, it swims in the ocean.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Like a fish, only it's not a fish.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ah. A fish.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We used to have them in our oceans.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I've never seen one before.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I used to live near the ocean on Zadar Four.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "My dad's an oceanographer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He says that.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Never mind.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It doesn't matter.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Rashella's room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(ALEXANDRA) I want Mommy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I want Mommy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[First Unit]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You haven't eaten.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You didn't eat anything.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "We need very little.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I know you miss your family.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But you will grow accustomed to us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I promise you'll be happy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We were happy before we came.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "We are offering more, especially for you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Duana, I feel badly for you that you have no children, but I have to tell you that we don't want to be here and we will not cooperate.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "[Doctor's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No. That doesn't fit the pattern.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Give me a list of the appropriate medical literature from the twenty-second century.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "There's not enough time!", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Is there anything I can do?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You just did, Jean-Luc.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Now I have to get back to work.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Wesley's room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(That night, Wesley sneaks out of bed)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Custodian's room.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "[Custodian room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Custodian, this is Wesley Crusher.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Proceed, Wesley.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Show me where the Enterprise children are in relation to this room.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Thank you.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Goodnight.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Good night, Wesley.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Katie's room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Katie is startled awake by a hand on her arm)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I think I know a way to get us home.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I need your help.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Chief Medical Officer's log, stardate 41512.9.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I've begun to suspect that whatever is killing the Aldeans is related to a danger faced by Earth in the twenty first century.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Can it be that Aldea's ozone layer has been weakened?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Wesley's room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why will not talking or eating make them send us home?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's called passive resistance.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "We don't do what they want us to do and then they won't want us.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Will they get mad at us?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They won't hurt us.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "But, Wes, I do kind of like them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "So do I, but I don't want to stay here forever.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Do you?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "No, but I like working with the wood.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Harry, we all have to be in this together or it's not going to work.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Okay.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll do it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What are you all doing here together?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(She reaches for Alexandria, but Wesley picks her up instead)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What is going on here?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I've got it, Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The Aldeans are suffering from a form of radiation poisoning.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Is that what made the Aldeans sterile?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Is it reversible?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "With the proper treatment, yes.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Captain.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Sir.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "These permutations are going to take a very long time.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "How long?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Weeks.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "There is the option.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We were able to further define the weakness in their shield.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now, it's not a hole as much as it is a fluctuation.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "If the timing is precise, it's possible to beam through and onto the planet.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Why don't we just beam the children up through the hole?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The timing would be more difficult, and the risk greater.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "All right.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You and Data beam down.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Locate the power source of that shield.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Knock it out.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll delay the Aldeans until you're ready.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Wesley's room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Will you at least eat?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(She puts the tray on the table and leaves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "No, Alexandra.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Why?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We can't eat.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I know you're hungry.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "We all are.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "But it's our way of telling them that we want to go home.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Understand?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Riker and Data get on the pads)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "We're ready, Lieutenant Worf.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(On viewscreen) Captain, you may come down now to conclude our discussions, or your ship may leave.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's your choice.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Doctor Crusher and I are prepared to conclude our negotiations.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(They are beamed away)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Transporter room, now!", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Aldean corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Data and Riker arrive safely and head off into)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Custodian room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is it, Commander.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It is programmed to accept only authorised voice command.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "There's no way we can override.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No, but I can scramble the input so that nobody else can.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[First Unit]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Before we begin, Captain, you must speak with the children.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It seems they are on some sort of strike.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't understand it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You must deal with this, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm not very good with children.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Strike?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll see what I can do.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Wesley's room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm sure if we just stick together, guys, we'll all be", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(Picard beams in)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Hi, Captain Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Hi, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sir, I knew you'd come.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "It's not over yet, Wesley.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I think I figured out their computer controls, sir, but I'm not sure.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "It's a much different system than ours.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Good, Wesley.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, Harry.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If something happens and you have to leave us here, will you tell my Dad that I'm sorry that I made him angry, and that I miss him.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You will tell him that, Harry, and I will tell him how much I admire his son.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hello, Alexandra.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Arms open for picking up) Hi.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(So he does, uncomfortably, while she hangs on to her pet tribble.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Did I mention that before?)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[First Unit]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The children and Picard beam in)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What is this?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They want to go home.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No. They're staying.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) We're in position.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Hold for orders.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The radiation levels on Aldea have been building up over a long period of time.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It is similar to what was experienced on Earth in the twenty-first century.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "When the ozone layer had deteriorated and the surface of the planet was flooded with ultraviolet radiation.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The shield that protected your world in one way is weakening it in another.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It is the radiation of your own sun that is destroying you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You're trying to confuse the issue.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please, listen.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "You're all suffering from radiation poisoning.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Sterility is only the latest symptom.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "If the children remain, they will be affected as well.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It's impossible.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Our scientists would have known.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How could they?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Your scientists have forgotten how everything works.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "No. All lies.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No. Wait.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Don't send them back yet.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What if they're right?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Instead of the children being our hope, what if we're just condemning them to our fate?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Rashella, they're just protecting their own interests.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "As are we.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But hear them out.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Captain and Doctor Crusher are saying that the very thing which has given us this wonderful world is what has caused this tragedy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That's it, exactly.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Your Custodian has controlled you so completely you've lost even the desire to even question it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lies, and the discussion is over.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(He tries his arm device, but nothing happens)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Commander Riker.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Here, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I assume you have control of the computer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) We've disabled the system temporarily.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Custodian room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We found the power source to the shield and we've neutralised it.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[First Unit]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Good.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Enterprise, this is Picard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Beam the children up.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Wait.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Enterprise, hold.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let me get the dolphin for you to take with you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No, you keep it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll make another one.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All right, Harry?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Okay, kids.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Let's go home.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Beam them up.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The children are beamed up)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You have destroyed us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We are not here to destroy you, Radue.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We can help you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Power room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(It's massive.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "On Forbidden Planet scale)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What is it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A legacy of your Progenitors, Radue.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The source of your power and of your problems.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It is reading incredible amounts of energy, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Even more since we have taken down the shield.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "What are we going to do with it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Learn, all over again.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All this time we've been destroying ourselves, so sure of our technological invulnerability.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now we must learn to use this power safely.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Will you let us help you?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
