		{ text = "(PICARD) Captain's log, stardate 41416.2.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We're orbiting Relva Seven, where Wesley Crusher is about to be tested for entrance into Starfleet Academy.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And to my great surprise, I have just learned that my old friend Admiral Gregory Quinn is on Relva Seven, and has requested to be beamed aboard the Enterprise immediately.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
