		{ text = "I heard there was a very smart, very young man who'd be tough competition.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well, you wouldn't be here", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I weren't smart.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I know.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But there's a lot more to it than just that.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're lucky.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You've had practical experience aboard the Enterprise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
