		{ text = "[Classroom]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(COMPUTER) Last question on the hyperspace physics test.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If the matter and antimatter tanks on a Galaxy class starship are nine tenths depleted, calculate the intermix ratio necessary to reach a starbase a hundred light years away at warp factor eight.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Begin.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "1.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Oliana runs out of time)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Time elapsed.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You now have one hour free before the next test.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I must admit, Wesley, you have a very fast mind.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Once as I realised it was a trick question, there was only one answer.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Yes, there is only one ratio with matter antimatter.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "One to one.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You don't know how lucky you are.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I can't imagine what it would be like to have things come so easily.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I have to push every step of the way.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh, no, Oliana, it doesn't come that easily.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I have to study all the time.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "It's a good thing you're cute, Wesley, or you could really be obnoxious.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "See you later.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Oliana leaves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Did you hear what she said, Mordock?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "She said I was cute.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Is that good, Wesley?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes!", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I think.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I think I should apologise, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No need, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister Remmick's presence is unnerving, to say the least.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Are you available now, Mister Riker?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Or do you still have duties to perform?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm available, Mister Remmick.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Any problem with using your Ready room, Captain?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No, Mister Remmick.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Be my guest.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Ready room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Remmick sits himself in the Captain's chair and gestures Riker to sit too)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "If you prefer to stand, fine, Mister Riker.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It won't have an effect on the length of my inquiry.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Riker sits) Now, there are several seeming discrepancies in the Captain's log.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let's go over them one by one, shall we?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Captain's log?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "To the best of your knowledge, has the Captain ever falsified a log?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Have you discussed this with him?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Right now, I'm asking you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If you want to discuss anything about Captain Picard, you bring him in here and ask him face to face.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You are required to answer my questions, Mister Riker, unless you're trying to cover something up!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now, there are several discrepancies in the Captain's log.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Shall we go over them one by one?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Proceed.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "So, you are saying Captain Picard had no control over this vessel.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He handed it over to Kosinski, who took the entire crew to the edge of the universe.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "That's not what I'm saying.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now, Kosinski was sent by Starfleet to improve our warp drive system.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain Picard was ordered to take him aboard.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "According to his own logs, his Bridge crew didn't think highly of Mister Kosinski's theories, yet the Captain allowed him to access to the engines anyway.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is that true, La Forge?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Not exactly, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "One way or the other, La Forge, Picard lost control of this ship.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is that true?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, but that's not how it happened.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "So, the answer is yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Ready room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do you believe the captain is emotionally and psychologically fit for command of this starship?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There is nothing in his history or his personality that would suggest mental lapses?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(TROI) Nothing.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Not even the Ferengi incident with his old ship, the Stargazer?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He was being controlled by a mind altering machine, Commander.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Without his knowledge.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I would call that a mental lapse.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Holodeck]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(There is no programme running)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(WORF) Ensign, what are you doing on the holodeck?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I thought you were still on Relva Seven.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm finished testing for the day.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I've disturbed you.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll leave.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Wait.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I thought I wanted to be alone, but I guess I don't.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "How is the testing?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Okay.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "So far.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "It's not the ones that I've studied for that I'm worried about.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "It's the psych test.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Facing my deepest fear and living through it.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I'm trying to figure out what images to bring up.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Why?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I guess I want to scare myself.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "What do you think?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Bulgallian rats?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Lightning storms?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Do those things frighten you?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sort of, I guess.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "The psych test is no more or less important than the rest of the process.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "That's what they said, but I can't stop thinking about it.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Thinking about what you can't control only wastes your energy and creates its own enemy.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "How can they know what my deepest fear is when I don't?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "By analysing your psychological profile.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "They were very accurate about everyone I tested with.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Including myself.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I thought there was nothing that could frighten a Klingon warrior.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Only fools have no fear.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm sorry, Lieutenant.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I'm asking too many personal questions.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "It is very difficult for me to depend on anyone for anything.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "But especially for my life.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "But on the Enterprise you do that every day.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Everyone depends on everyone else to protect them.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Yes.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "So you overcame it?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "No. It is still my enemy.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "With this new extricator, sir, we can eliminate three more bulky machines from cargo space.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "That's excellent, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(TASHA) Captain, there's an unauthorised entry in main Shuttlebay.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Unauthorised?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Who is it, Lieutenant?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Computer reads the ID number of Jake Kurland.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Bridge to main Shuttlebay.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Mister Kurland, this is Lieutenant Yar.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Respond.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Isn't the area secured?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Not now, Remmick.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "He's going to take this out, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'm locking off the bay launch doors, Captain.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Too late.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "He's using the flight emergency override", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Smart kid.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Kid?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He's launching.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "On viewer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "lieutenant Yar, open channel.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Enterprise to shuttlecraft.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister Kurland, this is Captain Picard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister Kurland.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Captain, I'm going to Beltane Nine", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Shuttlecraft]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "To sign onto a freighter.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Tell my father I'm sorry.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You can tell him yourself, in person.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Bring that ship back at once.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) No.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Shuttlecraft]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I can't face him.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm leaving.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(alarm sounds)", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, he's unbalanced the dilithium reaction.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Shuttlecraft]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I've lost power!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All he's got left are his manoeuvring jets, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "At this trajectory, he'll enter the atmosphere and burn up at two hundred kilometres.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Probable impact?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Atmospheric entry seventy eight seconds.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Options?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Tractor beam?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Not possible.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "He's too far away for a positive lock.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "He's out of transporter range.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, you are completely responsible for that boy's life.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Mister Remmick, either get out of my way and keep quiet, or I will have you removed from the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Viewer on shuttle cockpit.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Visual on main viewer.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Mister Kurland.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) I can't get the engine started.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What am I going to do?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm going to crash.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He needs another fifty seconds for the core to cool down before it can restart.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Stay calm, Jake.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We'll get you back.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Shuttlecraft]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please, help me!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Jake!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's no use, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm going to die out here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He'll need another thirty seconds before he can restart.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Irrelevant, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Even if he restarts his engine now, he will not have enough thrust to escape impact.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Mister Kurland, you are not going to die out there.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You are going to do exactly what I tell you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now, take the nose of the shuttlecraft and aim it directly at Relva Seven.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Shuttlecraft]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Aim it at Relva?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I can't do that.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's crazy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do it!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) But I'll burn up!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Twenty eight seconds to impact.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Jake, listen very carefully.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is Captain Picard and I am giving you an order.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aim the shuttle at Relva!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Shuttlecraft]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Okay.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's done.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Good.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now, monitor your speed exactly.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "When it hits point zero two zero, I want you to restart the engine", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Shuttlecraft]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) And when I tell you, pull up hard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "JAKE: (Offscreen) Point zero two zero speed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, I sure hope you're right.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) You'll just have to trust me.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Point zero zero three.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Zero zero nine.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Zero one two.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Six seconds to impact.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Hold on, Jake.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's going to be a little bumpy.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Shuttlecraft]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Zero one five.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Zero one nine.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Restart the engine, now!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Pull up hard!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(amongst others) Yeah!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Incredible, Captain!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "How did he do it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I thought", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He built up enough speed and then bounced her off the atmosphere!", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister Kurland, I assume you can manoeuvre it home?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Yes, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Then report to Mister Riker.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Yes, sir!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Very original, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But how did that child acquire access to a shuttlecraft?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Kurland is a highly qualified Enterprise Academy candidate, fully trained in many areas including shuttles.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And did this full training include discipline?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Mister Remmick, young men sometimes make rash choices.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Which is why Mister Kurland will receive a strong refresher specifically in discipline", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll note that in my report.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Full cooperation, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Relva 7 Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(RONDON) You.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I have a package for Operations.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Where is it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "End of the corridor, room on your right.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "One oh four.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(The seven foot tall man bumps Wesley as he walks between him and Mordock)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Excuse me.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "You blocked my path.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "you Bulgallian sludge rat!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm sorry, it was an honest mistake.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I apologise.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Is there a problem here, gentlemen?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No, sir, I", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "How dare you!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am Rondon, you despicable Melanoid slimeworm!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Liar!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Who do you think you're bullying?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "You bumped into me.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "It was your mistake.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "You were at fault.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Do you want this to become violent?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Friend.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I like you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Rondon leaves, smiling)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "A very strange reaction.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Not really.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "When he raised his hand, I saw that it was webbed.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "The sign of a Zaldan.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "But you became hostile.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Zaldans are infuriated by courtesy.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "They view it as a form of phony social behaviour, designed to cover true feelings.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Congratulations, Mister Crusher.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You handled that particular incident very well.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Was this incident deliberate?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's important to know how you candidates deal with other cultures, other species.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then it was a test.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Not all tests are announced, or what they appear to be.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Chang leaves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Zaldans have webbed fingers?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I wouldn't have passed.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Observation Lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're an android, correct?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "And as an android, you are programmed to tell the entire truth?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "There is a problem with this ship, Mister Data.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's in the records, somewhere.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I need your help to find it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All of the ship's records are available to you, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "This information is very cleverly hidden.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Your Captain is not what he appears to be.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do not forget you have loyalty to Starfleet above all else.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Loyalty is not the issue, Commander.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "There is nothing wrong with Captain Picard or the ship's logs.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Therefore there must be something wrong with your original assumption.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "That is not acceptable, Mister Data.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Acceptable or not, sir, it is the truth.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Just how did this contaminant get aboard the ship?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "By accident, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Meaning Captain Picard has no standing procedure for this type of situation?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No. Meaning by accident, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You don't like me very much, do you?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is it required, sir?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "How would you characterise your relationship with Captain Picard?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We're Starfleet officers who've known each other for many years.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Everything said here is confidential, Doctor.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You can be completely open with me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "About what?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "About how you feel serving with a man who is responsible for the death of your husband.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "My personal feelings about Captain Picard are irrelevant to this investigation, and none of your business.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Then you confirm the accuracy of the log report.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You violated the Prime Directive with the Edo.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You deliberately interfered with their laws.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's exactly as I explained it in the log records.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "All to save Doctor Crusher's son?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A member of my crew was being held unjustly.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I stand by my decision.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister Remmick, you have talked to every member of this ship.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I think you've had enough time to find out whatever it is you're looking for.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Are you afraid if I keep looking that I'll find you're guilty?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The only thing I'm guilty of is allowing this charade to go on so long.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Admiral's Quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Admiral, if it is me you're investigating, ask me directly what it is you want to know.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And please tell me exactly what is going on.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I need a little more time, Jean-Luc.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How much time?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I won't tolerate my crew being harassed any longer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Remmick's full report is almost ready.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I want to be here when he presents it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That has always been my intention.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Commander Remmick, report to Admiral Quinn.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) On my way, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This has been a strain on our friendship.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I know.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Believe me, Jean Luc, I regret that, but it has been necessary.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Classroom]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The dynamics relationships test is on the screens, and there are a lot of beeps going on)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I can't get it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, you can.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Don't fight it.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Relax into it and let it come by itself.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "No, it's going too fast.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Wesley, I can't do it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Wesley turns from his own screen to look at Mordock's)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Yes you can.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "You've got your rotation factor.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Just put in your vector coordinates.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(Mordock finishes first, Wesley second.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The Vulcan finished too.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Time elapsed.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Chang enters.)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Congratulations, Mordock.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That was the second fastest time ever recorded on this test.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You all did well.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No, it should not have been that way.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Mister Crusher helped me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I know.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "An interesting choice, Mister Crusher.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Especially considering how close you and Mister Mordock are in overall score.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You all have an hour to prepare for your last test.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The psych test, sir?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "We prefer to think of it as a psychological evaluation based on reactions to various individual problems, but psych test will do.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Admiral's Quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Remmick enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Sir?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please sit down, Mister Remmick.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Proceed with your report.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Admiral, I've done my best to be thorough during this investigation.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Continue.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I couldn't find what you asked, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I spoke to officer after officer, at length.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I pried into the ships log reports.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And yet I could find nothing wrong.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Except, perhaps, a casual familiarity among the Bridge crew, but mostly that comes from a sense of teamwork, and the feeling of family.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm sorry, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I did my best.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Quite.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're dismissed, Commander.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain Picard, my tour in the Inspector General's office will be up in six months.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "When I'm finished, this is where I'd like to serve, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(No reply, and Remmick leaves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Don't judge the young man too harshly.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He's a good officer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's not him I'm inclined to judge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Don't judge me too harshly either, until I've finished.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We had to be very sure of you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Some of us at Starfleet Command became suspicious of certain problems in the Federation.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What kind of problems?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Something or someone is trying to destroy the fabric of everything we've built up in the last two hundred years.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What's your evidence?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I can't go into that.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There are too many people involved.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What do you want from me?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I don't know whether the threat comes from the inside or whether it's from outside.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I need people I can trust in strong positions throughout the Federation.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You have my complete support.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You know that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That's not enough.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I want to promote you to Admiral, and I want you to take over as Commandant of Starfleet Academy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Academy.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Academy?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I need you close.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then there was never a problem with the Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No, but I had to be sure you hadn't been co-opted.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Greg, this is politics, and I'm not good at politics.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Surely there are others who are better suited.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "All right.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Even if I am wrong, and I hope I am, you're still the best man for the job.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I appreciate the value of what you're offering.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's not a decision I can make quickly.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I need an answer soon.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All right.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You'll have it tonight, Admiral.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Relva 7 Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Wesley is sitting nervously on a bench when Chang comes out of a room)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Mister Mordock will be finished with his psych test momentarily.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The door to room 101 opens, Mordock is shaking)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Mordock?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Are you all right?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I will be.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Mister Crusher.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're next.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Good luck.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Room 101]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(A small room with bare walls and a single chair in the middle)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I'm here.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I'm ready.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(nothing) Maybe they forgot.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Breathe.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Got to remember to breathe.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(The sound of an explosion outside.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Wesley goes to investigate)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(There's a series of bangs, and the sound of a voice calling out)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Evacuate immediately.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sixty five seconds to seal off.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Somebody help!", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "There's something's wrong in the Environmental lab!", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(Offscreen) Help!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Wesley opens the door)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Environmental Maintenance Laboratory]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(There's steam from ruptured pipes)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Help!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We're trapped.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I can't move.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please, help me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Wesley sees him pinned under a piece of pipe)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "We're going to die in here!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(There's a second man cowering in the corner)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The liquid hydrogen's going to blow!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The shut off valve ruptured.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Hurry up!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "My legs are crushed.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You've got to help me!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Wesley goes to lift the debris, and calls to the uninjured man in the corner)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Give me a hand with this!", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(MAN 2) We're going to die!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Help me!", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I can't!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's too late!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thirty seconds to seal off.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Wesley gets the pipe off the injured man)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Come on!", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "If we don't get out of here fast, they'll seal us in to contain the explosion.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(dragging the casualty) Come on, you're not hurt.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I can't go through that!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, you can.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "You've got to.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Twenty seconds to seal off.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Come on!", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I can't carry you both!", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "You're not hurt.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Ten seconds to seal off.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Come on!", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "It's too late.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The doors are closing as Wesley drags the casualty out)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I'm sorry.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "[Relva 7 Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Officer Chang!", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "There's", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Wesley!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's all right.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The casualty gets to his feet, dusts himself off and shakes Wesley's hand)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Thanks.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "An excellent performance, Mister Crusher.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The other man walks out of the lab and leaves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Performance?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "That was the test?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A man could have died.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Theoretically, yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You had to make a choice.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And you did.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There's no right or wrong about it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Your greatest fear has been that you couldn't make that decision.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Because of my father?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Because Cap.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Because someone made that choice, and my father died.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "[Ready room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sir?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes, Number One?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister Remmick has left the ship.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He found nothing wrong on the Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And you can inform the crew that Admiral Quinn is most impressed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "They'll be pleased that.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Can you explain now what he was after?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "They were after me, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They want me to take over as Commandant, Starfleet Academy.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Congratulations!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What a wonderful choice, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You'll be able to shape the minds of the future leaders of Starfleet.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You haven't decided what you're going to do.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes, I have, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I've decided I'm going for a walk.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Classroom]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm proud of all of you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You've done a superb job.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Each of you would make a fine Starfleet officer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's unfair that only one candidate from Relva will attend the Academy this year, and a loss to the Federation if the rest of you do not return to test again.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Mister Mordock will be the candidate.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "His results were slightly higher than Mister Crusher's.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Congratulations, Mister Mordock.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're the first Benzite in Starfleet.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thank you, sir, but it's not right.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It shouldn't be me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Wesley lost points because he helped me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He should not be punished for his generosity.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He wasn't.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He lost time, but it wasn't only that.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Candidates, thank you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And good luck.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Chang leaves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I am sorry, Wesley.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's okay.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "You deserved to win.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Besides, you would have done the same thing for me.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I believe I would.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Congratulations.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well, personally, I hate losing.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "So you'd better be ready next year, Wesley.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I won't be easy to beat.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard is in dress uniform when he meets an embarrassed Jake)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Mister Kurland!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Are you feeling better?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir, Look, I'm sorry I damaged the shuttle, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But Mister Riker says I can work on fixing it as part of my discipline training.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Good idea.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I hope you learned that running away solves nothing.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And I am sorry I messed up.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "At least you kept your wits about you out there.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Don't forget that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And thank you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thank you for saving my life.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That's my job, young man.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Observation Lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Mister Crusher?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why aren't you in your dress uniform for Admiral Quinn's farewell dinner?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I didn't think that would be appropriate.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Why not?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I failed, Captain.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I didn't get into the Academy.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I failed you and I failed the Enterprise.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Ridiculous.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Did you do your best?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "When you test next year, and you will test next year, do you think your performance will improve?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Good.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The only person you're truly competing against, Wesley, is yourself.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Then you're not disappointed?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Wesley, you have to measure your successes and your failures within, not by anything I or anyone else might think.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But, if it helps you to know this, I failed the first time.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And you may not tell anyone!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "You failed?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Yes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But not the second time.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now, you'll do me the courtesy of joining us at dinner.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I have to disappoint an old friend.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Wish I could convince you to change your mind.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll serve you better here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is where you belong.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If you need me.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I've been playing politics too long.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Perhaps I see conspiracies everywhere.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Don't worry.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Safe travels, my friend.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Set course to Algeron Four, Mister Crusher.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Course plotted and laid in, sir.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Then shall we continue with our mission?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Excellent.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister Crusher, engage.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[img=music]  End Credits [img=music]", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,STS-0.4,UHP-3.0" },
	},
};

