	lines = {
		{ text = "(WORF) Captain.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Communication from Starfleet.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "They have reported a disturbance in the Neutral Zone.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(PICARD) Of what nature?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "A battle.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Quadrant nine, coordinates zero seven zero, mark three.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "There's no information who is involved.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "They are asking if we can investigate.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Answer affirmative.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant La Forge, lay in the course.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(LAFORGE) Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(DATA) I have no reports of any Federation ships in that area, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(RIKER) Ferengi?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's a little out of their territory.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Shall we separate the saucer?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Let's get a little more information first.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Course set, Captain.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Speed, warp seven.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir, warp seven.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Engage.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(A short flight later)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "We are now approaching the Neutral Zone.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(TASHA) Sir, sensors indicate several recent photon explosions and heavy phaser activity.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Sir, I have analysed the residue from the explosions.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "This is of no known Ferengi design.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It is possibly Romulan.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Now there's a name we haven't heard for a while.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I could go a lot longer without hearing it.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "If the Romulans have returned to this sector we should know about that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister Worf, any further activity?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Negative, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Slow to half impulse.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Going to one half impulse.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now entering quadrant nine zero seven, mark three.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Shields and deflectors up.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Go to Yellow Alert.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I have located a vessel, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It is drifting.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Put it on main viewer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Magnify.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(An angular, practical ship, like an Eagle transporter)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The life support systems on the ship are failing, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Propulsion, navigation, and all communication, inoperative.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Any trace of the other vessel?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "If it is a Romulan vessel, it could be cloaked.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Tell Starfleet we're entering the Neutral Zone.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Mister La Forge, take us in a little closer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Stay sharp everyone.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's Talarian.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Life signs?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Nothing yet, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I have identified the vessel, Captain.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It is the Batris, a general cargo vessel.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "A long way from home.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Keep alert.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, possible life signs.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "What do you mean, possible?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The readings emanate from a location near what is left of main Engineering.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "There is a great deal of magnetic and radioactive clutter making a positive determination difficult.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll prepare an away team.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Lieutenant Yar, you stay at your post.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If this is the result of a Romulan attack, they may still be in the area.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Data, Geordi, let's go.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Mister La Forge, are we close enough to use the Visual Acuity transmitter?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We can certainly try it, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Please do.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And Number One?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Everything about this seems wrong.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Agreed.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It smells like a trap.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Let's go.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Captain's log, stardate 41503.7.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We have entered the Neutral Zone, where a Talarian freighter has been severely damaged in a battle.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I have sent an away team to investigate.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Geordi has attached a block to the side of his visor)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What exactly does this device do?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Data and I have been working on a way to transmit what my visor sees.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "If it works, the Bridge'll be able to monitor us.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It has restrictions.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The information from Geordi's visor is so complex it is difficult to encode.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Therefore the signal breaks down easily.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "That means it doesn't have much range.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Exactly.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "So far, the effective range is only few kilometres, but we're working on it.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "La Forge to Bridge.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) This is the Bridge.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Worf, I've switched on the Visual Acuity Transmitter.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) We are receiving.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The signal is strong.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Okay, Worf.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I am switching off for transport.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Set phasers on stun.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Let's be ready for anything.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Energise.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Batris Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Fallen girders, hanging wires, steam, and creaking sounds)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "La Forge to Bridge.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) This is the Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, I'm switching on the transmitter.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ready to receive.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Put this on main viewer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(A vision of weird coloured shapes, edges moving and blurring.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "As if the viewer has been taking something highly illegal and mind-altering)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Extraordinary.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now I'm beginning to understand him.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Geordi.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What was that?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Over to the left.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) What?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No, no, back.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Right there.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What is that?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Batris Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That's Commander Riker.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ah, to me it's just an undefined form, standing in a visual frenzy.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Can you filter out the extraneous information?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) No, I get it all simultaneously.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "But it's just a jumble.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Batris Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) How can you make head or tail of it?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I select what I want and then disregard the rest.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) But how is that possible?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well, how, in a noisy room, can you select one specific voice or sound?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Of course, something you learn.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Exactly.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) It's something I have learned.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Does that make it more clear?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Look over at Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "There's an aura around Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Well, of course.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "He's an android.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You say that as if you think that's what we all see.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Don't you?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Batris Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sir, I hate to break this up, but", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh, yes, of course, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Proceed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Batris Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let's go.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(As they move on, a conduit falls.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "This ship is ready to fall apart)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I am detecting high levels of deuterium gas, probably from the leakage in the drive system.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Toxic?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Not as yet, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The life signs are emanating from the far side of Engineering.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "What's the safest way around?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "All routes are equally dangerous, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Well, what's the least dangerous route, Data?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "There is no significant difference.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Steady on.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Commander Riker!", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Commander.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, Geordi.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "There's a fissure developing in the bulkhead.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The skin of the ship is losing its integrity.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Where is it?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I can't see it.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Right there.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Geordi, step closer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It looks to me like a spectrograph indicating metal fatigue.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Batris Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Is that how you interpret it?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Very good, Captain.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Exactly right.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "How long before this hull ruptures?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's impossible to be exact.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'd say five minutes.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Probably less.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Let's go.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Geordi, we've lost transmission.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Batris Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(As well as debris, there is a bright light source)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Signal overload.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm surprised it lasted this long.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We've reached the core, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The life signs are very strong now, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "They are coming from over there.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Is there any other way around?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Negative.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "This is the only way.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The safest way to proceed is for me to cross alone.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The heat and toxic gases have less effect on me.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Agreed.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Captain, Data's gone ahead.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) The life signs seem to be coming from the far side of Engineering.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Batris Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I've found them, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The door to their compartment is jammed.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(They follow him around the warp core to a big door that is very slightly open)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The control mechanism's not operative, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "This is Commander Riker of the USS Enterprise.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Do you hear me?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I advise against the use of phasers, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The gas buildup is too great.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "He's right, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "A phaser discharge now could blow us all out of here.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Shall I, sir?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Offscreen) Number One, what's going on?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We're about to force the door.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Data makes the gap big enough for Riker to step through into the compartment)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Offscreen) What is it?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What do you see?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Klingons.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Klingons?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Batris compartment]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(There are two Klingons standing there)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(KORRIS) I am Korris.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We'll handle the formalities later.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Right now we have to get off this ship.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Are there any others survivors?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I believe I have found a quicker way out of here, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Check him out.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(There is a body on a table)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "He is alive, sir, but just barely.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Then pick the body and let's get out of here.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No. I will carry him.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "As you wish.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Come on, let's go!", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Tasha, go to transporter room three.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I want you there when the away team returns.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Batris Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Commander, we are out of time.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "This ship is blowing.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Transporter room, have you got a lock on us?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Too much interference.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You have to get farther away from the Engineering section.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sir, the Engineering section is critical.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Destruction of the Batris is imminent.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "They're out of options.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do it!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Now!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The first attempt fails and the group arrive back on the Batris as explosions head towards them.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The ship goes bang on the viewscreen, then Tasha materialises them)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Bridge, this is Commander Riker.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) We are taking the survivors to Sickbay.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'll be in Sickbay.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Request permission to join you.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Granted.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Debris is still being analysed.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Inform me as soon as it is complete.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Situation, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(CRUSHER) His injuries are very critical.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I am Jean Luc Picard, Captain of this vessel.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "My name is Korris, Captain of the Klingon Defence Force.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is Lieutenant Konmel.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Would you mind telling me what has happened?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We were passengers.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Talarians were taking us to outpost M Zed Five.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What was the ship doing in the Neutral Zone?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We were attacked without warning by a Ferengi cruiser.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "During the course of the battle we must have unknowingly entered the Neutral Zone.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The weapons were not Ferengi.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "What is your name?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am Lieutenant Worf.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "And you are a member of this crew?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You are correct.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The weapons were Klingon, but the vessel was Ferengi.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What precipitated the attack?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I don't know.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We were in our quarters.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(KONMEL) The captain of the freighter had no combat experience so he did not anticipate the first attack, which was nearly fatal.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We took control with his permission.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Ferengi called for surrender.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I told the Captain to agree to all their terms.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We had only one chance, but I was confident it would be enough.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "As adversaries the Ferengi are not very worthy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Still, your weapons were limited and their ship superior.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All we had was an ancient battery of Merculite rockets.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Our only chance was to trick them into lowering their shields.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We reduced power and lured them in.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They suspected nothing.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then, when they lowered their shields to beam over a boarding party, we opened fire.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Still, there are some points that I'm not very clear about.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, we are hungry and tired.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Of course.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If there is anything else you wish to know about this incident, we will be available.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Permission to show our guests to their quarters, Captain.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Permission granted.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Worf, Korris and Konmel leave)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What do you think, Captain?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "There's more to this than we've been told.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why was the Talarian ship so far off course?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What was its point of departure?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why would three Klingon officers hitch a ride on a broken down freighter?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Contact Starfleet.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Find out what they know about Korris and company.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It'll take forty eight hours for a message to get to Starfleet on subspace frequency.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "They're going to be with us for a while.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Let's find out all we can about them.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do you think I should have assigned a security team to keep an eye on our guests?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No. Worf can deal with anything that might arise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How's your patient, Doctor?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Not good.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I'll keep you posted.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Guest Quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sit, friend.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let us eat.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(to replicator) O'Mat gri tea and piviots.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(a tray with a bottle and food arrives)", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I did not know there were Klingons serving on human Starfleet vessels.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "As far as I know, I am the only one.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Tell me, what it is like for the hunter to lie down with the prey?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Have they tamed you, or have you always been docile?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Does it make you gentle?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Has it filled your heart with peace?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do glorious battles no longer inspire your dreams?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why do you mock me?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why do you wish to anger me?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Only to see if it is still possible.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It is.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(NURSE) Doctor!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I think you'd better come and look at this.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Klingon's condition is worsening.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He's convulsing.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Get the hypospray.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Guest Quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The guests are finishing their meal)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Offscreen) Commander Korris, this is Captain Picard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) I am sorry to report your comrade's injuries are beyond our medical abilities.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He's dying.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(Korris, Konmel and Worf stand over the bed.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "As the young man breaths his last, Korris holds his eyelids open.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Then they all start a deep throaty growl, which becomes a roar as they are sure he is dead.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Konmel removes a hook from the boot)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Is there any special arrangement you would like for the body?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It is only an empty shell now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please treat it as such.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The opponent that killed Kunivas should have been an enemy, then his death would have been even more glorious.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If the opponent was not an enemy, who was it?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Tell me.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "What really happened?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I do not wish to anger you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We are after all, brothers lost among infidels.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Tell me, how is it that you come to this ship, that uniform?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Through an act of kindness.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Romulans attacked the Khitomer outpost.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Everyone was killed.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I was buried under the rubble and left for dead.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "A human Starfleet officer found me.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "He took me to his home on Gault and told his wife to raise me as his son.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "How old were you?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Before the age of inclusion.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "That young?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Gault is a farming colony.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "When my foster brother and I were of age, we entered the Starfleet Academy.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "He hated it and returned to Gault.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I stayed.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You have not spent much time among your own kind.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Hardly none.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "So, when the night was still and quiet, and the sound of the blood rushing through your veins filled your ears, the only way to silence it was to slip out into the night and, like the hunter that spawned you, join in the struggle of life and death.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You were unable", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And those around you did not understand.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You frightened them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They shunned you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Cursed you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Called you vile names, and you knew not why.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Even now do you know why you are driven?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why you cannot relent or repent or confess or abstain?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How could you know?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There have been no other Klingons to lead you to that knowledge.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, those feelings are part of me.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "But I control them.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "They do not rule me.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "To fit in, the humans demand that you change the one thing that you cannot change.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yet, because you cannot, you do.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That too is the mark of the warrior.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You said that I mock you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I do not.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I salute you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But against whom do you test yourself?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Against what enemy do you charge into battle?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I have been in battle.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Then you understand.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, I do.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Brother, this peace, this alliance, is like a living death to warriors like us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're right, we lied to your Commander.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We commandeered that freighter and left the crew behind, and we were in search of a place where we could live our lives like true Klingons.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You did not battle the Ferengi.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It was one of our own cruisers sent to bring us back.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You destroyed a Klingon vessel?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I did not want to battle our brothers.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I had no choice.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They had been corrupted by the illusion of peace.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They traded our birthright so they could die in their sleep.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A peace that makes the Klingon heart that beats in my chest wither and die.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is it permitted for you to show us around this ship?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, of course.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And as I watched Worf, it was like looking at a man that I had never known.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, long range sensors indicate another vessel approaching this area.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Can you identify it?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No, sir, not yet.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Keep a close watch on it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "So close to the Neutral Zone it can't be random.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What magnificent battles we could have at the helm of this ship.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Perhaps your dreams of glory no longer fit the time.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "They belong buried with the past.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Standing here we will never know.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Our answer lies out there.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Our instincts will lead us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Instincts that have not been dulled by living among civilised men.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I believe, sir, that was the first time outsiders have witnessed the Klingon death ritual.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I can understand them looking at a dying man's eyes, but the howling?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It was a warning.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "To whom?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They are warning the dead, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Beware, a Klingon warrior is about to arrive.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Captain, that unidentified vessel is approaching us at warp five.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Intersect one hour, sixteen minutes, thirty three seconds.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Can we get a visual?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I can try, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Magnify.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hello.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Klingons.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Open hailing frequencies, Lieutenant Yar.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hailing frequencies open.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Klingon cruiser, this is Captain Jean Luc Picard of the USS Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) I am Commander K'Nera.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What is your purpose in this area?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We came to investigate a battle.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We rescued three Klingon survivors.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) That is all that is left of the crew of the cruiser T'Acog?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They were not off the cruiser.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They were from the freighter Batris.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The leader is Captain Korris.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) You have him on your ship?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He is alive?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) He is a criminal.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A renegade, who with two others stole that freighter, and somehow destroyed the Klingon cruiser sent to bring them back.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We expect the criminals to be delivered into our custody as soon as we are within transporter range.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(ends transmission)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Lieutenant Yar, where are they now?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They're with Worf on deck seventeen.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Deck seventeen?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Near the auxiliary turbolift to the battle bridge.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Shall I alert Lieutenant Worf?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "No. Send a security team.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You don't think Worf would allow them access to the battle bridge?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I think, Number One, we cannot assume anything.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, shall I stay at my tactical position or lead the security team?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Lead the security team, and, Lieutenant, you understand with whom you are dealing.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We have heard this ship can separate in time of battle.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "When relieved of its bulk, the Enterprise becomes an exceptional weapon.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Worf.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "They have come for us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Step aside.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "What is the problem?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Captain wants those two taken into custody.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Do not let them take us, Worf.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Help us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Listen to the voice of your blood.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You are not of these people.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Join us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(There's a tense moment, then a nearby turbolift opens and a little girl runs out)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Go back!", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Please, turn around and go back to your mother.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Korris picks the girl up)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Bridge, we have a hostage situation on deck seventeen.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Korris glares, and hands the girl to Worf, who gives her to her mother)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(GIRL) Mommy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Bridge, this is Lieutenant Yar.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Disregard.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Situation is under control.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "By order of the Captain, you are confined to Security.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Korris and Konmel are taken away)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I thought for a minute we had a problem.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Oh?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "It looked like Korris was going to hold that little girl as a hostage.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "That is not our way.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Cowards take hostages.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Klingons do not.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Brig]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ri'ario.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Worf enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Lieutenant.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Klingon vessel has to arrived.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They have requested return of the renegades.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They will be tried and executed, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I know.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant, understand that I am not unmindful of the mixed feelings you must have about this incident.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Are there no other options, sir?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "None that I can see.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He seems to be handling this quite well, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "So far.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He must be torn.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "These are his people.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Klingon vessel is within range, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "They are requesting visual contact.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Open frequencies.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hailing frequencies open, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(On viewscreen) We are in position now to receive the criminals.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We're prepared to transfer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Request permission to address the Captain on the Klingon vessel.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I know it is against standard procedure, but there is something I must say.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Permission granted.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "K'Mongi B'Mus.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) G'Armond T'Ris.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What is it you want?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "To plead.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) You waste your time.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Their actions threaten the alliance.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They disobeyed and must be punished.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, they must be punished, but not executed with dishonour.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Why do you care?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What burns in their eyes, fires my soul.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I hear their words, and I see it all as it was.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Part of me longs for that time.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) It's bred in the bone.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We all do.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then send them to a planet in the Halee system, where they can meet death on their feet with a weapon in their hands, not tied and helpless.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) When one of us dies that way it diminishes all of us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Brother, I feel as you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I too wish they could fly free, but I have no choice.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) We await the transfer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Lieutenant Yar, escort the prisoners to the Transporter room.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Aye, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Brig]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The prisoners are taking elements from their belts, the hooks from their boots and building a gun of some sort.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Then Korris uses a metal piece to deactivate the forcefield)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(GUARD) This is Ramos.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The forcefield in Security Three has been broken.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(He tries to go in, and gets shot)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Another guard dodges  Konmel's shots and gets him, although it takes three blasts to put him down.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,LFT+1.0" },
		{ text = "Korris uses Ramos's phaser to kill the guard)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,LFT+1.0" },
		{ text = "Lieutenant Yar to Bridge.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) There's been an escape.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "At least one of my security is dead.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "So is Konmel.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Korris is loose and armed.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Keep me informed, Lieutenant.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Data, visual on K'Nera.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Visual on, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Captain, there will be a short delay.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Korris has escaped.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) He is a trained Klingon warrior, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Perhaps more than you can handle.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It is not a disgrace to request our assistance.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I think we can handle the situation.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Korris runs in and sees the warp core.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "He heads up a ladder.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Tasha enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(CREWMAN) He's up there!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, Korris is in main Engineering.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Commander Korris.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is a futile effort.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) You cannot win.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I will speak only to my countryman!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Only to Worf!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, he has a phaser aimed directly at the dilithium crystal chamber.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) At that range, one blast and", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I understand, Lieutenant.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, permission to leave the Bridge.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We'll both go.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What's the situation?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He's on the second level armed with a phaser.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "We do not have a clear field of fire.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "How do you suggest we proceed?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Wait him out.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, let me talk to him.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I do not think that's a good idea, Worf.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Right now he's running on adrenalin.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Let it cool.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "There's no where he can go.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "He will wait only as long as he believes it is to his advantage.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The moment it is not, he will fire his phaser into the dilithium crystal chamber.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "That would destroy the Enterprise and him along with it.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "He knows.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Talk to him, Worf.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Worf goes to under where Korris is standing)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Brother, I knew you would come.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Worf climbs the ladder) Now I, we have a chance.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I could not do it alone, but I would rather die here, than let the traitors of Kling pick the meat from my bones.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "With you it will work.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What will work?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I will demand Captain Picard give us access to the battle bridge.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We will separate from the rest of the ship, then together we will light up the galaxy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Imagine the fear which will roll before us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain Picard will not comply.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "He can not, dare not refuse us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "In order to save themselves, they will give us what we demand.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then, brother, we are free.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Put down the phaser.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Wait.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I do not believe this.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Believe it.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I have tasted your heart.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You have been with them, but you are still of us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do not deny the challenge of your destiny.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Get off your knees and soar.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Open your eyes and let the dream take flight.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "My brother, it is you who does not see.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You look for battles in the wrong place.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The test of the warrior is not without, it is within.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Here, here we meet the challenge.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It is the weaknesses in here a warrior must overcome.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "No.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You have talked of glory and of conquest and legends we will write.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, the birthright of every Klingon.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yet in all you say, where are the words duty, honour, loyalty.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Without which a warrior is nothing.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "What are you saying?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Living among these humans has sucked the Klingon heart out of you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Put down the phaser.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You are a sham!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "My words were dust upon the ground.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Your blood has no fire.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You are weak like them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't care what you look like you are no Klingon.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Perhaps not.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(And he shoots Korris, who falls through a glass screen to the floor below)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Wait.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Worf goes down the ladder, turns Korris over, and does the death ritual for him)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Mister La Forge, open hailing frequencies.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hailing frequencies open, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Commander K'Nera, this is Captain Picard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Yes, Captain?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Korris and the other Klingon are dead.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) May I speak with Lieutenant Worf?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, Commander?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) How did they die?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They died well.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do you wish the bodies returned?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) They are now only empty shells.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Dispose of them as you see fit.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Worf, when your tour of duty on the Enterprise is complete.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "consider serving with us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Your training and experience would be of benefit to us, and perhaps there are some things we could teach you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am honoured.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(end transmission)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I was just being polite, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Ah. Commendable, Lieutenant.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister La Forge, set course for Starbase Eighty four.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Really.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I have no desire to leave the Enterprise.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Good.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Coordinates set in, Captain.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Speed, warp five.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir, warp five.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "And Mister Worf, the Bridge wouldn't be the same without you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Engage.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[img=music]  End Credits [img=music]", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,STS-0.4,UHP-3.0" },
	},
};

-- Star Trek:TNG S1.21 - The Arsenal Of Freedom
RecMedia["TNG_S1E21"] = {
	itemDisplayName = "VHS: ST:TNG S1.21",
	title = "Star Trek:TNG S1.21 - The Arsenal Of Freedom",
	subtitle = nil,
	author = nil,
	extra = nil,
	spawning = 0,
	category = "Retail-VHS",
	lines = {
		{ text = "(PICARD) Captain's log, stardate 41798.2.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We have been ordered by Starfleet to proceed to the Lorenze Cluster and investigate the disappearance of a light cruiser, USS Drake, which was in that system trying to unravel a mystery of its own which began when recent long range probes indicated that all intelligent life on the planet Minos has disappeared.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Number One?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(RIKER) No help from their communications log.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The Drake reported arriving at Minos and then wasn't heard from again.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Mister Data?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(DATA) The citizens of Minos gained fame during the Ersalrope wars as arms merchants.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "They manufactured sophisticated and highly advanced weaponry.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(LAFORGE) Yeah?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "For which side?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Both.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "We are approaching the planet Minos, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Standard orbit, Mister La Forge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Standard orbit.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Whatever happened to the Drake, happened quickly, otherwise a man like Rice would have reported again.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You know Captain Rice?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We were at the Academy together.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Tell me about him.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Able.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "A good officer.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(TROI) How would he react under stress?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Paul Rice is confident to the point of arrogance, he but carries it well because he's usually right.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "He's a risk taker.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Really?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll give you an example.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "One of the final tests in advance navigation at the Academy provides the student with three options.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Rice was given this test, rejected their options and offered one of his own.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "That's taking a risk.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And it paid off.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "He received the top grade and now that same test has four options.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "In a difficult situation a man like that would act aggressively.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Agreed.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(WORF) Commander, weren't you offered the Drake?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(TASHA) You gave up your own command to take this assignment?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "At the time I thought it would be more advantageous for me to do a tour on the Enterprise.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Captain, readings correspond with the findings of the probe.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "No signs of intelligent life forms.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Vegetation and animal life only.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "What happened to all the people?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "War?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Disease?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "A dissatisfied customer?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "In any war, natural disaster, even the most virulent plague, there are always survivors.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, we are being hailed.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "How can that be from a planet with no people?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Your sensors indicated no intelligent life forms?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Correct, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Something has scanned our language banks and is hailing us.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "On main viewer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Whoever you are, wherever you're from, greetings.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Welcome to Minos, the arsenal of freedom.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am Captain Jean-Luc Picard of the USS", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) If you need a little something special, be it for one target or multiple targets, we got it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You'll see it here on Minos, where we live by the motto 'peace through superior firepower'.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "To whom am I speaking?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) To be totally armed is to be totally secure.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Remember, the early bird that hesitates gets wormed.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It is a recorded message, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(On viewscreen) Minos, the arsenal of freedom.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Perfection in highly advanced weaponry.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Versatility.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We must have triggered something left over from the Erselrope wars when the arms business was booming.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) So lock on to my signal and beam on down, because we don't just provide weapons", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Shut that off.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) We provide complete weapons systems.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's a heck of a sales pitch.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "If an automated message system is still functioning, there could be other systems on that planet that are still operational.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Weapons systems?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Possibly.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We're going to have to go down there to find it.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Number One, prepare am away team.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant Yar.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Data.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Commander, I recommend a minimum complement.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Oh?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I would have thought otherwise.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We'll keep the first landing party small and mobile, until I'm confident that whatever killed the inhabitants of this planet isn't still down there.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Okay.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Planet Surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(There's a mist on the ground and lush foliage standing over a flat horizon)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Spooky.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Riker.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) We'll start a pattern search from this point.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Keep this channel open.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Commander, our communications are being monitored.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "By whom?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Since there is no intelligent life, the question is by what?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Captain?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes, we heard that, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We'll try and track it down from here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Ruins]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Tritanium.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "It's been melted.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "What could do that?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I don't know.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Whatever it was, it's beyond our technology.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Over here.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Wow.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Data takes the moss from a big ray gun)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "We've never even seen anything like this.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's undamaged.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Maybe a demonstration model for some potential buyer.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Maybe.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Let's spread out a little.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(T'SU) Commander Riker, this is Ensign T'Su.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Ruins]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) I'm monitoring a slight energy buildup near your position.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am not able to pinpoint the source.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Give me a direction.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "East of your position, ten metres.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I will continue monitoring.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Jungle]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I can't see anything through here.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The underbrush is too thick.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(RICE) Hello.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Commander Riker.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Rice!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Where did you come from?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I was over there.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We were worried about you.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I should have known you'd pop up.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "How are you, Commander Riker?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Me?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Forget about me.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "How are you?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No word, no message, nothing.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "How many are with you?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There are two others.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Where's the Drake?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Where's your crew?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) Number One, a word.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We have some information you should be aware of.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sensors indicate low level energy readings, but the only life signs are the away team.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You copy, Number One?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Jungle]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Understood.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You look strange, Paul.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I was injured getting here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Tasha and Data arrive)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Do you need medical help?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No. What's your purpose here?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Commander Data, Lieutenant Yar.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Sir, other than ourselves, I am picking up no life signs here.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I know.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Riker, you didn't answer me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Who sent you here to look for me?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Your mother.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "She's worried about you.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Tell me about your ship, Riker.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's the Enterprise, isn't it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No. The name of my ship is the Lollipop.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I have no knowledge of that ship.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's just been commissioned.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's a good ship.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Refresh me, would you, Riker?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What's its size, it's complement?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Who is here with you?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What's the armament on the Lollipop?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Paul, I will only answer your questions if you answer mine.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Why do you want to know so much about my ship?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We're friends, aren't we?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Old friends.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Our top speed is warp three.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What's yours?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Then the Drake has not been destroyed.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The Drake?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, your ship.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Of course, my ship is the Drake.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Where is it?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Classified.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Classified?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Please, it's important.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Our survival depends on knowing.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What's the armament on your ship?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ten.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Ten?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't understand.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ten what?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Six.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Commander Riker, your answers make no sense.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You haven't used my first name once, Paul.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You remember it, don't you?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You don't, do you?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Because you don't exist.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You're a fake.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You're an image of the original, aren't you?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Answer me!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Paul Rice disappears and a flying machine appears)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Offscreen) Report, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The image of Rice has been replaced by something which is hovering several metres away.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Number One, get your team back up here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Jungle]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Prepare to beam up.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(The device envelopes Riker with a green field)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,PAN+20.0" },
		{ text = "Commander!", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Tasha destroys it with her phaser but the field remains)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,AIM+1.0" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What's happened, Number One?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1,PAN+10.0" },
		{ text = "Report.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1,PAN+10.0" },
		{ text = "Report!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1,PAN+10.0" },
		{ text = "Report!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1,PAN+10.0" },
		{ text = "[Jungle]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He can't answer you, Captain.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Commander Riker just has been encased in some kind of energy field.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1,FEA+10.0" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is he alive?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Jungle]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Unknown.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Captain's log, supplemental.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Some kind of weapon, which for a time took the form of Captain Paul Rice, has enveloped Commander Riker.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Transporter room three, have you been able to lock on to the away team?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) I am unable to lock on to Commander Riker, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ensign T'Su, situation report.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Previous energy readings are gone.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Data, have you been able to assess Commander Riker's condition?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) He appears to be in some kind of stasis.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Theorise, Mister Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What would be the purpose of such an encasement?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Jungle]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Typically, the purpose of such an enclosure is for storage.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Offscreen) Which would suggest what?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That sooner or later someone or something will be along to collect him.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Stand by.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm coming down.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You, sir?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Yes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Doctor Crusher, this is the Captain.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Meet me in Transporter room three.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister La Forge, you have command of the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "And whatever happens down there, your prime responsibility is to the ship.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Understood, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, I take great exception to your decision to beam down.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Noted.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(CRUSHER) Is it just Riker?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Yes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Apparently he's confined in some sort of force field.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Energise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Jungle]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Any changes?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Data's figured out what it is.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Are you getting any life signs?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "He is alive.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "What appeared in the form of Captain Rice is an intelligence gathering device used during the Ersalrope Wars.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Probably developed here.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "It projects an image of someone the subject will trust, gets as much information as possible.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "If it's found out, it encases the subject to await a more detailed interrogation later.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Clever.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Can you remove it?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Possibly, but I will need to know the exact frequency and I cannot determine the risk to Commander Riker.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "We have little choice.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Whatever has him is interrupting the transporter beam.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Remove it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(adjusting his phaser) This will take some time, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1,AIM+1.0" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am picking up energy readings.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, Lieutenant La Forge here.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sensors indicate energy readings in your area.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Jungle]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(As Data tries to neutralise the force field, another device arrives and starts shooting)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Doctor, over here!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Picard and Beverly run, right to the edge of a pit, where they fall into)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Cavern]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Picard is unhurt, but Beverly is injured)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Planet Surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This one is different.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "It's anticipating.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Data, I need you.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Set your phaser on kill.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I'll lead left, you get it.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(It takes both phasers to destroy it)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Doctor Crusher?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Captain Picard?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(the communicator does not beep) I have a malfunction.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Enterprise, this is Lieutenant Yar.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Mine's out too.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "The close pass by the weapons must have disrupted our communicators.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Keep on working.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I'll look for the Captain and Doctor Crusher.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Cavern]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ow!", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "My arm.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "How bad is it?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Oh, it's bad.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Come on.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Alright, don't worry, we'll get you out of here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Enterprise, this is the Captain.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Two to beam up.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Enterprise?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Enterprise, come in.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Data?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant Yar?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What's wrong?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Why aren't they working?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I don't know.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But don't worry.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "As soon as they realise our communicators are not working, the sensors will find us and Geordi will beam us up.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now, just you hold on.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm sorry about this.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(He tears her jacket for a bandage)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,DOC+1.0,TAI+2.0" },
		{ text = "Hold on, it won't be long.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I must keep conscious.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Planet Surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Data, I can't find them.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Step back, please.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I believe I have found it.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Data.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "All vital signs returning to normal.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Good.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Now Geordi can get us out of here.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Come on.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "The Captain and Doctor Crusher can't be far away.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sir, I'm reading life forms on the planet.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All five members of the away team.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Good.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "They must have freed Commander Riker.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Transporter Room, get a lock on the away team and beam them up.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Shields just came on.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Deflectors also up.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm picking up an object off the port bow.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's firing!", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Enterprise's shields light up from a green beam)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Red Alert!", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Battle stations!", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We can't the away team beam up with our shields in place.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Phasers ready.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Photon torpedoes standing by.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Locking on target.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Correction.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Target has disappeared.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Give me a full scan.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Scanning.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "No readings yet.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Wait.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Something dead astern.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Emergency power to shields!", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Object is firing.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Return fire!", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aft shields weakened.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Whatever they're using, our shields can't stand up to it much longer.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) This is Chief Engineer Logan.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Are we breaking orbit?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I need to know now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ship's log, supplemental.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Lieutenant La Forge in command.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am unable to beam up the away team due to an unseen assailant attacking the ship.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "To make matters worse, Chief Engineer Logan is on his way to the Bridge, and he's not paying a courtesy call.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why are we still in orbit?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We're taking a beating.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We've got to hold out as long as we can.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now, if we can disable our attacker, if only for a few seconds, we can drop our shields and beam the away team back aboard.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "If we follow that plan, we'll lose the Enterprise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "In view of the present crisis, I believe you should relinquish command to me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I outrank you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Mister Logan, I'm in command.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Captain did not anticipate the Enterprise would come under attack.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If he had, would he have left the Bridge to you?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If he had, he wouldn't have left the ship.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Picking up an object to starboard, sir, heading toward us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Lock phasers on that thing and fire the moment it's in range.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Locking in.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The object has vanished.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The Enterprise is hit again)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Phaser lock lost.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Starboard shields weakened.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's cloaked itself.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You can't fight this thing and win.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We've got to break orbit now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Worf, analyse its firing pattern.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Maybe we can anticipate its location.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Solis, correlate the trajectory of the energy bolts with the attack path.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll compute the optimum spread of phasers and photon torpedoes to hit it.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Right.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant La Forge.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Geordi.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I know you want to do what's best for the Enterprise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "So do I. Now the best thing", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The best thing, Mister Logan, is for this discussion to end and for you to return to your duties.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now, I'm in charge until relieved by Commander Riker or Captain Picard.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You're ignoring my greater rank and experience.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Not at all.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "In fact, just to opposite.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm counting on it.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now I need you to get back down to Engineering and get me every available scrap of emergency power you can.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The more power we can channel to the shields, the longer we'll be able to hold out.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now, Mister Logan.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Logan leaves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Worf, have you got a lock on it yet?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's impossible to be sure.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Jungle]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Maybe you should wait here while Data and I search for the Captain and Doctor Crusher.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "No, I'm a little groggy, but I'll be all right.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I am getting energy readings.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Look out!", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(They're under attack again)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "We'll do it just like before.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(But there's a difference)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "It's got a deflector shield.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "The product continues to upgrade and improve.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "We'll concentrate our fire and try to collapse its shield.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Commander, we'll need you too.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(It drops it's shield to fire at them)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Now!", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Three phasers and it takes some time for it to go boom) You're right, they keep getting better.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "The weapons are appearing at intervals of precisely twelve minutes.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Well this one took everything we had.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I don't know how we'll handle the next one.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "We're not going to wait around.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Let's find the rest of our team.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Cavern]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We seem to be in some kind of underground structure, but I don't see any.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hey, no, you don't.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No time to sleep.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm tired.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Come on, stay with me.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Come on now, stay awake.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That's an order.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I must have lost a lot of blood.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I've stopped the bleeding.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No, there's another wound.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1,PAN+20.0" },
		{ text = "My leg.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1,PAN+20.0" },
		{ text = "(He brushes the dirt off her leg and she winces)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Not to worry, Everything is going to be fine.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now, this is going to hurt a bit.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I've heard that before.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Hey, hey, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Stay awake.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Come on.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "There's a lot of blood.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What do I do next?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The wound needs a clotting agent.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I couldn't find your medical kit.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Those roots.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "What are they?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Roots?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What, these?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Break off a piece and taste it.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Don't swallow it.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Very bitter.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Apply some to your hand.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Is there a colour change?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It's turning yellowish.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Good.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Now, spread as much of it as you can onto the wound.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Hey, hey, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1,PAN+20.0" },
		{ text = "Now, stay awake.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1,PAN+20.0" },
		{ text = "You've got to help me.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1,PAN+20.0" },
		{ text = "I need your help.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1,PAN+20.0" },
		{ text = "All right, now listen.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1,PAN+20.0" },
		{ text = "Your patient has lost a great deal of blood from multiple lacerations.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1,PAN+20.0" },
		{ text = "The patient's going into shock.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1,DOC+1.0" },
		{ text = "So what do I do?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Elevate the legs and try to keep her warm.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1,DOC+1.0" },
		{ text = "Good.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "All right.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Stay with me, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Keep talking.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You didn't happen to bring a blanket with you, did you?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "No. I'm going to look for a way out.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now, you keep up your end.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Stay awake.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All right, I know it's risky, but a shot in the dark is better than nothing.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Let's see if we can get lucky.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I can't seem to pin down the pattern.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sorry, sir, I should have seen that.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That's all right, you're doing fine.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Programme laid in.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Phasers and photon torpedoes ready.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Okay.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister Solis, bring her about to two three mark one eight five.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(SOLIS) Aye, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(They are attacked again)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Fire!", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now!", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Phasers and photons firing.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We missed.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Number four shield buckling.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Number three shield nearing failure.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Bridge, this is Engineering.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I can't hold this power level much longer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You've got to do something.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Backup systems are overloaded.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Backup systems failing.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) La Forge, this is Logan.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Tell me something.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Mister Logan, report to the Bridge.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) On my way.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Deflectors are breaking down.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We have less than one minute of reserve.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Logan enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "We are getting out of here.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant Solis, set course three one five mark zero, zero seven.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Course set.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You are leaving them on the planet.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Speed warp five.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Warp five.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Aye.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Engage.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hold course and speed for twenty eight seconds, then come to a full stop.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister Logan, had we stayed, we would have been destroyed.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now, there are over a thousand people on this ship.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I have a responsibility to them.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "What about your responsibility to Captain Picard and the members of the away team?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I have a responsibility to them as well.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister Logan, you are going to take command of the Saucer Section.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Backup crew, report to the main Bridge.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You're going to separate?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, and I want you to take the saucer section and proceed immediately to Starbase one zero three.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You can't fight what you can't see, and you still won't be able to see that thing.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Maybe we won't have to.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Risky.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "That's why we're going to separate.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Worf, Solis, T'Su. Report to the Battle Bridge.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll join you in a moment.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Ensign, secure connector levels.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(ENSIGN) Aye, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Ready room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Come in.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant, I need to speak to you.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Counsellor, we're a few minutes away from separating the battle section.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I don't have a lot of time.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I know, but as Counsellor I have a duty to evaluate the emotional fitness of the crew.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "And you sense I'm nervous.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well, you're right.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Counsellor, deep down I'm shaking.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm about to take this ship into battle, and there's a good chance she'll be blasted out of the sky.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm taking a huge risk here, and there are a lot of lives at stake.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "So, yeah, I'm nervous.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Did you expect otherwise?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Not at all.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "You're under tremendous pressure, more than you've ever experienced.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "You think I'm about to crack?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "On the contrary.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "You should be proud of the way you're handling command.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "You've kept a cool head, taken charge, and made some very difficult decisions.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Then what did you want to see me about?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Lieutenant La Forge.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Battle bridge is manned and ready.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "However, deflectors have not yet returned to full efficiency.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let's get them there, Mister Solis.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Aye, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll join you shortly.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "La Forge out.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Did you hear the uneasiness in his voice?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Solis is doing a fine job.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, but he isn't handling the stress as well as you are.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Both he and Ensign T'Su are very young.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "No, wait.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "They're good officers.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, but they lack battle experience.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "They're worried about making mistakes, and they need some encouragement.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "What do I do?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Just remember it's you they draw strength from.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "They look to you for guidance and for leadership.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Help them.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Show confidence in them.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Like Captain Picard showed confidence in me.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Right.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I understand.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thanks, Counsellor.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Battle bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Prepare to initiate separation sequence.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "All decks report ready.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "On my mark.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The saucer rises away)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(COMPUTER) Main latch bags to zero.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Main latch retraction one to one eight, initiate.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Static charge compensate.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Separation complete.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant Solis, lay in a reciprocal course for Minos, warp five.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Course set.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "On my mark.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Engage.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Cavern]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard and Crusher are far enough apart to be out of sight of each other, but not earshot)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "We seem to be pretty well sealed in, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No exits?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "None that I can see.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If you find one, go.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Tired of my company already?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Beverly.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Beverly!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm tired.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I need to sleep.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Woke up too early.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Talk to me, stay awake.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Tell me something.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Tell me about those roots.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How did you know their medicinal values?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "My grandmother.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Your grandmother was a doctor?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Oh. She was a botanist, then?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No. She helped to colonise Arveda Three.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Arveda Three?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That's such a tragedy.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Did she survive?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Once the medical supplies had run out, she had to use what was at hand.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "So she learned all about roots and herbs, and then taught it to me.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "You were part of that colony.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I didn't know that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But then there must be a lot of things about you that I don't know.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Quite a few.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "What?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now just a minute.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Here's something odd.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's glowing.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What is it?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I don't know.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's covered up.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm just clearing away the dirt.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's a viewscreen.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It seems to still be operative.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(He presses a button and the whole wall lights up)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "It's a tracking device of some kind.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Tracking what?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "There are three moving indicators.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Possibly the away team.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Beautiful, isn't it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's the centrepiece of the whole unit.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Who's that?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "A projection.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's the automated salesman who greeted us on the Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What unit?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why, the Echo Papa Six Oh Seven.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Our proudest achievement.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The ultimate in weapons system technology.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is that what's behind the attack on my people?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Impressive demonstration, isn't it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Demonstration?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It tried to kill us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Versatile, powerful, and easy to use.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Six Oh Seven does it all.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Its various modules can gather information, neutralise ground personnel, even destroy enemy space vessels.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Is one of those things after my ship?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Tell me.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Of course I can tell you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am programmed to answer any and all questions about the unit.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I can talk terms, arrange for delivery, whatever you need.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It doesn't understand anything other than what it's peddling.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The Six Oh Seven represents the state of the art in dynamic, adaptive design.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It learns from each encounter, and improves itself.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "So what went wrong?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Where are it's creators?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Where are the people of Minos?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Once unleashed, the unit is invincible.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The perfect killing system.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Too perfect.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You poor fools, your own creation destroyed you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What was that noise?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The unit has analysed its last attack and constructed a new, stronger, deadlier weapon.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "In a moment, it will launch that weapon against the targets on the surface.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Abort it!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why would I want to do that?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It can't demonstrate its abilities unless we let it leave the nest.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They are down there, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Let's clear some of that away.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "If Data's calculations are correct, the next one should show itself in two minutes.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Captain!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Cavern]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Can you hear me?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes, we're here!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Are you all right?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(The away team are looking down through the hole at Crusher)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Doctor Crusher is seriously hurt.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We can't contact the Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The communicators are out.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, our communicators are out too.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Those devices are part of a system located down here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Another one is about to be released.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Watch yourselves.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We know, Captain.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "It's too far down there.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "There's nothing to hold on to.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No way to climb down.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm surprised either of them survived.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I can do it, Commander.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "How?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Jump.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Data, it's over ten metres.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Eleven point seven five, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Data, you may be sturdy, but you're not indestructible.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I believe I can safely traverse the distance.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Captain, Data's going down to join you down there.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Be my guest.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Cavern]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Data lands easily next to Beverly)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "At your service, Captain.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Planet Surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We'd better find a defensive position.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "If Data's right", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "He usually is.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We're going to have company any minute now, and this time I don't know how we're going to stop it.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Ship's log, supplemental.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Lieutenant La Forge in command of the star drive section.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "After separating from the saucer, we have returned to Minos to rescue the away team.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Unfortunately, I have only one option left, and it's a long shot.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Battle Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Shields and deflectors are up.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ensign T'Su, when I order the shields down, you have one responsibility.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Locate the away team and get them up here.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You may only have a few seconds.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Approaching the planet, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ride's going to get a little bumpy.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Things are going to happen fast.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Just keep alert, stay calm.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Let's focus on what we're doing.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You know your jobs.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You've been trained, you've been tested.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You've earned the right to sit in those chairs.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Cavern]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That sound again.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Another weapon has been launched, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "We've got to find some way to stop this system.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I would need to see the programme schematic.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "You've got it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is it possible to re-adjust the targeting sequence?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Absolutely.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It wouldn't be much good without it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Data, assign it a neutral target.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The target must be specific, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Itself, then.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Itself or it's own power source.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The force of that explosion would destroy this cavern and everyone on the surface.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Watch now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is the fourth and final projectile.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Echo Papa series Six Oh Seven is about to complete this phase of its mission.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Planet Surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We could split up.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "What good would that do?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Confuse it, delay it.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Something.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "It would still get us.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It would just take a little longer.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It might give one of us long enough to get out of range.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Out of range?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Forget I said it.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "These devices wiped out an entire planet.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I don't think it has a range.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Then what does that leave us?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Right.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "That's what I thought.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(a blast behind them)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "On the other hand, we could look for deeper cover.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You got any ideas?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "None.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Cavern]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Give me some options, Data, quickly.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why don't you just shut it off?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Is that possible?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why not?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It's a machine, isn't it?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Shut it off.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You haven't seen half of what this beauty can do.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We've seen enough.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Does that mean you're going to buy it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, we've seen enough.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You've made a sale.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You won't be sorry.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Good.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
