		{ text = "Done.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The salesman disappears, and so does the 'projectile')", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Planet Surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Tasha's mouth is wide open)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Offscreen) Number One!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
