		{ text = "Steady as she goes.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hull temperature twenty five hundred degrees.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Deflectors nearing overload.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
