		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, I'm in the middle of realigning the dilithium crystals.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) There is an emergency.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We need warp drive.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How long?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Twenty minutes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Maybe more.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We don't have it, Mister Lynch.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) I'll align it by hand.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Whatever it takes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Aye, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Shuttle, this is the Enterprise.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Your coordinates now read two three seven point one zero one.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Ben, you're dangerously close to a planet.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) I can see it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(DATA) The planet is Vagra Two, sir, in the Zed Lapis sector.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Uninhabited.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Lieutenant, report!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) This is Counsellor Troi.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I'll relay, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "The pilot is busy.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "We've lost most of our impulse power.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Engineering, status report.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Working on it, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Three minutes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) But there are no guarantees.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Captain, we're out of control.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We're caught in the planet's gravity.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain's log, supplemental.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We've lost all contact with shuttlecraft thirteen, and can only assume they've crash-landed on Vagra Two.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Meanwhile, main Engineering is trying to return us to warp power.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Forget the final check.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Initiate start-up sequence.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(COMPUTER) Beginning check list.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Override.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We are going directly to start-up.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That procedure is not recommended.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Understood.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Prime matter-antimatter injectors.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Set ratio at twenty-five to one", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ratio set.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Power engine core.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Inject reactants.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Leland T. Lynch here, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We now have minimum warp drive.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Course plotted and set for Vagra Two.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Warp eight.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) I said minimum warp drive, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You heard the order, Mister Lynch.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Make it so.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Yes, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(later)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Approaching Vagra Two, Captain.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Strange, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "There's no emergency signal from the shuttle as yet.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "What is this place, Mister Data?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "There is little information in the library computer other than the fact of its existence.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Our sensor show no signs of life forms, virtually no vegetation.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Atmosphere?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Minimum for our needs, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Standard orbit.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Standard orbit.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I've located the shuttle.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Life signs?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Not yet, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Still probing.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It appears to be buried under a lot of debris.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I may have something, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Faint life signs.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Very faint.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "How many?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "There's no way of telling from here.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Data, can we beam up the injured?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Our sensors are not fully penetrating whatever the debris is.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "That's very unusual.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I cannot explain.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Number One, prepare your away team.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Right away.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Data, Yar.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Doctor Crusher, have you been monitoring all this?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Yes.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I'll meet the away team in transporter room four.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The team beam down onto a barren rock-strewn surface under a salmon pink sky.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "One of the nacelles has broken off the little shuttlecraft)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Let's go.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The life signs are weak.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(Their way is blocked by what seems to be an oil slick)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What is this?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No idea.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "We'll go around, just to be on the safe side.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(The slick moves to stay between them and the shuttle)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Let's try the other way.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(It moves again)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Enterprise, this is Riker.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We've got a problem.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What kind of problem, Number One?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) I'm not sure yet.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "There appears to be some kind of a slick blocking our path.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Will keep you apprised.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Maintain an open frequency.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Analysis, Mister Data.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Inconclusive, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I cannot tell you what it is, but I can tell you what it is not.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Explain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "There is no evidence of neural or circulatory systems.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "No internal organs.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Cellular structure unknown.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It does not have any proteins which are known to us.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It's narrower over here.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "We can get over it.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "How is it moving, Data?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I do not know, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It does not appear to have a skeletal framework or musculature.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Then what's causing it to move?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It appears to be following us, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "No sign of intelligence, no brain as we know it, yet evidence of thought, Mister Data?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Insufficient information, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is it a life form, Data?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Again, insufficient information, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Theorise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It is possible.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(The oil slick ripples, and speaks)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(ARMUS) Very good, tin man.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1,PAN+10.0" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What is it, Number One?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What are you seeing?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(As a vaguely humanoid shape rises wet and glistening from the slick)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,PAN+30.0" },
		{ text = "Trouble.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Captain's log, supplemental.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "While on a mission to rescue survivors from an unexplained shuttle crash, the away team has encountered a strange creature which seems able to assume different forms, including one which resembles humanoid.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Easy, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Let's find out what it is we're dealing with.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Agreed, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Number One, I don't believe that the location of the crash and the proximity of the creature is a coincidence.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am Commander William Riker of the USS Enterprise.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I am Armus.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why are you here?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We mean you no harm.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We have injured crewmen in the shuttlecraft.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We need to get to them.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "May we pass?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You haven't given me a good enough reason.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Preserving life, all life, is very important to us.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Why?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We believe everything in the universe has a right to exist.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "An interesting notion which I do not share.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You may now leave, if you wish.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We're not going without our shuttle crew.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I warn you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Enough!", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "We have people who need attention.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "We won't hurt you, but we must help them.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Armus lets lose a bolt of energy which sends Tasha flying a long way.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Riker and Data's phasers have no effect on Armus)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Offscreen) Number One!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The creature attacked us.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Lieutenant Yar is down.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It seems to feed on our phaser energy.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "We had no effect on it.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Armus sinks back into the slick)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What's Lieutenant Yar's condition?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Doctor Crusher, report!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "She's dead.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Transporter room, get them up, now.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Transporter Room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I need her in Sickbay now.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Go to Yellow Alert, Lieutenant Worf.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll be in Sickbay.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Status, Doctor?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Unchanged.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Can you bring her back?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We'll see.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Neural stimulator.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(NURSE) Neural stimulator locked in.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Interlock current feeds.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Set sensitivity factor to four point four.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Affirmative.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(NURSE 2) Monitoring two point three, one point eight.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You did it.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No. I've got her on full support.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "There is no independent brain function.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Current feeds operating.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "She's not responding, Doctor.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Her synaptic network is breaking down.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Inject norep.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Neurons are beginning to depolarise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let's go for direct reticular stimulation.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Direct?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do it!", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(Tasha jerks from the shock) Increase to seventy microvolts.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(Tasha moves less this time)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Eighty five microvolts.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(nothing) Again.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(still nothing) Ninety.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Again.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Again.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "She's gone.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Gone?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "There was too much synaptic damage.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "That thing just sucked the life right out of her.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "There's nothing I can do.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Shuttlecraft]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Armus has slithered over the shuttlecraft, and Troi has found her communicator doesn't work)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Offscreen) Your friends have deserted you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They're not coming back.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're wrong.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) I killed one of them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I know.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) How could you be in there and know that?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I felt her die.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) Do you want to know why I killed her?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Your answer would be meaningless.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "That act had no reason.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) Exactly.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It had no meaning.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I did it because I wanted to.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It amused me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No. You thought it would amuse you, but it didn't.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "You felt no satisfaction.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) No. It was too easy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You wanted her to suffer.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "You have a great need.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) I need nothing.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Liar.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "End this.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Let us go.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) Not yet.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They won't give you what you want.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) And what is that?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "To break their spirit.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) Oh, is that what I want?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If breaking their spirit will amuse me, then that's what I will have.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Never.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Observation lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "She didn't do anything.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Her phaser was lowered.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "She only wanted to get to Troi and Lieutenant Prieto.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "There was nothing provocative about what she did.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "She was killed in a brutal, senseless act.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Lieutenant Yar's death is very painful for all of us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We will have to deal with it as best we can for now.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Until the shuttle crew is safely beamed aboard this ship, our feelings will have to wait.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Is that understood?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant Worf, you are now Acting Chief of Security.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I will do my best, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Doctor, what is the state of the shuttle crew?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We're still receiving faint life signs, but the sensor readings are fluctuating.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "They may not be accurate.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Armus is capable of creating undefined forcefields.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "In effect, we are powerless to communicate or use the transporter unless it allows it.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "A creature against whom we seem to have no defence.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Number One?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's down on that planet waiting for us to come back.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It killed Tasha and it could have killed us, but it didn't.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Deanna and Ben are alive for a reason, and it knows we're not going anywhere as long as they're still alive.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Are you saying it's attack on Yar was not arbitrary but part of some tactic?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The only way to find out is to go back down.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Commander, I may be able to see something in the creature which might be helpful.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Agreed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Prepare your away team, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant Worf?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I will remain on the ship.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The object here is not to engage the creature in battle.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The goal is the safe return of Counsellor Troi and Lieutenant Prieto.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I can best accomplish this at the Tactical Station.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Very good.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Number One?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Enterprise.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We're approaching the shuttlecraft.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The creature is covering it.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Shuttlecraft]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) I lied to you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They came back.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let me talk with them.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) No.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Does the thought of my having contact with them make you uneasy?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) No. Not being able to contact you, not knowing if you are alive, makes them uneasy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Can't you feel how worried they are?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Yes, they are worried.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) They care for you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You must be very special.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We are members of a community.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "We all care for one another.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) Equally?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You were really surprised they came back.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Because the others did not?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) What others?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You can't hide the emptiness from me.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "The others.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "The ones who hurt you.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Who left you alone, rejected.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "The ones who make you so angry.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) What do you know of them?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Only what you tell me.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) I will tell you nothing.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Not now.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "But soon.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, look at this.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The force of the energy field around the shuttlecraft decreased for a few moments when the creature was draped over it.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "But not low enough to beam them out.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Almost.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(WESLEY) It's approaching the away team now, and the energy field over the shuttlecraft is back to full intensity.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Chart it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I want to see if there's a pattern.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Armus rises from his slick)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "She said you'd be back.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then she is alive.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "For now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why have you returned?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We have no choice.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We're here to negotiate for our team.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What do you want?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Maybe I want nothing.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then you would have killed all of us.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I still might.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What do you want?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Tell me.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Maybe we can reach an accommodation.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "If I tell you, will you give it to me?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I might.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It depends.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I am a doctor.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I need to treat our injured friends.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Say please.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "You ask nicely.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I will allow it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Wait!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I've changed my mind.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Talk to her from here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Troi, can you hear me?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Shuttlecraft]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Deanna, are you all right?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Beverly?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) I can hear you.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Are you all right?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Shuttlecraft]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We've encountered some difficulty.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Shuttlecraft]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I know.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "She needs our help.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "So what.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Our friends are suffering only a few metres away, yet you block our path.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Why?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "You are all ungrateful.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What is he made of?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It did not register on the tricorder.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Does that mean I am not alive?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No. Clearly you are some kind of intelligent form.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "But you said I did not register on your instrument.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Perhaps your instruments are useless.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Data's tricorder and phaser fly away, and Geordi's visor falls to the ground.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "He gropes to find it)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Don't help him.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Data.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Half metre to the right, Geordi.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Armus moves it again)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Aren't you going to lead him to his sight again, robot?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No. You will just move it again, and I will not help you hurt him.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Then give it to him.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I will find something else to amuse me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The slick covers the shuttlecraft again)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Shuttlecraft]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) You said they wouldn't amuse me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You were right.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And the emptiness remains.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "You sound so alone.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) I am alone.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Abandoned.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Who deserted you?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) Creatures whose beauty now dazzles all who see them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They would not exist without me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You were together?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) They perfected a means of bringing to the surface all that was evil and negative within.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Erupting, spreading, connecting.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "In time it formed second skin, dank and vile.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They discarded you and left.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) And here I am.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You have my pity.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Armus gets angry and shakes the shuttlecraft)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Offscreen) Your pity?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Save that for yourself.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The team are not watching when Armus leaves the shuttlecraft and approaches them.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Riker is grabbed by an invisible force and dragged by the feet to the slick)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Help!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1,PAN+10.0" },
		{ text = "Data, something's got me!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1,PAN+10.0" },
		{ text = "Touch him and he dies.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1,PAN+10.0" },
		{ text = "No!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1,FEA+10.0" },
		{ text = "No, don't!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1,FEA+10.0" },
		{ text = "(Riker disappears into the slick)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,FEA+20.0" },
		{ text = "Enterprise?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Armus has enveloped Commander Riker.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I'm beaming you up.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If any of you leave now, he dies.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) And so do the survivors of the crash.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Riker's screaming face appears in the slick, and sinks away again)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain's log, supplemental.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "There is grave danger to the crew on Vagra Two.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "My first officer is missing, attacked by this entity known as Armus.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, perhaps you should look at this.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We have the chart of the energy field surrounding the shuttle.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "There are a great deal of fluctuations.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, but there is a pattern.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Here is when it killed Lieutenant Yar, and here is when it absorbed Commander Riker.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "And here, here, where the energy is lowest?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Both times it had enveloped the shuttle.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It has something to do with Counsellor Troi.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Somehow when it's dealing with her, the energy field is affected.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I want to talk to her.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm going to beam down.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant Worf, you have the conn.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Shuttlecraft]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Armus, and presumably Riker, are draped over the craft again)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Imzadi!", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "No!", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Please, stop hurting him!", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) He resists.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If he would give himself over, the pain would diminish.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He struggles.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You should feel his strength.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I can.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) Should I let him go?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You only ask to torment me.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) Perhaps.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How should I answer?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "What can I offer except myself?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) And would you give yourself for him?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Would you give that much?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Without hesitation.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) Just for him?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No, not just for him.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I would do the same for any of the others.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Armus, you have me.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Let them go.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) Perhaps.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ah, another has arrived.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard is with the team.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Armus slithers over to them)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You are the one in charge?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(to Data) Is Commander Riker alive?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Answer, Tin Man.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I would guess that death is no longer sufficient entertainment to alleviate its boredom.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Therefore, Commander Riker is alive.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Maybe.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Don't you want to ask me what I want?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Not even to protect your own existence?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I want to see my people in the shuttle.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Entertain me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard shakes his head) Then I will do it myself.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You, Tin Man.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Data moves jerkily, and points his phaser at Beverly, then Picard)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Now tell me, how would you feel if you were the instrument of death for your leader?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Don't struggle, Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I have no control over what you do with the phaser.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Therefore, I would not be the instrument of his death.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Perhaps killing the Doctor would engender more feeling?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No, the control is still yours.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "And what about you, Doctor?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Are you ready to die?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Tell me you are not afraid.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am afraid.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Beg me to spare you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "One of you is going to die, and you, Doctor, get to choose.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You don't like that, do you?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then I choose myself.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "No. You are going to live.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "One of them dies.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Data's arm swings around and ends pointing the phaser at his own head)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,PAN+40.0" },
		{ text = "Maybe this one.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Though I would not call it death, since he is only a device.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Tell me, Tin Man, how does it feel to face your own extinction.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Curious.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(the phaser drops) You are capable of great sadism and cruelty.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Interesting.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "No redeeming qualities.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "So what do you think?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I think you should be destroyed.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1,PAN+10.0" },
		{ text = "A moral judgment from a machine.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Armus, we're finished dealing with you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I have your man in here, and the others in the shuttle.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It doesn't matter.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We will no longer be a source of amusement.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I can kill them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You can.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But only I can command them.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They follow my orders.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Have them amuse me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Only if you let me see my people on the shuttle first.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I must see them.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Not possible.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then our business with you is concluded.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And you claim you care about your comrades.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I care.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Which is why I must see them.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You want to see your people?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then, here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Look at this one.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Riker is tossed out of the slick, covered in oil)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "He's alive.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Don't move.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Is he all right?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "All signs show normal.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "So much frustration.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It had to get rid of me.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Now, let me see the others.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I will not allow my people to entertain you until you do.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They are incapable of entertaining me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I want the four of you out of here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Enterprise, beam up the away team immediately.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Aye, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "They're no longer involved.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is between you and me.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They may leave.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The team are beamed away)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Now.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I want to leave this place.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You want me to give you transportation?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "For which I will trade you lives.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I must see my people in the shuttle.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Will you give me what I want?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I have the means.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But first I must see my people.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If you must.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Shuttlecraft]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Armus transports Picard into the shuttlecraft)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Troi, are you all right?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, but Ben is not.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "He's alive.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Were you able to help Tasha?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "No. Troi.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Troi, we must talk.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I believe it's possible to outmanoeuvre this creature, and beam you and Ben back up to the ship.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We've been monitoring the energy field that surrounds the shuttle.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "When the creature is here, the field weakens.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do you know why?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The creature is filled with rage.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Undirected, unfocused rage.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "When he confronts it, his guard goes down because he's feeling it instead of suppressing it.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Acknowledging his needs makes him vulnerable.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "What caused the rage?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He was left here.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Abandoned.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The forcefield will have to drop below two point seven before we can beam them up.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard is brought back from the shuttlecraft)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Satisfied?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Then can we leave?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Where do you want to go?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do you want to try to find those who left you here?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "She told you about them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How long have you been here?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Since they left.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A very long time.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A long time to be alone.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Save your compassion.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's revolting.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You offer it like a prize when in fact it's an insult.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Because you feel unworthy.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You overrate your gift.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You humans are puny, weak.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But our spirit, it is indomitable.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And still you die from a flake of my power.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A great poet once said, 'all spirits are enslaved that serve things evil'.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You do not understand.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I do not serve things evil.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am evil.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh, no, you are not.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I am a skin of evil left here by a race of Titans who believed if they rid themselves of me, they would free the bonds of destructiveness.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "So here you are.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Feeding on your own loneliness.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Consumed by your own pain.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Believing your own lies.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Set the computer.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "When the energy level reaches two point six point two zero five, automatically beam up Troi and Prieto.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We'll do a parallel transport on the Captain.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You say you are true evil?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Shall I tell you what true evil is?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It is to submit to you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It is when we surrender our freedom, our dignity, instead of defying you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I will kill you, and those in there.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But you will still be here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "In this place.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "For ever.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Alone.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Immortal.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(male) Stand by for parallel transport.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The energy level has dropped to two point six point three.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That's your real fear.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Never to die.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Never again to be united with those who left you here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Energy field is two point six.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Computer beginning transport.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm not taking you anywhere.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Armus screams with rage as Picard is beamed away)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain's log, stardate 41602.1.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The shuttlecraft has been destroyed to prevent any possibility of Armus leaving the planet.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Vagra Two will be declared off limits.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But the damage has been done.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "One of the saddest duties I've ever had to perform is now ahead of me.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Holodeck - meadow]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We are here together to honour our friend and comrade, Lieutenant Natasha Yar.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Coming to terms with the loss of a colleague is perhaps the most difficult tasks we must face in the work we have chosen to pursue.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We will all find time to grieve for her in the days that are ahead, but for now she has asked that we celebrate her life with this.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(An image of Tasha appears on a stand)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Hello, my friends.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You are here now watching this image of me because I have died.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "It probably happened while I was on duty, and quickly, which is what I expected.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Never forget I died doing exactly what I chose to do.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "What I want you to know is how much I loved my life, and those of you who shared it with me.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You are my family.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You all know where I came from and what my life was like before.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "But Starfleet took that frightened, angry young girl and tempered her.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I have been blessed with your friendship and your love.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Will Riker, you are the best.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You trusted me, you encouraged me, and most of all you made me laugh.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Deanna, you are capable of so much love.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You taught me without ever having to say a word.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I realised I could be feminine without losing anything.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Ah, Worf.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "We are so much alike, you and I. Both warriors, orphans who found ourselves this family.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I hope I met death with my eyes wide open.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Beverly.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Your fierce devotion comes from within.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "It can't be diminished.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "From you, I have learned to strive for excellence, no matter what the personal cost.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Wesley, I'm sorry I won't be able to see you grow into the exceptional man you'll be.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "But your kindness and innocence are ageless.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Geordi, in those moments I felt the most despair, you took my hand and helped me to see things differently.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You taught me to look beyond the moment.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "My friend Data.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You see things with the wonder of a child, and that makes you more human than any of us.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Captain Jean-Luc Picard.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I wish I could say you've been like a father to me, but I've never had one, so I don't know what it feels like.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "But if there was someone in this universe I could choose to be like, someone who I would want to make proud of me, it's you.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You who have the heart of an explorer and the soul of a poet.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "So, you'll understand when I say, death is that state in which one exists only in the memory of others.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Which is why it is not an end.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "No goodbyes.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Just good memories.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Hailing frequencies closed, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Au revoir, Natasha.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The gathering is concluded.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Everyone but Picard and Data leave, sniffing a bit)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Sir, the purpose of this gathering confuses me.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Oh?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How so?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "My thoughts are not for Tasha, but for myself.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I keep thinking how empty it will feel without her presence.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Did I miss the point?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "No, you didn't, Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You got it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[img=music]  End Credits [img=music]", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,STS-0.4,UHP-3.0" },
	},
};

