		{ text = "Stay sharp, everyone.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(COMPUTER) Attention all decks.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yellow Alert.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Engineering, this is Lieutenant Commander Data.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Bring all systems online and direct full power to the shields.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All sections secure, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Lieutenant La Forge?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(LAFORGE) Course is set, Captain.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Half impulse.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir, one half impulse.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "On main viewer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mask out the photosphere.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Magnify.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Quadrant one seven, magnification factor twelve.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(RIKER) I've never seen anything like this before.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The violence of these eruptions is awesome.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Captain, I'm reading an unusual number of sunspots and eruptive prominences, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The magnetic field is extremely irregular.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(WESLEY) Captain, deflectors are being hit by a huge burst of X-rays.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(The science station lights up with blue static and he steps away)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Wow!", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Look.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Captain, my console seems to be overloading.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(WORF) The X-ray burst is disrupting systems, Captain.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm adjusting deflectors to compensate.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Engineering, increase power to forward shields.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(CREWWOMAN) Forward deflectors online, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Her console lights up blue as well)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Status report.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "All systems operational, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(TROI) Captain, the level of tension on the ship is mounting.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Understandable.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister Data?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If we are to investigate, sir, we must get closer.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Mister La Forge, move us in closer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is the Captain.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We will be pushing the shields to the limit, but we are getting a splendid view of this phenomenon.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, I have intercepted what appears to be a distress signal.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Let me hear it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) This is T'Jon.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am on the Ornaran freighter (garbled).", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We have a serious problem here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Can't seem to fix it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We need help.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The transmission is coming from a freighter in orbit around the fourth planet in the system.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "However, the receiving station is on the third planet.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Open hailing frequencies.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(TASHA) Hailing frequencies open.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Unidentified freighter, this is the USS Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "May we be of assistance?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) (a very poor image) Whoever you are, yes!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm having trouble navigating.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We can't maintain a steady course.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please help us if you can.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Can't you enhance the frequency?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Too much interference.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Set course to intersect with the freighter.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Warp two.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Maintain Yellow Alert.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) I can't control the helm any longer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm losing orbit.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We're heading into the atmosphere.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please, do something.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We're going to burn up.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We haven't much time.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It is a freighter, very old.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, our sensors are being severely affected by the sun flares.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Helm, take us in as close as possible to the freighter's orbit.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Moving in, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "There are six life forms aboard the freighter, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Captain, the freighter's orbit is decaying.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "How long before it loses integrity?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "A matter of minutes.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Unidentified freighter, this is the USS Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Enterprise, this is the Ornaran freighter Sanction.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Put this on the main viewer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The visual transmission is still breaking up.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Freighter Sanction, this is the Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What is your situation?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Not so good.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Helm isn't working right.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What is the problem?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is the Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "With all this interference it is difficult to copy your transmission.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If you all talk at once it is almost impossible.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now, please say again.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What is your situation?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) I am T'Jon, Captain of the Sanction.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "At last.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How can we help?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) We have lost, I don't know, something.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am no longer able to maintain this orbit, nor am I able to use the main thrusters.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's all, you know, dead, I guess.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's all shut down?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well, that is a little vague.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What is the computer analysis?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Well, the computer's not working very well.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Data, can you tap into their computer and clarify the situation?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I will attempt it, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Captain, the tractor beam is available, if you want it.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "At least we can pull them out of orbit before they enter the atmosphere.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Freighter, we're going to lock on the tractor beam and pull you out of orbit.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Hey, that's, that's great.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, the freighter's orbit continues to deteriorate.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The solar flares are interfering with the tractor beam, Captain.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I can't lock on.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Captain T'Jon, we are unable to attach our tractor beam because of the intense solar activity.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) I understand.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thanks for trying.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sir, I have determined what is malfunctioning on the freighter.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Captain, we have analysed your problem.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Great.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Your ship's design uses an electromagnetic coil to constrict the exhaust flow.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "That coil is misaligned.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(On viewscreen) Really?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do you have the necessary tools to realign the coil?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) I don't think so.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I believe, Captain, we can provide them with a temporary substitute.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Our ship's stores contain a coil of the proper type.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Can we beam one over?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, we're beaming over a replacement coil.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) That's great.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And that'll fix us up?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, once it's installed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Right.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And how do we do that?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What is the matter with these people?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How can he be Captain of that vessel and not understand its simplest function?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, how long have you been in command of this freighter?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Seven years.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is my twenty-sixth voyage to Brekka.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And you don't know how to align a control coil?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) It's never come up.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Can anyone else over there do it?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(On viewscreen) Hold on.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll ask.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sorry.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Nobody here knows anything about it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'd better get over with a team.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Captain, I strongly recommend against anyone from this ship beaming over.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "The solar interference is too great.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "The freighter has entered the planet's atmosphere.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Disintegration is imminent.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We're running out of options.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Let's get them off there.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "T'Jon, stand by to beam over.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) If you think that's best.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Unless you have any other options, yes, I think that's best.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Whatever you say.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Blue is playing on the transporter controls)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I can't maintain a positive lock.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Unusual E-M burst, sir.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Readings off scale.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Freighter Sanction, this is the Enterprise.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We're having difficulty getting a fix.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Have them go to their own transporter room.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "It will be tricky, but perhaps I can link the two transporters in series and get them over with the increased power.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "It's worth a try.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Captain T'Jon go to your transporter room.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Contact me from there.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) Right, Enterprise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's right next door.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But if you can't get us with your transporter, what makes you think ours will do the job?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm going to interconnect them.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) I didn't know you could do that.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Hurry!", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "We're running out of time.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) We're on our way.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't think they're going to make it.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Captain T'Jon doesn't sound like he cares one way or the other.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Worf, how much time do we have?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) One minute, twenty eight seconds.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain T'Jon, are you there?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) We're in the transporter room.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Great.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Activate your transporter.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Set coordinates nine seven zero three mark two six eight.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Did you say two eight six or eight eight six.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Mark two six eight!", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Got it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sir, the hull temperature of the freighter is passing three thousand degrees.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "What's the hold up, Number One?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We're establishing the link right now.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) We're ready.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then all six of you, get on the transporter platform.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "On my signal, energise.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I've got their initial transporter signal.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Interlock complete.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Energise on my mark.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Four, three, two, one, energise.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(A big barrel arrives, no people)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Offscreen) Number One, did you get them off?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "They stayed in the freighter", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But they beamed over their cargo.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Cargo?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Are those people crazy?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What could possibly be so important?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir, the freighter is disintegrating.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Their lives are in danger and they're beaming over cargo?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Number One, I don't understand these people.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, the freighter's hull temperature is approaching critical.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Unbelievable.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) We're losing the freighter.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Got to beam those people over now.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Clear the cargo.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Beam it to a hold.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Freighter Sanction, this is the Enterprise, Get to your transporter pad and.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No, forget about that.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Lock on to any life form you can find and get them over now.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Trying, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The freighter is going down.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Transporter Room.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You're out of time.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Reading six life forms, but I can't get a solid lock.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "We have no choice.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Energise.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I thought you said there were six.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Where are the other two?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The lock didn't hold.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Re-establish.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's too late.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The freighter goes bang)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Number One?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Two scruffy men, one smart man and a smart woman arrive)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Two are lost, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "But we saved four.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'm Commander William Riker of the USS Enterprise.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'm sorry we were unable to rescue the other two.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We did everything we could.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "If you had come instead of sending your cargo, you all would have made it.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The cargo.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Where is it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(ROMAS) You did save it, didn't you?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The two men are scruffy, with five o'clock shadow)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Yes, we did.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's in one of our cargo holds.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You act like it's more important than your comrades.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The cargo.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "May we see it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Follow me.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Cargo bay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thank you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thank you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We thought we had lost it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(SOBI) (the smart man) Careful with your choice of words, T'Jon.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's ours, not yours.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We paid a fair price for it!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(LANGOR) (the woman) You offered a fair price.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We have not received it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You accepted our goods in trade!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then where are they?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Tell me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That's not our problem.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No more than the barrel is your property.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's as simple as this.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The goods were never delivered.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They were destroyed with your ship.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Hence the deal was incomplete.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Hence possession of the felicium remains ours.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We need it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Commander, I request you transport my associate and myself down to Brekka, with our merchandise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, get them out of here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But the barrel stays.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There's no reasoning with you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Langor, you expect too much from them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You always have, and you've always been disappointed.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You didn't pay for it, therefore it's not yours.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Damn you, Sobi.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(T'Jon puts his hand on Sobi's chest and electricity sparks.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Sobi returns the attack.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Langor immobilises Romas when he tries to interfere)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Stop this.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Now!", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Security to cargo bay eleven.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Tasha's phaser separates Sobi and T'Jon)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Behave yourselves, gentlemen.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Security arrive)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Escort our visitors to the observation lounge.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(They leave)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "A natural electrical charge?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Formidable.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes, and a difficult weapon to confiscate.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I have never seen humanoids with that power.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Neither have I. I wonder how it evolved?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I wonder if the strong magnetic field of their sun might have something to do with it.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Could be.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I wonder how much power our guests can produce?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Or if they need to recharge?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "That's an interesting ability.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "The question is, how do I defend against it?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You think our visitors pose a threat?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I don't know.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "But if they do, I'd better be ready for it.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Precisely, Lieutenant.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Observation Lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We have to resolve this soon.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There isn't much time.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What can you offer?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Everything we had went down with the Sanction.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then it's going to be difficult to accommodate you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Solar flares are increasing in magnitude, Captain.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Shields are shaky, but they're holding.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Maintain.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Riker and Tasha enter)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "How are our rather quarrelsome guests?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They're waiting for you in the observation lounge, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Data, what information can you give us about the inhabitants of this system?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Not very much, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The only recorded contact with the Delos system was over two hundred years ago.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "A scout ship reported two inhabited planets, one of which was on the verge of acquiring space travel.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Odd that in two centuries neither civilisation advanced much further.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir, I find it strange that none of our visitors feels much remorse about the two freighter passengers who perished.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Yet that barrel of cargo is a source of great anxiety to them.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Yes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Let's pay a call, get some answers.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Number One, Mister Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister La Forge, you have the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Observation Lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, may I present Sobi and Langor from the planet Brekka, and T'Jon and Romas from Ornara.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I am sorry we couldn't save your ship.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Tell me, you could have repaired it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh, yes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We have only two left.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Neither works properly.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They are critical to our survival.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Will you fix them?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Mister Data?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Our computer has downloaded the specifications for the magnetic coils the freighters depend on.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Assuming the malfunction is similar, suitable replacements can be fabricated.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Instruct Engineering to proceed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Now, as for our cargo.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Our cargo, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Wrong, Sobi.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "My First Officer has said that ownership of this cargo is in dispute, but this question must be settled by whatever legal mechanism exists between your societies.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, you don't understand.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is a matter of life and death.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why is that?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What is this cargo?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Medicine.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Indeed?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Our planet is in desperate need, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We have people suffering from a lethal plague.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The cargo you have impounded is the only hope of life for our people.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is that so?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You must think us heartless brutes, Captain, but look at our side of it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The plant which yields the medicine felicium grows only in remote areas of Brekka.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It must be painstakingly cultivated, harvested, purified.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A complex and expensive process.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That single shipment of felicium represents an enormous investment.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We can't just give it away.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We paid for it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We are asking only for what is ours.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That is your viewpoint.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ours, of course, differs.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You are going to hold to that position?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am constrained to abide by the terms of our agreement.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then you condemn us to death!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Romas.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You disgust me!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If you could see the suffering the plague has caused.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well, you are going to, when you see what it does to us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Romas, are you and T'Jon carrying this plague?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Every Ornaran does.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then you may have brought it aboard this ship.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Was there a medical scan when they transported?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Unverified.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The solar flares could have caused a malfunction in the biofilter.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Med Alert.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Medical emergency.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Doctor Crusher to the observation lounge at once.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Romas is suffering on a bed)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You find us well, I trust?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(CRUSHER) As far as I can tell.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Your physiology is somewhat different than I've ever encountered, but I detect no dangerous virus or bacteria.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I assure you we're in perfect health.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But we are not.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We need our medicine.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Our medicine.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We paid for it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's ours now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Enough!", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Of course.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You are right, Doctor.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Could we leave now?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I see no reason why not.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Escort them to their quarters.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(Picard enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, could I speak with you please?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "In private.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Not right now.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Doctor's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What have you learned?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Brekkians show no sign of infection.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The Ornarans show all the symptoms of a disease but I can't find a cause.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Perhaps it was filtered out by the transporter when they were beamed aboard?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "There's no record of it.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Then again, the solar flare activity might have caused a malfunction in the biofilters or their monitors.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Are they going to die?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I need to check further, but my instinct says no.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Do you think we are in danger from this plague?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Again, I need more time.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "There are some missing pieces to this puzzle.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What's missing is a little compassion.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Are we losing our professional detachment, Doctor?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Perhaps.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "But Captain, I must tell you, I'm developing a very active dislike for these Brekkians.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Understandable.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, you must give us back our cargo.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I can't do that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It belongs to us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Brekkians claim it belongs to them.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They lie.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That may be so, but it's not my decision.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We need some now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't care if it's your decision.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Get us some.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, what is happening to us, is happening to thousands more on Ornara.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please understand the magnitude of the problem.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Your people don't need it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Brekkians don't need it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Our people do.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If you don't give it to us, you will be a party to murder, not only of us, but of an entire civilisation.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm sorry, I do not mean to insult you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm feeling very shaky.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We cannot hold out much longer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They believe it will help them.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "That in itself might control their symptoms.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I'll talk with the Brekkians.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We appreciate anything you can do.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Guest quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Langor and Sobi are relaxing and enjoying a drink when the doorbell chimes.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Picard enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "May I say, Captain, how impressed we are with your ship and all its facilities.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thank you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And its crew.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Everyone is so efficient and professional.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am glad you are comfortable.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I've come to seek your agreement to an Ornaran request.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We cannot agree", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Wait.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let the Captain speak.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We want to be reasonable.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The two Ornarans are really quite ill and insisting that they need felicium.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That is not surprising.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Would you object to giving them enough for their own immediate needs?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, we Brekkians are in business.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We are not in the habit of giving away what has not been paid for.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You would see them die rather than share the medicine?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We want to be fair, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We agree to permitting them two dosages for immediate use.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No charge.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll let my Medical Officer handle it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, I trust you won't mind if we're present while you open the cargo.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Cargo bay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The barrel is opened and a device lifted from the top of the powder)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What does that device do?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "This measures the individual portions.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(He takes a few grains and puts them into the device)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What's the dosage?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Point zero one millilitres.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Very potent substance.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "We've improved our distillation process over the years.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "In my grandfather's day, the same amount of felicium would have filled five rooms this size.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But now with our improved processes, this one barrel contains over four billion doses.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I would estimate four billion, three hundred seventy five million", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Thank you, Mister Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How long is the dosage effective?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It varies with the individual.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But never more than seventy two hours.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And then the symptoms return.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Unfortunately, there is no cure for the plague.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But felicium inhibits the sickness with total efficiency.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Allowing the Ornarans to lead normal lives.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "An unusual disease.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Virulent, extremely persistent, yet I can't isolate it.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The plague has baffled doctors on both planets for two hundred years.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We've therefore concentrated on treatment, finding new ways to improve the potency and purity of felicium.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No doubt you have applied your technology from other industries to the refinement of this product.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "We have no other industry.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "None at all?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We don't need any.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Ornarans provide us everything we need in exchange for this.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Fascinating.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Your society is dedicated exclusively to the production of a single product.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "A product for which you have no use, but which the Ornarans can't live without.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "One of the little ironies of life, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But one we'd be fools not to take advantage of.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's mutually beneficial.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Ornarans provide us with the necessities of life, and we provide them with the necessities of living.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It is a fair exchange.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Interesting relationship.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Excuse me.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I'll take that to Sickbay.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(T'Jon and Romas pounce on the device)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Can I help?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "No.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(T'Jon puts a hypo onto the device and sets it going)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Hurry up!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(T'Jon injects Romas, then himself.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Their shakes vanish and they become very, very calm and relaxed.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Totally blissed out, in fact, man)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You're feeling better?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Yes, thank you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm fine now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Ready room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Come.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Beverly enters, face like thunder)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I may not know felicium's full effect on Ornarian physiology, but I know how to interpret physical reactions.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Are the Ornarans recovering?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "T'Jon and Romas are feeling fine.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "In fact, too fine.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Felicium's a narcotic.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Then T'Jon, and Romas, indeed everyone on their world?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Is a drug addict.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I think you'll find it's a fascinating tale that we've come across here, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You've piqued my interest, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Please continue.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Data, what have we got?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Beginning several thousand years ago, the two worlds took different paths.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Ornara became technologically sophisticated, Brekka did not.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Then two hundred years ago, Ornara was stricken by a devastating plague.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Their advanced technology could provide no solution.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Somehow, and there is limited information on this point, the cure was found in a plant indigenous to only Brekka, and which resisted all attempts at cultivation on Ornara.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "In any case, a trading situation developed which still exists.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "A nice arrangement for the Brekkians.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "And for the Ornarans.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Without the medicine, they would all die.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No they wouldn't.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Despite what the Ornarans have been saying, it's not a medicine.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It was a medicine, but it cured the plague two hundred years ago.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The plague is irrelevant now.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It doesn't exist.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Then why are they so desperate for the felicium?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's an addiction.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The physical and psychological need is very real.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Thank you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What are you going to do?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Based on what we know so far, there's nothing I can do.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You don't think drug addiction and exploitation is sufficient cause to do something?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "This situation has existed for a very long time.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "These two societies are intertwined in a symbiotic relationship.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "With one society profiting at the expense of the other.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "That's how you see it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I can synthesise a non-addictive substitute which will ease their withdrawal symptoms.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "No, I can't do that either.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You can't let them have the felicium.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Why?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Because it offends against our sensibilities?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It is not our mission to impose Federation or Earth values on any others in the galaxy.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well in this case, Captain, I disagree, one hundred percent.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Captain, I'm receiving a call from Ornara.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "The signal is ragged, but I think I can put it on the viewscreen.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Do it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) I'm Margan.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Are my people on board your ship?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Some of them, yes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) May I speak to them?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant Yar, have the Ornarans brought in.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No, wait, I don't want them to have access to the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We will contact you in a few minutes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(end transmission) I will continue this in the guest quarters.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Commander, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Picard, Riker and Crusher leave)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Data, I can understand how this could happen to the Ornarans.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "What I can't understand is why anyone would voluntarily become dependent on a chemical.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Voluntary addiction to drugs is a recurrent theme in many cultures.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Wesley, no one wants to become dependent.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "That happens later.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "But it does happen.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "So why do people start?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "On my home planet, there was so much poverty and violence, that for some the only escape was through drugs.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "How can a chemical substance can provide an escape.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "It doesn't, but it makes you think it does.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You have to understand, drugs can make you feel good.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "They make you feel on top of the world.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You're happy, sure of yourself, in control.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "But it's artificial.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "It doesn't feel artificial until the drug wears off.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Then you pay the price.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Before you know it, you're taking the drug not to feel good, but to keep from feeling bad.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "And that's the trap?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "All you care about is getting your next dosage.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Nothing else matters.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I guess I just don't understand.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Wesley, I hope you never do.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Lieutenant Yar, we're ready in the guest quarters.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Ornaran Guest quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(On monitor) T'Jon.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Have you got it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well, it's here, but", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(On monitor) You've got to get it to us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "T'Jon, please.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We're dying down here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You don't know.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's worse than it's ever been before.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm doing everything I can.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You see, what happened was", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(On monitor) Stop!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I can't listen any more!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "T'Jon, there are so many people here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "So much suffering.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We need the medicine.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You've got to get it to us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I can't go on.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Help, T'Jon.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm sorry.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(T'Jon uses his electric power on Riker)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You will take us to our planet and leave us there with our medicine or this person dies.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Don't you see I have no choice?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We were sent to bring the felicium back.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The suffering on my planet is too great.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "People are dying.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It doesn't matter whether we're entitled to it or not.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We must have it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let him go.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You will take us there now, or give us a shuttle.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But we must have the medicine.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If you refuse, this person will die.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I will not be coerced.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I will do it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I will kill him.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No. No, you won't.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You're not a killer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(T'Jon lets Riker go)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Help us, please.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Help us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm not sure that I can.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "My insides are still shaking, but I'm all right.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(The door opens)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain Picard, could I see you a moment?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll stay here.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Come with me, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This could be interesting.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Brekkian Guest quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We have thought about this a great deal.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We both of us feel deeply about what is happening on Ornara.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Although it is going to cause hardship to us and to our people, we have decided to give the felicium to them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They can pay whenever they are able.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We don't want to be the ones responsible for their suffering.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There goes the other shoe.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They know.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What do they know?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "They know that the Ornarans no longer have the plague.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They know that felicium is no longer a medicine.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "So, of course, they are willing to give this shipment because they don't want to take the chance that the Ornarans will lose their addiction.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They don't want to lose their only customers.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How would they have known all that, unless the plague had once infected their world as well.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "They were infected.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They used the felicium to cure themselves, but somehow their ancestors realised that it was a narcotic.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They broke the cycle of addiction and never told the Ornarans.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "They let them continue to believe that without the felicium they would die.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I think it's worse than that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "My guess is this refining process of which you are so proud", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "is only to increase the potency of the felicium and tighten your grip!", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "What are you going to do?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Are you going to tell them?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No. I'm bound by the rules of the United Federation of Planets, which order me not to interfere with other worlds, other cultures.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If I were to tell them any of this, I would violate that Prime Directive.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But you are talking to us about it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is information you already know.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And so nothing has changed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If you can't interfere, then you are going to allow the Ornarans to have the felicium?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, I am.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No!", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Don't do it.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Jean-Luc, this is not a symbiotic relationship.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "This is exploitation, pure and simple!", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The Brekkians have caused all of this suffering and hardship only to make their pitiful lives easier!", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "And all of it based on a lie.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "No, deny them this shipment.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It is the least we can do.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "From the moment they agreed to give them the felicium, my hands were tied.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You are absolutely right, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's not your business.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Cargo bay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Are these the spare parts for our freighters?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They look complicated.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Are they already?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Aligned?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Right.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You'd have to ask the Captain.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Picard, Crusher, Sobi and Langor enter)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Offscreen) Captain Picard, we've arrived at Ornara and assumed standard orbit.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Acknowledged.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, please.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "My planet is suffering.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We beg you to give us our medicine.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The matter is already decided.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You're beaming down to your world with the felicium.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Great.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I knew it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I knew you'd help us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We thank you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Don't thank me.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sobi and Langor, they decided to let you have it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We'll discuss the payment terms later.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "In the mean time, there's no need to deprive you of your needed medicine.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That's terrific.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm sure we can come to a fair deal for payment.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "May we beam down with you and discuss it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Of course.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You are quite welcome on Ornara.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We appreciate your hospitality.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And Captain, we appreciate your gift of the coils.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Once our freighters are fixed, everything'll be back to normal.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The coils stay here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What about our freighters?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You want to repair them, you'll have to learn to do it yourselves.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We can't.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If you don't help us, our ships will soon be inoperable.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Quite possibly.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If you withhold those coils, you'll be disrupting the stability of both our planets.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And interfering with a trade arrangement that has lasted for generations!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What of your Prime Directive?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "In this situation, Prime Directive prohibits me from helping you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That's absurd!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You did not think so when it worked in your favour.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do you want our world to suffer?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh no, I don't want that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Without the freighters, there will be no more shipments of felicium.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We will die.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You must trust yourselves.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "There are other options.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Ensign, prepare to beam our guests and their cargo down to Ornara.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, I hope you realise what you've done to us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Of that you can be sure.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Good luck.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Turbolift]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Main Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "When the Felicium runs out, the people of Ornara will suffer horrible withdrawal pains.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "No doubt, but they will pass.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That seems so cruel.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "We could have made their burden easier.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Could we have?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Perhaps in the short term.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But to what end?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hold.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Beverly, the Prime Directive is not just a set of rules.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It is a philosophy, and a very correct one.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "History has proved again and again that whenever mankind interferes with a less developed civilisation, no matter how well intentioned that interference may be, the results are invariably disastrous.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's hard to be philosophical when faced with suffering.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Believe me, Beverly, there was only one decision.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I just hope it was the right one.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "And we may never know.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Resume.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Mister La Forge, take us out of orbit.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Destination, sir?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I don't care.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Let's just get some distance between us and this system.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Course nine seven zero mark three one eight.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Speed, warp three.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Where will that take us, Mister La Forge?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The Opraline system.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "An interesting choice.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Why?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Curiosity.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We've never been there.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Engage.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[img=music]  End Credits [img=music]", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,STS-0.4,UHP-3.0" },
	},
};

-- Star Trek:TNG S1.24 - We'll Always Have Paris
RecMedia["TNG_S1E24"] = {
	itemDisplayName = "VHS: ST:TNG S1.24",
