	spawning = 0,
	category = "Retail-VHS",
	lines = {
		{ text = "(PICARD) Captain's log, stardate 41697.9.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We're en route to Sarona Eight for much needed shore leave.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The entire crew is looking forward to the diversion.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "On a personal note, I have allowed myself the luxury of a head start.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Gymnasium]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard and his opponent are fencing.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "His opponent gets a winning hit)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(DEAN) I took advantage.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No, no, Lieutenant, the advantage was yours.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Come again.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "En garde.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(This time Picard gets the winning hit)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Interesting move, sir, but what technique was that?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The technique of a desperate man.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Then things go blurry and)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Interesting move, sir, but what technique was that?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The technique of a desperate man.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, what was that?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Picard to Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Captain?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Number One, did something unusual just occur on the Bridge?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Yes, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We experienced some kind of loop where everything repeats itself.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Here too.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm on my way.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(still in fencing gear) Report, Mister Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(DATA) Sensors show nothing, sir, but it appears a moment in time repeated itself exactly for everyone.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(LAFORGE) Just like a feeling of deja vu.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(WORF) Reports from all decks coming in, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Computers were also affected, which would indicate the phenomenon was not an illusion but occurred in real time.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Number One, find out if anything similar happened in this sector.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir, I am receiving an emergency transmission from the Pegos Minor system.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Put it on.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Five four two point two.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I repeat.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is Doctor Paul Manheim.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We are in need of help.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Urgent.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All ships, please respond.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I repeat.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Coordinates are six six seven two eight point nine", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It is an automated signal, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I am unable to establish contact.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Shut it off.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister La Forge, lay in a course on those coordinates.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, you act as if there's a connection between the time distortion and the distress signal.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "There is.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Paul Manheim.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Fifteen years ago he went off to work on experiments relating to non-linear time.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It appears he may have achieved some measure of success.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Speed warp eight.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Warp eight.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Engage.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Estimated arrival Pegos Minor, four hours, thirty five minutes, seventeen seconds.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I've never heard of Paul Manheim.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Mister Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "A highly respected scientist, considered a visionary, he advanced several time-related theories.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "One regarding the relationships between time and gravity was quite intriguing.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "But neither that theory nor any other received wide acceptance.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Fifteen years ago, he assembled a team of scientists to expand that research.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They disappeared.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Haven't been heard from since.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Did you know him, Captain?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I knew of him.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He was teaching at the University when I was in Paris, but I didn't have the pleasure.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I must change.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Number One, inform me half an hour before we reach those co-ordinates.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Keep trying to determine if the time distortion was specifically located on the Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(TROI) Captain?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Excuse me.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Yes, what is it, Counsellor?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I think you would prefer to discuss this in private.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "That's not necessary.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Go on.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "When Professor Manheim's name was mentioned, you reacted with intense emotion.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Yes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Please get to the point.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I don't want to interfere with your personal life, but unresolved strong emotion can affect judgement.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Well, thank you for your concern.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "As Ship's Counsellor, I offer my assistance.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "What do you suggest?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Confronting deep personal issues is not easy for you.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "You tend to suppress them.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "There are a few hours until we arrive.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Perhaps you should use this time to analyse your feelings and put them into perspective.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Thank you, Counsellor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If I should need you further, I'll let you know.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Turbolift]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(After returning to the gymnasium and cleaning his sword, he gets changed)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Belay that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Computer, estimated arrival at Pegos Minor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(COMPUTER) Two hours, nine minutes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Holodeck three.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Outside Holodeck]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Computer, this is Captain Picard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Holodeck three is clear.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Location, Paris, Cafe des Artistes, as it appeared twenty two years ago.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "April the ninth, fifteen hundred hours, three o'clock.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Warm spring day.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Programme complete.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Cafe des Artistes]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard is greeted by the head waiter)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(EDOUARD) Monsieur, welcome to the Cafe des Artistes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is this your first time in Paris?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This way.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That table.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mais oui, bien sur, monsieur.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We are here to please you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard gazes from the balcony towards le Tour d'Eiffel, and the otherwise future city-scape)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I've been away far too long.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Some wine, some cheese?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm not very hungry.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I really came for the view.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Perhaps what you hunger for is not on the menu.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Perhaps not.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It was many years ago, I had a rendezvous.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I was to meet someone.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Someone here, at this very table.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Your young lady, she did not come?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Actually, I don't know.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I always imagined that she did.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You, however, did not.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ah. Well, trust Edouard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I will bring something very special, just for you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Two young women are talking at the next table)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(FRANCINE) Let's go.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We've waited long enough.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(GABRIELLE) Fine.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You go.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll stay a little longer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He's not coming, Gabrielle.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No, after last night, I know he will.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I just know.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then he would be here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You are making a fool out of yourself, and I will not watch.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Francine walks away)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Do we know each other?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The way you look at me, do I remind you of someone?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No. Yes, you do, somewhat.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He's not coming.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What did I do to drive him away?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Maybe you did nothing.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Maybe he had no choice.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Maybe he was afraid.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Of what?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Of me?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh, of being connected, rooted.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Perhaps if he's as young as you are, he doesn't know yet exactly what he wants to do.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Maybe.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Enough of this self-indulgence.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Exit.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, we've received communication from the freighter Lalo, as well as from a farming colony on Coltar Four.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Both described the same time distortion.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The Captain of the Lalo described it a hiccup.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Hiccup?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Actually sir, that may be an incorrect analogy.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "How so, Data?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "A hiccup is a spasmodic inhalation with closure of the glottis.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "accompanied by a peculiar sound.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "If we were to continue this analogy to a body function, what occurred would be best represented by a", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "That's enough, Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Have you been able to learn more specifics about the Manheim project?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "All I have found sir, is what you already know.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Manheim was concentrating on time gravity experiments when he left.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Captain, we've reached the coordinates specified.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "There's nothing here.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm receiving new coordinates.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's a relay signal.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Same source as the first one.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "What are they, Worf?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Six six four point eight by one three two three point seven by four nine four nine point nine.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Very remote area, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's in the middle of the Vandor system, a binary star system.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Main star's a B class giant, the companion star's a pulsar.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Set course for the new coordinates, Mister La Forge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why is he making it this difficult to find him?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Hopefully he'll tell us, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Later)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "We have reached the coordinates, Captain.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sensors indicate it is Vandor Four, a planetoid in elliptical orbit around the binary system.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Standard orbit, Mister La Forge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Give me a visual.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Viewscreen on.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "There's a small forcefield on the planet.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Latitude twenty degrees, nine minutes north.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Longitude forty degrees, two minutes east of the present terminator.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Penetrable?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Open hailing frequencies.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hailing frequencies open, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is Captain.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is the Captain of the USS Enterprise responding to your signal for help.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Enterprise, thank you for hearing us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Where are you?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We're in orbit around Vandor now.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Then you can help me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't know what to do.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's only the two of us left.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He's having convulsions.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Can you lock on to her coordinates?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The forcefield is preventing any kind of contact other than audio.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "There is a force field at your location.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I know.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Good.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But it is preventing us from helping you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now, you must find some way to shut it down.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) I'll try.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Force field is off.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Good.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lock onto their coordinates.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Beam them up directly to Sickbay.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Doctor Crusher, prepare for a Medical Emergency.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Two to beam directly up to Sickbay.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) We'll be ready, Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "That's where I'll be.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(A woman and a shaking man materialise on the floor)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I'll help.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Easy.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Easy.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(She and a male nurse get Paul onto a bed)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I've got his legs.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "How long has he been like this?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Several hours at least.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He was in his lab, so I can't be sure.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I need to do some tests.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(Picard, Riker and Data enter)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Er, I, er.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Jean-Luc.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I thought the voice sounded familiar.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Hello.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I should have known.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Who else would have charged to my rescue?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is my First Officer, Commander William Riker.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant Commander Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is Jenice Manheim.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "A pleasure, Mrs.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Manheim.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Thank you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I have a number of questions for you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I hope I can be of some help.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why don't we sit down?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Doctor's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You said there were only two of you left.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What happened to the rest of the crew?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They were working in the second lab.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Something happened there a few weeks ago.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They were all killed.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It was a terrible accident.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't know exactly what happened.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "So many brilliant, wonderful minds, just gone.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do you know the nature of Doctor Manheim's work?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Paul's always been interested in time.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He's never believed that it was immutable, any more than space is immutable.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Over the last decade, he came to believe that we reside in one of infinite dimensions, and what holds us here is the constancy of time.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Change that and it would be what he called opening the window to those other dimensions.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Which begins to explain what happened.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Have you been experiencing something up here?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What is emanating here is having repercussions light years away, maybe even further.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That would explain his anxiety.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I had no idea it had gone so far beyond Vandor.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why this place?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Why Vandor?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "All I can tell you about that is Paul and the rest of the team searched for two years to find it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Vandor's exactly what they needed.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A planetoid around a binary star.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Because of the dense gravity of the pulsar.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Did your husband ever attempt to define these dimensions, give you an idea of what he expected?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No. But he did say that he was very close to proving his theories.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And then the accident.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Did he anticipate that these experiments might be dangerous?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I didn't think so.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now, in retrospect, he probably did.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That would explain all the unusual precautions he began taking, even before the accident.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The force field, the elaborate security system.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Every time he started a new experiment, he insisted that I stay in what he said called a protected room.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ah, and that's why you weren't affected.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Jean-Luc, he would never knowingly do anything to hurt anyone.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, I believe that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But as he saw his goal getting closer, seeming possible, he became more and more obsessive.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Maybe that clouded his judgement.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is not how I imagined seeing you again.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Nor I you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You've done well.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A great starship in the far reaches of the galaxy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's everything you'd hoped.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Not exactly.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Nothing works just as you hope.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If you can't tell us any more, I need to send a team down to the lab.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You can't.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's protected.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "One of the other scientists made sure no one could get in.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Excuse me.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Is he worse?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He's resting.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "But I'd like you to undergo some tests as well.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "My nurse will start them.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Thank you for your kindness, Doctor.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Jenice kisses Picard's cheek and leave)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "She's an old friend.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I gathered that.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It's her husband I'm more concerned with at the moment.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "What's the prognosis?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I believe he's dying.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "His neurochemistry's been affected, but I don't know how or why.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I've never encountered anything like it before.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "How long does he have?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Maybe a couple of days.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It's hard to predict.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "All I can do is maintain him or attempt to maintain him until I find out what's causing the damage.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Can we talk to him?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Not now.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Not yet.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Turbolift]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard, Data and Riker are about to enter)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Incidentally, Captain, the effects of the time distortions are now being felt in the Ilecom system.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(They enter)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "In the past decade, Manheim has turned some vague theories into a practical application.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, but without his help, I'm not sure we'll be able to pose any intelligent questions, let alone come up with any solutions.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(The turbolift door opens, and Picard, Data and Riker are about to enter)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Incidentally, Captain, the effects of the time distortions are now being felt in the Ilecom system.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It's us before we stepped into the turbolift.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(PICARD 2) It's happening again.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(DATA 2) I feel no disorientation.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Nor do I.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(The doors close)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What was that?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I believe what could be termed the Manheim Effect is becoming more pronounced.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "This is where we started, if we are us.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Oh, we are us, sir, but they are also us.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "So indeed, we are both us at different points along the same time continuum.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(A turbolift arrives, they look inside and enter)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Ready room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What have you learned?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We have completed a scan of the planet surface.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "We've discovered the second lab is on the far side of the planet, completely destroyed.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Unable to determine what caused it.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Otherwise, very little to clarify the situation.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Our sensors show an immense volume of energy emanating deep within the planet, and concentrated near Manheim's remaining laboratory.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "How is the energy being used.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No idea.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What's it's source?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I cannot be sure, sir, but I believe Manheim has developed a method for harnessing energy from the pulsar.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It comes down to this, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We've learned everything we can from here, and we are no closer to understanding it than we were twelve hours ago.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Manheim is unable to help us, but hopefully, having been a good scientist, he kept notes.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I would need to study Manheim's records.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "And in order to do that we're going to have to go down there.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What about the defence system Mrs.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Manheim spoke of?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It may have been connected to the main system, and when she lowered the shield she may have turned that off as well.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "If not, we'll deal with it as best we can.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Prepare your team.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Coordinates set?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(CHIEF) Yes, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Energise.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "There's a lack of integrity at the landing point.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) I'm losing them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Bring them back.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Chief Herbert, what's going on?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There seems to be some kind of strange bouncing effect.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) I can't get them to materialise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "My readings aren't complete.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Keep trying.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Riker and Data materialise, Worf a few seconds later)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What are we doing back here?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You're lucky you made it back at all, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Where am I?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Paul.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thank God.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're on the USS Enterprise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They answered your distress signal.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I sent one.?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh, Paul, you're going to be just fine.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am not fine, Jenice.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm not even close to fine.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Bridge, this is Doctor Crusher.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Professor Manheim is conscious.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Remember, it was worth it, what happened.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What will happen, all of it, it was all worth it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Again.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's changing again.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What is?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "What do you see?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I'm having difficulty.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "With what?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Are you in pain?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I have been on the other side.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I have touched another dimension.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Part of me is still there.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Help him.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Try to stay calm, Doctor Manheim.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I don't think it's going to help you struggling against it.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "My mind is floating between two places.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It is difficult to know which is which.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There is no way to explain it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard, Data and Troi enter)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Doctor Manheim, I'm Captain Picard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The same one?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Jenice nods) She has told me about you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Not all, but enough.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We need your help.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The situation is not good.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It will get worse.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What do we do?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How do we stop it?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm having difficulty holding the moment.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Doctor, this is Lieutenant Commander Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Will you explain the situation to him?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I am fully versed, sir, on all your theories regarding time and gravity.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "How is that possible?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am not even fully versed on all of my theories.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am an android.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Android?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "On a Starfleet vessel?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am the only one, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Your knowledge is useless, because the work we have done here has made most of those theories obsolete.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then you have harnessed a dynamic energy source.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Then you do understand.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We were able to locate an energy source in the centre of this planetoid.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We learned to enhance it, to focus it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Everything worked too well.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The energy from the pulsar, the energy from the planetoid.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We opened a crack, a window into another dimension.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The time distortion we felt.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Felt?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then it is not confined to the planetoid?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The range is at least several thousand light years.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Captain, it must be stopped!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You must help me to execute a controlled shut down of this experiment.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Get my notes, they're in the lab.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How do we bypass the security system?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll give you the correct coordinates to beam safely down to Vandor, and the codes you need to bypass security systems to get into my lab.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Observation lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If what the Professor has given me is accurate, it is possible to repair the damage.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "But it must be perfectly timed to coincide with another time distortion episode.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Can we predict the timing?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I believe so, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "We have to.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If we don't seal that hole, this other dimension he's opened will rip into the fabric of the galaxy.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Reality as we perceive it will not be the same.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant Worf.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I want those codes that Manheim gave Mister Data rechecked and then checked again.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No one is beaming down to Vandor unless we can be reasonably sure they can get through.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The door opens)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I'm sorry for intruding.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I was told I'd find you here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're not intruding.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We're just finished.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Come in.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Riker, Data and Worf leave)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I knew you wouldn't come to me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No, not under these circumstances.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We have unfinished business.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, we do.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why didn't you come to meet me that last day in Paris?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I was afraid.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Oh, I didn't want this.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The truth.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh, you want me to lie?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Of course.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A nice, soft, painless lie.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh, I got the days confused.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I thought it was Tuesday when it was Wednesday.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I went to the Cafe Moulin instead of the Cafe des Artistes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Ah, that's better.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It was raining and you couldn't find a cab.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I waited all day.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And it was raining.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It rained the rest of the week.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I went to Starfleet headquarters looking for you, but you'd already shipped out.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "So, come on, Jean-Luc.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let's hear the truth.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It was fear.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Fear of seeing you, losing my resolve.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Fear of staying, losing myself.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Fear that neither of these choices was right, and that, and that either would have", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "For a long time, not a day went by when I didn't look up into the sky, and wonder.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Each time that I returned to Earth, my thoughts were filled with you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I've thought a lot about this over the years, and perhaps you're leaving out your greatest fear.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The real reason you left.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Which was?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That life with me would have somehow made you ordinary.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're wonderful.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And am I that transparent?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Only to me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I wish I could talk to you, Professor Manheim.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I bet you were really something.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(Troi enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Deanna.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I wanted to see how he was doing.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "The same.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Nothing I do seems to make any difference.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "That's not why you're here.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I thought I was the empath.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Doctor's Office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Are you all right?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Why wouldn't I be?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I've got one of the medical wonders of the galaxy dying in my sickbay.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "That's not what I meant.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I don't think I want to talk about what I think you mean.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Captain Picard", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I can't compete with a ghost from his past.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "No one could.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "She's not a ghost.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "She's here right now.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "She may be in the here and now, but it's the ghost he sees.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Excuse me, I have to get back to my patient.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How soon, Mister Data?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If Doctor Manheim's information is correct, by my calculations, the next time distortion should occur between twenty eight to forty seven minutes.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Offscreen) Bridge, this is Sickbay.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Doctor Manheim is awake and asking to speak with you, Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Alone.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "On my way, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You asked for me.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I am not sure I remembered all of the codes for the security system.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You should warn anyone going down there", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thank you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll tell them to be cautious.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What I really wanted to talk to you about is Jenice.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Doctor Manheim, I did not come here to discuss your wife.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It is only this.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If anything should go wrong, please, take care of her for me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Of course.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "She never would admit this, but she has had a terrible time these last years.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Had we not been so isolated, she might have left me, and I never would have known.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "At least, not right away.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Perhaps I'm not a man who should have a woman like her.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "She deserves better.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You underestimate her.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I know, because I once did.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "In both cases, the time distortions occurred along the same continuum as a preview or a reprise of a specific point in time.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Where we are, where we were, and where we will be.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Data, I want this to be an away team of one.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I don't think there's any reason to risk anyone else.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It is reasonable, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "After all, I am a machine and dispensable.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Indispensable is the appropriate word.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I think it should be only you because you seem more able to control the effects of the time distortion.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Oh, I see, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "That is quite true, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I see time as a constant, whereas humans perceive time as flexible.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Hence the expression, times flies when you're having fun, which until now has always confused me.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Well, I want you to put a stitch in time and, er, save much more than nine.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "If other members of the away team became disoriented, it could create additional problems and perhaps increase the danger.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I will go immediately, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Good luck, Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All right.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We have the coordinates exactly as the Professor specified.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Vandor corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Data arrives safely)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Enterprise.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Offscreen) Picard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I am proceeding to the lab, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Offscreen) Maintain an open frequency, Mister Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Then he has to dodge a laser beam and take out the emitters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,LFT+1.0" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Mister Data, what's going on?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Vandor corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Doctor Manheim forgot to mention one of his security precautions, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I am proceeding into the laboratory.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Laboratory]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The code works, but the fluorescent tubes only open part way.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Data enters cautiously.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "In the centre is a distortion)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Offscreen) Mister Data, this channel will remain open.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Please continue to report.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This appears to be the instrument Doctor Manheim described.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It should confirm when the next time effect will occur.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Offscreen) Data?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Did Manheim give you enough information to decipher the security code?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Laboratory]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I will know in a moment, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "According to calculations, the next time effect will occur in one minute thirty seconds.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What's the next step, Mister Data?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Laboratory]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I will need to add a specific amount of antimatter to rebalance and align the system.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Offscreen) Will that plug the hole?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Theoretically, yes.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "In reality I do not know.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(He opens a container and gets the antimatter, putting it on the end of a long rod)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "When the effect hits, the forcefields will align, opening a clean straight path to the other dimension.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Whatever the time distortion, I must add the antimatter at the appropriate moment.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Geordi, if the Professor was right, I will need a twenty seven second countdown.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Offscreen) You got it, Data.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, I now have the antimatter and am moving towards the opening at the end of the lab.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The next time distortion should occur within seconds.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Geordi, begin countdown on my mark.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Now.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(There's a big sparkle.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Data looks back to see himself midway across the lab - no 2 - and getting the antimatter - no 3.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Meanwhile, Geordi's voice is counting down, very echoy)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(DATA 3) Captain, there appear to be three of us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(DATA 1) Should I drop the antimatter or wait for one of you?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Only one of us is at the correct time continuum.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Which one?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Me. It's me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Five, four, three, two, one.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The middle Data arrives at number 1 and the antimatter goes in, before number 3 gets there.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The effect stops.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "There is only one Data)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Offscreen) Data, report.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Are you all right?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Offscreen) Is it closed?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It is well patched, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Closed indicates a permanent condition, which I cannot guarantee.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Patched is good enough.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well done.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Beam back immediately.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Laboratory]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "With pleasure, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is amazing.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "All your readings are completely normal.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(Picard enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Where is my wife?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is she all right?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "She's right here.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Jenice.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Paul.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The effect has been reversed.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I can feel it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We're safe.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How is he?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He needs rest.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "There might be some residual effects, but other than that, he'll be all right.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I feel like I'm coming out of a long tunnel.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's there.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Not at all like I thought it would be.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Different.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Describe it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I can't, not yet.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The only words that fit are too pale because the images are so vibrant.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's not like anything anyone has ever experienced before.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There was, no, no, there is this kind of life.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Not like us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Not like this.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What's the condition of my lab?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Intact, for the most part.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Don't tell me we're going back?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh, Jenice, we are so close.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We have learned so much to walk away.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Besides, we owe it to the others, our friends.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There have been so many sacrifices by so many good people.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We'll be going back.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, I can see that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm sure the Federation will want to help in any way that it can.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This time it will be different, I promise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It always is, my love.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You said my life would never be dull, and it never has been.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thank you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Outside Holodeck]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Troi and Jenice step out of the turbolift next to the holodeck door)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Computer, this is Counsellor Troi.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Request access.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Do you wish to terminate the current programme?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No, continue the programme.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Enter when ready.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Captain is waiting for you inside.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Cafe des Artistes]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How is this possible?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's Paris.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Unbelievable.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Madame, this way.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Captain is waiting for you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The wine is on ice)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "This is so real.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Bien sur.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Pourquoi pas?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Jean-Luc, don't tell me how you did it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't care.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's perfect.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's as if we were really there.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I wanted to say goodbye properly this time.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I shall always picture you here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(They toast each other)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I expect you to always come charging to my rescue.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll do my best.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Goodbye, Jean-Luc.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Be well.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you for Paris.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(She walks away, but)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Well, so much for my dramatically romantic exit.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The door appears and opens)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is anything wrong?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Then set course for Sarona Eight.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "As I remember we were on our way for some much needed shore leave.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Course set and laid in, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Warp five.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Engage.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I've only been there once, but they've got this great club.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I don't remember the name of it.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "They serve these blue concoctions", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's across the square from the Zanza Men's Dance Palace.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "It's called the Blue Parrot Cafe, (to Troi) and you're buying.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[img=music]  End Credits [img=music]", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,STS-0.4,UHP-3.0" },
	},
};

-- Star Trek:TNG S1.25 - Conspiracy
RecMedia["TNG_S1E25"] = {
	itemDisplayName = "VHS: ST:TNG S1.25",
	title = "Star Trek:TNG S1.25 - Conspiracy",
	subtitle = nil,
	author = nil,
	extra = nil,
	spawning = 0,
	category = "Retail-VHS",
	lines = {
		{ text = "(RIKER) First officer's log, stardate 41775.5.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We are en route to the ocean world of Pacifica.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "While our mission is scientific in nature, we look forward to the warm blue waters and fine beaches that make Pacifica a jewel of the galaxy.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(LAFORGE) So the guy staggers to his feet and goes back to the girl, right?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well, she smiles, looks him right in the eye and says 'just try that in hyperspace!'", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Geordi laughs)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(DATA) I see.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "So the difficulty in attaining such complex positioning in zero gravity environment, coupled with the adverse effect it would have on the psychological well being of the average human male, is what makes this anecdote so amusing.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Yes.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Very humorous indeed.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Hysterical, in fact.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Data attempts to imitate a laugh.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "It is very forced)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What is our e.t.a.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "at Pacifica, Mister La Forge?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Twenty two hours fourteen minutes, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Increase to warp six.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Aye sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Full impulse.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do you think anyone would object to our arriving ahead of schedule?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(TROI) I know I won't.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I've been really looking forward to a nice swim.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "You are aware, Counsellor, that the holodeck can be programmed to recreate an oceanic environment.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Data, it's just not the same.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Have you ever been for a real moonlight swim?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "One can swim in moonlight?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "How about you, Mister Worf?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(WORF) Swimming is too much like bathing.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Commander Riker.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I am receiving a Code Forty Seven.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Verify.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It is a Code Forty Seven, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Starfleet emergency frequency.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Code Forty Seven.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Captain's eyes only.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Picard's quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard is in bed, asleep.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "That pillow cum bolster looks very uncomfortable)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Offscreen) Captain, I'm sorry to disturb you.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(PICARD) Yes, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What is it?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On monitor) An incoming message.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Code Forty Seven, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Pipe it through.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(COMPUTER) This is an emergency communiqu.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It is not to be discussed with fellow officers unless deemed absolutely necessary.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There will be no computer record of said transmission.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Understood.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Proceed with voice print identification.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Picard, Jean-Luc.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, USS Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Voice print verified.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Walker!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On monitor) Hello, Jean-Luc.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's been a long time.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Too long, old friend.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why are you contacting me on this frequency?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On monitor) It was a difficult decision.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I felt it was worth the risk.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Risk?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On monitor) It's about Starfleet.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "About something we've always considered to be impossible.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't understand.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On monitor) I can't explain it now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We need to talk, face to face.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're using a Code Forty Seven.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I have to know what this is all about.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On monitor) Not over subspace, no.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh, for God's sake, Walker.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is a secured channel", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On monitor) No. I want you to meet me on Dytallix B.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "When?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On monitor) Immediately.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Not possible.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We're expected at Pacifica,", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On monitor) That can wait.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You owe me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And you owe it to yourself to hear what I have to say.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Something is beginning.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Don't trust anyone.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Remember that, Jean-Luc.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Don't take anything for granted.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Walker.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On monitor) Dytallix B. We'll be waiting.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Data, are you familiar with Dytallix B?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Dytallix B is one of seven uninhabited planets mined for the Federation by the Dytallix Mining Company.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It is in the nearby Mira system.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Mister La Forge, chart a course for it immediately.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Warp seven.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And there will be no records or logs mentioning any aspect of this diversion.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, they're expecting us at Pacifica.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Shouldn't we contact them and let them know.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Negative.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Nothing goes out without specific orders from me.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hopefully the delay will be minimal.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(As the Enterprise rushes off on its new course, and Picard is in his Ready room, Data is at a science station)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I have the readout on Dytallix B, Commander.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "What have you got?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Dytallix B is the fifth of six planets circling the red giant known as Mira.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "One side always faces the sun, where temperatures reach up to one hundred and eighty degrees.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The mines line the temperate zone between the day and night sides, but they are long deserted.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Why the devil are we be going to this planet?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Are there any miners or indigenous life forms on the planet?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I believe the answer to both questions is no, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "In a manner of speaking, it is nothing but a lifeless hunk of rock, a useless ball of mud, a worthless chunk", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Thank you, Data.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I get the idea.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Captain, we are approaching Dytallix B.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) Very good, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm on my way.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Picard enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain, sensors are detecting three Federation starships already in orbit around the planet.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Identify.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Two are frigates.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Renegade commanded by Tryla Scott, and the Thomas Paine, Captain Rixx commanding.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The third is just coming into range now, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It is Ambassador Class heavy cruiser, USS Horatio.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Horatio?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Isn't that Walker Keel's ship?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Attempts at communication have been ignored by all three vessels.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Make no further attempts, Mister Worf.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Any life form readings on the surface, Data?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Three, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "All gathered inside what appears to be the entrance to a mining tunnel.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Very well.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Relay those coordinates to the transporter room.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm beaming down.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Alone, Captain?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Alone, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Mine entrance]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard walks carefully down the entrance ramp, and is greeted by a pink-skinned Bolian with a phaser.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Then Walker Keel and a woman appear, also armed)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Thanks for coming.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is some greeting, old friend.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Tell me, Jean-Luc, where did we first meet?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(RIXX) (the alleged Bolian) Answer the question.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Tau Ceti Three.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It was a bar.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Quite an exotic one as I remember.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What do I win?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do you recall the night you introduced Jack Crusher to Beverly?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You know full well I hadn't even met Beverly then.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You introduced them.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "My brother introduced them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You don't have a brother.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Two sisters, Anne and Melissa.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What the hell is this all about?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Keel nods, and weapons are put away)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Apologies, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We had to make sure you were really you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Walker, what is going on?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain Picard, meet Captain Rixx.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I believe we've met.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Altairian conference.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And Captain Scott.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Tryla Scott.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's said you made Captain faster than anyone in Starfleet history, present company included.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Are you that good?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(TRYLA) Yes, I am.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Starfleet's finest.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Fancy meeting you here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We all came secretly, Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "To discuss the threat.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What threat?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Have you noticed anything about Starfleet Command lately?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Anything unusual?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No. But we've been on the outer rim for a while.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We haven't had much contact with them.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Some of us have seen strange patterns emerging.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Unusual orders.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "High-ranking officials backing irrational proposals.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Starbase twelve was completely evacuated for two full days.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No explanation given.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And what about the deaths?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "McKinney, Ryan Sipe, Onna Karapleedeez.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All dead?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "A series of accidents.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Or so they say.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's hard to be certain of anything.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Interfleet communications are at a minimum.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But something is happening.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And we fully expect the Enterprise to be targeted soon.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Targeted for what?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "By whom?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We're not sure yet.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Damn it, Jean-Luc.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I tell you that some of Starfleet's top command people are changing.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This could affect the very core of our organisation.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Officers I've known for years are bluffing their way through talk of old times.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That's their weakness, a lack of memory.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He doesn't believe us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He thinks we're crazy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You've given me nothing to believe in.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Just a lot of vague talk about strange patterns, irrational proposals.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Who's behind this, and to what purpose?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How are people being changed?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I can't say exactly, but I think its spread to my own ship.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "My First Officer hasn't been the same since we stopped off at Earth.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Our Medical Officer says he's perfectly normal, but I don't think I trust him either.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Walker!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We know we don't have all the answers.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All we ask is that you keep your eyes open.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That's sound advice at any time, Captain.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Stay in touch with us, covertly.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This meeting never took place as far as Starfleet is concerned.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please, I'm asking this as a personal favour.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm glad, Jean-Luc.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm glad you're still one of us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Tell Beverly I said hello.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And watch your back, Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Ready room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Friends, close friends, are few and far between.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Two of the oldest and closest are Jack Crusher, may he rest in peace, and Walker Keel.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Before various missions split us apart, we were virtually inseparable.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I trust Keel completely.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If he felt it necessary to violate regulations, he must have had a good reason.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But you're putting your career at risk for him.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Friendship must dare to risk, Counsellor, or it's not friendship.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They illicitly used the emergency channel to draw you here.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Then they asked you to keep secrets from your superiors.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Effectively, to disobey Starfleet regulations.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "The people involved in this are of the highest calibre.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If I didn't believe in their loyalty, I wouldn't have gone this far.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Don't you think you should tell the rest of the crew?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "No. I don't want to risk implicating them.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Not until I have solid evidence that something really is wrong.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Take us out of orbit, Mister La Forge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Resume heading to Pacifica, warp factor eight.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye sir, warp eight.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "At that speed sir, we should arrive nine point six three hours behind the original schedule, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Very good.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Data, I have an assignment for you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "One especially suited to your talents.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Ready room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Computer, this is Lieutenant Commander Data.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Please access all Starfleet Command orders to starships, starbases and colonies for the past six months.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Working.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(CRUSHER) I understand the Horatio was in orbit around Dytallix.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Did you see Walker?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "No.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Oh. I would have loved to have seen him.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It wasn't possible, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, I am picking up an unusual disturbance in a nearby quadrant.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Confirmed, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sector sixty three.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Specify.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What kind of disturbance?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hard to say at this distance, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I guess the trip Pacifica will have to wait.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Let's investigate.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Maintain speed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Alter course accordingly.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Changing course to three five one mark four.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(After a quick trip)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Approaching sector sixty three, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Slow to impulse.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Going to impulse power, now.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sensors beginning to pick up small objects, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "On screen.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The objects are radiating out from a single location)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "It doesn't look natural.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Agreed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Enlarge and identify.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It looks like debris.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "From a space vessel of some kind.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It could be one of those ships that was orbiting Dytallix.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We are in close proximity to that planet.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Identifying marks, Mister Worf?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Nothing so far.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sensors not detecting any bodies in the flotsam, but from the amount of wreckage.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm sorry sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It can only be the Horatio.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "From the looks of it, she's been totally destroyed.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain's personal log, stardate 41776.1.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The apparent death of Walker Keel has had a powerful impact on me.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I now believe there may be a cancer growing within the ranks of Starfleet.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "As a result, I have alerted my Executive Officer to the suspicions voiced by Keel and the others.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Observation lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There's no proof of anything.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The last time I saw Admiral Quinn he tried to warn me about a subversion within the Federation.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Then Walker tried, and he's dead.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Subversion?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Personally, I don't believe this conspiracy theory.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Ready room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Information is still flashing in front of Data's eyes)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Startling.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Quite extraordinary, in fact.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Directions unclear.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please repeat request.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That was not a request.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I was simply talking to myself.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "A human idiosyncrasy, triggered by fascination with a particular set of facts.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Or sometimes brought about by senility.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Or used as a means of weighing information before reaching a conclusion.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Or, as a", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Thank you, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I comprehend.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please specify how you would like to proceed, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please continue with record scan.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Intriguing.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Observation lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But we have no facts.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We have to assume the explosion was an accident.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Data enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Number One, I believe what happened on the Horatio was sabotage.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But we can't be certain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Perhaps we can.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "My orders were to search for abnormal patterns in Starfleet's directives.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I believe I have found just that.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(He puts a data disc into a desk monitor)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "These are various outposts and starbases where I have detected unusual activity over the past few months.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "What sort of activity?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "An uncustomary reshuffling of personnel, usually in the command areas.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The new officers have had frequent contact with the highest levels of Starfleet Command.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Why hasn't anybody discovered this before?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The orders were given with great subtlety.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "To use an aphorism, Starfleet's left hand did not know what its right hand was doing.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Data, can you speculate as to the purpose of these reassignments?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I believe it is a clandestine attempt to control vital sectors of Federation territory.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "This could be a prelude to an invasion.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "But who's behind it?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "There is insufficient data to make such an assessment, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Are you suggesting that we warp over to Starfleet headquarters and demand to know what's going on?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Why not?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Yes, why not?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We're talking about a threat to the entire future of the Federation.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I don't think any of us can rest easy until we've been to the source.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain's personal log, supplemental.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "While it is quite unusual for a starship to return to Earth, we seem to be left with no other choice.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I have apprised the remaining bridge crew of our situation.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Approaching Earth, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Standard orbit, Mister La Forge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Standard orbit, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Any word from Starfleet Command, Data?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Nothing so far, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Strange.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Enterprise glides over the moon on final approach)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I am now receiving a message from Starfleet Command.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "On screen.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(A trio of elderly Admirals are seated behind a desk.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Two humans flanking a Vulcan)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(On viewscreen) Greetings Enterprise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am Admiral Savar.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is Admiral Aaron, and I believe you already know Admiral Quinn.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, indeed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's good to see you again, Admiral.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) And you, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(On viewscreen) Of course.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We are always delighted when the Enterprise returns to the nest.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yet we are puzzled by the timing of your visit.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I can imagine you would be.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Governor Delaplane of Pacifica informs us that you cancelled your scheduled stop there.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is this true?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, sir, it is.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Explain yourself, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why have you returned to Earth?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I would rather discuss in person, Admiral.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Dexter Remmick appears and whispers to Savar)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(On viewscreen) Excuse us for one moment, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They seem normal enough.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "On the surface.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Counsellor, any thoughts?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hard to say.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Someone is hiding something, but I can't tell who or what.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I must say I'm not overjoyed to see Remmick again.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Considering what we've done, their response has been pretty temperate.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I don't like it.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You can't trust them.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Forgive the delay, Captain Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We'd be delighted if you and your First Officer would join us for dinner.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(On viewscreen) Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Delighted.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(On viewscreen) It would give you a good opportunity to expatiate your viewpoint.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(On viewscreen) To elaborate upon whatever it is that's troubling you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thank you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We accept your gracious offer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Excellent.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Preparations are already underway.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(On viewscreen) We'll greet you in the reception area in, say, twenty minutes?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Splendid.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) I won't be able to attend dinner, I'm afraid, but I would like to see the ship and say hello again, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Of course.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We look forward to seeing you, Admiral.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Enterprise out.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Quinn wants to see us alone.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "At least we know he's still on our side.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister Data, you have the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Starfleet Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Quinn has a case with him, that contains a living creature similar to the Tau Ceti thing from Wrath of Khan)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(REMMICK) Are you ready to beam up to the Enterprise, sir?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, Commander, quite ready.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Energise, Mister Remmick.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Welcome aboard, Greg.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Good to see you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You remember Commander Riker.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hello again, Commander.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I must say, you're looking remarkably well.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Never felt better in my life.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "When you were here last, you were saying that you were feeling tired.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Tired of life I was, but not anymore.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm ready for new challenges now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What about another look at this ship?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Remember what you told me back at Relva Seven, About the threat that you perceived to the very fabric of the Federation?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Is that why you're here?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Because of what I said?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's one of the reasons, yes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But Jean-Luc, you took me far too literally.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I was only referring to the problems involved in assimilating new races into the Federation.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's an ongoing, tumultuous process which can cause stress and strain on every aspect of our alliance.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm sorry, I must have misunderstood.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's not for you to apologise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If I led you up the garden path, I humbly ask your forgiveness.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Of course.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now, gentlemen, you have a dinner to attend to, while I would like to go and freshen up before I look around a little bit more.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We've arranged your usual accommodation, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "That's outstanding.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, I should be getting down to the planet.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Commander Riker will be available here to show you anything that you want to see.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's really not necessary.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I insist.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(to Riker) When you've finished showing the Admiral anything that he needs, you can join me down on Earth.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I may want to stay a while, Jean-Luc.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Of course.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Stay as long as you wish.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Quinn goes on) No.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's not Gregory Quinn.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It may look like him and sound like him, but it isn't him.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Are you absolutely certain, Captain?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'm certain, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I want you to stay close.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Keep your eye on his every move.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Have Beverly concoct some reason for giving him a full medical examination.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I don't know, just do it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He's a senior Admiral.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's not him, Will.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I know that now.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What I have to find out is how this happened, and who or what is behind it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, if you're right.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'm right.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Should you be beaming down there alone, unarmed?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'll take care.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Find out what you can, then join me, in force if necessary.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Energise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Good luck, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Starfleet Reception area]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Welcome home, Captain Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thank you, sir.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "My first officer will be joining us shortly.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, I'm sure he will.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You've met Commander Remmick, haven't you?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Under less than ideal circumstances.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's nice to see you again, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We've prepared a special meal in your honour.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Delightful.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Tell me, why is the corridor so quiet?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The last time I was here, it was bustling with activity.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It usually is.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Tonight's a quiet night.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, a quiet night.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Guest quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ready for your tour, Admiral?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Quite ready, Commander.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What's in the case?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Actually, I brought it for Doctor Crusher.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But perhaps you would like you to see it first?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What is it?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "A form of life.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It was discovered accidentally by a survey team on an uncharted planet.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why haven't we heard anything about that?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Oh, you'll be hearing about it shortly, but first there remains much scientific study to be done.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "After all, it is a superior form of life.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Superior?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Totally.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Come, have a look.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I think I'll summon my Science Officer.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Quinn grabs his arm with a surprising amount of strength)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "It won't like your Science Officer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It does like you!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Vitamins.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They do wonders for the body.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(There's a very one-sided fight - Quinn easily throws Riker around the room)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Riker to Security.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Guest quarters seventeen.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Emergency.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Quinn knocks Riker out cold)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Ante room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let's have some Andonian tea while we wait for your Riker.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then you can tell us about what's brought you here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I see you keep up with our duty roster.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, of course, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Enterprise is very important to us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Excuse me, gentlemen.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Remmick leaves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "To the Horatio, gentlemen.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Horatio.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard does not drink)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What an awful tragedy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Such a terrible loss of life.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's interesting that you should mention the Horatio.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's one of the subjects I wanted to discuss.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Really?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Has the cause of her destruction been ascertained?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Absolutely.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Implosion, due to extreme negligence on the part of her Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Enjoy, Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I believe you'll find it an excellent aperitif.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Guest quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Worf and La Forge rush in)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "It's good thing you're here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He slipped and hit his head.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is Lieutenant La Forge in guest quarter seventeen.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We have a medical emergency.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Your Doctor Crusher is most capable.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm sure he'll be all right.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now if you'll excuse me, my time here is most limited.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Admiral.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Don't you think we should wait until the Doctor gets here?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Quinn throws him through the plywood doors into the corridor)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Now, Klingon, it's between you and me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Worf's double handed blows have no effect.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Quinn floors him easily)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Do Klingons fear death as much as humans?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I could snap your neck in a second, but it wouldn't be as much fun.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Three sustained bursts from Beverly's phaser finally put Quinn down)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Are you all right?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "If I could see, I'd be seeing stars.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "What is he?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Let's find out.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Retinal scans are an exact match It really is Quinn.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "How can that be?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The man picked me up like a rag doll.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm going to have to do a complete internal scan.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Keep me posted, Doctor, I'll be on the Bridge.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(She spots something at the back of Quinn's neck)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What is that?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(A pointed something sticking out, wiggling)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Ante room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What do you know of conspiracies, Captain?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Not nearly enough, I suppose.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That's the charming thing about them, isn't it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "When a machination is real, no one knows about it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And when it's suspected, it's almost never real.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Except, of course, in paranoid delusions for those who believe.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sorry to interrupt.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Dinner is served.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Wonderful.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Bantering with the Captain has given me quite an appetite.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I need to contact my ship to let Commander Riker that we're about to sit down.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Of course, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We'll wait for you at the table.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard is left alone)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Picard to Riker.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Doctor Crusher here, Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "May I speak freely?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Ante room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I suppose I have little choice.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Where's Riker?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) He was attacked by Quinn, or what we thought was Quinn.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Specify, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A parasitic being has invaded Quinn's body.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It has complete control over all brain functions.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It seems to breath through a small gill protruding from the back of Quinn's neck.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Ante room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Look for this, Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I believe it will be visible on anyone who has been compromised.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Any idea what it is or where it comes from?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Negative.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I've never seen anything like it before.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) Lieutenant Worf, if the situation is under control, please report to the Bridge.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Doctor?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's okay.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "He won't be waking up for a long time.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Ante room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Continue, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) By the placement of the tendrils", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The parasite appears to stimulate the victim's adrenal glands, generate great strength", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Ante room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) And resistance to the", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Never mind the details.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Can you remove it?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) I don't think so.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Not without killing the patient.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Ante room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) You must set your phaser on kill.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Stun has little effect.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Doctor, one does not beam down to Starfleet Headquarters armed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Your food is getting cold, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm going in to dinner now, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Tell Commander Riker to join me as soon as he's ready.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Picard out.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Dining room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Three curved tables with two chairs each, to form a circle.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "There is a covered bowl in front of each person)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Please, sit down, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We've been waiting.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Go ahead and start, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We don't stand on ceremony here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The bowl contains live mealworms.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Picard recoils)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Oh do eat up, Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Raise your hand if you want seconds.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard gets up and starts to back out of the room)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Riker!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank God.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We're leaving.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You're not going anywhere.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You'll be one of us soon.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You were meant for the Doctor.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It couldn't be helped.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Riker walked in on us unexpectedly.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Aaron checks the back of his neck.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The gill is there)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "No matter.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Doctor will soon be joining us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All in good time.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Tryla Scott enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Well, Captain Scott.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Good.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now the setting is complete.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You don't really think we were in the dark about your intentions, do you?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Patience is one of our virtues, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We didn't go after you, we allowed you to come after us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "More dramatic that way, don't you think?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, the one thing both races share is a love of theatre.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And you've put on a fine show.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What race are you?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Where are you from?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's not important.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let us just say we've come a long way to join you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's a perfect match.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We're the brains, you're the brawn.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You'll understand soon, Picard.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Indeed.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We've been moving slowly, cautiously, for many months now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Careful to cover our tracks.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Careful not to arouse suspicion, until it's too late.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And now it's too late.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "For you, that is.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Well said, brother.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Picard and Riker control the Enterprise, which means we control the Enterprise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Eat hearty, brother!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Relish your new body!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Riker takes a handful of mealworms, and is about to eat them when instead he draws his phaser and shoots the other person sitting opposite.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Picard disarms Tryla Scott so Riker can shoot her, Aaron runs out, the parasite leaves Tryla and Savar neck-pinches Riker.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Picard shoots Savar before Riker passes out)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Aaron!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Hallway]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Aaron!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(They dodge  his phaser blast and the two of them shoot the Admiral in the back.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,LFT+1.0" },
		{ text = "His purple parasite comes out of his mouth and scuttles under a nearby door.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,LFT+1.0" },
		{ text = "They follow it)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,LFT+1.0" },
		{ text = "[Room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Can I help you, gentlemen?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is there any trouble here?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Perhaps if you'd tell me what it is", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Remmick!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The parasite climbs the chair Remmick is sitting in)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "No, Will.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Remmick swallow the parasite.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "His neck bulges with movement)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You don't understand.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We mean you no harm.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We seek peaceful co-existence.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(So Riker and Picard shoot him and as his body falls apart, the alien inside him that spawns the parasites looks out at them.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "So they kill that too)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain's log, stardate 41780.2.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How difficult after all these years of learning to respect life, to be forced to destroy it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But there seems to be no alternative.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Admiral Quinn is expected to make a full recovery.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "There is no trace of the parasite which took control of him.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We'll never know how many of these life forms infiltrated Starfleet, but it seems they could not survive without the mother creature which had taken over Commander Remmick.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It was Doctor Crusher's idea to simulate the blue gill.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We had to make sure it would fool everyone, including you.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "That it did, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Rest assured.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, I have attempted to trace the message Remmick was sending.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I believe it was aimed at an unexplored sector of our galaxy.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Any idea what the message was, Data?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I believe it was a beacon.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "A beacon?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "A homing beacon, sent from Earth.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[img=music]  End Credits [img=music]", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,STS-0.4,UHP-1.0,FEA+10.0" },
	},
};

-- Star Trek:TNG S1.26 - The Neutral Zone
RecMedia["TNG_S1E26"] = {
	itemDisplayName = "VHS: ST:TNG S1.26",
	title = "Star Trek:TNG S1.26 - The Neutral Zone",
	subtitle = nil,
	author = nil,
	extra = nil,
	spawning = 0,
	category = "Retail-VHS",
	lines = {
		{ text = "(RIKER) First officer's log, stardate 41986.0.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We are awaiting the return of Captain Picard who was summoned to Starbase 718 for an emergency conference.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Meanwhile, our sensors are monitoring an ancient capsule floating in our vicinity which appears to be from Earth.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(LAFORGE) I wonder how it got out here.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(WORF) At its present speed and heading, it will eventually enter the Kazis binary system and will certainly be destroyed.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I could attach a tractor beam and adjust its heading.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I think not, Mister Worf.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's just a piece of space debris.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "If we hadn't sitting here waiting for the Captain, we wouldn't have noticed it.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Leave it be.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Let nature take its course.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "How long until Captain Picard returns?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The last communication indicated it would be several hours.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(DATA) Commander, request permission to investigate this vehicle.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Why, Data?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's just a derelict.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It is a piece of history.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The opportunity to examine such an ancient vehicle does not come around very often, and as you pointed out, we do have the time.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Very well.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Be prepared to beam back before the Captain returns.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Thank you, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Lieutenant Worf, go with him.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Derelict ship]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Minimal oxygen atmosphere.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "An ancient solar generator, still operating.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Commander, look at this.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The on-board computers have ceased functioning.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I may be able to download this old style disk drive back to the Enterprise.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Worf tries to walk through the next hatch, but it does not automatically open for him)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "It must be sealed, probably with age.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(he prepares his phaser)", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Not necessary.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Data pulls the lever and the doorway opens)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Cryo chamber]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(This module of the ship contains frost-covered capsules in groups of three, both sides of the passageway.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Data scrapes the frost off the first to view a skull)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The seal was broken and the environment has been corrupted.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Here's another.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "This one is empty.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Commander, look at this.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(They look at the face of a woman.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Other capsules reveal two men still intact)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Were they frozen for an extended journey?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "If that were the purpose, there would be evidence of a more sophisticated monitoring system.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "These containers were designed solely for refrigeration.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Captain's shuttle is approaching the main Shuttlebay, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = ") : (Offscreen) Enterprise, this is the Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Number One, I want to get underway as soon as the shuttle is secure.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We'll make all preparations.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Commander Data, return to the Enterprise immediately.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = ") : (Offscreen) We have run into an unusual situation, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There are people on board.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Frozen.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Frozen.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "How many?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Cryo chamber]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Three.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The vehicle has suffered severe damage.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Most of her systems have failed.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Are you suggesting they be transferred to the Enterprise?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Cryo chamber]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I do not believe we should leave them here, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "With your permission, we will be returning with three containers.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Offscreen) Whatever you do, do it quickly.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(PICARD) Mister La Forge, set course zero five eight, mark one seven three.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Laying it in, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Number One, I want a staff meeting on the observation deck.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, those coordinates will take us right into the Neutral Zone.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "That is correct, Mister La Forge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Warp factor eight.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Warp eight.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Engage.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Observation lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Two Federation outposts in sector three zero have been destroyed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "There's been no communication with Federation starbases in sector three one since stardate 41903.2.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Romulans.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "That's the assumption.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "There's been no direct contact with the Romulans since the Tomed Incident.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The question are, why now?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What's their objective?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "For fifty years there's barely a whisper out of them, and now for no apparent reason they seem to be back with a roar.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Everything we know about them is based on rumour or conjecture.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The strategic decision is to send one ship.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Enterprise.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We could get out there and find ourselves greatly over-matched.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "True enough.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's a gamble.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This first encounter, coming so suddenly after all this time.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We have to assume it's a setup.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "If force is necessary, we will use it, but that will mean we have failed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Our goal here is to establish some kind of relations with the Romulans.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If we don't succeed, then to convince them of our resolve.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The general feeling at Starfleet is that they are seeking a confrontation.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They may want to test themselves in battle against a Federation starship.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "See how far we have advanced.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If that is the case, then I need to know it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Counsellor, I shall need a full profile on them.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(TROI) There is limited information, but I will prepare something.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Computer, estimated arrival time at the Neutral Zone?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(COMPUTER) Nineteen hours, twenty eight minutes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let's reassemble in six hours.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Stay sharp.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No surprises.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I would rather outthink them than outfight them.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Questions?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What is it, Doctor?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(CRUSHER) It's the people from the capsule.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Capsule?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "People?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What people?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The people Data beamed over.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I wasn't aware that he had.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well he did, and they were frozen.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I thawed them.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "You what?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I didn't know what else to do.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The crypts in which they were frozen were literally falling apart.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "So what's their condition?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Right now, they are all sleeping.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Each of them needed minor medical attention.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Minor now, but then their conditions were obviously terminal.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "One had a heart problem, another had an advanced case of emphysema with extensive liver damage.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "You know the most surprising thing of all, is that each of them had been frozen after they died.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "After they died?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Cryonics.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It was a kind of fad in the late twentieth century.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "People feared dying.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It terrified them.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "At the moment of death, they would be frozen, so that later, some time in the future, when presumably medical science had a cure for whatever killed them, they could be thawed back to life, healed, and sent on about their business.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "In the case of this group, it apparently worked.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister Data, will you report to Sickbay.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Immediately.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Look, I am never critical of any member of my staff being curious, but it's just that the timing is so", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I could not leave them there, Captain.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The condition of their vehicle was deteriorating.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "But Data, they were already dead.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I mean, what more could have happened to them?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I see your point, Captain, but at the time it seemed the proper thing to do.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Well, they're alive now.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We're going to have to treat them as living human beings.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Alive and well and ready to be awakened.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Before you wake them up, I want to have Security here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant Worf, report to Sickbay.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I have them sedated now, but they should be up and about as soon as possible.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Well, then we have no other choice.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "None that I can see.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(Worf enters, and Beverly puts a hypo to the woman's neck.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "She wakes, sees Picard, then Beverly, and smiles.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Then she starts to sit up, sees Worf, and faints)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Welcome to the twenty fourth century.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I was able to retrieve some information from the ancient disk I removed from the module's computer.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Her name is Clare Raymond, age thirty five, occupation homemaker.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Must be some kind of construction work.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "She died of an embolism.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It probably happened very suddenly, otherwise her physical condition was excellent.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(A nurse wakes Clare up, again)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "His name is Ralph Offenhouse, age fifty five, occupation financier.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Advanced cardiomyopathy.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Inoperable at the time, but easily correctable now.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "He must have known his condition was terminal for quite some time.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(Ralph is woken)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Much of his file we could not retrieved.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "His name is L Q Sonny Clemonds.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Apparently his occupation had something to do with music.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "There was marked deterioration of every system in his body.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Probably from massive chemical abuse.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Unbelievable.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "That sounds like someone who hated life.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yet he had himself frozen presumably so he could go through it all again.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Too afraid to live, too scared to die.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Doctor, this seems to be a situation more suited to your talents.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll leave it with you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll be on the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Number One, kindly take charge of our guests until we have time to decide what we're going to do with them.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Number One, keep them out of my way.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Guest lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The three resurrected people are dressed and trying to pay attention)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I know this is all very confusing to you so I'll attempt to explain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You are on the starship USS Enterprise.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(RALPH) American?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No, it's a vessel of the United Federation of Planets, and Earth is a member.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What year is this?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "By your calendar two thousand three hundred sixty four.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "What?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "My heart, is it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's perfectly fine.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "In fact, you are all now in excellent health.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It worked.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I made it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(SONNY) (a down-home country boy, y'all) What is that?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "An android.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You mean a robot?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Actually there is a distinct difference between an android and a robot.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(CLARE) And him?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The one I saw before, with the head?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "She means Worf.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "He's a Klingon.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "That takes a little more explanation.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Now, listen, guys.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I paid some idiot a lot of money to freeze me when I died, and I just got to hear the words.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Am I alive?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh, yes.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Absolutely.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And the liver that was about to explode in my face?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Perfectly sound.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Excuse me, could someone please tell me what's going on here?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "About three hundred and seventy years ago, you died of a massive embolism.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I don't remember anything about that.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You and the others were frozen.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Cryonics.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You know, freeze you now and heal you later.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yeah, I've heard of it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I just never gave it much thought.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How did we get here?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You were in a space module.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The whacko who sold me this scam said that by putting us in orbit there weren't gonna be no chance of a brown out.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, several cryonics companies fell into disrepute because of power failures which adversely affected their refrigeration system.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Their stocks were severely depressed.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The whole deal was a long shot but I figured what the hell, might as well give them the dough instead of leaving it to my ex-wives.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But you know, son, I figured it was all just a bunch of hooey.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Hooey?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Ah, as in hogwash, malarky, jive.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "An intentional fabrication.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "There you go, now you got it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now, if you didn't contract for this, who did?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It must have been my husband, Donald.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If it was new and foolish, he would have popped for it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well, I'll say this for it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's the first thing he's ever bought into that worked.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I, for one, never had a doubt.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "My stock in this company must have split at least a dozen times by now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Actually, the process of cryonics was never more than a fad, and did not continue much beyond the mid-twenty first century.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I need to make a phone call as soon as possible.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A phone call?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I have provided for myself.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I have a substantial portfolio.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's critical that I check on it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let the bank know that I am alive and well.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ah, that's going to be a little difficult right at the moment.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Well, do you at least have a copy of The Wall Street Journal?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I think we should take the Doctor's advice.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Take this very slowly until we've all adjusted, and then we'll talk to the Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "As you know, there is very little available on the Romulans.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Counsellor, anything would be helpful.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They seem to be creatures of extremes.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "One moment violent beyond description, the next tender.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "They are related to the Vulcans, but as each race developed, their differences grew wider.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "They are intensely curious.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Their belief in their own superiority is beyond arrogance.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "For some reason they have exhibited a fascination with humans and it is that fascination, more than anything else, that has kept the peace.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "One other thing.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "They will not initiate anything.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "They will wait for you to commit yourself.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Counter-punchers.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you, Counsellor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That's quite valuable.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Guest lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Data and Sonny are at the food dispenser)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Talk.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I'd like me a thick Kansas City steak, and some country fried potatoes, and a mess of greens.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh, hell, just forget all that and give me have a martini, straight up, with two olives.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "For the vitamins.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The glass appears out of thin air.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Sonny samples it and whistles)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Is there something wrong?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Wrong?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Only that your computer here fixed about the best martini I have ever had.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I just might get to like this place.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let's see if the Braves are on.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How do you turn on this teevee?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "TV?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yeah, the boob tube.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'd like to see how the Braves are doing after all this time.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Probably still finding ways to lose.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I believe he means television, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "That particular form of entertainment did not last much beyond the year two thousand forty.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Well, what do you guys do?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I mean, you don't drink, and you ain't got no TV. Must be kind of boring, ain't it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Riker here.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Would you and Mister Data report to the Bridge?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "At once, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "With whom were you speaking?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Good.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Would you tell him I would like to see him as soon as it's convenient?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What's going to happen to us?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do we stay here with you?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do we go back to Earth?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That will all be up to the Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Well, get him in here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I have to phone Geneva right away about my accounts.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The interest alone could be enough to buy this ship.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Yankee's right.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let's get the big boy in here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll pass along your request.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "If you'll excuse us.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Duty calls.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Hey, I understand.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(to Data) Why don't you come back later on.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You and me'll find us a couple of low mileage pit woofies, and help'em build a memory.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What is to be done with them, sir?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I don't know.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Commander, a low mileage pit woofie?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "This time you've got me.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I haven't a clue.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "These are the most unusual humans I have ever encountered.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Well, from what I've seen of our guests, there's not much to redeem them.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It makes one wonder how our species survived the twenty-first century.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Ready room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Report.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We are six hours from the Neutral Zone.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I have been unable to establish communications with any Federation colony or station in this vicinity.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "How many outposts are there in this sector?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Nine.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I think we work on the assumption they've all been destroyed.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "By the Romulans?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It fits their historical pattern.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Since we have no contact with the Romulans for fifty three years, seven months, eighteen days, we must consider that the information we do have, is out of date.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The positive side of that is the information they have about us is also out of date.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Captain, I think it's that lack of information that this is all about.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Go on.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "In my opinion, the Romulans want a confrontation specifically with us.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The Enterprise?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well, not by name, but the Federation.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "They know the Federation will send their best.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "That'll give them a perfect chance to see firsthand how far we've advanced both in technology and technique.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What are you recommending?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm not sure I have a specific recommendation.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Perhaps we should assume the initiative.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I agree with Commander Riker.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "This may be our only opportunity.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We should seize it.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister Data?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The strategy is built on a single premise.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The hostile intent of the Romulans.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "If the premise is sound, so is the proposal.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "If their intention isn't hostile, what is it?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Captain Picard?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is Captain Picard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "To whom am I speaking?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Ralph Offenhouse.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Guest lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I need to talk to you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Ready room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What is going on here, Number One?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Did you give him permission to contact me?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Of course not.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "He must have seen me use the comm.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "panel.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Listen, Mister Offenhouse.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We're in a very important conference right now.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) I'm sick and tired of being put off", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Guest lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "By you and your staff.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is the worst run ship I have ever been on.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Ready room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) You should take some lessons from the QE Two.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now that's an efficient operation.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Data, identify.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What is a QE Two?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It was a passenger liner which mostly travelled Earth's Atlantic Ocean during the late twentieth and early twenty first centuries.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "He's comparing the Enterprise to a cruise ship?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Captain Picard, I demand that you see me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I think I have been very, very patient.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Guest lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I demand a phone, or a radio whatever else you have.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Frankly, enough is enough.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Especially under the circumstances, and considering what I paid for this procedure!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I must make contact with my law", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I'm Captain Picard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Excellent.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now, maybe we'll be able to get some things straightened out.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We may indeed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Those comm.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "panels are for official ship business.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If they are so important, why don't they need an executive key?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Aboard a starship, that is not necessary.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We are all capable of exercising self-discipline.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now, you will refrain from using them.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now just a minute.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We are in a very serious and potentially dangerous situation.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm sure whatever it is seems very important to you, but my situation is far more critical.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't think you are aware of your situation, or of how much time has passed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Believe me, I'm fully cognisant of where I am, and when.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It is simply that I have more to protect than a man in your position could possibly imagine.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No offence, but a military career has never been considered to be upwardly mobile.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I must contact my lawyer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Your lawyer has been dead for centuries.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, of course I know that, but he was a full partner in a very important firm.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Rest assured, that firm is still operating.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That's what this is all about.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "A lot has changed in the past three hundred years.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "People are no longer obsessed with the accumulation of things.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We've eliminated hunger, want, the need for possessions.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We have grown out of our infancy.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You've got it all wrong.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's never been about possessions.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's about power.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Power to do what?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "To control your life, your destiny.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That kind of control is an illusion.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Really?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm here, aren't I?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I should be dead but I'm not.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Clare starts weeping)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What is it?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I don't know.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It just started and it won't stop.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I keep thinking about my boys.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Counsellor Troi, will you report to the guest lounge?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Captain, we are approaching Science Station Delta Zero Five.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Slow to half impulse.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm on my way.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, I need to see that pretty little Doctor of yours.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll inform her.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I didn't mean to come on so strong.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's just that I've built my whole life on knowing what's going on.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "For the first time I feel completely out of touch.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's making me crazy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You can understand that?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's the first thing you've said I do understand.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll see what I can do.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And, please, stay off the comm.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "panels.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Counsellor, will you get those people under control.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We cannot afford this continuing distraction.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Clare's quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(There's a model of NCC1701 on a stand)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Hello.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "My name is Deanna.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "They sent you here to settle me down?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm the ship's Counsellor and I thought you might want to talk.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "The local shrink.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm sorry, I'm not familiar with that term.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "It doesn't matter.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're feeling profoundly sad.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I can't stop crying.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I miss my sons, and then I think I'm here, but they are gone.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Donald, that's my husband.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I love that man but, good intentions aside, he'd mess up a free lunch.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why on Earth he ever decided to do this?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He must have thought you would have wanted it.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "No, I can tell you exactly what happened.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He couldn't think about going on alone.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I died and he couldn't face it, so he figured if he had me frozen, then in his mind I wasn't really gone.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It sounds crazy, but you had to know Donald.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I wish I knew what happened to my children.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Did they get married, did they have kids?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let's see if we can find out.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Computer, this is Counsellor Troi.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Request personal history on.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "What are their names?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Tommy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He's eight, and Eddie's five.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Full names, date and place of birth.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They were born in Secaucus, that's in Jersey.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Can this really work?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Can this tell me what's happened to them?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well, there must be a record somewhere.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "There is a good chance we can find it.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Well, whether we do or not, I want to thank you for trying.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Date of birth.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Tommy was born February seventeenth", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I just got to have a little something to jump start the morning, and a little something else to shut down the night.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You have no medical need.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It ain't a question of need, darling.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's a matter of survival.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sorry.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Not to worry.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Old Watosh'll scuffle along the best way he can.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How do I get a hold of that feller with the strange looking face?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I beg your pardon?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "What's his name.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That android fella.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You mean Lieutenant Commander Data?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "That's the one.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll let him know you're looking for him.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Much obliged.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You know, you're just about the prettiest little old Doctor I ever seen.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Much obliged.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Clare's quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Family trees are whizzing across the screen)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "This is amazing.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's all right here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ten generations of your progeny.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Everyone I've ever known is dead.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do you mind?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'd like to be alone.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No, not at all.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Sonny's quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(also with a model NCC1701)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You asked to see me.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Look, I'm about to go out of my mind for something to do.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I mean, sitting here alone just don't get it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What say you and I put together a little party?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A celebration?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "No, nothing that fancy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Just some folks, some suds, and some sounds.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Hell, it ain't nothing but something to do.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I will speak to the Captain.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Great, you do that.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Inquiry.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "You do not seem to be having as much difficulty adjusting to your current circumstances as the others.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "You mean being here on this tub four hundred years from where I started?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Heck, it's the same dance, it's just a different tune.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You think anybody here's got a guitar I could borrow?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No, but the computer can replicate such an instrument.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I was kind of hoping to get one while I still remember the chords.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Commander Data, we are approaching the Neutral Zone.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Report to the Bridge.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I must leave now.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "What's this Neutral Zone?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It is a buffer between the Romulan Empire and the Federation.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Why does that make me nervous?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I do not know.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "We won't be inviting these Romulans to our party, will we?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No, that would not be appropriate.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Captain's log, supplemental.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We have arrived at the edge of the Neutral Zone, where we will now have an opportunity to learn firsthand what happened to our distant outpost.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, there is nothing left of Outpost Delta Zero Five.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Must have been one hell of an explosion.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sensors indicate no evidence of conventional attack.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Can you determine what happened?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The outpost was not just destroyed, it's as though some great force just scooped it off the face of the planet.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Could it have been a natural phenomenon?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Insufficient information, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister La Forge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Set course for the next closest station.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Guest lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I have spent my career being able to tell when the other guy's mouth is dry.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There is something going on here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Something serious.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The tension level on this ship has jumped up.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Even if you're right, what can we do about it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And besides, these old boys here don't need us telling them where the bear sits.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Look here what that young fella made for me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(It's a guitar)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "How did he do that?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He called it a pattern replication.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It plays real good, too.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm trying to tell you that there is a situation developing on this vessel that directly affects us, and all you can talk about is that stupid guitar!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Hey, now, these folks don't need us swimming in their soup, so why don't you just relax and let'em do their jobs?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That may be all right for you, but I am not willing to allow my fate to be decided by others.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I at least want to know what is going on.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We are approaching Tarod Nine.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The condition is identical, Captain.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Everyone and everything is gone.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, I strongly recommend that we go to Red Alert.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "If the Romulans have improved their cloaking device, and we'd be fools to believe they haven't, we should assume a more defensive posture.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I agree, Captain.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I recommend we go immediately to battle stations.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I appreciate your advice and concern, but this is not the time for rash actions.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We are still investigating.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "There are three other stations in this sector we have to visit.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Let's proceed in a calm and orderly manner.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If not battle stations, may we at least take the ship to Yellow Alert?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That would be prudent.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Make it so.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Ralph is walking along, and notices a woman come out of a turbolift.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "He goes in)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Turbolift]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ah, let's see.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I want to go to the, er.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Where would the Captain be?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain Picard is located on the main Bridge.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well then, take me to the main Bridge.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, my sensors indicate a disturbance.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It is large and moving, but I cannot get a positive lock nor can I get it on the viewscreen.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Shields up.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, I recommend we transfer all power to phasers, and arm the photon torpedoes.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Wait.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If that is a Romulan ship, they will read our intent.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It'll force them into taking a similar posture.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We don't want to engage in battle.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, this is sufficient evidence.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Outposts have been destroyed.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Countless lives have been lost.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I have a positive lock.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "They're disengaging their cloaking device.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "They'll only be vulnerable for an instant as they become visible.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Captain, this may be our only chance.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "No.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Where are they?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The signal is weakening.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Ralph quietly enters the Bridge)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I've lost them.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Damn.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister Data, are your sensors picking up anything?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You should be detecting a disturbance.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Negative, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "We wanted to know if they have improved the cloaking device.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Guess we have our answer.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(to Riker) They were trying to determine our intent.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They wanted to see if we would fire.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Who the hell are they?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Get that man off the Bridge, now.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'm not going anywhere.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, they're back.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(A big green warbird appears on the viewscreen)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Amazing.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I never thought I would ever see a Romulan ship.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Not this close.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The last time we encountered them was decades ago.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Cost thousands of lives.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I can believe that.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Stay calm, everyone.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Open hailing frequencies.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, these are Romulans.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "They are without honour.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "They killed my parents in an attack on Khitomer when they were supposed to be our allies.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "They believe humans and Klingons are a waste of skin.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant, control your emotions.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Please, open hailing frequencies.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hailing frequencies open.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Romulan vessel, this is Captain Jean-Luc Picard of the USS Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sensors indicate all of their systems are armed, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "But they've not fired.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Let's try them again.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Two seated Romulans appear, one relaxed and sitting back, one leaning forward.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The relaxed one speaks)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(On viewscreen) I am Commander Tebok.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Commander, you have crossed the Neutral Zone.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is Federation territory.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) It was necessary.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It might be viewed as an act of aggression.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) If our intent were aggression, you would not be here now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(TEBOK [On viewscreen]) If we go to war, let us be sure it is for the right reason.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We are here because our outposts, which border the Neutral Zone, were also attacked.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(On viewscreen) Destroyed in the exact same manner as your own.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Even so, what gives them the right to enter Federation space?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Silence your dog, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Lieutenant Worf's question is valid.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) To even to ask such a question implies that we need permission.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We do not.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do you think that we attacked your outposts?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Once we realised the level of destruction, we knew it could not have been you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Who is responsible?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They haven't got a clue.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They're hoping you know, but they're too arrogant to ask.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're out of line, Mister.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes, but it's a correct assessment.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) We do not know who is responsible.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why entire outposts on both sides have been carried off.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I would like to offer a proposal.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) An alliance?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Between the Romulans and the Federation?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Nothing so grandiose.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Just this.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Cooperation.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "There was an intent here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Whoever or whatever did this is more powerful than either of us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Let's collaborate.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Let's share whatever we learn about what has happened here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Agreed.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "On this one issue.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And only if it is convenient and appropriate at the time.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(On viewscreen) Captain Picard, because your actions are those of a thoughtful man, I'll you this.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Matters more urgent caused our absence.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now, witness the result.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Outposts destroyed, expansion of the Federation everywhere.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, we have indeed been negligent, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But no more.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Commander, we have made some progress here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Let's not ruin that with unnecessary posturing.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Your presence is not wanted.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do you understand my meaning, Captain?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We are back.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Transmission ends)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I think our lives just became a lot more complicated.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Get him off my Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Clare's quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I've found something.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I have been able to locate a family living outside of Indianapolis.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "The man's name is Thomas Raymond.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "That's my son's name.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "My son's name is Tommy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Computer, let us see Thomas Raymond.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Oh, my God.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That's Donald.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That's my husband.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Actually, it's your great, great, great, great, great grandson.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "He's the spitting image.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well, I have his address.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "When you get back to Earth, you can go and see him.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "No, I'll be out of time.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A relic.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Perhaps.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "But it's a place to start.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "After all, he is family.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "There's no denying that.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Observation lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Here's what I propose.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You can't stay on the Enterprise, but I have arranged for us to rendezvous with the USS Charleston, bound for Earth.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They will deliver you there.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Then what will happen to us?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There's no trace of my money.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "My office is gone.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What will I do?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How will I live?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is the twenty fourth century.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Material needs no longer exist.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Then what's the challenge?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The challenge, Mister Offenhouse, is to improve yourself.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "To enrich yourself.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Enjoy it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well, one good thing is, since everybody's forgotten everything I ever did, it'll all be brand spanking new.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll be a bigger hit than I ever was.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Perhaps so.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Anything is possible.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What say you, son.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You'd make a hell of a sideman.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That offer does present a certain fascination.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, I have plotted a course to intercept the Charleston, but they've just informed me that they'll be making an extended stop at Arloff Nine.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Your point, Mister La Forge?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well, at warp eight, we could have our guests at Starbase Thirty Nine Sierra in five days.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Take months off their journey.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "But they'll benefit from the extended time.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It will allow them to acclimate before returning to Earth.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's a pity we can't take them ourselves.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Having them on board is like a visit from the past.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "That would take us in the wrong direction.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Our mission is to go forward, and it's just begun.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Set velocity.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Warp six, Mister La Forge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Warp six.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "There's still much to do.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Still so much to learn.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister La Forge, engage.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[img=music]  End Credits [img=music]", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,STS-0.4,UHP-4.0" },
	},
};

-- Star Trek:TNG S1.03 - The Naked Now
RecMedia["TNG_S1E3"] = {
	itemDisplayName = "VHS: ST:TNG S1.03",
	title = "Star Trek:TNG S1.03 - The Naked Now",
	subtitle = nil,
	author = nil,
	extra = nil,
	spawning = 0,
	category = "Retail-VHS",
	lines = {
		{ text = "(PICARD) Captain's log, Stardate 41209.2.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We are running at warp seven to rendezvous with the science vessel SS Tsiolkovsky, which has been routinely monitoring the collapse of a red super giant star into a white dwarf.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What has brought us here is a series of strange messages indicating something has gone wrong aboard the research vessel.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(There is the sound of laughter coming over the comm.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "system)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(DATA) SS Tsiolkovsky, repeat your message.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Offscreen) Well hello, Enterprise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Welcome.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I hope you have a lot of pretty boys on board, because i'm willing and waiting.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "In fact, we're going to have a real blow-out here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Do it!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yeah, go ahead.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do it!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(BANG)", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, what we just heard is impossible.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Report.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I believe that last sound we heard was an emergency hatch being blown.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Are you certain?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, of course you are.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(RIKER) Data, Geordi, Tasha.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(WORF) Sensor scan now reveals no life signs aboard, Captain.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The Enterprise comes up to the ship in orbit)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Tsiolkovsky]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The group beam into an area with scattered furniture and a Red Alert)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Cover the ship as planned.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Move out.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Indications of what humans would call a wild party?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(They find a monitor, with a static laden image)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Their Bridge.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "If this thing works, be sure to record everything.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(The picture gets cleaned up)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You were right.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Somebody blew the hatch.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "They were all sucked out into space.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Correction, sir, that's blown out.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Thank you, Data.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "A common mistake, sir,", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Offscreen) Commander Riker, Lieutenant Yar.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Location Engineering.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Ten people here, sir, all frozen.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "No vital signs.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Frozen?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "How?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) It looks like someone has been playing with the environmental controls, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Just let all the heat bleed away into space.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Tsiolkovsky Crew Quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Another group of frozen people just lounging about.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Some are in a state of undress)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(LAFORGE) Sir, Lieutenant La Forge in the crew quarters.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Something in here.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(He finds a door which is trying to close.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "He opens it and a corpse falls out into his arms.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Tsiolkovsky]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Riker to Captain, I have a report for you, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) Picard here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They're all dead.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Apparently some of them were apparently blown out the emergency hatches.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "But there were eighty people on that ship, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Yes sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Tsiolkovsky]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "As I said, all dead.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Captain's log, supplemental.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We are downloading the research information gathered on the collapsing star nearby.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I am concerned at being in such close orbit, but the Tsiolkovsky's research records will no doubt predict the time of the star's final collapse.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Crusher's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(CRUSHER) I can't find anything unusual in any of the tricorder readings they've sent over, Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Give me a theory, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Anything.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Madness?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mass hysteria?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Delusion?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(TROI) Any or all, Captain.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "All right.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Let's bring the away team back.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Set the transporter to maximum decontamination, and then full examination and observation when they're here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If you were any more perfect, Data, I'd write you up in a Starfleet medical textbook.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I am already listed in several bio-mechanical texts, Doctor.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Yes of course.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "You're next, Lieutenant.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(Geordi lays on the bed)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Normal all across.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Except, why are you perspiring, Lieutenant?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I suppose because you have it too hot in here.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "What else would it be?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "That doesn't sound like you, Geordi.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Well, maybe it's not.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Maybe she threw her voice.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hey, it was a joke.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Of course, but I would like to run one or two more tests on you, Lieutenant.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = ") : (Offscreen) Sickbay to Bridge,", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Picard here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Go ahead, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) I'm confining Lieutenant La Forge to Sickbay until further notice.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do we have a problem, Doctor?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Crusher's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't know yet.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Data, I need help in locating some library computer information.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Specifics, sir?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "All I have is a vague memory of reading somewhere about someone taking a shower in his or her clothing.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Ah. The body Geordi discovered.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I believe it may have happened before.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "To someone, somewhere.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "This ought to be easy for someone written up in bio-mechanical texts.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "About that, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Did the Doctor believe I was boasting?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Probably.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "This may take some time.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "At least several hours.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "But what I said was a statement of fact.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Perhaps she will look it up.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "You can depend on it.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(While Beverly puts the results of her latest scan into her computer, Geordi takes off his comm.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "badge and walks out into the corridor.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "When she gets there, he is nowhere in sight)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Geordi!", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Crusher's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Security.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Lieutenant La Forge just left Sickbay while I was in my office.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) He doesn't have his communicator.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It is very important that we find him.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Security team alert, pick up Lieutenant La Forge.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "He just left Sickbay moments ago.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, anything further?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Affirmative.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Make it a ship-wide search, Lieutenant.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Crusher's quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Geordi is with Wesley, who is showing off his latest device by floating a chair around the room)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(WESLEY) It's a model of the same kind of tractor beam our ship uses with a few ideas of my own added.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "So that's your science project, huh?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Wes, you're really something.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "And since the Captain won't let me on the Bridge, I use this to imagine I'm there.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(Offscreen) Take the helm, Mister Crusher.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Set a course for thirty seven mark one hundred eighty.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Warp six.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's the Captain's voice.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's pieced together from words he's used on the intercom.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "With this, I can pretend he's ordering me to take the Enterprise anywhere.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "And listen to this,", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(Offscreen) Chief Engineer, report to the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Commander Riker, report to the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Doctor Crusher, report to the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What do you think?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I think the Captain's lucky you're on his side.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "But he still won't let me on the Bridge.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "And there's nothing there I don't understand.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I wish I understood myself that well.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Are you okay?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "No, Suddenly I seem to be burning up inside.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's hot in here.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Observation lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Geordi is staring out at the stars)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Geordi?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Geordi?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant Yar in the Observation lounge.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Send a team here now.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Medical's been worried about you.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Help me.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Help me to not to give in to the wild things coming into my mind,", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Geordi, my job is security,", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Tasha, please.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Alright.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Alright, helping is more important.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Geordi, how can I help you?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Help me to see.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Like you do.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "But you already see better than I can.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I see more.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "But more isn't better.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(He takes off his Visor)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Geordi, please put", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I want to see in shallow, dim, beautiful human ways.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(He strokes her face)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "We'll talk about it, Geordi.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Right now I'm going to take you to Sickbay.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "All right?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Yeah.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Okay.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Good.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And then we got him down to Sickbay so Doctor Crusher could examine him.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "He wasn't violent?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "He was very upset.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "He kept talking about wanting normal vision.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you, Lieutenant.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Tasha wipes her forehead and leaves.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "She's feeling warm)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "According to our medical readouts, there's still nothing wrong with him.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "He looks like he's running a temperature but every instrument we have says he's not.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Doctor, every person on that ship over there died.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Is there any chance that whatever did it is loose on my ship?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If you mean a disease, sir, I'd say there's no chance of it.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "We used full decontamination, we examined every team member very carefully,", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The entire crew somehow managed to kill themselves, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If it's not a disease, what else could have made them do that?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The obvious alternatives would be in areas of insanity and severe emotional upset.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Troi, do you feel anything unusual in the lieutenant?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Security just told me that he was longing for normal eyesight.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That's a sudden yearning for that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Since his records show no previous mention of that, the fact that it's happened now could be important.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "But all I sense from him is confusion.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "If I didn't know better, I'd say he was intoxicated.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Our tests would have shown that.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Also any other signs of drugs, hallucinogens or other contaminants.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Data is going through library records with Riker hanging over his shoulder)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Can you provide more information, sir?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Seeking an instance of someone showering in his or her clothing is", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I know.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's like looking for a needle in a haystack.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Why should anyone wish to expend his time in such a search?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Correction, Data, I should have said proverbial needle in a haystack.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Ah, a human proverb!", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "As in folklore, or an historical allusion, or tribal memories", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Historical.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "That's it.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I remember I was reading a history of all the past starships named Enterprise.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Enterprise history.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Aberrant behavior.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Medical cross reference,", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Picard enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain, I believe we've have the answer to what happened over there.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(reading the screen) The Constitution class Enterprise, Captain James T. Kirk commanding.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Similar conditions.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "They were monitoring a planet that was breaking up, not a collapsing star as in this case.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "But there were the same huge shifts in gravity,", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Which somehow resulted in complex strings of water molecules which acquired carbon from the body and acted on the brain like alcohol.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Data, download this information to Medical immediately.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Downloading.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Fascinating!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The entire crew going out of control,", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Like intoxication, but worse.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Judgment almost completely impaired,", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Until they found this formula, barely in time.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Picard to Doctor Crusher, come in.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Crusher's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is Crusher.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Go ahead Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) You can relax, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The answer to all of this is feeding into your medical banks right now.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Including a cure.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Crusher's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Are you certain, Captain?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Absolutely.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Troi's Quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Deanna enters to find her clothes strewn over her furniture, and laughter from the bedroom)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Tasha?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "What are you doing?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I need your advice.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "That's why I came to your quarters.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Of course.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Anything I can do", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "On clothes.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You always wear such beautiful clothes off duty.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "And your hair always looks so nice.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I want to change my image.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "What do you think about this?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Or this one?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "It's not for you.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Tasha, I feel you're very uncertain.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Yhat you're fighting something.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(their hands touch) What is it?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Never mind.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I'll find what I need myself.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Ship's stores will have it.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Tasha?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Tasha, wait.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Tasha leaves, giggling)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Troi to Captain Picard.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Picard here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Troi's quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sir, I think Tasha's been infected too.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) She just left my quarters,", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Counsellor, it's not actually an infection.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Troi's quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "It's more like an intoxication.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Nut whatever it is, she's got it.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thank you, Counsellor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Number One, it seems our Security Chief has the equivalent of a snootful.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Inquiry, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Snootful?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Forget it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Beverly is working in her office when Wesley calls out to her)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Hey, Mom, look what I can do.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(She dashes into Sickbay to see him playing with his tractor beam device)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I've been able to widen and strengthen the beam, just like I told you last night.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Do me a favour, Wes.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "There's something happening on this ship.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Just to be safe, I'd like you to stay in our quarters until it's solved.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Sure, Mom, sure.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Your wish is my command.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Now, Wes.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Okay, but you could be stunting my emotional growth, you realise that.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Why is it so hot in here, anyway?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(Offscreen) Picard to Crusher.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Crusher here.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Have you made a test injection yet?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We're getting indications that this condition is spreading.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No test yet, Captain, but very soon.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Tasha is sashaying down the corridor, eyeing up men, as other crew canoodle.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "She grabs a guy in blue and gives him a long hard kiss)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, another forty-one minutes will see the information from the Tsiolkovsky downloaded to us.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Why so slow?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Slow, sir?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The Tsiolkovsky has been eight months in accumulating it.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "How much danger from that star?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Worse case.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Like a full collapse, sir?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Any stellar material it threw this way we could still outrun on half impulse power.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Picard to Engineering.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Chief Engineer report to the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(A woman marches out)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Offscreen) Assistant Chief Engineer Shimoda, report to Medical.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Wesley enters and offers his sweaty hand for a shake)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Hi, Jim.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Was that the Captain sending you to Medical?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(SHIMODA) Which would leave no one on duty here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Chief was just summoned to the Bridge.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What about me?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I could call the Chief on the Bridge if anything happened.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(Shimoda leaves, and Wesley looks smug)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(MACDOUGAL) Reporting as ordered, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You ordered me to report to the Bridge, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I did no such thing.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I want you down in the Engine room just in case we need to move out of here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Attention all decks, all divisions.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Effective immediately, I have handed over control of this vessel to Acting Captain Wesley Crusher.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Acting Captain?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Thank you, Captain Picard, thank you.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "And with that order dawns a brave new day for the Enterprise.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Captain's log, Stardate 41209.3.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The strange contaminant that led to the deaths of the Tsiolkovsky crew is now aboard the Enterprise and our Engineering section has been commandeered by young Wesley Crusher.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And henceforth, a dessert course shall precede and follow every meal.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Including breakfast.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(CREWMAN) Hurray for the acting Captain!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The entire Engineering crew are out of their skulls)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Never got as far as Sickbay, Wes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I feel too good for that.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(He walks into a force field)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Incredible.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How did you do that?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Hooked my model tractor beam into ship's power.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Now its a repulser beam too.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Want in?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Swear to be faithful to you, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Wesley lets him stagger into his area)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Number One, MacDougal, get that boy out of Engineering.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Riker and MacDougal leave)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Sir, I'm getting very strange reports from all decks.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Such as?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Such as the ship's Training Division ordering all officers to attend a lecture on metaphysics.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Metaphysics?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Confirmed, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "And there was a rather peculiar limerick being delivered by someone in the Shuttlecraft bay.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I am not sure I understand it.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "There was a young lady from Venus whose body was shaped like", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Captain to Security, come in!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Did I say something wrong?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I don't understand their humour either.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Yeah, Captain?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Where is my security chief!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Get me Lieutenant Yar.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Keep your britches on.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) (lilting) Captain Picard.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant, where are you?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) I'm in my quarters, and I'm pretty busy right at the moment, Jean-Luc.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "All right, Lieutenant, you just stay right there.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Data, please go to Lieutenant Yar and take her down to Sickbay.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Data leaves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain to Security, I want all your senior supervisors to report to the Bridge immediately.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Tasha's Quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(We hear giggling as Data enters cautiously)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Lieutenant Yar?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Here, Data.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You wanted me?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(She has slicked down her hair, and is wearing a curious outfit that leaves the ribcage and stomach bare)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain Picard ordered me to escort you to Sickbay, Lieutenant.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Did he say when?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I am sure he meant now.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "So, you need time to get into uniform.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "But I got out of uniform for you, Data.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Do you know how old I was when I was abandoned?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Chronological age?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "No, I am afraid I am not familiar with", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Five.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Five years old, but I survived.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I learned how to stay alive, how to avoid the rape gangs.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I was fifteen before I escaped.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I am sorry.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I did not know,", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "And what I want now is gentleness.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "And joy.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "And love.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "From you, Data.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You are fully functional, aren't you?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Of course, but", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "How fully?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "In every way, of course.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I am programmed in multiple techniques, a broad variety of pleasuring,", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Oh, you jewel!", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "That's exactly what I hoped.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(She leads him into the bedroom and sits him down on the bed)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Engineering ]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) What have you learned, Number One?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, the ship's engines are cut off from the bridge.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The Assistant Chief Engineer pulled out the isolinear optical chips from command.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "All engines are offline.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Wesley has hooked some kind of tractor beam to ship's power and he has it aimed at the door.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We can't get past to get at the computer.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Can you short out the power?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, I can.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But it's going to take some time.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do it!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering ]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Deanna, what?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "So many minds on this ship, all free.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Released,", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Deanna.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(She strokes his chest)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I can feel them all.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "What they want, what they feel.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "It's a side of humans I've never felt before.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Come on, I'm getting you to Sickbay.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(He picks her up and carries her)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Wouldn't you rather be alone with me?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "With me in your mind?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(injecting Geordi) The medical records we found say this works almost instantly.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It's not fair, Doc.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I've never seen a rainbow, sunset, sunrise.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is going to help me?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Help me see like you?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Riker enters carrying Deanna)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Doctor Crusher?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Crusher's office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Deanna needs your help.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The formula from the old Enterprise didn't work.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "What?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "This water-carbon complex may induce the same symptoms, but somehow it's different.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Maybe it's mutated.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "But I've got to isolate it in order to analyse it,", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "We don't have that kind of time.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You brought Deanna in.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "She's infected with", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Then you touched her!", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Oh, God.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "And you touched me.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Wait!", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I've got to quarantine you.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "If I don't get the command computer back online soon, none of this, whatever this is, won't matter.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We'll all be dead.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Captain's log, supplemental.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It is no longer an inconvenient, childish prank.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Young Wesley Crusher, admittedly a victim of the Tsiolkovsky infection, is now in control of my starship.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Wes, this is Captain Picard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do you see me?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Yes sir?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "You will now return control of this vessel to the Bridge where it belongs.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "At once.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) I'm sorry, sir.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Why don't you just tell me what you want done and I'll do it.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Because ship captains control their own vessels, young man!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) But, sir, you don't do it yourself.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "You give the orders, but someone else does it.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "What's wrong with giving me the orders to do it?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Captain, getting unusual readings now from the dwarf star.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Stand by a moment on that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Wesley.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Wes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Are you aware that you're acting strangely, that a kind of infection was brought over from the Tsiolkovsky which acts like intoxication?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Are you saying that's why I feel so so hot?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "So strange?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "That's a very adult bit of reasoning, Wes,", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) So you mean I'm drunk!", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I feel strange, but also good.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Because, because you've lost the capacity for self-judgment.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now, alcohol does this, Wesley.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But this contaminant we've brought back from the Tsiolkovsky does it even more so.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) What would you do if you got your ship back?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Oh, it's very important I do, Wesley, because I must immediately lock a tractor beam onto the Tsiolkovsky, then tow it out of,", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Tractor beams are my specialty, Skipper!", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I'll contact you when that's done.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Wesley out!", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Wesley!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Wesley!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The man at Conn.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "gets up and staggers off)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Conn, where are you headed?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The star.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's beginning to collapse.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "What the hell is happening in Engineering?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Shimoda is using the chips as building blocks, Wesley is muttering and Riker and MacDougal are trying to get past the force field)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Lock on.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Lock on Tsiolkovsky.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Where's that sonic driver?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's over there.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Okay, let's see if this cuts out his tractor beam power.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(No, it doesn't) Oh, come on.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Crusher's Office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The symptoms are starting to set in)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Oh no!", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I must find the answer.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I've got to find the answer.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, tractor beam.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We just locked onto the Tsiolkovsky.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain to.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Wesley.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Wesley Crusher, come in!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Data enters, smiling and weaving slightly)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Ah, Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "At least you're functioning.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Fully, Captain.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Data, intoxication is a human condition.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Your mind is different, it's not the same as", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We are more alike than unlike, my dear Captain.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I have pores.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Humans have pores.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I have fingerprints.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Humans have fingerprints.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "My chemical nutrients are like your blood.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "If you prick me, do I not leak?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Crusher enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Doctor,", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, can I see you in your Ready Room?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It's a private matter.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "No, actually it's an urgent one.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "But.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Damn it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Data makes as if to lean on a chair, but there is nothing there and he falls)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Ready Room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now, Doctor", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I believe I'm infected myself, Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Do you know what the infection is?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Come on, quickly.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sorry.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "It is definitely like alcohol intoxication.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The same lack of good judgment, For example, right now I find you extremely, extremely, Of course we haven't time for that sort of thing,", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "What sort of thing?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Oh God, would I love to show you.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Doctor, there must be a cure.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Some formula, similar to the old one,", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Damn it, damn it, Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "My dear Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(She tries to kiss him, but he dodges)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,LFT+1.0" },
		{ text = "You owe me something.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "You do realise that, don't you?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I'm a woman.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I haven't the comfort of a husband.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "A man.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Not now, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Please.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Beverly goes to the turbolift, and Picard waves her goodbye)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Bridge to Riker, Urgent.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering ]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Riker here.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) Sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sir, regret to inform you that the Captain appears to be infected.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "And Data.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thank you, Lieutenant.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'm on my way.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You'll have to handle this.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Are you saying you're going to handle that?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The star suddenly shrinks and explodes)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What we're seeing, sir, is a huge chunk of the star's surface blown away, heading for us.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Take us", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Are you alright, sir?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Worf, you know what to do.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Take us er", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Take us out of here.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Right.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Controls are still offline, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Override.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Same result, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Wes, come in please.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Wesley Crusher, this is Riker.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Come in.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) This is urgent.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Come in, please.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(MacDougal gets the forcefield down)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Offscreen) Engineering, urgent.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We must have ship's power,", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Those are control chips!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Bridge from Engineering.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The star is still collapsing.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We're directly in the path of", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I can't help you, Bridge!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Someone here has yanked out all the control chips.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It was an adult who did it!", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sir, I estimate fourteen minutes until that mass gets here.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) No way, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I cannot replace these chips in fourteen minutes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Two hours, three", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Maybe.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Data could assemble them back faster.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What, what's that?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What's that, Wesley?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well, they're just simple isolinear chips, sir, to Data anyway.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "He can shuffle them like cards.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(Riker hauls the drunken Data to his feet)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Come on, Data, hurry.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Ship's log, First Officer Riker.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Enterprise will be destroyed unless it can be moved out of the path of the star material hurtling toward us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Our only hope is for Lieutenant Commander Data, in the time we have left, to regain his senses and reconnect engine power to the Bridge.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Nice to see you, Wesley.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Hi, Mister Data.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "No time for courtesy.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Get the damned control chips back in place, in the correct order.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Now!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's like a game.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "How fast can you do it?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Ah, a game!", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(So, with Red Alert screaming, Data sits down at the panel and starts slotting chips in, very quickly)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I think I can switch this to main viewer, sir,", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(Once he sees the reality of the situation, he steps back horrified)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Data, we've got eight or nine minutes at most.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Can you finish by then?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No, this will take slightly more time than we have, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Riker starts sweating)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Damn it, no.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I can't afford to get this.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Crusher's Office]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard enters with a little skip)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Beverly.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, Jean-Luc?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "You will address me as Captain.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Well then, my dear Captain, you will address me as Chief Medical Officer or Doctor.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I will?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That's true.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I started off calling you Beverly, and of course, naturally, you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm still not thinking straight.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Likewise.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Where the hell was I headed?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "If that's something you were going to test,", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, on Geordi.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Come here.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "See how I reversed the fields on this, Commander?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I made it into a repulser beam.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "If we just had one minute more, sir,", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "If this were a hundred times more powerful than it is.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Why not try it with the real thing?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Why not reverse fields on this, Ma'am?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "If we just need an extra minute,", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "It would take weeks of laying out new circuits.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why not just see it in your head?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Come off the main lead, split off at the force activator, then, then.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "If I could just think straight about this,", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I made this a broader based remedy.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I hope.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "But it's still close to the formula from the old Enterprise records.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Decades ago, light years away,", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But almost exactly the same conditions as here.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Wow!", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "What was in that, Doctor?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "My head's beginning to clear,", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Come here.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(injects Picard, then herself) Here, take this to Engineering.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I'll make up more hypos for the others.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Okay, Bev.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard arrives and starts injecting people)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "We're not going to make it, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "If we had just a minute or so.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Then reversing power leads, back through the force activator.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Repulser beam hard against Tsiolkovsky.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Don't you see?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "It's giving us a push off.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "The extra time we need.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "We're pushing away.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(The stellar matter hits the Tsiolkovsky, then Data finishes his task)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Bridge, engage engines!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Enterprise warps away in the nick of time)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Crusher's injections wipe the smiles off the remaining Bridge crew)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain , something seemed to move us aside at the last minute.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do we owe our thanks to Commander Data, sir?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Yes, and Wesley may have given us a few seconds, too.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Did he say Wesley?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The boy?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "He said Wesley.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's only fair to mention Wesley in a log entry, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Fair's fair.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And let's credit his science teacher, too.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Picard gives Wesley his sobering up injection, and leaves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard, Riker and Data enter)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Congratulations, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "To many people.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Troi and Tasha enter.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Tasha goes over to Data at a rear station)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Data.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I'm only going to tell you this just once.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "It never happened.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I put it to you all.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I think we shall end up with a fine crew, if we avoid temptation.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "So, Number One, let's go to our next job.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Helm, prepare for warp three.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Heading two hundred and ninety four mark thirty seven.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Warp three, heading two hundred and ninety four mark thirty seven, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Engage.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[img=music]  End Credits [img=music]", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,STS-0.4,UHP-3.0" },
	},
};

-- Star Trek:TNG S1.04 - Code Of Honour
RecMedia["TNG_S1E4"] = {
	itemDisplayName = "VHS: ST:TNG S1.04",
	title = "Star Trek:TNG S1.04 - Code Of Honour",
	subtitle = nil,
	author = nil,
	extra = nil,
	spawning = 0,
	category = "Retail-VHS",
	lines = {
		{ text = "(PICARD) Captain's log, stardate 41235.25.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Our location, planet Ligon Two, source of a rare vaccine needed on Federation planet Styris Four.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Starfleet has instructed me to engage in a friendly visit and open treaty negotiations to acquire this medicinal substance.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(LAFORGE) Standard orbit, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(RIKER) Lieutenant Yar is calling from Cargo Bay One, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Standing by to beam Ligonian welcoming party aboard.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "On our way.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You have the helm, Mister Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Turbolift]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Cargo One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This should be an interesting experience.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Agreed.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Not only are they closely humanoid, but their history has remarkable similarities to ours.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(TROI) A highly structured society.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "and they're exceedingly proud.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Cargo Bay One]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(TASHA) They've insisted on using their own transporter device, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "It's their way, Lieutenant.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do they have our coordinates?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They have, sir, precisely, and they're standing by for your signal.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "This is Captain Picard aboard the Starfleet vessel Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Please do us the honour of visiting our vessel.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Five people beam aboard.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "One rolls out a red carpet, then stands aside as an imposing man beams in)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(LUTAN) I am Lutan.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain Jean-Luc Picard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Welcome aboard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "These are my officers.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "My second in command, Commander William Riker.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Ship's Counsellor, Deanna Troi.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And my Security Officer, Lieutenant Natasha Yar.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "A woman?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Your Chief of Security?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, Lutan, that is her expertise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I am honoured to meet your officers.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is my Secondary, Hagon.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A sample of the vaccine.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Tasha steps forward to intercept Hagon and his box before he gets to Picard)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "My duty, Lutan.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I'm sorry, but I'm required to", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(HAGON) Out of my way, woman.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Tasha does a neat manoeuvre which takes the box and leaves Hagon face down on the floor)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "How interesting.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "May we prove as surprising to you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If I may suggest, sir, no apology.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "In their view, it would weaken us.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Nothing concealed, Captain.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Would you care to accept it?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Unless you care to examine it further.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, yes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Please prepare it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We shall join you shortly.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Picard, Riker, Troi and Yar turn and leave)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I ask forgiveness.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They are strange alien beings.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You bear no fault.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But the female?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "May be exactly what I have needed.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Observation Lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Data brings in a statuette of a horse)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Lutan, we are aware of many of your planet's achievements, and its unique similarity to an ancient Earth culture we all admire.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "On behalf of the Federation, therefore, I would like to present this token of our gratitude and friendship.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "From China's Sung Dynasty, Fourteenth Century.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(DATA) Thirteenth Century, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Ah, yes, indeed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "A most thoughtful gift.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We are pleased.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We of Ligon have been apprehensive about strangers.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We are not technologically advanced as you, yet we possess something you do not.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A vaccine which has been found to be an effective antidote to your dreaded Anchilles fever.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If you respect our customs and if we see that respect, we will be friends.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And we will make the antidote available to all who need it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If you require respect from us, I am sure that you will see it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Surrounded by such friendship, I feel no need for my guards.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I will return shortly.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Prepare to transport me then.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The four guards leave)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "If there is something else, any further courtesy?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Would it possible to see one of your wondrous holodecks?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We have heard how they are used to train your officers.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And used for many other things too.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Commander Riker, perhaps you and Counsellor Troi will demonstrate.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Would it be possible for Lieutenant Yar to do so, Captain?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Some demonstration of defence training?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We've noticed you're intrigued with her having security responsibilities.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "But these things are not at all unusual with us.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "With us, it is the duty of women only to own the land, and the duty of men to protect and rule it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Much the same has happened in human history too.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I'd like to do it, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "As a sign of respect, perhaps.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Very well.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This way, please.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Holodeck]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Tasha enters a command on a control, and puts on the robe it creates)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Aikido one.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(a mat and a white clothed figure appear) It won't move until my actions activate it.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You can create people?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Without a soul?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's not a real person, Lutan.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "It has no life.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Everything it does is controlled by computer.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Who am I?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Do you hear me?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(It only moves when she feints an attack)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "But it will feel real when it hits you.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(She demonstrates a couple of throws)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "A force like that cannot possibly come from an image, Lutan.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then you will show me, Hagon.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Hagon gets thrown)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Thank you, Lieutenant.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How very enlightening.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Aikido, vanish.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I could create two, even three, but really one is enough.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "As you fight with it, it learns, and before long it knows exactly how to defeat you.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Even the extraordinary Lieutenant Yar?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It forces us to keep improving.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Your skill impresses me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I like you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Cargo Bay One]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Farewell, my new friends of the noble Enterprise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Understanding has made friends of many different people.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We've had a good beginning, Lutan.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "May I also extend a personal farewell to Lieutenant Yar?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "In your Federation terms.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(He shakes her hand then pulls her close so she, Lutan and Hagon are beamed out together)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Picard to Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Red alert.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(at Tasha's station) Shields up, photon torpedoes activated, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What comm.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "contact have we had with them?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "With their orbital control station, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Make contact there and on all hailing frequencies.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is the Enterprise to Lutan and the Ligonian government.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You have committed an unfriendly act.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We insist that you reply immediately.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Photon torpedoes ready, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Set them for a display blast a thousand metres short of the planet's surface.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Set.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Fire.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do we know the source of their transporter beam?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Our own transporter people have tried to trace it, sir, but to no effect.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It reads similar to early Starfleet efforts but uses the Heglenian shift to convert matter and energy in different.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(turns) Which is actually not important at this time.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "This is Captain Picard of the Enterprise with a message to Lutan, whom I have so far acknowledged as a friend.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But you have now committed what our laws regard as an attack upon us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Since you have visited our vessel, you most certainly know the power of it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We insist that you reply to this message.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Opinion, Counsellor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Will they injure Lieutenant Yar?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I believe not, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "They seem mainly curious.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "In the case of Lutan, however, I did feel other needs.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "What kind of needs?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Some sexual attraction from all the males.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Lieutenant Yar is physically very attractive.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "But with Lutan I felt something else.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Something more like avarice or ambition.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Other comments?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If I may, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "One of the things about them in the briefing studies was their respect for patience.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Strongly emphasized.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And you can see it in the precise, ritualistic way they do things.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'm worried about Lieutenant Yar too, sir, but maybe we should sit and wait them out.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Stardate 41235.32.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am concerned.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Over a full day of waiting with no response from Lutan.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We are now testing whether they are aware of our sensors probing them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "So far, we believe we have pinpointed Lutan's government compound.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Ready Room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(CRUSHER) The vaccine, sir.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Yes, Doctor?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The vaccine.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I'm a physician, I've seen death, but not on the scale this could mean.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "You were testing if you can replicate the vaccine.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And we can't.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The sample works fine when used as an injection, but it becomes unstable when we try to replicate it.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "You must get the vaccine from the planet, Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "As much as you can.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Immediately.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I'm aware of that, Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You've never had to watch a patient die from this disease.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "That's true.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But I have seen my share of death.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Damn.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Where are the calluses we doctors are supposed to grow over our feelings?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Perhaps the good ones never get them.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "May I speak about my son, Wesley?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "What?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Oh, yes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Alright.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He seems quite interested in starship operations.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "And speaking as a mother, of course, he seems quite knowledgeable.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Well, yes, speaking as a mother.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He's on the turbolift.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "You'll remember you ordered him to stay off of the Bridge.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Wesley?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(WESLEY) I haven't stepped one foot on your Bridge, Captain.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "It's all right, sir, I'll see that he leaves immediately.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Why don't you sit at Ops next to Lieutenant La Forge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Sir?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Is the whole ship deaf?!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sit down over there, young man.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Temporarily.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, we have more information from the briefing studies on Ligon.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Excuse me.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Doctor Crusher, some of this may interest you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lets hear the analysis.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It is a highly structured society in which people live by strict codes of honour.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "For example, what Lutan did is similar to what certain American Indians once did called counting coup.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "That's from an obscure language called French.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Counting coup", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Mister Data, the French language for centuries on Earth represented civilisation.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Indeed?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "But surely, sir", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I suggest you drop it, Mister Data.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Counting coup could be as simple as touching an enemy with a stick in battle, or taking something from him and escaping.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It was considered extremely heroic.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "And under these circumstances Lutan considers himself heroic, risking literally everything in the face of our superior power.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And it fits Lutan's personality profile as well.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "He has an abnormally high need for achievement.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Self image to him is a function of what he thinks he's achieved.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Those who set their standards too high can kill to meet them.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Why Tasha?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "As a Starfleet Security Officer, she may have represented his riskiest prize.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Transmission from the planet surface, sir, Main viewer on.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Well, Lutan, what do you want?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) You will display your image, please.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What is required is an image of Lieutenant Yar, well and", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Are you making demands, Captain?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "According to the Ligon Code of Honour, Lutan has done what he set out to do, achieve recognition for being daring and bold.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "We've studied this in some depth now, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The proper thing for you to do now is to ask to get Tasha back.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Ask for her?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Politely, Captain.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "And now, Lutan, you have boldly taken Lieutenant Yar from us, and we ask that you now please return her.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Then come visit us, Captain, and we will return her to you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(a little later)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Commander.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Commander?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "That's quite formal.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "So is this request, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "We believe it would be preferable if the Captain led this away party.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Agreed, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Their customs concerning guests make it much preferable.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "And I'm very much against that idea.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Lutan is clearly a liar, and devious.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Counsellor Troi has admitted she believes he's capable of killing.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It is my duty to keep the Captain from danger, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Except that Ligonian custom makes it clear that a visiting leader becomes an honoured guest.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Not us, not second in command, him.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "And the custom requires that Lutan should die rather than violate that.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Yes, it seems reasonable put that way.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "But I warn you, if you get hurt, I'll put you on report, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Centreplace]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard and Troi beam into a colonnaded area with a water feature)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Welcome to my Centreplace, Captain Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Consider yourselves my honoured guests.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(YAREENA) Every hospitality will be accorded you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is my First One, Yareena.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Lutan is a fortunate man.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You've met Counsellor Troi.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "As on your vessel, you have only to name whatever courtesy we can provide.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then, sir, the courtesy of seeing Lieutenant Yar.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant Yar will be returned to you tonight at a banquet I have arranged in your honour.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'd like to see her now.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Bring Lieutenant Yar.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I find it odd, Captain, that a man of your experience has such difficulty in understanding ordinary politeness.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Such as the politeness of saying please before abducting someone?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The expression please is used only when requesting the person back.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yours is a different world.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "With clear and simple ways deeply rooted in our culture.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If you are willing to ask for Lieutenant Yar's return tonight in front of all, honour will be satisfied.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "One can see the importance of honour here.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Honour is everything.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Tasha is brought in by two guards.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "She is not happy)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Have you been treated well, Lieutenant?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Fine, Captain but they're showing some signs of wear.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "There isn't any need to be concerned, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "She's being well cared for.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "My ship's company and I are certain of your gentle wisdom in that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "For a moment I thought I heard a threat implied.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But that would be foolishness.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Something to be carefully avoided.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I agree.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The festivities for her return to you are in preparation.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Shall we retire till then?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Until tonight, Lutan.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(A juggler is entertaining them when Tasha enters with her guard.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "She is seated at Lutan's left, with Yareena at his right)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Lutan, you have granted us hospitality and the safety of your Centreplace].", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The gifts you give us are rare and precious.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We thank you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And now, according with the customs of your ancestors, whom we honour and respect, I am here in peace to ask for the return of Lieutenant Yar.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well spoken.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There are those among my equals in this gathering who were wary of my approaching the Federation.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am proud to have taken this first step towards a treaty, and proud that we have something of great value to offer you, a priceless life-giving vaccine.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The abduction I have done according to our custom, for all to see.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Your conduct in this matter has been beyond exemplary, Captain Picard, but now that the moment has come, I find I cannot part with her.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(General consternation!)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You speak of a code of honour, but what you are saying now, according to our customs, is called an act of war.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is not an act of war, but of love.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I want Lieutenant Yar to become my First One.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I challenge your right of supercedence!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No woman has challenged supercedence for over two hundred years!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The right is mine and I will have it!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Natasha Yar, I challenge you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A struggle to the death.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The challenge is unequivocally refused!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Then you shall have no treaty, no vaccine, and no Lieutenant Yar!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ship's log, First Officer Riker reporting.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We have been informed of the challenge to Lieutenant Yar, and are maintaining combat readiness round the clock.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We are probing Lutan's compound deeply with our sensors now, still unnoticed.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Tasha's room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Deanna.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Did you have any idea, Lieutenant, that Lutan was suddenly going to announce that he wanted you for his First One?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Tell me what you know about this?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Nothing, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "But it was a thrill.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Lutan is such, such a basic male image and having him say he wants you", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Yes, of course it made me feel good when he.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Troi, I'm your friend and you tricked me.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Only so you'd think about it, completely and clearly.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "We're all being manipulated, Lieutenant, myself most of all.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How simple all this would be without the Prime Directive.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "That thought had passed through my mind, Counsellor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What is this message from Starbase Fourteen?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's showing the infection rates, percentage illness increases.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "The plague on Styris Four has flared up out of control.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "They're estimating deaths in the millions, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain Picard, come in.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Tasha's room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I know I can win.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Not that I'd take her life, of course, but I'd be glad to embarrass her.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "The idea of accusing me of taking", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Picard enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The plague?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "If anything, even worse than we'd heard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Which means they desperately need the vaccine, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "And I know I can win this challenge.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You have nothing to prove anything here, Lieutenant.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But the vaccine is important in this case, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Counsellor, you're the last person I'd expect to argue in favour of accepting the challenge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Betazoid blood is also practical, Captain.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "The odds are very good she'd defeat Lutan's wife easily and you would win all the bargaining points you need.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "The odds are.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You have pointed out yourself we are all at risk every day of every mission.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I want some explanations from Lutan.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Centreplace]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Lutan, why are you so obsessed with this Lieutenant Yar?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Surely Yareena is more desirable?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Certainly she can offer you much more.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "She owns many things, many lands.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard enters, and Lutan stops Hagan talking)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "It's a great pity you began by abducting my Security Officer, Lutan, because I should tell you I do admire the hospitality you offer here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I am in the grip of forces you do not understand.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Some of it I do understand.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "She is a rather lovely female.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You surprise me, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What do you know of needs and feelings?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Nothing.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well, almost nothing in my position of ship's Captain.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I see.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But it puzzles me.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If you feel that way for Tasha, why have you challenged her to a fight to the death?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Not I, Yareena.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am merely a spectator.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ah, but you are also a man of great importance and wealth.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Great importance, perhaps, but he does (silenced by Lutan)", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ah, I see.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You, too, understand the proper value of women.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We understand they are highly pleasant things, but after all, unimportant.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Except for the land they own.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You are a truly clever person, Lutan.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You stand to lose nothing, either way the challenge goes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "A code of honour protects one, Captain, like a magic cloak.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I will order Lieutenant Yar to fight.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And may your cloak bring you all you deserve.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Geordi's Quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Geordi has his Visor off, and is using an electric shaver)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Come in.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why that razor, my friend?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Why not the one I adjusted to perfect efficiency?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Shaving is a human art form, Data.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Technological perfection can shave too close.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Puzzling.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "How can anything be too efficient?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Thousands of things are too efficient, Data, at least for humans.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We always come back to the human equation.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Exactly.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Have you continued to work on it?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Constantly, my friend.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Particularly the humour.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "A man goes to a store to buy some kidneys.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Oh, no.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Geordi tries to escape)", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "He says to the shopkeeper, I'd like a pound of kiddillies, please.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The shopkeeper says to him, You mean kidneys, don't you?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The man says, I said kiddillies, diddle I?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It's too old.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "And you didn't tell it very well.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "How do you know when something is funny?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It's not explainable.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You just do.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Perhaps it is you, Geordi.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Includling the kiddillies, I've learned six hundred sixty two jokes, and you have not", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Includling the kiddillies!", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now, see, that's funny.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It was not meant as a joke.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The tongue slipped.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Oh, boy.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Riker to Commander Data, Lieutenant La Forge.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Report to the Transporter Room for away party duty.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Centreplace]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) First officer to Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Ready with the away personnel.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Lock in on this location.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Transporter locked in, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Energise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Geordi and Data arrive, behind the water feature, in front of a wall decorated with weapons)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You've both seen the message about the plague?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And how badly the vaccine is needed.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We need to know as much as possible about Ligonian armaments.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Data, especially important is an analysis of their combat capabilities.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Geordi, concentrate on their cutting edges, wherever applicable, durability, composition, weaknesses of material.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "From any particular point of view, sir?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "From the perspective of Lieutenant Yar using them in combat with Lutan's wife.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Most interesting.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Could this be human joke number six hundred sixty three?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Negative, Data.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "That's a Captain's order.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Which makes it important to know which of these weapons are to be used.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "And that won't be known until combat begins.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You're right, Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It does sound like a joke.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "With the power of the Enterprise, we could overwhelm this place easily, just take what we want.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I may not understand human humour, sir, but I am a Starfleet Academy graduate.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Which means, of course", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "understanding the Prime Directive, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "That is, ironically, what this is about.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "By our standards, the customs here, their code of honour, is the same kind of pompous, strutting charades that endangered our own species a few centuries ago.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We evolved out of it because no one else imposed their own.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm sorry, this is becoming a speech.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You're the Captain, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "You're entitled.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Not entitled to ramble on about something everyone knows.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Carry on.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Ship's log, Commander William Riker reporting.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Continuing our appraisal of Ligonian technology.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Captain has made it clear he wants Lieutenant Yar beamed immediately to safety if her life should become endangered, and I fervently hope the rules of that contest make it possible.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Riker to Transporter Chief.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Do we have our people on sensors yet?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) Their general area, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Visual on screen now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "As you see, we're still doing some fine tuning.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Tasha's room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I've agreed to this meeting, but I see no point to it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You've accepted the challenge and there is nothing further to say.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I think you should know that there is no physical training anywhere that matches Starfleet, especially its security people.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "And you should know that even though these contests are rare, we have a tradition of making ourselves capable of them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yareena, my acceptance had nothing to do with Lutan.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "It has everything to do with Lutan.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Lutan wants you to be his First One.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Impossible, Yareena.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I am a career Starfleet officer.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "How could you not love him?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Every woman loves him.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I fight for the vaccine, That's the truth.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "The truth is I will kill you if I can.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And believe me, I can.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There is nothing else to say.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "In my world, it's a greater honour to refuse", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You are on our world!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Picard's room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, I'm your Counsellor.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "You brought me with you to Ligon to be of help.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Then help me, please.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What is a way out of this?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "With the vaccine?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "None.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Geordi and Data enter)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The weapons in that room, Captain, are surprisingly flexible, durable, and deadly.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "And light, as if they were made for women to use.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Some of them still have traces of blood and poison.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Poison?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Alkaloid base.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lethal.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "What about those lengths of metal in the yard outside?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Uncertain, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "However, joined together they would make a rectangle or square enclosing one hundred twenty one square metres.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "If put end to end vertically, they would make a pole forty four metres high, or two of twenty two.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Thank you, Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You're welcome, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Tasha enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "She won't budge.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "She loves him, without reservation.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "And she thinks I love him too.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Most interesting.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Do you?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Of course I don't, Data.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "As Troi pointed out to me, I'm attracted to him but that is entirely different.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Riker to Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Go ahead, Riker.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We're alone.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) We're fine tuned enough to see your hosts gathering in the open area outside, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Three of them are heading for your location.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Thank you, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant, do you still feel that you want to go through with this challenge?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do you judge your condition good?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I judge it excellent, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Hagon enters, followed by two guards carrying boxes)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Your weapons, Lieutenant Yar.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You may choose your size.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(She opens a box containing a glove with metal spikes and a curved blade)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Tasha, don't touch the spines.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "They're poisoned.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "If you're not careful, you could kill yourself.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "These I understand.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Through the window they see Yareena practising on a multi-level surface, with the metal poles in it.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Four lights soar to the sky)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "First Officer's log, supplemental.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Captain Picard, faced with a critical need for a vaccine produced on this planet, has permitted Lieutenant Natasha Yar to engage in a fight to the death.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I have yet to understand his reasoning or his plan.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Wesley enters, shyly)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Care to lend a hand?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Sit at Ops.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Transporter Room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Data beams aboard)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Was I seen leaving?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "They've got eyes only for the programme.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Riker and Crusher enter)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain Picard wanted no risk of our communications being overheard.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "You are to proceed now as he indicated.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Captain's orders too.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Does the Captain understand what you and Geordi reported about those weapons?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "They're razor sharp, split-second lethal.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'm here to brief you on what he plans.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Centreplace]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The Starfleet group enter, Tasha is wearing the weapon glove)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Any last minute instructions, sir?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Stay alive.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We'll hope to do the rest.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Yareena enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I speak for Lutan.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The rules are known.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Let combat continue until there is a victor.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It will not be interrupted.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Yareena goes on the offensive straight away.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Tasha pushes her weapon away and it hits one of the vertical laser beams.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "It flies off Yareena's hand into the lap of a spectator)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Combatants, hold your positions.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Return the weapon.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The spectator dies of his wound and is dragged away.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Yareena is given the glove back and she returns to the business of trying to kill Tasha.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Tasha manages to make Yareena loose her balance)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Careful, Yareena.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Finally the two women get into very close combat, and Tasha lands a blow on Yareena's back.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Then she lies across the body and the two are beamed away)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Transporter Room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Move!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We're too late.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "She's growing cold.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Sorry, that clashes with my instructions.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(She injects Yareena)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Oh no.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "No.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Centreplace]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Exactly what do you find unfair, Lutan?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They fought to the death.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You saw the final blow.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You know the effects of your poison.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But what of your Lieutenant Yar?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "She is to become my First One now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I certainly won't stop her, if she cares to claim that honour.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Remember, you now have all Yareena's lands and wealth now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "At least all has not been lost.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Riker to Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Picard here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Since you've fulfilled your agreement, Captain, can we now beam the vaccine aboard?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Ah, yes, of course.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No problem, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Send down a medical team.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) And to complete our business here, we are locked onto you.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Five to beam up.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Energise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "First Officer's log, Stardate 41235.6.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I am returning the conn to Captain Picard as we begin loading the vaccine supply aboard.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Shortly, we hope to signal mission complete.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard, Troi, Lutan and Hagon enter)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Welcome back, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You're wanted in the lounge.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "This way, gentlemen.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Observation Lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Tasha and Beverly are there with Yareena)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "She is not dead!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There was no death combat.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You violated our agreement.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "There will be no treaty, no vaccine!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The challenge was carried out.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "She died, Lutan.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "There was no challenge!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "She lives!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I am a physician and saw her die.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "If you doubt this poison, why don't you test it on yourself?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Lutan, we can provide you with records of her death and how Doctor Crusher brought her back.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And at the instant of death, Lutan, a mating agreement dissolves.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But this is witchcraft, Yareena.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "To discard a mate in this manner", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is less painful than the one you selected for me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(She takes the necklace off him)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Yareena, no.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Even as I battled, Hagon, I heard you calling out for me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yareena, be my First One.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All my land and all my goods, all I have is yours to rule.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(She puts the necklace on Hagon)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "How so sad for you.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You've lost everything.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I have my honour.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's such a waste.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Do you want him?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No. There would be complications.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Then I will have you as my Number Two.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Take your place accordingly.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well, as you see, Captain, you may excel in technology, but not in civilised behaviour.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Wesley is still at Ops.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Data is standing by Tasha when Picard enters and notices)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Wesley.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Young Wesley, he'd been manning that station for me.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I forgot.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Well, thanks again for manning one of our bridge stations, Wesley.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We'll see that you have another chance.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes sir.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(Wesley leaves and Data takes his rightful station)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
