	lines = {
		{ text = "(PICARD) Captain's log, Stardate 41294.5.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Our destination, the class M Beta Cassius planet known simply as Haven.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It is a world so renowned for its peaceful beauty that some believe it to have mystical healing powers.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We will rest and relax, all too briefly, I fear", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
