		{ text = "Such a beautiful world.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Legends say it has been known to mend souls and heal broken hearts.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(DATA) Legends which are totally unsupported by fact, Captain.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Legends like that are the spice of the universe, Mister Data, because they have a way of sometimes coming true.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Riker's quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Riker is relaxing to a holographic pair of women playing musical instruments)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Offscreen) Lieutenant Yar to Commander Riker, your presence is requested in Transporter room one.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(RIKER) Sorry ladies.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Duty calls.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You needed me, Lieutenant?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "There's an object of some kind beaming in from Haven.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "What is it?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We're not sure.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(CHIEF) Surface Station approval coming in now, Lieutenant.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All right, let's bring it in.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(It's a box, with a face in relief on the front of it)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Odd looking.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Troi enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(TROI) What's going on?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(The face on the box comes alive)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(FACE) I hold a message for Deanna Troi.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Lwaxana Troi and the honourable Miller family will soon arrive.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The momentous day is close at hand.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
