		{ text = "No. No.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "What's going on?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(The box bursts open and scatters gems on the transporter pad)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Jewels.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Look at these jewels.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "They're bonding gifts.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "What you would call wedding presents.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Who's getting married?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I am.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Ready room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I was certain it would never happen, Captain.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "The years I'd spend on this mission, the distance it has taken me away from home.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "As you must have heard, genetic bonding is a Betazoid tradition.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Steven Miller was my father's closest friend.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Your father was human, Deanna.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The Millers are human", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Will you and your husband be staying with the ship, Counsellor?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Then I'll just say congratulations for now, Deanna.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You'll excuse me?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Picard leaves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Bill, more than anything else in the world, anything, you want to be a starship captain.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "True?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "That's not all I want, Deanna.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I can feel that.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I know you care, within those limits.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Did you hear what I said?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Every word.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "This whole thing is still bizarre.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'm sorry.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Come dance at my wedding.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I'll try.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Riker leaves as Data enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "A message from planet Haven, Counsellor.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "They wish to beam the Miller wedding party aboard.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Mr. Steven Miller, Mrs.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Victoria Miller and their son Wyatt beam aboard)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I'm Jean-Luc Picard, Captain of the Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Welcome aboard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(STEVEN) Quite some starship you have here, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Look forward to this visit.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(VICTORIA) You couldn't be", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm Deanna.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Deanna darling!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You probably don't even remember me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Wyatt was absolutely right.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "She is a beauty.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(WYATT) I'm Wyatt.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Isn't this simply beautiful?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I knew romance was still alive, somewhere.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'd like you to have this.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's a Chameleon Rose.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It changes colour with the mood of its owner.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's wonderful.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Thank you.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "When is my mother arriving?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Your mother is still down on the planet, Deanna.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Er, is there a place for my parents to rest, Captain?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well, we're not really tired, son.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Besides, I'd like to see some of the ship.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Steven, you know full well Lwaxana Troi isn't about to beam on aboard until we leave.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "So?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You can see your quarters now, if you like.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Will you show the Millers to their accommodations, please, and I shall join you later.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The Millers leave)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Two more are ready to beam in, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "So be it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Wyatt seems a fine young man.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, he does.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "But I'm not what he expected.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I don't understand.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Neither do I, but I'm definitely a surprise of some sort to him.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I should warn you, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "My mother is a little eccentric.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(A large man is sitting on a chest, and a woman has her back to them)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(LWAXANA) Where is everyone?", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Oh, I hate that.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Hello, Mother", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) Don't say it, think it.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Use your mind, not your mouth.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Hello Mother.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) Deanna, shame.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "What has this life done to you?", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "No, don't tell me.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You're the Captain.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Of course.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Your daughter has explained your telepathic ability.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That wasn't telepathy, it was just common sense.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Who else would they send to greet me but the Captain?", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You may carry my luggage.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Mother!", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "No, no, that's quite all right.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm indebted to your mother for the fine Counsellor she", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The luggage is heavier than he expected.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "A lot heavier)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(straining himself) This way.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The reincarnation of Christine Chapel and voice of the Enterprise computer follows, with her daughter and Lurch-sized servant)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Mother, it's quite inappropriate to ask the captain to", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Yes, you do seem to be having difficulty.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "A man your age must work to keep himself in shape.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Mother, I'm not going another step like this.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "A starship has its customs, just as we do.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "If you're my mother's valet, then please valet!", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Oh, please don't let me keep up from doing your duty.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I apologise for her behaviour.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Do you realise you've embarrassed your Captain?", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Oh, no.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Anything to avoid a quarrel on this occasion.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "It's amazing how that accent of yours reminds me of your father.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(Mr. Homn the valet lifts the case with ease)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Your last valet tried so hard to rid me of it.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Whatever happened to Mister Xelo?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I was forced to terminate his employment.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Xelo was strongly attracted to me.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "His thoughts became truly pornographic.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "[Turbolift]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Of course, the thoughts of Wyatt's father toward me were almost as vulgar, but he really doesn't have Xelo's", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Passenger accommodation.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He doesn't really have Xelo's imagination.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Mother.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Between him and that woman's inane chatter, it's a wonder I made it here at all.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "How do you like the Millers, Captain?", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "I'm sure I find them perfectly pleasant", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "As for me, I find it shocking how they've changed in the years since my husband and I knew them.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Of course, it's probably because I've grown beyond them.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You realise of course that with Betazoids, our ability to read the thoughts of others does see us grow much faster than the typical plodding human who", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) Mother, that's enough!", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) So, you're not totally out of practice.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Good.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Very good.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "[Lwaxana's quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We hope you find the room comfortable.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, the room is adequate.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Small, but adequate.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You will of course adjust the temperature to a civilised level?", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "I'll see what I can do.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And now, if you will excuse me, I'm sure the two of you have a lot to talk about.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, Captain.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You may go.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(Picard backs out of the room with a bow)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Offscreen) You've been slack, little one.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Allowed your mental powers to rust.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) Only to avoid confusion, mother.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Humans constantly think one thing and say another.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) Yes, they do, don't they.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Poor dears.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Our style of complete honesty frightens them.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "On that subject Mother, there is such a thing as too much honesty with humans.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) If they'd only say what they think instead of hiding it.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "An entire shipload of such inconsistency could drive one insane.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Darling, I'm terribly sorry about what happened here.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Truly I am.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Steven Miller tracked me down and reminded me of the vows we had made.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Mother, I'm having some trouble believing in those vows as once I did.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Deanna.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "But I'll honour them, of course.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I'm a Betazoid.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "You may find Wyatt an unusual person.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "I've sensed remarkable depths in him.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(LAFORGE) Message coming in from Haven, Captain.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "On screen.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(An elegant woman appears)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(On viewscreen) I'm Valeda Innis, First Electorine of Haven.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain Picard?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Greetings, Electorine.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm Picard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Your presence honours us, and your timing is fortuitous, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "In what way?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) An incoming vessel has bypassed our stargate, violating our law.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It has refused any attempt at communication.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Are you saying you believe it to be hostile?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Failure to communicate is inherently hostile.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We have no defensive capabilities here and our treaty with the Federation specifies your obligations in that matter.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Agreed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But let's hope it doesn't become a defence matter.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Of course, Captain, but I'm very happy we have you here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Wyatt's quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Come in.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I wanted to apologise for my mother's behaviour.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Your mother's honest.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I respect that.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, but she never lets up.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I'll admit, her honesty is a bit persistent.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I never heard it described better.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "But it is a Betazoid trait.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I'll try to be only half as annoying.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Let's see, what can I tell you about myself?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm a medical doctor, for whatever that's worth.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Doctor?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "If you're picking up my thoughts, you'd know.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You can do that, can't you?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sometimes.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "This must be what Mother felt about you.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I believe we could read each other eventually.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Can I take that as a compliment?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Absolutely.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I only ever felt this, well, with someone who's on this ship.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Oh?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do I have competition?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No. What he wants most is to captain a starship.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "What I want is to cure people.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well, I'm a practicing psychologist.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Maybe we can work in concert.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "We are going to be together a long time.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That is the point of marriage, I suppose.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I really thought you'd tell me you were an artist.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I just felt your mind very much on these.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "They are your work, aren't they?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I can see it'll be very hard to keep secrets from you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is why you were surprised when you first saw me.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "This woman.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "You thought that I would be this woman.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I have seen this face ever since I was a boy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "When I closed my eyes at night, I could hear her whispering my name.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And knowing you were Betazoid, I just assumed it was you projecting yourself into my mind.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm sorry I'm not what you hoped for.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "No. Please don't mistake a childish fantasy for disappointment.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You are so beautiful.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I feel honoured.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You've no idea who she is?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "It doesn't really matter now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain's personal log.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I trust my concern over the problems of ship's Counsellor Troi are not based merely on losing a highly valuable crew member.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But it seems to me that she is trapped by a custom of her home world which the facts of the twenty-fourth century life have made unwise and unworkable.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I wish I could intervene.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "On the viewer, Captain.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Unidentified vessel travelling sub-warp speed, bearing two three five point seven.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Sub-warp?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's several hours away then?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Let's take a look at it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Enlarge to maximum.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Increasing magnification, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister Data, is that the trouble I believe it is?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If you mean a Tarellian vessel, sir, it is.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I thought the Tarellians were all dead What are the poor devils doing here?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Picard to Sickbay.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Doctor Crusher to Bridge, urgent.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They must not be permitted them to destroy us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Or the planet.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain's log, supplemental.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It has been believed the Tarellian race was extinct, an assumption contradicted now by the sight of one of their vessels approaching Haven.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Observation lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The fact that it's travelling at its present velocity suggests a possible answer.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "A damaged vessel, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "That could explain it.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "If it were unable to reach warp speed, it would have taken all these years to get here.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Go on with your briefing, Mister Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Tarella was class M, much like your Earth, with similar humanoid life forms.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Unfortunately they faced the old story of hatred out powering intelligence.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "There were hostilities?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Between the inhabitants of their two land masses, resulting in one group unleashing a deadly biological weapon on the other.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(CRUSHER) And in the end the other became infected as well.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Makes one question the sanity of humanoid forms.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Can you identify the origin of the infection, Doctor?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Tarellians had reached Earth's late twentieth century level of knowledge.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "That's all you need if you're a damned fool.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "A deadly, infectious virus which at that modest level of knowledge is not difficult to grow.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "We learned the rest of the story in security training.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Some Tarellians made it to other worlds only to die along with the populations they infected.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "It's pretty well covered in Academy training now, Captain.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Many of them tried to avoid other civilised worlds as they escaped only to be hunted down and destroyed anyway.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "And it was believed that the last Tarellian vessel was destroyed eight years ago by the Alcyones.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Exactly when will they reach Haven?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Exactly thirteen hours, nine minutes, twenty two seconds, three hundred fifty", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Thank you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Which creates a very difficult problem for the Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Our treaty requires us to protect Haven, and Federation policy requires that we assist life forms in need, which must include the Tarellians.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll want you to help me find some answers.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "However, there will be ample time for your second assignment, voluntary of course.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The pre-joining announcement of Counsellor Deanna Troi", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Riker leaves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "And Wyatt Miller.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We've talked it over, Captain, and the ceremony will be tomorrow if you agree.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And Captain, would it be possible for you to perform the ceremony?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well, yes, of course, if all parties request it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "All parties do not request it.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "It's simply out of the question.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "I'm sorry, Captain, but unfortunately you are not practiced in the ways of Betazed joining.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "No, that's quite true.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Therefore you are totally unqualified.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "He is qualified to lead a traditional Earth ceremony, which is what this will be.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I thought you had no sense of humour.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Earth wedding?", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Ridiculous!", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "My family and I are living on Earth now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "So, that's why you want that backward ritual.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Terrible, Captain, to see a woman go downhill like this.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Mother!", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Downhill?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The matter is settled.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Mister Homn will conduct the joining.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "But Homn can't even talk!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No matter, he is highly adept in the acts of sign language.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "The matter is closed.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Who are you to tell us what we should do?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Your ignorance is astonishing.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "I am Lwaxana Troi.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Daughter of The Fifth House, Holder of the Sacred Chalice of Rixx, Heir to the Holy Rings of Betazed.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Who are you?", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Ladies and gentlemen, it is a Starfleet tradition that at social gatherings, disputes are not permitted.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I hereby declare therefore all disagreements resolved.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(At table, during the meal)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "A toast.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "To the young couple and their families.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And may this union be a productive one.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(General hear, hear, and cheers)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Data is standing by Mr. Homn, who is standing by a small gong, and knocking back drinks as if he's spent a month in a desert)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Considering the rate at which you imbibe, sir, is your lineage at all mixed with human?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Homn exaggeratedly sticks his nose in the air.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Such an insult, young android)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Is it true, Captain, that there's a Tarellian ship headed for Haven?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Lwaxana takes a bite of food, Homn strikes the gong.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "This goes on during the following conversation and gets on everyone's nerves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Yes, it is true.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That's amazing.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I've read everything I could about them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Biological virus analysis was a favourite subject at medical school.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "In which case I'd very much like to confer with you.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I'm pleased to have a medical colleague aboard.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Yes, ma'am, Doctor.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Would it be possible to prepare some medical needs, geared toward the Tarellian's probable needs?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We could beam it over without any fear of infection.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What do you think, Doctor?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's a very considerate idea.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Our Sickbay is at your disposal, Doctor.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Thank you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Mister Data?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "You're circling the room like a buzzard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Perhaps being human yourself, sir, you do not find them as intriguing as I.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Must he do that?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "As you well know, it is the Betazed way of giving thanks for the food we eat.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You giving thanks?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Besides, you never did this before.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I do it now.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Unlike some people, I am in growth.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(The apparently decorative leaf-like thing on her sleeve, shakes at her)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Victoria, I've forgotten whether you enjoy pets or not.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Love them, of course.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Good.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(So the thing on her sleeve slides over to Victoria Miller, who of course screams)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Gently, gently.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Poor baby, did she hurt you?", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "If you'll excuse me, I'd like to spend some time considering the Tarellian situation", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes, of course.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Ladies and gentlemen.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Riker leaves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Mrs.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Troi, I'm very interested in the Betazed ceremony you mentioned.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Could you tell us more?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Why, I'd be delighted, Commander.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "It's an ancient ceremony, widely regarded as the most beautiful in the universe.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "After the young couple have removed their clothing", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "The bride and groom go naked?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "All guests must go unclothed.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "It honours the act of love being celebrated.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Oh, you needn't worry too much, dear.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Your body's not that bad.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Besides, your husband quite likes the idea of seeing me unclothed.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Steven!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You did know he's attracted to me, didn't you?", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Untrue!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Stop this petty bickering, all of you!", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Especially you, Mother!", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Troi storms out, knocking over the gong on her way)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Could you please continue the petty bickering?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I find it most intriguing.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "[Holodeck]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Riker is brooding in an evening desert landscape)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "May I join you?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I will miss you, Deanna.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'm no longer Imzadi to you?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "You taught me that word means my beloved.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And the human heart is too small to permit that feeling now.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Have you discussed this with Wyatt?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I think you should.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's also damned unfair to me.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I understand.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I should have realised.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Humans, young human males particularly, have difficulty separating platonic love and physical love.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "The problem is, Imzadi, I couldn't.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Not now.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Call it an old Earth tradition, habit of the beasts, whatever.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Hello, you two.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We were just talking about you, Wyatt.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "This is incredible.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And in that discussion, I", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Actually, Bill was concerned that you might be upset that I care deeply for him, too.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Oh. You're the one who wants to be a starship captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, I've heard that bonding or marriage would complicate things in that case.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "To each his own.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Good luck with that ambition.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And I very much respected what you did tonight.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All I did was lose my temper.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "If you'll excuse me.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Of course.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Running all this is a big job.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Riker leaves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "In fact, you shamed them into compromise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "So they've decided that the joining will be half Betazed, half Earth.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Captain will do the ceremony and Mister Homn will be my best man.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And we'll take our clothes half off?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Some of us still go naked.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You do, I do, your mother, my father, but not my mother or the guests.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain Picard will be very relieved.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "How did you manage it?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Your mother relented.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And I just caught my father practicing naked in front of his mirror.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And so, a question I should have asked before.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Deanna, do you really want to go through with this?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, I want to.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I'm a very lucky man.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(They kiss, one short, one very long)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain's log, supplemental.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "All attempts at warning off the Tarellian ship have failed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They still refuse to communicate and I am growing concerned.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How can you be certain they're receiving us?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Because our sensors are showing a responder echo, sir, on the frequency they once used.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "We have a message, sir, coming in from Haven.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "On viewer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Captain, the plague ship is approaching transport range.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We are aware of that, Electorine.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Do you realise that they can turn this lovely world of ours into a graveyard?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please, please take action now before it's too late.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We recognise your situation.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(On viewscreen) Please destroy them now!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We will not fire on them, Electorine.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) You must!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(transmission ends)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I'm certain I could disable their ship with a phaser burst, Captain.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "And then, Lieutenant?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They're within transporter range, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Then we can't delay any longer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Ready the tractor beam, Lieutenant Yar.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Target the ship.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Activate on my command.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Tractor beam ready.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Engage.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Got them, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Do we have them securely, Lieutenant?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Can they beam out to the planet?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Negative, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "They can't leave that ship.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I know they can receive us, Captain.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "At this distance they can respond with running lights if necessary.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Unless they have all died.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Their ship could have been brought in by automation.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Captain!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(The viewscreen crackles into life, and it's a face Troi recognises)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "It's the woman in Wyatt's drawings.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Captain's log, stardate 41294.6.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Orbiting Haven with the Tarellian vessel locked in our tractor beam.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Question.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What strange of circumstances has caused a woman out of someone's imagination to appear on the plague ship?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Enterprise to Tarellian vessel, are you receiving us?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(A balding man moves the young woman aside)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(On viewscreen) My name is Wrenn, and before I enquire why your tractor beam has trapped us here, is there one aboard your vessel named Wyatt?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Crusher and Wyatt enter.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "He has his drawings with him)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain, I don't understand.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(On viewscreen) It's astounding.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ariana was right.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He is here, daughter.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(ARIANA) Wyatt, you've come just as you promised.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How could I be so accurate?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Except for the dream images, I've never seen her.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sir, our concern is with the threat your vessel poses to the planet below.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If you're still carrying the infection which destroyed your world", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Oh, we still carry it, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "My daughter, I, all eight of us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Eight?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) All of us that are left, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Most of the rest passed on during the years that it took to reach Haven.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If you've come here because of the legend about planet Haven miraculously healing the sick.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) We don't ask to make contact with those living below.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All we ask is to be on the edge of some sea, some unpopulated island, a faraway peninsula.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is not our planet, sir, but I will present your needs to those who do govern this world.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Present the fact we intend to die here, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And if we die while caged by your tractor beam, so be it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Lwaxana's quarters]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh, Wyatt, how do you like my new hairstyle?", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Of course, it's going to look much better on me when I'm naked.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Mrs.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Troi, can I talk to you about something serious?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh, but I am always serious, dear boy.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Only my pleasant nature makes it appear otherwise.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Please, Mrs.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Troi.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, that was puzzling.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "A woman out of another place who insists that she knows you.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "And", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And whom you've dreamed of all these years.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "And I hoped that the way you handle thoughts", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Fascinating, Wyatt, how easily your thoughts come through.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "The answer to the puzzle of Ariana and you is so simple, it's too simple for most humans to understand.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Too simple?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Of course.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "It's something they all know instinctively but go to great effort to reject or to build complicated superstitions about.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "All life, Wyatt, all consciousness, is indissolvably bound together.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Indeed, it's all part of the same thing.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I have wondered if something like that", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "That weren't so.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "And no doubt so has Ariana, which helped the two of you to make contact.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Wyatt, tell me something seriously.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Which of these would look best on me naked?", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Wyatt?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Are you feeling all right?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Just wedding nerves.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The supplies are ready.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll take them to the transporter room.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll notify the Captain they're ready.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "(When her back is turned he gets a hypo and fills it)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Look at your father, Wyatt.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He just can't wait to strip off his clothes for that barbaric ceremony.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Please take care of each other.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(to Deanna) You are beautiful.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But you looked best of all in the desert on the holodeck when we did this.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You can put the supplies on the pad.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Doctor Crusher had me set the coordinates.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All we need is the captain's order.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Wyatt injects the Chief and he goes down.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Then Wyatt activates the transporter and stands on the pad with the supplies)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain!", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Someone's transporting over to the Tarellian ship.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Override.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I can't sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's too late.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Tarellian ship]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Wyatt beams into a corridor lines with drawings of himself at different ages)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Hello, Wyatt.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We always thought you were a dream.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're not surprised.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Did you know I'd beam over?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Once we saw you were real, we knew.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You are a doctor?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I've brought medicines and supplies.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And I knew you would be this brave.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How could you let this happen?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "My son, surrounded by those horrible lepers!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Mrs.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Miller, if I could have prevented this I would have.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Beam him back.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He can never come back, Mrs.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Miller.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(On viewscreen) You may turn off your tractor beam, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We will not be going to Haven.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We have what we really came for.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Wyatt?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Mother.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Father.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Forgive me, but I must.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm going to try to continue the work to cure these people.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And Wyatt will do it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I've believed that all along.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I knew I was coming to Haven to meet my destiny.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I thought it was to be with you, Deanna.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It was Ariana who drew me here.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm sorry.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Wyatt, I'm happy for you, and for Ariana too.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "You've done very well for a human, Wyatt.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Mother.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Captain, my respects.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And mine, sir.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Transmission ends)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Goodbye.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Keep the chest.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You'll have use for it some day.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The Millers beam away, and Lwaxana sweeps in)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Seems such a shame to waste the whole trip.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Perhaps I should stay and be joined to a new mate?", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "What?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Well, the Captain's highly attracted to me, but he's a little too old.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Perhaps I should choose you.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(Riker)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "He has other obligations, Mother.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Oh. Very well.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "It's his loss.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Mister Homn.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(HOMN) Thank you for the drinks.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Try and remember your heritage, little one.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(out loud) Captain!", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Even Xelo never had such thoughts about me.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You may energise.", r = 0.7, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(Lwaxana and Homn are beamed away)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "That was meant as a joke, Captain.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I was not amused.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Take us out of here, Mister Riker.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Warp two, helm.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Heading five seven mark three one nine.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Our destiny is elsewhere.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But I'm happy that yours is here with us, Counsellor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Warp two, heading five seven mark three one nine.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Engage.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[img=music]  End Credits [img=music]", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,STS-0.4,UHP-3.0" },
	},
};

-- Star Trek:TNG S1.06 - Where No One Has Gone Before
RecMedia["TNG_S1E6"] = {
	itemDisplayName = "VHS: ST:TNG S1.06",
	title = "Star Trek:TNG S1.06 - Where No One Has Gone Before",
	subtitle = nil,
	author = nil,
	extra = nil,
	spawning = 0,
	category = "Retail-VHS",
	lines = {
		{ text = "(PICARD) Captain's log, Stardate 41263.1.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We have rendezvoused with the USS Fearless, from which a Starfleet propulsion expert and his assistant are beaming over to conduct tests on the Enterprise's warp drive engines.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They have completed similar adjustments on two other Starfleet vessels.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I don't understand your concern, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They're not authorised to make any alterations in our engines, and according to Starfleet's report, they will simply test different ways of entering warp speed and different intermix formulas.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What's the harm in that?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(RIKER) It's the specs that Kosinski sent us.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "In my opinion, sir, they're gibberish.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Gibberish?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mister Data, would you explain?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(DATA) Sir, we put Mister Kosinski's specs into the computer and ran a controlled test on them.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "There was no improvement in engine performance.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Then how do you explain Starfleet's report that the same tests on the USS Ajax and on the Fearless over there, resulted in a measurable increase in propulsion.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Our engines are new, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Top condition.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The tests on those older ships may have simply been to straighten out some engine inefficiency.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) Bridge from Transporter Chief.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Two from USS Fearless are ready to beam over.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Stand by for Commander Riker, Chief.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He's on his way.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Aye, sir,", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Since you're concerned about these tests.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Captain, if I may, I'll ask Counsellor Troi to look these visitors over.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Very good.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Welcome aboard, Mister Kosinski.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'm the First Officer, Commander William Riker, and this is", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(KOSINSKI) Where is the Captain, please?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He is engaged in other duties, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "A ship's engines should be a concern of the ship's Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They are, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Which is why they have made the First Officer directly responsible for an engine's condition and performance.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Guided, of course, by one of our Chief Engineers, Lieutenant Commander Argyle in this case.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(ARGYLE) A pleasure, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I appreciated receiving the specs.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "To which you have no end of questions?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Aye, I have.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And you, sir, are listed as", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(TRAVELLER) As Mister Kosinski's assistant.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "My actual name is unpronounceable by humans.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You're from Tau Alpha C. That's very distant.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "All approved and described in the Starfleet communications.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now, I would like to set up in the Engine Room immediately.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll have our Chief Engineer show you to his Engine Room.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No need.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I know my way around starships.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(He barges between Riker and Argyle and out into the corridor, followed by his assistant, then Argyle)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "One thing that Kosinski isn't hiding, his bad disposition.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(TROI) Agreed.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Also, he's arrogant, overbearing, self-important, and very sure of himself and his ability.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "And the other one, his assistant?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "He's the puzzle.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "With most life forms I can usually feel something.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I may not be able to understand or interpret it, but I feel something, if only a presence.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "With him, nothing.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Empty space.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "It's as though he isn't even here.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Something about this concerns me.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I don't know what, I can't point out a reason yet.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Stay concerned, please.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The safety of the Enterprise may be entrusted to these two.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Kosinski sweeps into Engineering as if he owns it, entourage in tow)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Inform the Bridge I shall begin the first test in precisely fifteen minutes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why is that child here?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Wesley is at an ancillary station)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "He's working on a school project.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Before you begin, there are some questions.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "First, tell us how you arrived", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "In order to save myself time, let me ask those questions for you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You received the information which Starfleet provided, you fed it into your computer as precisely as humanly possible, then you did a controlled test.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And then, to your astonishment, nothing happened.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "So you said, what's going on?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This doesn't work.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Kosinski's a fraud.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You see, I have had this conversation on other Starfleet vessels before.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They didn't understand it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "why should you?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Meanwhile, the Traveller is interested in what Wesley is doing, looking over his shoulder)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Surely you're not saying it's unexplainable?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm saying I'm not a teacher, nor do I wish to become one.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I have neither the inclination nor the time.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You have all the time you need.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I don't think you understand.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This has already been approved by Starfleet Command.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But it hasn't been approved by the Chief Engineer or by me.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I didn't know that was necessary.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now you do.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Perhaps I should speak to Captain Picard.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "If you like.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It won't change anything.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "How basic shall I be?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll leave that to you.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(They go over to the big multi-station console in the middle of the room)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Would you get onto the auxiliary panel, please?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Wesley gives up his console to the Traveller)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "In order to save time, my assistant is going to lay in my base formulas more rapidly than any human being possibly could, including even myself.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "So, here then, in the simplest possible terms, what I do.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now, this warp drive system was tuned only in the grossest possible sense, at least according to my standards.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What I do is specific.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thank you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well, sufficient to say for now, these symbols (continues under Wesley and Traveller's whispered conversation)", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Something troubles you with the way this is configured?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "How about it now?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "(WESLEY) Yes.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "But shouldn't these be connected?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Here and here.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Now will it do what Kosinski says it will?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "It has a chance.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "It might work better this way.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Yes.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(The Traveller is impressed with Wesley)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "View with me if you will this screen as we consider the following.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now, is this merely mechanics or is it nature that we deal with in all of this?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And what else than nature are the elementals of space and time?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You are trained in the system.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You go in a straight line, competent, yes, and perhaps even innovative in a minimalist way, but what I do here is not the end of the process, it is the beginning.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "So, what do I do?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Go back to the Fearless, which I left with a more efficient warp drive than I found?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Or do you cast off your ignorance and allow me to continue?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Could anything he's proposing damage our system?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "How could it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's meaningless.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then we should let him try it?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What do you mean, let he him try it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Don't talk about me in the third person like I'm not standing right here!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, we might as well let him try it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Oh yes, we might as well let him try it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You are too generous.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Boy!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Boy, don't play with that.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(WORF) Captain, main Engineering is ready to proceed.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Engineering, this is the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's your call.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do this one just like the last time.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Nothing changes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Commander, I'll make my preliminary adjustments at warp one point five, and complete them as we achieve warp four.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Engineering to bridge, did you copy that?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Affirmative, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Are you ready?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We are.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) La Forge, set in warp one point five.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(LAFORGE) Warp one point five, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Engage.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All right, here we go.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Wesley watches the Traveller make adjustments on his console, while Kosinski taps at him.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The Traveller turns to smile at Wesley)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What are you doing?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Consternation.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The warp core appears to be racing.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The Traveller puts his hands on the console and partially vanishes.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The Enterprise takes off at ridiculous speed)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, we're passing warp ten!", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The viewscreen looks like the opening credits to Doctor Who.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "There's a jolt which throws the Traveller off his stool.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Kosinski is clueless)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What is our velocity?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's off the scale, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Reverse engines.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, no one has ever reversed engines at this velocity.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Because no one has gone this fast.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Reverse engines.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(They arrive at a beautiful place)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "All stop.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Reading all stop, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Position?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Calculating it, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Data, what do you read there?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Malfunction, I trust.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Position, Mister La Forge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well, sir, according to these calculations, we've not only left our own galaxy, but passed through two others, ending up on the far side of Triangulum.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The galaxy known as M Thirty Three.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "That's not possible.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Data, what distance have we travelled?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Two million seven hundred thousand light years.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I can't accept that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You must, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Our comparisons show it to be completely accurate.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "And I calculate that at maximum warp, sir it would take over three hundred years to get home.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain's log, stardate 41263.2.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This will be a rather unusual log entry, assuming Starfleet ever receives it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "As I have already informed my crew, a phenomenal surge of power during a warp speed experiment has sent our starship hurtling out of our own galaxy, past another, taking us over two million seven hundred thousand light years in a few minutes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Message on this has been transmitted to Starfleet, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Which, traveling subspace, they should receive in fifty-one years, ten months nine weeks, sixteen days", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Mister Data!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Kosinski, Riker and Argyle enter)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain Picard, I presume?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We're still trying to determine what happened, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The truth is, Captain, I made a mistake.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A wonderful, incredible mistake.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Just explain what brought us here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "As the power grew, I applied the energy asymptomatically.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I anticipated some tilling, but it didn't occur.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Now that was my error, using the Bessel functions at the beginning.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What is he saying, Number One?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "To tell the truth, sir, it sounds to me like nonsense to me.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "But considering", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Considering where we are, we must assume it isn't.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Can I do something to help?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I can call my mother.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "She's a doctor.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "No, there's nothing she can do.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "I need to rest.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "I've been away too long.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "What happened to you, is it part of what happened to the ship?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Please believe me, I mean no harm to this vessel or those in it.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Is Mister Kosinski like he sounds?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "A joke?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "No, that's too cruel.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "He has sensed some small part of it", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "That space and time and thought aren't the separate things they appear to be?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I just thought the formula you were using said something like that.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Boy, don't ever say that again.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "And especially not at your age in a world that's not ready for such, such dangerous nonsense.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I've always suspected this rate of speed was possible, of course, but at this level?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No, never.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We're going to need new definitions.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "New parameters.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Perhaps you could call it the Kosinski scale.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why not?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, of course.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Since I'm the one who has made the so-called warp barrier meaningless.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And, Captain, this must be a special thrill for you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Thrill?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "As an explorer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "In three centuries of space flight, we've charted just eleven percent of our galaxy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And then we accomplish this.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, but isn't the real point, can you do it again?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Can you get us home?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Of course I can.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'll just do what I did before.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Coming, Riker?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Commander Riker will join you in a moment.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Kosinski leaves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Comment is invited.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Counsellor?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He's convinced he's right.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "I have no doubt of that.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Captain, can you allow a man who has made one mistake back into a position where he may make another?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, what are our options really?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I mean, if this guy can't get us back, who will?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, we're here.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Why not avail ourselves of this opportunity for study?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "There is a giant protostar here in the process of forming.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "No other vessel has been out this far.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Spoken like a true Starfleet graduate.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It is tempting, eh, Number One?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir, it is.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "But as they say, sir, you're the Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I know that if Kosinski can get us home, Starfleet can use his technique to bring back a pure science vessel to do even more.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Number One, tell Kosinski prepare to get us out of here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do you realise how many great advancements of mankind have been tied to speed?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This is a moment in history.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Right here, right now.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And your names will be forever linked with mine.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Excuse me, Commander Riker.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I don't think he did this.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I think", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Not now, Wes.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) Standing by, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But sir, when this all happened, I was watching his assistant", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I'm sure it was fascinating, Wesley.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I am looking forward to hearing about it.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "But not right now.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(to the Traveller) Come on.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He's too tired.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Why don't you do it by yourself?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Yes, why not?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No, I will help.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "As you wish.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I've laid in the reciprocal course back, Captain.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Tell the captain I am ready, First Officer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We're ready for you to engage, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Offscreen) As before", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Begin at warp one point five.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Warp one point five, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This isn't working.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Seen only by Wesley, the Traveller vanishes and reappears again.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Then Riker notices as Enterprise head off at totally ridiculous speed down a StarGate wormhole effect)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All stop.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Answering all stop, Captain.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "According to the instruments, sir, our speed never exceeded warp one point five.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "All stopped, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, but where is this place?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Lights are dancing in a blue scene)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Where none have gone before.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Captain's log, stardate 41263.3.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Instead of returning to our own galaxy, the Enterprise has gone forward to a place in the universe which is uncharted and unknown.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Our present position puts us at over a billion light years from our galaxy.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Data, you have the helm.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll be in Engineering.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Picard leaves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(There is a grunting sound, and Worf turns to see what looks like a hairy pig with a horn and wicked looking spines)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(TASHA) What is it?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "A Klingon Targ!", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "My pet.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "From home, but when I was a child.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You're telling me that's a kitty-cat?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, I suppose you could call it that.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The Targ vanishes, and a ginger cat lands on Tasha's console)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Turbolift]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The turbolift doors open, and Picard nearly steps out - into space rushing by at warp speed.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "He pulls himself back in, the doors close then open again into an ordinary corridor.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "He steps out carefully)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Tasha is fondling the cat)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You darling, what are you doing here?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "[Tunnel]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Then she's back on her home planet, and a rape gang is after her)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Now, run.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "This isn't a safe place at all.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Someone puts a hand on her shoulder)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Tasha jumps)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Tasha, what's wrong?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "You look scared to death.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I was, I was.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "This is crazy.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I was at the colony where I grew up, being chased by a rape gang", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Are you all right?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well, you're safe now.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Elsewhere on board - a group in period costume is playing Mozart Eine Kliene Nachtmusik, except for one Starfleet violinist)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Picard encounters a man and woman running from something)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(CREWMAN) Captain!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain, we need help.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What's wrong?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Don't you see what's following us?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(They carry on running)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Picard passes an open door and sees a ballerina in a golden tutu pirouetting)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Ensign, what are you doing?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Her tutu becomes her uniform again)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(The violinist is sitting at a table, having a meal)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(Picard turns a corner and meets an elderly woman sitting at a tea table)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(MAMAN) (french accent) You look tense, Jean-Luc.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Come and have a cup of tea.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Maman?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll make it good and strong, the way you like it.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We will have a nice long talk.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "This can't be.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You've been", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Dead?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But I'm always with you, you know that.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, I've felt that.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But why now, suddenly.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You mean out here?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "At what you say us the end of the universe?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Or do you see this as the beginning of it?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We believe it the outer rim.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Maman, do you understand these things?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Can you tell me where my ship is?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What is this place?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You were reported headed for", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Just a moment, Number One!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(But the corridor is empty now)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Can I help you, sir?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "No. No, let's help all of us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(goes to comm.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "panel) General quarters.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Red Alert.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Crusher is examining the Traveller, who is lying on a padded bench)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What is it Captain?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why are we at General quarters?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I had to get everyone's attention.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It was the quickest way.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is the Captain.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is not a drill.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It seems that in this place, the world of the physical universe and the world of ideas is somehow intermixed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What we think", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Also becomes a reality.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We must, therefore, I repeat, must begin controlling our thoughts.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) We will give you more on this as our understanding increases.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Enterprise will stay at full alert until the crisis is over.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(to Kosinski) What did you do?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It wasn't him.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It never was.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It was his assistant.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What are you talking about?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Kosinski wasn't the one controlling the warp experiments.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It was me!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The equations he punched in were nonsense, just as we thought.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I honestly thought it was me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I thought somehow, somehow I was operating on his level.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's also my fault, Captain.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I should have realised it wasn't Kosinski.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How could you?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How could any of us?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Wesley did.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "If you knew something, why didn't you say so?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He tried, twice.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I didn't listen.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "He's unconscious.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(CRUSHER) I'm not certain yet.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "He phased, sir.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "What does that mean?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "When we saw it this time, he seemed to be struggling, fighting it.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "He's dying.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "He mustn't.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He's the only one who can get us back.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Realistically, it does not seem possible.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Captain's log, supplemental.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Our position is unknown, and our only chance of returning to the known universe is a dying alien who is generally humanoid but with a physiology sufficiently different from our own to create medical problems in caring for him.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Sickbay]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Crusher is tending the Traveller)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Cause?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I don't know.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "My equipment doesn't register his kind of life signs.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I would have to guess exhaustion, fatigue.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Is he going to be all right?", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "What is the boy doing here?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Mom, he's my friend.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "You may want him here before we're done, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "He seems to have developed some sort of special attachment to the boy.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "My name is Wesley, Commander Riker.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "He knows.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We all know.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Will he live?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm not sure.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Wake him.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I recommend we let him come around in his own time.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "We don't have that luxury.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Wake him.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He could die, and with him any chance we have.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Doctor.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Wes.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We all have other friends aboard this ship, too.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If we stay here much longer we may lose the ability to distinguish between thought and reality.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now, regardless of the risk, wake him.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Crusher gives the Traveller an injection)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Do you recognise me?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm the Captain and I need answers.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll do my best to provide", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Who are you?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Or what?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I am a Traveller.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Traveller?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What is your destination?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Destination?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Yes, what place are you trying to reach?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Ah, place.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "No. There is no specific place I wish to go.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Then what is the purpose of your journey?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Curiosity.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "That's not an answer.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I have certain abilities.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "They give me an understanding of propulsion.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "I've been trading this for passage on Starfleet vessels.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "And allowing Kosinski to take credit for what you did.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It seemed the sensible way.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Until now.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, I seek only transportation in order to see and experience your reality.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "I am no a threat to you, your ship or your crew.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "He isn't, Captain.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I know he isn't.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Our reality?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And in order to satisfy this curiosity, you have brought my ship and my crew into great risk.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I have made some mistakes.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Some mistakes?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What mistakes could possibly explain these incredible explosions of velocity?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I don't know if I can put this in terms you'll understand.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "I believe there may be a warp speed that can get us beyond Galaxy M Thirty Three, but there is no velocity of any magnitude that can possibly bring us wherever this is.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Is it true what our navigation sensors are telling us?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Are we millions of light years away from where we were?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well, yes.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Well, what got us here?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thought.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Thought?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You do understand, don't you that thought is the basis of all reality?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "The energy of thought, to put it in your terms, is very powerful.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "That's not an explanation.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I have the ability to act like a lens which focuses thought.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "That's just so much nonsense.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You're asking us to believe in magic.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Well yes, this could seem like magic to you.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "No. No, it actually makes sense to me.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Only the power of thought could explain what has been happening.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Especially out here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thought is the essence of where you are now.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You do understand the danger, don't you?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Chaos.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What we think is what happens.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It pains me I was so careless, Captain.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "My intent was only to observe, not to cause this.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You should not be here until your far, far distant future.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Certainly not until you have learned control.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You are from a different time, aren't you?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Well, no, not exactly from another time.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Although as you understand the concept, yes, perhaps that term fits as well as any.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "And you have this ability to travel.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "And others of your kind have the same ability?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yes.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Then why, in all of our history, is there no record of you or someone like you ever having visited us?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What wonderful arrogance.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "There is no record because we have not visited you before.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Why not?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Well, up until now, if you'll forgive this, you've been uninteresting.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "It's only now that your life form merits serious attention.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "I'm sorry.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "What's happening?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He's unconscious again.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Revive him.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Whatever you need from him, you'd better get it soon.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "If I may suggest, sir, that first leap out of our galaxy was, as he said, a mistake.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Unless he was distracted by something.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And it weakened him, in some way leading to the incredible leap out here.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Theory!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do you have any facts that fit this?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Can you get us back?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Wait, Captain, not so fast.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We have an opportunity here for scientific discovery.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And we report our observations how?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "To whom?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(to the Traveller) Can you get us back?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I will try.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Number One, take him down to main Engineering.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'll be on the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Captain's right.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "We must hurry.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "But first, I request a moment with the Captain.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Alone.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "(Riker, Kosinski, Crusher and Wesley leave)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Strange how he seems to care for you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He will forget me in time, which is as it should be.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "It's Wesley I wanted to speak to you about.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "The boy?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's best you do not repeat this to the others, especially not to the mother.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Whatever may happen, it is imperative that it proceed naturally.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "I must get my ship back.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do we have time for this?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Oh, yes.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "He and a few like him are why I travel.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You have it in your power to encourage him without interfering.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Encourage him in what?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How shall I explain?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Are you familiar with the intricacies of what is called here music?", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Somewhat.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Such musical genius I saw in one of your ship's libraries.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "One called Mozart, who as a small child wrote astonishing symphonies.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "A genius who made music not only to be heard, but seen and felt beyond the understanding, the ability of others.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Wesley is such a person.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "Not with music, but with the equally lovely intricacies of time, energy, propulsion.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "and the instruments of this vessel which allow all that to be played.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You're right, I must hurry now.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "But you're right in something else.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "He is just a boy for now.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "He should be encouraged, but told none of this.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "[Corridor]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Riker is waiting)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "We're going to get back home.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Take him to Engineering.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'll be on the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(A little way along, a crewman is trapped by flames)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Put it out!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Think!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Put the flame out in your thoughts.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(He eventually does so)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Now, Get to your station and concentrate on your assignment.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Captain's log.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Any time entry is meaningless.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We have no choice but to repeat the same warp experiment.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "but wish one variation.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "A principal part of this warp formula will be the thoughts of everyone aboard the Enterprise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We have no idea exactly how this works.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We understand only that the Traveller makes use of this somehow.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It will be most important that those aboard avoid random thoughts that might change the reality of what we're attempting to do.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do you have any further advice, Counsellor?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "When you begin the attempt, there will be stress, and it's only natural the crew's concentration will shift.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Plus some genuine fear, Captain.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "You can't notice what's happening outside without feeling some of that.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "All decks, all stations.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Various parts of the ship]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) This is the Captain speaking.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "All decks, I must have your full attention.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "In a few moments, as we attempt to warp back home, it is vital, absolutely vital, that you centre your thoughts on your duty or on the welfare of the one called the Traveller.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Think of giving him some of your strength.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now, this is an order.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You must try to do this.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And now, attempt to concentrate completely on your duty of the moment.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Or on the Traveller, on his well being.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Offscreen) Think of him as someone you care deeply about.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All decks, all stations.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Battle stations.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I will need Kosinski back on the main computer.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "You need me?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 0.5, g = 0.3, b = 1.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Helm, set in warp one point five, retroactive course.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Warp one point five, retroactive two six one mark three one, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Bridge to Engineering.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Stand by.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I feel such an abundance of well being on the ship.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "It feels like, quite wonderful.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "(Offscreen) Engineering to Bridge, we're ready.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "On my order, Mister Data, Mister La Forge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Engage.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The Enterprise whooshes off into streaks of light)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "It's not happening.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's not enough.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Down in Engineering, Wesley senses something is wrong, and squeezes the Traveller's hand.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Then the Traveller has a go at phasing.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Enterprise disappears in a crack of light, and the Traveller does not rematerialise.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The ship is back in what looks like normal space)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Warp one point five, sir, which is what my instruments have read all along.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "And our position reads exactly what it was before this sleigh ride began, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Cease Red Alert.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sleigh ride?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Or whatever you want to call it, Data.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I don't have a proper name for it.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Riker enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The Traveller's gone, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Gone?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He's phased completely out of existence.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "At least, out of our existence.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Attention all decks.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is to inform you that with your support the Traveller has returned us to our galaxy.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "However, he has now left us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Wherever he has gone, we wish him well.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Have the boy sent to the Bridge, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Wesley Crusher, report to the Bridge, on the double.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Our next assignment is on this heading?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Mister Data, Mister La Forge, increase to warp five.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Same heading.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Warp five, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "On that same heading, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The turbolift doors open)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Ah, Wesley.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Come on the Bridge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Move!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Commander Riker has told me how supportive you were in Engineering.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well done.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "At ease.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sit here in Command.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, it's not allowed.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Your orders.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Oh, that's true.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well, I can't waive them again.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Only commissioned officers.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's quite all right, sir, I understand.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Please don't interrupt me, Wesley.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm sorry, sir.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "Any commissioned rank?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Even ensign?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "That would give him authorised access to the Bridge.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Captain's log, stardate 41263.4.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "For outstanding performance in the best of Starfleet tradition, Wesley Crusher is made Acting Ensign, with the duties and privileges of that rank.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And whether that rank becomes permanent, Mister Crusher, depends on you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "At the earliest opportunity, your entrance application for Starfleet Academy will be tendered.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Until then, you will learn this ship.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Every operation, every function.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Commander Riker, a duty schedule for Mister Crusher, heavy on study.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Meanwhile, you can sit here and learn something.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Wesley perches on the end of the arch supporting the command seats)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Sir, shall I send for Doctor Crusher?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Why?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Is someone ill?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Or would you rather tell her about this, Wes?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If you don't mind, sir, I'd like to sit here awhile.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "I'll tell her later.", r = 0.7, g = 0.45, b = 0.2, codes = "BOR-1" },
		{ text = "[img=music]  End Credits [img=music]", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,STS-0.4,UHP-3.0" },
