	spawning = 0,
	category = "Retail-VHS",
	lines = {
		{ text = "(PICARD) Captain's log, stardate 41386.4.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We are in pursuit of a starship of Ferengi design.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Our mission is to intercept and recover a T9 energy converter which the Ferengi stole from an unmanned monitor post on Gamma Tauri Four.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "A theft which automatic scanners recorded, providing us with the long awaited opportunity to make close contact with a Ferengi vessel.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If we succeed in this chase, it will be Starfleet's first look at a life form which, discounting rumour, we know almost nothing about.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(RIKER) There she is.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Enlarge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What is their course?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(LAFORGE) They are now angling through that solar system, Captain.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Identify.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(DATA) Listed as Delphi Ardu, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Eleven planets, unexplored.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Stay with them, La Forge.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(TASHA) Sensors read a power surge from their last manoeuvre, Captain.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "They are dropping to subwarp speed.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Reduce to impulse power.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sensor record fluctuations in the energy readings from it, Captain.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Possibility, a malfunction in their engines, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Breaks my heart.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(WORF) In visual range, Captain.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Fully enlarge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The Ferengi ship is nicely curved)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Hello, stranger.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Very impressive design.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Anything on that design, Data?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Nothing specific, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "As you know, Ferengi technology is estimated to be generally equal to our own.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "But that does not mean identical, however.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Correct, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "We are no doubt advanced in some areas, they in others.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Showing another power surge, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "They are firing on us.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Damage report?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Shields holding.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Mostly electromagnetic, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Fusion generator and batteries down by thirty percent.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Our impulse engines are surging now.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "They're firing again.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(A bright, bright flash on the viewscreen)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Deflector shield power weakening, Captain.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Phasers ready.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Photon torpedoes ready.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Do we return their fire, sir?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Negative, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They're just reacting to our close pursuit.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Fall back a bit but stay with them.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They are slowing too, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "They may be turning to fight.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Open hailing freq.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(puzzled) Why are we gaining on them?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Don't anticipate.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm not, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Something's wrong.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir, something is dragging us forward.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I read that, too.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant Yar, what do your sensors show?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Not certain, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I'm getting very confusing readings.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, this shouldn't be.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Our ship's power systems are failing.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Deflector shield failing.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Phasers going inoperative, Captain.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, something is completely immobilising us.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Immobilised by the damn Ferengi.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We need more information.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "What the hell are they are using?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Obviously we've underestimated their technology, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Considerably.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It appears the Ferengi have us right where they want us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "In their sights.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Are you searching all frequencies?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Any sign of any kind of messages?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Negative, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Weapons report, Lieutenant.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I have phasers and torpedoes armed, but I show insufficient power to fire them.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Engineering, give me status on power recovery.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Engineering, come in.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Why aren't they answering?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "La Forge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Get me a full report from Engineering.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(La Forge leaves)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "It's impossible they could be draining all power from all systems.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Counsellor?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(TROI) I'm sensing nothing from them Captain.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Which could mean they can shield their thoughts and emotions from others.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "It still makes it our move.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir, they may know as little about us as we do about them.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Except that they know they've got us in deep trouble.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "If so, the question becomes how will they use that knowledge?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Data, do you have any information touching this on any file?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "None, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Only hearsay and third hand reports, most of which conflict.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Which reports do not conflict?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "That the Ferengi are, well, the best description may be traders.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "What kind of traders?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "A comparison modern scholars have drawn from Earth history likens the Ferengi to the ocean-going Yankee traders of eighteenth and nineteenth century America, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "From the history of my forebears.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Yankee traders.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Who in this case sail the galaxy in search of mercantile and territorial opportunity.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "And are those scholars saying the Ferengi may not unlike us?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Hardly, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I believe this analogy refers to the worst quality of capitalists.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The Ferengi are believed to conduct their affairs of commerce on the ancient principle caveat emptor.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Let the buyer beware.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Yankee traders.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I like the sound of that.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Well, sir, I doubt they wear red, white and blue, or look anything like Uncle Sam.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Engineering?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Join La Forge down below in Engineering, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Without warp capacity, we're just a floating target.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Uncle who?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "What have bright, primary colours got to do with it?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I understand the allusion.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Colours representing countries at a time when they competed with each other.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Red, white and blue for the United States.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Whereas the French more properly used the same colours in the order of blue, white and red.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And the German nation red, black and gold.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The Italians green, white and red.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The British", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "That's enough, Data.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It was you who", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "We're discussing the Ferengi.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I wish I had some clue as to what they will do next.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Given what is now happening to our ship, sir, their weapons could be vastly superior to ours.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Yes, Data, that is a natural assumption.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Engineering?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Engineering]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Bottom line, La Forge.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's not good, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The Ferengi forcefield that holds this ship compensates almost as fast as we can increase power.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Almost?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Well, there's a point three hundred seventy two millisecond delay between use of our power and the neutralising counter force of the Ferengi.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "See, we push and they push back in equal force, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "What's our acceleration delay between slow-reverse impulse and top warp speed?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "That's point three-hundred milliseconds.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "There's (realisation) Ah, I see where you're going.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We shift down and then kick hard into warp nine.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yeah!", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Come back fighting!", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "whooey!", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Can we do it, Geordi?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Ask me after it's done, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(to engineer) I want a slow reverse into drop off over five minutes.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We'll show them what this baby can do, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Give me everything you've got.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Aye, aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "In a sudden, abrupt power surge to high warp speed, Captain, we may be able to break loose from that Ferengi forcefield.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I wonder what they're thinking over there.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They're wondering what we're going to do next.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I say fight, sir.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "There's nothing shameful in falling before a superior enemy.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(La Forge enters)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "And nothing shameful in a strategic retreat, either.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "All systems ready, Captain.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "And communication now restored to Engineering.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Excellent, La Forge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Now let's, er, let's throw them off guard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant Yar.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Open hailing frequencies.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hailing frequencies open, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "At least we won't begin with weakness.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Attention Ferengi starship!", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is Captain Jean-Luc Picard of the USS Enterprise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "In the name of the United Federation of Planets, I demand you return the T9 energy converter you removed from Gamma Tauri Four.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Send that in all language forms.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "All language forms, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Sometimes, Riker, the best way to win a fight is not to be there.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "He will triumph who knows when to fight and when not to fight.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Glad the Academy's still teaches the strategies of Sun Tzu.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This delay had better prove out, LaForge.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Point three hundred milliseconds, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Let's blast full power into warp nine.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Ready?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "On your command, Captain.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "On a count of three.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Stand by on phasers.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "One, set warp to nine.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Two, divert shield power to the main engines.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Three.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Everything vibrates, but they don't leave)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Merde.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Shields up.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain, I think you had better see this.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "What's wrong?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Someone is reading every file, every bit of information stored in the Enterprise memory banks.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "They can do that?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "And more, perhaps.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Captain, if I may recommend?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "With our attention on the Ferengi vessel, we have ignored the planet.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Data, consult the charts on this planet.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "See what we've got on it.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Conference evaluation.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Observation lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "So, while we still have some power left, it is time for difficult decisions.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Opinions please.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I say put all available power into a full-out combined phaser and photon torpedo salvo.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Destroy their ability to sustain this forcefield, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Yes!", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hit them hard and hit them fast.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Impractical and provocative.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Even assuming that we have the power to sustain such a tactic.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But Captain, isn't firing on us an act of war?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "The facts are the Ferengis did fire at us, but we were chasing them.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Since then, all they've done is searched our computers, trying to learn who and what we are.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "Your point, Counsellor?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Let's talk to them.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "It's been tried.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "No response.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But did we tell them anything they wanted to hear?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "La Forge?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Other opinions?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you for your advice.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The staff start to leave)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Will?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I haven't had your assessment.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I believe we've covered every available alternatives, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Are there other options that you want to analyse?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The only one remaining is the one that needs no conversation.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The only one we must avoid.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Sir?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The one that leads to total annihilation.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Open hailing frequencies.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Open, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Attention, Ferengi starship.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This is Captain Picard.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It is obvious that we are in a situation here which needs resolving, and we are willing to do whatever is required, whatever is necessary.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I would like, I would request, that you present your terms to us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Captain's log, stardate 41386.5.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It is with a heavy heart that I have offered to meet whatever reasonable and necessary terms are demanded by the Ferengi.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I fear for my people and my vessel in the event the unknown Ferengi ask the unreasonable.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "How can I oppose even unreasonable demands?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's moving, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We ask to surrender and now they fire on us?", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Ready torpedoes, sir?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "We have enough power for a few of them.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Ready, but hold on ready.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Open hailing frequencies.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hailing frequencies open, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Ferengi vessel, we are awaiting your response.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Suggest first strike, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Our shields now only have sixty three seconds of power left before lowering.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "If the Ferengi ship's sensors can read us, sir, they will be waiting until we are most vulnerable.", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I'm afraid I agree, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "They have not responded.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We'll wait a bit more, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Fifty seconds.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Patience.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Patience.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) The quicker we can come to terms, the better, NCC 1701-D.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Who is speaking?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I am Tarr.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "DaiMon of the Ferengi.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You wish to discuss surrender, Captain Picard?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What I said, DaiMon Tarr, is", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Unconditional surrender, I warn you, is totally unacceptable!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We will die to the last one of us before such dishonour!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Hailing frequencies closed, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Something has seized their ship too.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They're in the same predicament as us.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(to Geordi) Launch a sensor probe.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Set it to search for the source of whatever this something is that is holding both ships.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Go back to hailing frequency, fast.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And I regret, DaiMon Tarr, that I can no longer negotiate unless it is conducted visually.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) Your complete message not received, Enterprise, but visual communication is against our custom.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And it is against Starfleet orders to accept a surrender otherwise.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do you withdraw your surrender?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Offscreen) You give us no choice, Enterprise.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "May we have a visual on you as well?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Agreed, DaiMon Tarr.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "You should be able to view us now.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Are we on your screen?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The face of a humanoid with enormous ears fills the viewscreen.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "He even has crooked pointed teeth)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(On viewscreen) Yes.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The ugliness of the human was not an exaggeration.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I do not know how your twisted alien culture has paralysed our vessel, but I concede your Enterprise is superior.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We will return your worthless T9 device and we offer the life of our second officers as required by the Ferengi code.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(sotto voce to Geordi) Fortunately, Starfleet has no such rules involving our second officers.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Is this to your satisfaction, Picard Captain?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Your offer may be inadequate, but I will discuss it with my staff.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Stand by for further communications.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Observation lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(A couple of kids are playing by the Enterprise tableau)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Matthew!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Pola!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You know this area is off limits.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Come on, come on.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(they scurry out as Picard enters) Boys will be boys, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Lieutenant, have you launched the probe?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Aye, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We should getting those readings soon.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Well, if we're not holding the Ferengi and they're not holding us, who the hell is?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The probe will give us some of those answers, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Data has found a Chinese finger puzzle that the children have left behind)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Data, you were going to show us something.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "As requested, Captain, library computer information on this planet.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It has been charted only from long range scans.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It is Class M, but shows no indications of life forms, sentient or otherwise.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "However, you may find this of interest.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Resolving it into our language.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(a holographic display over the table of the Tkon Empire data entry)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The centre of a huge space federation, a population of trillions.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Trillions?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I've never heard the word Tkon before.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Understandable.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It has been extinct six hundred thousand of our (his forefingers are stuck in the puzzle) of our years.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "These planets were once outposts of that empire.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Data, what are you doing?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Apologies, Captain.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I seem to have reached an odd functional impasse.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I am stuck.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Then get unstuck and continue with the briefing.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Yes, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "That is what I am trying to do, sir, but the solution eludes me.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "My hero.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Picard reaches across, pushes his fingers together and removes the puzzle)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Continue, Commander.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Intriguing.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "It describes the Empire as being highly advanced and powerful, and capable of actually moving stars.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Stars whose planets are their defence system?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Correct, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Outposts.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "The planet below was possibly one of them.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Offscreen) Excuse the interruption, Captain, but this may be worth it.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "We're now receiving a signal from the probe.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "We'll take it here.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The display changes to the planet with streams of energy heading out to both ships)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Incredible!", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "There's our mysterious something, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It is a forcefield of some kind.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Reaching up from the planet surface.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Amazing power.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Data, what does the legend say about the end of the Tkon Empire?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Their sun went supernova, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Could this planet have escaped that?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "This planet may have been the most distant outpost, sir, but it shows no life form readings.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "We should take a look at it, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Stand by with an away team.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And if the Ferengi also realise the forcefield emanates from the planet, sir?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "That's a complication.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Maybe we should ask them to join us in this.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Team up with the Ferengi, sir?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "We've been ordered to learn all we can about them.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Do you know a better way?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Data.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Geordi throws the finger puzzle to him)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain's log, supplemental.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "In orbit of a mysterious planet, whose unexplained forcefield has seized us with a power almost beyond imagination.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If there is a solution to this, it almost certainly will involve cooperation from the Ferengi.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Ship's power drain is critical, Captain.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I must now shut down our shields to maintain life support systems.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Understood.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Open hailing frequencies.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hailing frequencies open, sir.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Ferengi vessel from Enterprise, come in.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Your alien images again shock us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "With regard to the surrender, DaiMon Tarr.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "The facts are somewhat different.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Our own probe has already shown us your trickery, human.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We withdraw our surrender.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Agreed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Perhaps you would agree to a more productive relationship?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) I prefer a more profitable one, human.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yankee trader.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(On viewscreen) Explain what means yankee traders?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He heard that.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "He recognises your interest in profit.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Or is that an incorrect assumption?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) We seek only what is equitable.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What do you seek?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why did you begin by attacking us?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We did not attack you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We gave chase in order to recover a Federation-owned energy device which", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Which we know is ours.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Your barbarous Federation placed it on one of our planets!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "On the contrary, Gamma Tauri Four is recognised by all civilised members of", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) The Ferengi are not uncivilised, human!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Are you suggesting otherwise?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All I'm saying is that you removed something which clearly did not belong to you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Are you now calling us thieves?!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "On the contrary, I have no wish to discuss issues of property or of territory, when our mutual problem remains.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Like it or not, we are both trapped by this energy draining forcefield from the planet.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) On that matter there can be no argument.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I would like to propose a swap.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) And what is a swap?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's a trade, an exchange.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Yes, trade, human.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What do you offer?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We will tell you what we know about the planet, and in trade, you will give us your information.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Ridiculous.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How will we know you will not withhold information?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We must trust each other, Tarr.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) Amusing, human.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Trust each other?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I propose that we test this relationship by cooperating in a joint exploration of the planet surface.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) And what profit is in this, Picard Captain?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The profit, DaiMon Tarr, of saving all our lives.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) We will agree, no doubt foolishly.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But you are warned that any further trickery on your part will be met with no mercy.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Agreed.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "If you care to join us, we have a well-proven transporter device", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(On viewscreen) We have a matter-energy device of our own.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We will beam a science team of three to whatever co-ordinates you propose.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Excellent.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "We will transmit the information shortly.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Enterprise out.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Hailing frequencies off.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I would log that agreement as promising, at least.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I wonder, Captain.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'm not usually one for distrust at first sight, but this may be an exception.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Especially in view of the fact the image he transmitted, sir, was somehow distorted.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I sensed the same thing, Captain.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "He's hiding something.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "With this power drain we can't delay.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Now, I'd like some additional help in case we run into trouble.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Can you spare Worf?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Take him.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "And be careful, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Transporter room]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "With this power drain, we may have trouble communicating with the Enterprise, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Understood.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Anything else?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Due to this forcefield, there is presently no way to beam us back, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "You had to ask.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Understood.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Energise.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(It's a barren place with interesting crystalline structures here and there, and lightning flashes almost continuously.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Riker appears alone)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Tasha?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Data?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Geordi?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Worf?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Anybody?!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(He goes searching for the others)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Data?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(He finds him standing on one of the crystalline outcrops)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What are you doing up there?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Most intriguing, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I assume a problem of inaccurately transmitted programme coordinates due to the force field around the ship, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Are we alone, sir?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Unfortunately.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Let's find the others.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What do you make of these?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Crystalline.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Mostly inert.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Nothing to write home about.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Excuse me?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Slang, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "I did use it correctly, did I not?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "They've got to be around here somewhere.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(They carry on searching)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What's that?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What?", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "That!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Geordi!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Geordi is hanging upside down from the only tree in the area)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Are you conscious?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Do I look conscious?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "What are you doing?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I'm resting, sir.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "My foot's stuck.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Up there.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I materialized upside down above the planet surface.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Tricorder's useless, sir.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Communication's gone too.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Fortunately you did not break any", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Data, who's that?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(The Ferengis are watching them.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Little crouching men with whips in their hands)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Who are you?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(The Ferengi let fly energy bolts from their whips, knocking the three men out.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Actually, it takes two jolts to knock out Riker)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Captain's log, supplemental.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "It is now six hours since our away team beamed down to the planet surface.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "On the Enterprise, our condition is rapidly worsening.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Ordinarily, with reserve power alone, we could maintain life support for several months.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "But the force holding has closed down all engines and is draining our reserve power too.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Recreation lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Blankets are being handed out)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "It's getting much, much colder, sir.", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "How far down is it likely to go?", r = 0.5, g = 0.45, b = 1.0, codes = "BOR-1" },
		{ text = "A lot.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Even in orbital space it'll get below minus two hundred degrees.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(CRUSHER) We won't have to worry much past minus seventy or so.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Feels like that right now.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Good.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(Inquisitive fingers remove Geordi's comm.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "badge.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Worf is dragged in to join his colleagues)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(LETEK) If he moves, kill him.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(MORDOC) Letek, has what we've done broken our agreement with their captain?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We will accuse the humans of preparing to ambush us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It will be our word against theirs and who is more trustworthy, Mordoc?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(the comm.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "badge) It looks like gold.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Tastes like gold.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It is gold.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Renewed thunder and lightning makes the Ferengi try to cover their ears)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Accursed planet!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "It's nothing compared to what our Captain will do when he finds out you've broken our landing party agreement.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What part of the agreement?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You appeared and attacked us.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Are you one of their assassins?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm Commander William Riker, First Officer of the USS Enterprise.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You have a lot to learn.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Work pounces on two of the Ferengi, Data and Riker join in the melee.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "The little aliens fight dirty)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "I've got this one!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(holding one aloft) Careful, Commander, they're much stronger than", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "(Riker gets thumped, hard)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Pygmy cretins!", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Kill them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Hideous monsters!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No!", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(they stop) Good, you've heard of phasers.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Now, over there.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Is this a female?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(KAYRON) A human female, Letek?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, it is true.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You work with females, arm them, and force them to wear clothing.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Sickening.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Recreation lounge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I've diverted the reserve power to the family decks.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "They'll last longest.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Is there anything else we can do, Doctor?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Where's Wesley?", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "He's in our quarters.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I was tempted to give him a sedative.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "You shouldn't.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "I know, but he's my son.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I love him.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "He has the right to meet death awake.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Is that a male perspective?", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "Rubbish", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The Ferengi are dodging about like a live 'find the lady' game)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Hold it right there!", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Stop.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Stop!", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Phasers on stun.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Already set.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(She fires, but the beam is deflected into a crystalline structure)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1,AIM+1.0" },
		{ text = "What's going on?", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "(Letek fires his whip, and that whirls around before being sucked into the crystals.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "His whip does not coil again)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "What is this?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I believe we can deduce that these crystalline tree shapes are actually energy collectors.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Along the same principle as that which is draining energy from our ships.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "But the Ferengi weapons were working when we first arrived.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Which means something new has happened.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "I'm finally beginning to understand what my eyes have been showing me.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "It's patterns of force everywhere.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "This entire planet has been turned into a power accumulator.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "And these?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It's the whole point of this place.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "I can see the planet's power emanating from.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Commander.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "(There's an impressive display of controlled energy, which resolves to a vague face)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "(PORTAL) Be you barbarians?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Speak!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The Ferengi clutch their heads in pain)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Who meets the challenge?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Who will it be?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(points at Riker) Him.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You have awakened Portal six three.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do you petition to enter the Empire?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Who asks the question?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "A guardian of the Tkon Empire.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The energy turns into a humanoid figure in a long hooded cloak, leaning on a staff)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Biped.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Excellent.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why should we petition to entry to an empire that no longer exists?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Why do you attempt deception?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The Empire is forever.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Your empire fell prey to a supernova.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "We are forever.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Data, Please repeat the file.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It is a matter of record, Portal.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "In the Age of Makto, the central star of the Tkon Empire destabilised and", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "There has never been an Age of Makto.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "In fact, there have been many ages which have come and passed since Makto.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "This is the Age of Bastu!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I'm afraid not.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "According to the Tkon use of galactic motionary startime charts, after Bastu came Cimi, Xora, Makto", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Enough!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Absurdities!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You must have been asleep, Portal, for hundreds of thousands of our years.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Deception is the way of the human.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We Ferengi will gladly make a petition.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We invite your petition.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Speak.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We wish to offer our services to serve the Tkon Empire at no profit.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Return control of our starship to us and we will happily destroy the human criminals and their vessel which attacked us without provocation.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They came to loot your empire, but we intercepted them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You are accused of deceit and treachery.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Do you give yourself up for judgement?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "If you believe those accusations, then you certainly should act on them.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And there is even more.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We can prove that the humans are destroyers of legal commerce, and that they selfishly withhold vital technology from backward worlds.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And necessary defensive weapons, too.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We Ferengi now challenge this human madness.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I admit we withheld modern technology from some worlds.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You see?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They are demented.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Their values are insane.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You cannot believe the business opportunities they have destroyed.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Proof of their barbarism.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They adorn themselves with gold, a despicable use of a valuable metal.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And they shamelessly clothe their females.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Inviting others to unclothe them.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The very depth of perversion.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Paws off, Ferengi.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "No female, human or Ferengi, can order Mordoc around!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Submit!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Just try it, shorty.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "At ease, Lieutenant!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And we still have more faults", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "They should add that Starfleet has permitted several civilisations to fall.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "We have at times allowed the strong and violent to overcome the weak.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "They admit their crimes!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Hear them!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "They admit the evil that they do!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Barbarian!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Yes, you show wisdom.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Barbarians all!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But this one first, in the fashion the Empire has always challenged savages.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "No!", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "For battle, come to me!", r = 0.75, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "No!", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "That's an order.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You have a single chance for life.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "One only.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What is the answer to my challenge?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I offer a thought.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "He will triumph who knows when to fight and when not to fight.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You are being tested, Riker!", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What is the answer?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "How do you know my name?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Portal whirls his staff, bringing it to a sudden stop by Riker's right ear.", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "He doesn't flinch)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Ahhh.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "You are facing fate with composure.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But what is the answer to my challenge?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Fear is the true enemy, the only enemy.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "(Portal lowers his staff, and becomes friendly and chatty)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Unlike these little ones who close their minds, your mind holds interesting thoughts.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Know your enemy and know yourself, and you will always be victorious.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Why that thought?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "And who is this Sun Tzu you revere?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "An old Chinese philosopher from ancient Earth history.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "You must tell me more of this wisdom, so much like our own.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Gladly.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "But first our starship.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Can you release it?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "It is released.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "All power is restored.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(The ship whirrs back into life, and people start to open their eyes)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "Not a moment too soon, Jean.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "I mean, Captain.", r = 0.0, g = 0.9, b = 1.0, codes = "BOR-1" },
		{ text = "[Planet surface]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I admit I had my doubts as your ships battled each other in overt acts of belligerence.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Normally, I would have destroyed all of you and your ships, but something confused me.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Which was?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "First, you wanted to murder each other.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then you were willing to help each other.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What of our vessel?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We knew the same answer.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "The truth is, I gave those words to this human.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "What of them?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Shall I destroy them?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Then they would learn nothing.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "A most interesting conclusion.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But what if they will never learn, Riker?", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Is this a test also?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "In life, one is always tested.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I find them very much as we were a several hundred years ago, but possessing the technology they have now, they're very dangerous .", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Untrue.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "We seek friendship with you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "But we can hardly hate what we once were.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "They may grow and learn.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "And learn ways to destroy you.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Our values require us to face that possibility.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "What of you, with your empire gone?", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The universe exists to me to create life.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "I shall sleep, until needed again.", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "(He walks away, becomes more stooped, then vanishes)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "[Bridge]", r = 0.3, g = 0.75, b = 0.85, codes = "BOR-1" },
		{ text = "Compliments, Lieutenant.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Thank you.", r = 0.7, g = 0.45, b = 0.0, codes = "BOR-1" },
		{ text = "Lieutenant Worf.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "(Geordi and Data enter)", r = 0.15, g = 0.85, b = 0.6, codes = "BOR-1" },
		{ text = "You've done well.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "Something to write home about.", r = 1.0, g = 1.0, b = 0.69, codes = "BOR-1" },
		{ text = "Something to write home about?", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "Data, that's very human.", r = 0.95, g = 1.0, b = 0.0, codes = "BOR-1" },
		{ text = "The T9 energy converter has been beamed aboard and secured, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "The Ferengi complained bitterly, but one final roar from Portal convinced them.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "I commend your performance, Number One.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
		{ text = "One final request, sir.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Permission to beam a box of Data's Chinese finger puzzles over to the Ferengi.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "A thank you for all they tried to do.", r = 0.95, g = 0.2, b = 0.1, codes = "BOR-1" },
		{ text = "Make it so.", r = 1.0, g = 0.0, b = 0.0, codes = "BOR-1" },
